			} else if root.nodeType != test._type {
				t.Errorf("Error on Unmarshal(%s): wrong type", test.name)
			} else if !bytes.Equal(root.source(), test.value) {
				t.Errorf("Error on Unmarshal(%s): %s != %s", test.name, root.source(), test.value)
			}
		})
	}
}

