// # Gno forms

// gno-forms is a package which demonstrates a form editing and sharing application in gno

// ## Features
// - **Form Creation**: Create new forms with specified titles, descriptions, and fields.
// - **Form Submission**: Submit answers to forms.
// - **Form Retrieval**: Retrieve existing forms and their submissions.
// - **Form Deadline**: Set a precise time range during which a form can be interacted with.

// ## Field Types
// The system supports the following field types:

// | type         | example                                                                                         |
// |--------------|-------------------------------------------------------------------------------------------------|
// | string       | `{"label": "Name", "fieldType": "string", "required": true}`                                    |
// | number       | `{"label": "Age", "fieldType": "number", "required": true}`                                     |
// | boolean      | `{"label": "Is Student?", "fieldType": "boolean", "required": false}`                           |
// | choice       | `{"label": "Favorite Food", "fieldType": "['Pizza', 'Schnitzel', 'Burger']", "required": true}` |
// | multi-choice | `{"label": "Hobbies", "fieldType": "{'Reading', 'Swimming', 'Gaming'}", "required": false}`     |

// ## Web-app

// The external repo where the initial development took place and where you can find the frontend is [here](https://github.com/agherasie/gno-forms).
package forms

package forms

import (
	"std"
	"time"

	"gno.land/p/nt/seqid"
)

// FieldType examples :
// - string: "string";
// - number: "number";
// - boolean: "boolean";
// - choice: "['Pizza', 'Schnitzel', 'Burger']";
// - multi-choice: "{'Pizza', 'Schnitzel', 'Burger'}";
type Field struct {
	Label     string
	FieldType string
	Required  bool
}

type Form struct {
	ID          string
	Owner       std.Address
	Title       string
	Description string
	Fields      []Field
	CreatedAt   time.Time
	openAt      time.Time
	closeAt     time.Time
}

// Answers example :
// - ["Alex", 21, true, 0, [0, 1]]
type Submission struct {
	FormID      string
	Author      std.Address
	Answers     string // json
	SubmittedAt time.Time
}

type FormDB struct {
	Forms     []*Form
	Answers   []*Submission
	IDCounter seqid.ID
}

func NewDB() *FormDB {
	return &FormDB{
		Forms:   make([]*Form, 0),
		Answers: make([]*Submission, 0),
	}
}

// This function checks if the form is open by verifying the given dates
// - If a form doesn't have any dates, it's considered open
// - If a form has only an open date, it's considered open if the open date is in the past
// - If a form has only a close date, it's considered open if the close date is in the future
// - If a form has both open and close dates, it's considered open if the current date is between the open and close dates
func (form *Form) IsOpen() bool {
	openAt, errOpen := form.OpenAt()
	closedAt, errClose := form.CloseAt()

	noOpenDate := errOpen != nil
	noCloseDate := errClose != nil

	if noOpenDate && noCloseDate {
		return true
	}

	if noOpenDate && !noCloseDate {
		return time.Now().Before(closedAt)
	}

	if !noOpenDate && noCloseDate {
		return time.Now().After(openAt)
	}

	now := time.Now()
	return now.After(openAt) && now.Before(closedAt)
}

// OpenAt returns the open date of the form if it exists
func (form *Form) OpenAt() (time.Time, error) {
	if form.openAt.IsZero() {
		return time.Time{}, errNoOpenDate
	}

	return form.openAt, nil
}

// CloseAt returns the close date of the form if it exists
func (form *Form) CloseAt() (time.Time, error) {
	if form.closeAt.IsZero() {
		return time.Time{}, errNoCloseDate
	}

	return form.closeAt, nil
}

// GetForm returns a form by its ID if it exists
func (db *FormDB) GetForm(id string) (*Form, error) {
	for _, form := range db.Forms {
		if form.ID == id {
			return form, nil
		}
	}
	return nil, errFormNotFound
}

// GetAnswer returns an answer by its form - and author ids if it exists
func (db *FormDB) GetAnswer(formID string, author std.Address) (*Submission, error) {
	for _, answer := range db.Answers {
		if answer.FormID == formID && answer.Author.String() == author.String() {
			return answer, nil
		}
	}
	return nil, errAnswerNotFound
}

// GetSubmissionsByFormID returns a list containing the existing form submissions by the form ID
func (db *FormDB) GetSubmissionsByFormID(formID string) []*Submission {
	submissions := make([]*Submission, 0)

	for _, answer := range db.Answers {
		if answer.FormID == formID {
			submissions = append(submissions, answer)
		}
	}

	return submissions
}

package forms

import (
	"strings"

	"gno.land/p/onbloc/json"
)

type FormNodeBuilder struct {
	*json.NodeBuilder
}

type FormArrayBuilder struct {
	*json.ArrayBuilder
}

func (b *FormNodeBuilder) WriteArray(key string, fn func(*FormArrayBuilder)) *FormNodeBuilder {
	b.NodeBuilder.WriteArray(key, func(ab *json.ArrayBuilder) {
		fn(&FormArrayBuilder{ab})
	})
	return b
}

func (b *FormNodeBuilder) WriteObject(key string, fn func(*FormNodeBuilder)) *FormNodeBuilder {
	b.NodeBuilder.WriteObject(key, func(nb *json.NodeBuilder) {
		fn(&FormNodeBuilder{nb})
	})
	return b
}

func (b *FormArrayBuilder) WriteObject(fn func(*FormNodeBuilder)) *FormArrayBuilder {
	b.ArrayBuilder.WriteObject(func(nb *json.NodeBuilder) {
		fn(&FormNodeBuilder{nb})
	})
	return b
}

func (b *FormNodeBuilder) WriteFormFields(key string, fields []Field) *FormNodeBuilder {
	b.WriteArray(key, func(builder *FormArrayBuilder) {
		for _, field := range fields {
			builder.WriteObject(func(builder *FormNodeBuilder) {
				builder.WriteString("label", field.Label).
					WriteString("fieldType", field.FieldType).
					WriteBool("required", field.Required)
			})
		}
	})
	return b
}

func (b *FormNodeBuilder) WriteFormSubmission(key string, submission *Submission) *FormNodeBuilder {
	b.WriteObject(key, func(builder *FormNodeBuilder) {
		builder.WriteString("submittedAt", submission.SubmittedAt.Format("2006-01-02 15:04:05")).
			WriteString("answers", strings.ReplaceAll(submission.Answers, "\"", "'"))
	})
	return b
}

func (b *FormNodeBuilder) WriteForm(key string, value *Form) *FormNodeBuilder {
	b.WriteString("id", value.ID).
		WriteString("owner", value.Owner.String()).
		WriteString("title", value.Title).
		WriteString("description", value.Description).
		WriteString("createdAt", value.CreatedAt.Format("2006-01-02 15:04:05"))
	b.WriteFormFields("fields", value.Fields)
	return b
}

func (b *FormArrayBuilder) WriteForm(key string, value *Form) *FormArrayBuilder {
	b.WriteObject(func(builder *FormNodeBuilder) {
		builder.WriteString("id", value.ID).
			WriteString("owner", value.Owner.String()).
			WriteString("title", value.Title).
			WriteString("description", value.Description).
			WriteString("createdAt", value.CreatedAt.Format("2006-01-02 15:04:05"))
		builder.WriteFormFields("fields", value.Fields)
	})
	return b
}

package forms

import (
	"std"
	"time"
)

// This function allows to submit a form
func (db *FormDB) SubmitForm(formID string, answers string) {
	// Check if form exists
	form, err := db.GetForm(formID)
	if err != nil {
		panic(err)
	}

	// Check if form was already submitted by this user
	previousAnswer, err := db.GetAnswer(formID, std.CurrentRealm().Address())
	if previousAnswer != nil {
		panic(errAlreadySubmitted)
	}

	// Check time restrictions
	if !form.IsOpen() {
		panic(errFormClosed)
	}

	// Check if answers are formatted correctly
	if ValidateAnswers(answers, form.Fields) == false {
		panic(errInvalidAnswers)
	}

	// Save answers
	answer := Submission{
		FormID:      formID,
		Answers:     answers,
		Author:      std.CurrentRealm().Address(),
		SubmittedAt: time.Now(),
	}
	db.Answers = append(db.Answers, &answer)
}

