func TestAnswerFormInvalidForm(t *testing.T) {
	testing.SetRealm(std.NewUserRealm("g1user"))
	db := NewDB()

	dataAllTypes := `[
		{
			"label": "Name",
			"fieldType": "string",
			"required": true
		},
		{
			"label": "Age",
			"fieldType": "number",
			"required": false
		},
		{
			"label": "Is this a test?",
			"fieldType": "boolean",
			"required": false
		},
		{
			"label": "Favorite Food",
			"fieldType": "[Pizza|Schnitzel|Burger]",
			"required": true
		},
		{
			"label": "Favorite Foods",
			"fieldType": "{Pizza|Schnitzel|Burger}",
			"required": true
		}
	]`
	dataOneRequiredText := `[
		{
			"label": "Name",
			"fieldType": "string",
			"required": true
		}
	]`

	tests := []struct {
		name        string
		answer      string
		expectPanic bool
		data        string
	}{
		{
			name:        "correct",
			answer:      `["Alex", 21, true, 0, [0, 1]]`,
			expectPanic: false,
			data:        dataAllTypes,
		},
		{
			name:        "invalid string",
			answer:      `[0, 21, true, 0, [0, 1]`,
			expectPanic: true,
			data:        dataAllTypes,
		},
		{
			name:        "invalid number",
			answer:      `["Alex", "21", true, 0, [0, 1]]`,
			expectPanic: true,
			data:        dataAllTypes,
		},
		{
			name:        "invalid boolean",
			answer:      `["Alex", 21, 1, 0, [0, 1]]`,
			expectPanic: true,
			data:        dataAllTypes,
		},
		{
			name:        "invalid choice",
			answer:      `["Alex", 21, true, 10, [0, 1]]`,
			expectPanic: true,
			data:        dataAllTypes,
		},
		{
			name:        "invalid multi-choice 1",
			answer:      `["Alex", 21, true, 0, [0, 1, 2, 3, 4, 5]]`,
			expectPanic: true,
			data:        dataAllTypes,
		},
		{
			name:        "invalid multi-choice 2",
			answer:      `["Alex", 21, true, 0, [5]]`,
			expectPanic: true,
			data:        dataAllTypes,
		},
		{
			name:        "invalid multi-choice 3",
			answer:      `["Alex", 21, true, 0, 0]`,
			expectPanic: true,
			data:        dataAllTypes,
		},
		{
			name:        "required string",
			answer:      `["", 21, true, 0, [0, 1]]`,
			expectPanic: true,
			data:        dataAllTypes,
		},
		{
			name:        "unrequired number",
			answer:      `["Alex", null, true, 0, [0, 1]]`,
			expectPanic: false,
			data:        dataAllTypes,
		},
		{
			name:        "correct one field",
			answer:      `["Alex"]`,
			expectPanic: false,
			data:        dataOneRequiredText,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			formID, err := db.CreateForm("Test Form", "Test Description", "", "", tt.data)
			if err != nil {
				t.Error(err)
			}

			defer func() {
				if r := recover(); r != nil {
					if tt.expectPanic {
						return
					}
					t.Errorf("%s panic occurred when not expected: %v", tt.name, r)
				} else if tt.expectPanic {
					t.Errorf("%s expected panic but didn't occur", tt.name)
				}
			}()

			db.SubmitForm(formID, tt.answer)
		})
	}
}

package acl

import (
	"std"

	"gno.land/p/nt/avl"
)

func New() *Directory {
	return &Directory{
		userGroups:  avl.Tree{},
		permBuckets: avl.Tree{},
	}
}

type Directory struct {
	permBuckets avl.Tree // identifier -> perms
	userGroups  avl.Tree // std.Address -> []string
}

func (d *Directory) HasPerm(addr std.Address, verb, resource string) bool {
	// FIXME: consider memoize.

	// user perms
	if d.getBucketPerms("u:"+addr.String()).hasPerm(verb, resource) {
		return true
	}

	// everyone's perms.
	if d.getBucketPerms("g:"+Everyone).hasPerm(verb, resource) {
		return true
	}

	// user groups' perms.
	groups, ok := d.userGroups.Get(addr.String())
	if ok {
		for _, group := range groups.([]string) {
			if d.getBucketPerms("g:"+group).hasPerm(verb, resource) {
				return true
			}
		}
	}

	return false
}

func (d *Directory) getBucketPerms(bucket string) perms {
	res, ok := d.permBuckets.Get(bucket)
	if ok {
		return res.(perms)
	}
	return perms{}
}

func (d *Directory) HasRole(addr std.Address, role string) bool {
	return d.HasPerm(addr, "role", role)
}

func (d *Directory) AddUserPerm(addr std.Address, verb, resource string) {
	bucket := "u:" + addr.String()
	p := perm{
		verbs:     []string{verb},
		resources: []string{resource},
	}
	d.addPermToBucket(bucket, p)
}

func (d *Directory) AddGroupPerm(name string, verb, resource string) {
	bucket := "g:" + name
	p := perm{
		verbs:     []string{verb},
		resources: []string{resource},
	}
	d.addPermToBucket(bucket, p)
}

func (d *Directory) addPermToBucket(bucket string, p perm) {
	var ps perms

	existing, ok := d.permBuckets.Get(bucket)
	if ok {
		ps = existing.(perms)
	}
	ps = append(ps, p)

	d.permBuckets.Set(bucket, ps)
}

func (d *Directory) AddUserToGroup(user std.Address, group string) {
	existing, ok := d.userGroups.Get(user.String())
	var groups []string
	if ok {
		groups = existing.([]string)
	}
	groups = append(groups, group)
	d.userGroups.Set(user.String(), groups)
}

