	}

	if dom.Title != "" {
		output += H1(dom.Title).String(dom) + "\n"
	}

	if header := dom.Header.String(dom); header != "" {
		if dom.WithComments {
			output += "<!-- header -->"
		}
		output += header + "\n"
		if dom.WithComments {
			output += "<!-- /header -->"
		}
	}

	if body := dom.Body.String(dom); body != "" {
		if dom.WithComments {
			output += "<!-- body -->"
		}
		output += body + "\n"
		if dom.WithComments {
			output += "<!-- /body -->"
		}
	}

	if footer := dom.Footer.String(dom); footer != "" {
		if dom.WithComments {
			output += "<!-- footer -->"
		}
		output += footer + "\n"
		if dom.WithComments {
			output += "<!-- /footer -->"
		}
	}

	if classes != "" {
		output += "</main>"
	}

	// TODO: cleanup double new-lines.

	return output
}

