import (
	"bufio"
	"bytes"
	"errors"

	"gno.land/p/demo/gnorkle/feed"
	"gno.land/p/demo/gnorkle/gnorkle"
	"gno.land/p/demo/gnorkle/ingesters/single"
	"gno.land/p/demo/gnorkle/message"
	"gno.land/p/demo/gnorkle/storage/simple"
	"gno.land/p/nt/ufmt"
)

// Feed is a static feed.
type Feed struct {
	id            string
	isLocked      bool
	valueDataType string
	ingester      gnorkle.Ingester
	storage       gnorkle.Storage
	tasks         []feed.Task
}

// NewFeed creates a new static feed.
func NewFeed(
	id string,
	valueDataType string,
	ingester gnorkle.Ingester,
	storage gnorkle.Storage,
	tasks ...feed.Task,
) *Feed {
	return &Feed{
		id:            id,
		valueDataType: valueDataType,
		ingester:      ingester,
		storage:       storage,
		tasks:         tasks,
	}
}

// NewSingleValueFeed is a convenience function  for creating a static feed
// that autocommits a value after a single ingestion.
func NewSingleValueFeed(
	id string,
	valueDataType string,
	tasks ...feed.Task,
) *Feed {
	return NewFeed(
		id,
		valueDataType,
		&single.ValueIngester{},
		simple.NewStorage(1),
		tasks...,
	)
}

// ID returns the feed's ID.
func (f Feed) ID() string {
	return f.id
}

// Type returns the feed's type.
func (f Feed) Type() feed.Type {
	return feed.TypeStatic
}

// Ingest ingests a message into the feed. It either adds the value to the ingester's
// pending values or commits the value to the storage.
func (f *Feed) Ingest(funcType message.FuncType, msg, providerAddress string) error {
	if f == nil {
		return feed.ErrUndefined
	}

	if f.isLocked {
		return errors.New("feed locked")
	}

	switch funcType {
	case message.FuncTypeIngest:
		// Autocommit the ingester's value if it's a single value ingester
		// because this is a static feed and this is the only value it will ever have.
		if canAutoCommit, err := f.ingester.Ingest(msg, providerAddress); canAutoCommit && err == nil {
			if err := f.ingester.CommitValue(f.storage, providerAddress); err != nil {
				return err
			}

			f.isLocked = true
		} else if err != nil {
			return err
		}

	case message.FuncTypeCommit:
		if err := f.ingester.CommitValue(f.storage, providerAddress); err != nil {
			return err
		}

		f.isLocked = true

	default:
		return errors.New("invalid message function " + string(funcType))
	}

	return nil
}

// Value returns the feed's latest value, it's data type, and whether or not it can
// be safely consumed. In this case it uses `f.isLocked` because, this being a static
// feed, it will only ever have one value; once that value is committed the feed is locked
// and there is a valid, non-empty value to consume.
func (f Feed) Value() (feed.Value, string, bool) {
	return f.storage.GetLatest(), f.valueDataType, f.isLocked
}

// MarshalJSON marshals the components of the feed that are needed for
// an agent to execute tasks and send values for ingestion.
func (f Feed) MarshalJSON() ([]byte, error) {
	buf := new(bytes.Buffer)
	w := bufio.NewWriter(buf)

	w.Write([]byte(
		`{"id":"` + f.id +
			`","type":"` + ufmt.Sprintf("%d", int(f.Type())) +
			`","value_type":"` + f.valueDataType +
			`","tasks":[`),
	)

	first := true
	for _, task := range f.tasks {
		if !first {
			w.WriteString(",")
		}

		taskJSON, err := task.MarshalJSON()
		if err != nil {
			return nil, err
		}

		w.Write(taskJSON)
		first = false
	}

	w.Write([]byte("]}"))
	w.Flush()

	return buf.Bytes(), nil
}

// Tasks returns the feed's tasks. This allows task consumers to extract task
// contents without having to marshal the entire feed.
func (f Feed) Tasks() []feed.Task {
	return f.tasks
}

// IsActive returns true if the feed is accepting ingestion requests from agents.
func (f Feed) IsActive() bool {
	return !f.isLocked
}

package static_test

import (
	"errors"
	"testing"

	"gno.land/p/demo/gnorkle/feed"
	"gno.land/p/demo/gnorkle/feeds/static"
	"gno.land/p/demo/gnorkle/gnorkle"
	"gno.land/p/demo/gnorkle/ingester"
	"gno.land/p/demo/gnorkle/message"
	"gno.land/p/demo/gnorkle/storage/simple"
	"gno.land/p/nt/uassert"
	"gno.land/p/nt/urequire"
)

type mockIngester struct {
	canAutoCommit   bool
	ingestErr       error
	commitErr       error
	value           string
	providerAddress string
}

func (i mockIngester) Type() ingester.Type {
	return ingester.Type(0)
}

func (i *mockIngester) Ingest(value, providerAddress string) (bool, error) {
	if i.ingestErr != nil {
		return false, i.ingestErr
	}

	i.value = value
	i.providerAddress = providerAddress
	return i.canAutoCommit, nil
}

func (i *mockIngester) CommitValue(storage gnorkle.Storage, providerAddress string) error {
	if i.commitErr != nil {
		return i.commitErr
	}

	return storage.Put(i.value)
}

func TestNewSingleValueFeed(t *testing.T) {
	staticFeed := static.NewSingleValueFeed("1", "")

	uassert.Equal(t, "1", staticFeed.ID())
	uassert.Equal(t, int(feed.TypeStatic), int(staticFeed.Type()))
}

func TestFeed_Ingest(t *testing.T) {
	var undefinedFeed *static.Feed
	err := undefinedFeed.Ingest("", "", "")
	uassert.ErrorIs(t, err, feed.ErrUndefined)

	tests := []struct {
		name               string
		ingester           *mockIngester
		verifyIsLocked     bool
		doCommit           bool
		funcType           message.FuncType
		msg                string
		providerAddress    string
		expFeedValueString string
		expErrText         string
		expIsActive        bool
	}{
		{
			name:        "func invalid error",
			ingester:    &mockIngester{},
			funcType:    message.FuncType("derp"),
			expErrText:  "invalid message function derp",
			expIsActive: true,
		},
		{
			name: "func ingest ingest error",
			ingester: &mockIngester{
				ingestErr: errors.New("ingest error"),
			},
			funcType:    message.FuncTypeIngest,
			expErrText:  "ingest error",
			expIsActive: true,
		},
		{
			name: "func ingest commit error",
			ingester: &mockIngester{
				commitErr:     errors.New("commit error"),
				canAutoCommit: true,
			},
			funcType:    message.FuncTypeIngest,
			expErrText:  "commit error",
			expIsActive: true,
		},
		{
			name: "func commit commit error",
			ingester: &mockIngester{
				commitErr:     errors.New("commit error"),
				canAutoCommit: true,
			},
			funcType:    message.FuncTypeCommit,
			expErrText:  "commit error",
			expIsActive: true,
		},
		{
			name:            "only ingest",
			ingester:        &mockIngester{},
			funcType:        message.FuncTypeIngest,
			msg:             "still active feed",
			providerAddress: "gno1234",
			expIsActive:     true,
		},
		{
			name:               "ingest autocommit",
			ingester:           &mockIngester{canAutoCommit: true},
			funcType:           message.FuncTypeIngest,
			msg:                "still active feed",
			providerAddress:    "gno1234",
			expFeedValueString: "still active feed",
			verifyIsLocked:     true,
		},
		{
			name:           "commit no value",
			ingester:       &mockIngester{},
			funcType:       message.FuncTypeCommit,
			msg:            "shouldn't be stored",
			verifyIsLocked: true,
		},
		{
			name:               "ingest then commmit",
			ingester:           &mockIngester{},
			funcType:           message.FuncTypeIngest,
			msg:                "blahblah",
			doCommit:           true,
			expFeedValueString: "blahblah",
			verifyIsLocked:     true,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			staticFeed := static.NewFeed(
				"1",
				"string",
				tt.ingester,
				simple.NewStorage(1),
				nil,
			)

			var errText string
			if err := staticFeed.Ingest(tt.funcType, tt.msg, tt.providerAddress); err != nil {
				errText = err.Error()
			}

			urequire.Equal(t, tt.expErrText, errText)

			if tt.doCommit {
				err := staticFeed.Ingest(message.FuncTypeCommit, "", "")
				urequire.NoError(t, err, "follow up commit failed")
			}

			if tt.verifyIsLocked {
				errText = ""
				if err := staticFeed.Ingest(tt.funcType, tt.msg, tt.providerAddress); err != nil {
					errText = err.Error()
				}

				urequire.Equal(t, "feed locked", errText)
			}

			uassert.Equal(t, tt.providerAddress, tt.ingester.providerAddress)

			feedValue, dataType, isLocked := staticFeed.Value()
			uassert.Equal(t, tt.expFeedValueString, feedValue.String)
			uassert.Equal(t, "string", dataType)
			uassert.Equal(t, tt.verifyIsLocked, isLocked)
			uassert.Equal(t, tt.expIsActive, staticFeed.IsActive())
		})
	}
}

type mockTask struct {
