package releases

import (
	"gno.land/p/moul/md"
	"gno.land/p/moul/mdtable"
	"gno.land/p/nt/ufmt"
)

type changelog struct {
	name     string
	releases []release
}

func NewChangelog(name string) *changelog {
	return &changelog{
		name:     name,
		releases: make([]release, 0),
	}
}

func (c *changelog) NewRelease(version, url, notes string) {
	if latest := c.Latest(); latest != nil {
		latest.isLatest = false
	}

	release := release{
		// manual
		version: version,
		url:     url,
		notes:   notes,

		// internal
		changelog: c,
		isLatest:  true,
	}

	c.releases = append(c.releases, release)
}

func (c changelog) RenderAsTable(max int) string {
	out := md.H1(c.name)

	if len(c.releases) == 0 {
		out += "No releases."
		return out
	}

	out += ufmt.Sprintf("See the %s changelog below.\n\n", c.name)
	table := mdtable.Table{
		Headers: []string{"Version", "Link", "Notes"},
	}

	if max > len(c.releases) {
		max = len(c.releases)
	}

	for i := len(c.releases) - 1; i >= len(c.releases)-max; i-- {
		r := c.releases[i]
		table.Append([]string{r.version, r.Link(), r.notes})
	}

	out += table.String()
	return out
}

func (c *changelog) Render(path string) string {
	if path == "" {
		output := "# " + c.name + "\n\n"
		max := len(c.releases) - 1
		min := 0
		if max-min > 10 {
			min = max - 10
		}
		for i := max; i >= min; i-- {
			release := c.releases[i]
			output += release.Render()
		}
		return output
	}

	release := c.ByVersion(path)
	if release != nil {
		return release.Render()
	}

	return "no such release"
}

func (c *changelog) Latest() *release {
	if len(c.releases) > 0 {
		pos := len(c.releases) - 1
		return &c.releases[pos]
	}
	return nil
}

func (c *changelog) ByVersion(version string) *release {
	for _, release := range c.releases {
		if release.version == version {
			return &release
		}
	}
	return nil
}

package releases

type release struct {
	// manual
	version string
	url     string
	notes   string

	// internal
	isLatest  bool
	changelog *changelog
}

func (r *release) URL() string     { return r.url }
func (r *release) Version() string { return r.version }
func (r *release) Notes() string   { return r.notes }
func (r *release) IsLatest() bool  { return r.isLatest }

func (r *release) Title() string {
	output := r.changelog.name + " " + r.version
	if r.isLatest {
		output += " (latest)"
	}
	return output
}

func (r *release) Link() string {
	return "[" + r.Title() + "](" + r.url + ")"
}

func (r *release) Render() string {
	output := ""
	output += "## " + r.Link() + "\n\n"
	if r.notes != "" {
		output += r.notes + "\n\n"
	}
	return output
}

package stack

type Stack struct {
	top    *node
	length int
}

type node struct {
	value any
	prev  *node
}

func New() *Stack {
	return &Stack{nil, 0}
}

func (s *Stack) Len() int {
	return s.length
}

func (s *Stack) Top() any {
	if s.length == 0 {
		return nil
	}
	return s.top.value
}

func (s *Stack) Pop() any {
	if s.length == 0 {
		return nil
	}

	node := s.top
	s.top = node.prev
	s.length -= 1
	return node.value
}

func (s *Stack) Push(value any) {
	node := &node{value, s.top}
	s.top = node
	s.length += 1
}

package stack

import "testing"

func TestStack(t *testing.T) {
	s := New() // Empty stack

	if s.Len() != 0 {
		t.Errorf("s.Len(): expected 0; got %d", s.Len())
	}

	s.Push(1)

	if s.Len() != 1 {
		t.Errorf("s.Len(): expected 1; got %d", s.Len())
	}

	if top := s.Top(); top.(int) != 1 {
		t.Errorf("s.Top(): expected 1; got %v", top.(int))
	}

	if elem := s.Pop(); elem.(int) != 1 {
		t.Errorf("s.Pop(): expected 1; got %v", elem.(int))
	}
	if s.Len() != 0 {
		t.Errorf("s.Len(): expected 0; got %d", s.Len())
	}
}

// Package subscription provides a flexible system for managing both recurring and
// lifetime subscriptions in Gno applications. It enables developers to handle
// payment-based access control for services or products. The library supports
// both subscriptions requiring periodic payments (recurring) and one-time payments
// (lifetime). Subscriptions are tracked using an AVL tree for efficient management
// of subscription statuses.
//
// Usage:
//
// Import the required sub-packages (`recurring` and/or `lifetime`) to manage specific
// subscription types. The methods provided allow users to subscribe, check subscription
// status, and manage payments.
//
// Recurring Subscription:
//
// Recurring subscriptions require periodic payments to maintain access.
// Users pay to extend their access for a specific duration.
//
// Example:
//
//	// Create a recurring subscription requiring 100 ugnot every 30 days
//	recSub := recurring.NewRecurringSubscription(time.Hour * 24 * 30, 100)
//
//	// Process payment for the recurring subscription
//	recSub.Subscribe()
//
//	// Gift a recurring subscription to another user
//	recSub.GiftSubscription(recipientAddress)
//
//	// Check if a user has a valid subscription
//	recSub.HasValidSubscription(addr)
//
//	// Get the expiration date of the subscription
//	recSub.GetExpiration(caller)
//
//	// Update the subscription amount to 200 ugnot
//	recSub.UpdateAmount(200)
//
//	// Get the current subscription amount
//	recSub.GetAmount()
//
// Lifetime Subscription:
//
// Lifetime subscriptions require a one-time payment for permanent access.
// Once paid, users have indefinite access without further payments.
//
// Example:
//
//	// Create a lifetime subscription costing 500 ugnot
//	lifeSub := lifetime.NewLifetimeSubscription(500)
//
//	// Process payment for lifetime access
//	lifeSub.Subscribe()
//
//	// Gift a lifetime subscription to another user
//	lifeSub.GiftSubscription(recipientAddress)
//
//	// Check if a user has a valid subscription
//	lifeSub.HasValidSubscription(addr)
//
//	// Update the lifetime subscription amount to 1000 ugnot
//	lifeSub.UpdateAmount(1000)
//
//	// Get the current lifetime subscription amount
//	lifeSub.GetAmount()
package subscription

package subscription

import (
	"std"
)

// Subscription interface defines standard methods that all subscription types must implement.
type Subscription interface {
	HasValidSubscription(std.Address) error
	Subscribe() error
	UpdateAmount(newAmount int64) error
}

package lifetime

import (
	"std"

	"gno.land/p/nt/avl"
	"gno.land/p/nt/ownable"
)

// LifetimeSubscription represents a subscription that requires only a one-time payment.
// It grants permanent access to a service or product.
type LifetimeSubscription struct {
	ownable.Ownable
	amount int64
	subs   *avl.Tree // std.Address -> bool
}

// NewLifetimeSubscription creates and returns a new lifetime subscription.
func NewLifetimeSubscription(amount int64) *LifetimeSubscription {
	return &LifetimeSubscription{
		Ownable: *ownable.New(),
		amount:  amount,
		subs:    avl.NewTree(),
	}
}

// processSubscription handles the subscription process for a given receiver.
func (ls *LifetimeSubscription) processSubscription(receiver std.Address) error {
	amount := std.OriginSend()

	if amount.AmountOf("ugnot") != ls.amount {
		return ErrAmt
	}

	_, exists := ls.subs.Get(receiver.String())

	if exists {
		return ErrAlreadySub
	}

	ls.subs.Set(receiver.String(), true)

	return nil
}

// Subscribe processes the payment for a lifetime subscription.
func (ls *LifetimeSubscription) Subscribe() error {
	caller := std.CurrentRealm().Address()
	return ls.processSubscription(caller)
}

// GiftSubscription allows the caller to pay for a lifetime subscription for another user.
func (ls *LifetimeSubscription) GiftSubscription(receiver std.Address) error {
	return ls.processSubscription(receiver)
}

// HasValidSubscription checks if the given address has an active lifetime subscription.
func (ls *LifetimeSubscription) HasValidSubscription(addr std.Address) error {
	_, exists := ls.subs.Get(addr.String())

	if !exists {
		return ErrNoSub
	}

	return nil
}

// UpdateAmount allows the owner of the LifetimeSubscription contract to update the subscription price.
func (ls *LifetimeSubscription) UpdateAmount(newAmount int64) error {
	if !ls.OwnedByCurrent() {
		return ErrNotAuthorized
	}

	ls.amount = newAmount
	return nil
}

// GetAmount returns the current subscription price.
func (ls *LifetimeSubscription) GetAmount() int64 {
	return ls.amount
}

package recurring

import (
	"std"
	"time"

	"gno.land/p/nt/avl"
	"gno.land/p/nt/ownable"
)

// RecurringSubscription represents a subscription that requires periodic payments.
// It includes the duration of the subscription and the amount required per period.
type RecurringSubscription struct {
	ownable.Ownable
	duration time.Duration
	amount   int64
	subs     *avl.Tree // std.Address -> time.Time
}

// NewRecurringSubscription creates and returns a new recurring subscription.
func NewRecurringSubscription(duration time.Duration, amount int64) *RecurringSubscription {
	return &RecurringSubscription{
		Ownable:  *ownable.New(),
		duration: duration,
		amount:   amount,
		subs:     avl.NewTree(),
	}
}

// HasValidSubscription verifies if the caller has an active recurring subscription.
func (rs *RecurringSubscription) HasValidSubscription(addr std.Address) error {
	expTime, exists := rs.subs.Get(addr.String())
	if !exists {
		return ErrNoSub
	}

	if time.Now().After(expTime.(time.Time)) {
		return ErrSubExpired
	}

	return nil
}

// processSubscription processes the payment for a given receiver and renews or adds their subscription.
func (rs *RecurringSubscription) processSubscription(receiver std.Address) error {
	amount := std.OriginSend()

	if amount.AmountOf("ugnot") != rs.amount {
		return ErrAmt
	}

	expTime, exists := rs.subs.Get(receiver.String())

	// If the user is already a subscriber but his subscription has expired, authorize renewal
	if exists {
		expiration := expTime.(time.Time)
		if time.Now().Before(expiration) {
			return ErrAlreadySub
		}
	}

	// Renew or add subscription
	newExpiration := time.Now().Add(rs.duration)
	rs.subs.Set(receiver.String(), newExpiration)

	return nil
}

// Subscribe handles the payment for the caller's subscription.
func (rs *RecurringSubscription) Subscribe() error {
	caller := std.CurrentRealm().Address()

	return rs.processSubscription(caller)
}

// GiftSubscription allows the user to pay for a subscription for another user (receiver).
func (rs *RecurringSubscription) GiftSubscription(receiver std.Address) error {
	return rs.processSubscription(receiver)
}

// GetExpiration returns the expiration date of the recurring subscription for a given caller.
func (rs *RecurringSubscription) GetExpiration(addr std.Address) (time.Time, error) {
	expTime, exists := rs.subs.Get(addr.String())
	if !exists {
		return time.Time{}, ErrNoSub
	}

	return expTime.(time.Time), nil
}

// UpdateAmount allows the owner of the subscription contract to change the required subscription amount.
func (rs *RecurringSubscription) UpdateAmount(newAmount int64) error {
	if !rs.OwnedByCurrent() {
		return ErrNotAuthorized
	}

	rs.amount = newAmount
	return nil
}

// GetAmount returns the current amount required for each subscription period.
func (rs *RecurringSubscription) GetAmount() int64 {
	return rs.amount
}

package svg

import (
	"encoding/base64"
	"strings"

	"gno.land/p/nt/avl"
	"gno.land/p/nt/ufmt"
)

type Canvas struct {
	Width, Height int
	ViewBox       string
	Elems         []Elem
	Style         *avl.Tree
}

type Elem interface{ String() string }

func NewCanvas(width, height int) *Canvas {
	return &Canvas{
		Width:  width,
		Height: height,
		Style:  nil,
	}
}

func (c *Canvas) AddStyle(key, value string) *Canvas {
	if c.Style == nil {
		c.Style = avl.NewTree()
	}
	c.Style.Set(key, value)
	return c
}

func (c *Canvas) WithViewBox(x, y, width, height int) *Canvas {
	c.ViewBox = ufmt.Sprintf("%d %d %d %d", x, y, width, height)
	return c
}

// Render renders your canvas
func (c Canvas) Render(alt string) string {
	base64SVG := base64.StdEncoding.EncodeToString([]byte(c.String()))
	return ufmt.Sprintf("![%s](data:image/svg+xml;base64,%s)", alt, base64SVG)
}

func (c Canvas) String() string {
	out := ""
	out += ufmt.Sprintf(`<svg xmlns="http://www.w3.org/2000/svg" width="%d" height="%d" viewBox="%s">`, c.Width, c.Height, c.ViewBox)
	if c.Style != nil {
		out += "<style>"
		c.Style.Iterate("", "", func(k string, val interface{}) bool {
			v := val.(string)
			out += ufmt.Sprintf("%s{%s}", k, v)
			return false
		})
		out += "</style>"
	}
	for _, elem := range c.Elems {
		out += elem.String()
	}
	out += "</svg>"
	return out
}

func (c Canvas) Base64() string {
	out := c.String()
	return base64.StdEncoding.EncodeToString([]byte(out))
}

func (c *Canvas) Append(elem ...Elem) {
	c.Elems = append(c.Elems, elem...)
}

type BaseAttrs struct {
	ID          string
	Class       string
	Style       string
	Stroke      string
	StrokeWidth string
	Opacity     string
	Transform   string
	Visibility  string
}

func (b BaseAttrs) String() string {
	var elems []string

	if b.ID != "" {
		elems = append(elems, `id="`+b.ID+`"`)
	}
	if b.Class != "" {
		elems = append(elems, `class="`+b.Class+`"`)
	}
	if b.Style != "" {
		elems = append(elems, `style="`+b.Style+`"`)
	}
	if b.Stroke != "" {
		elems = append(elems, `stroke="`+b.Stroke+`"`)
	}
	if b.StrokeWidth != "" {
		elems = append(elems, `stroke-width="`+b.StrokeWidth+`"`)
	}
	if b.Opacity != "" {
		elems = append(elems, `opacity="`+b.Opacity+`"`)
	}
	if b.Transform != "" {
		elems = append(elems, `transform="`+b.Transform+`"`)
	}
	if b.Visibility != "" {
		elems = append(elems, `visibility="`+b.Visibility+`"`)
	}
	if len(elems) == 0 {
		return ""
	}
	return strings.Join(elems, " ")
}

