			name:      "Multi-value index with multiple matches",
			indexName: "tags",
			key:       "dev",
			wantCount: 3,
		},
		{
			name:      "Multi-value index with single match",
			indexName: "tags",
			key:       "go",
			wantCount: 1,
		},
		{
			name:      "Multi-value index with no matches",
			indexName: "tags",
			key:       "java",
			wantCount: 0,
		},
		{
			name:      "Single-value non-unique index with multiple matches",
			indexName: "age",
			key:       "30",
			wantCount: 2,
