func TestList_Clone(t *testing.T) {
	var l List
	l.Append(1, 2, 3)

	clone := l.Clone()

	// Test same length
	if clone.Len() != l.Len() {
		t.Errorf("clone.Len() = %d; want %d", clone.Len(), l.Len())
	}

	// Test same values
	for i := 0; i < l.Len(); i++ {
		if clone.Get(i) != l.Get(i) {
			t.Errorf("clone.Get(%d) = %v; want %v", i, clone.Get(i), l.Get(i))
		}
	}

