func TestList_LargeOperations(t *testing.T) {
	var l List

	// Test with larger number of elements
	n := 1000
	for i := 0; i < n; i++ {
		l.Append(i)
	}

	if l.Len() != n {
		t.Errorf("Len() = %d; want %d", l.Len(), n)
	}

	// Test range on large list
	values := l.Slice(n-3, n)
	expected := []any{n - 3, n - 2, n - 1}
