// TODO: Support versioning

// Schema contains information about fields and default field values.
// It also offers the possibility to configure it as static to indicate
// that only configured fields should be allowed.
type Schema struct {
	name     string
	strict   bool
	fields   list.List // int(field index) -> string(field name)
	defaults avl.Tree  // string(field index) -> interface{}
}

// NewSchema creates a new schema.
func NewSchema(name string, options ...SchemaOption) *Schema {
	s := &Schema{name: name}
	for _, apply := range options {
		apply(s)
	}
	return s
}

// Name returns schema's name.
func (s Schema) Name() string {
	return s.name
}

// Fields returns the list field names that are defined in the schema.
func (s Schema) Fields() []string {
	fields := make([]string, s.fields.Len())
	s.fields.ForEach(func(i int, v interface{}) bool {
		fields[i] = v.(string)
		return false
	})
	return fields
}

// Size returns the number of fields the schema has.
func (s Schema) Size() int {
	return s.fields.Len()
}

// IsStrict check if the schema is configured as a strict one.
func (s Schema) IsStrict() bool {
	return s.strict
}

// HasField check is a field has been defined in the schema.
func (s Schema) HasField(name string) bool {
	return s.GetFieldIndex(name) >= 0
}

// AddField adds a new field to the schema.
// A default field value can be specified, otherwise `defaultValue` must be nil.
func (s *Schema) AddField(name string, defaultValue interface{}) (index int, added bool) {
	if s.HasField(name) {
		return -1, false
	}

	s.fields.Append(name)
	index = s.fields.Len() - 1
	if defaultValue != nil {
		key := castIntToKey(index)
		s.defaults.Set(key, defaultValue)
	}
	return index, true
}

// GetFieldIndex returns the index number of a schema field.
//
// Field index indicates the order the field has within the schema.
// When defined fields are added they get an index starting from
// field index 0.
//
// Fields are internally referenced by index number instead of the name
// to be able to rename fields easily.
func (s Schema) GetFieldIndex(name string) int {
	index := -1
	s.fields.ForEach(func(i int, v interface{}) bool {
		if name != v.(string) {
			return false
		}

		index = i
		return true
	})
	return index
}

// GetFieldName returns the name of a field for a specific field index.
func (s Schema) GetFieldName(index int) (name string, found bool) {
	v := s.fields.Get(index)
	if v == nil {
		return "", false
	}
	return v.(string), true
}

// GetDefault returns the default value for a field.
func (s Schema) GetDefault(name string) (value interface{}, found bool) {
	i := s.GetFieldIndex(name)
	if i == -1 {
		return nil, false
	}
	return s.GetDefaultByIndex(i)
}

// GetDefaultByIndex returns the default value for a field by it's index.
func (s Schema) GetDefaultByIndex(index int) (value interface{}, found bool) {
	key := castIntToKey(index)
	v, found := s.defaults.Get(key)
	if !found {
		return nil, false
	}

	if fn, ok := v.(func() interface{}); ok {
		return fn(), true
	}
	return v, true
}

// RenameField renames a field.
func (s *Schema) RenameField(name, newName string) (renamed bool) {
	if s.HasField(newName) {
		return false
	}

	i := s.GetFieldIndex(name)
	if i == -1 {
		return false
	}

	s.fields.Set(i, newName)
	return true
}

func castIntToKey(i int) string {
	buf := make([]byte, 8)
	binary.BigEndian.PutUint64(buf, uint64(i))
	return string(buf)
}

package datastore

import "strings"

// StorageOption configures schemas.
type SchemaOption func(*Schema)

// WithField assign a new field to the schema definition.
func WithField(name string) SchemaOption {
	return func(s *Schema) {
		name = strings.TrimSpace(name)
		if name != "" {
			s.fields.Append(name)
		}
	}
}

// WithDefaultField assign a new field with a default value to the schema definition.
// Default value is assigned to newly created records asociated to to schema.
func WithDefaultField(name string, value interface{}) SchemaOption {
	return func(s *Schema) {
		name = strings.TrimSpace(name)
		if name != "" {
			s.fields.Append(name)

			key := castIntToKey(s.fields.Len() - 1)
			s.defaults.Set(key, value)
		}
	}
}

// Strict configures the schema as a strict one.
// By default schemas should allow the creation of any user defined field,
// making them strict limits the allowed record fields to the ones pre-defined
// in the schema. Fields are pre-defined using `WithField`, `WithDefaultField`
// or by calling `Schema.AddField()`.
func Strict() SchemaOption {
	return func(s *Schema) {
		s.strict = true
	}
}

package datastore

import (
	"testing"

	"gno.land/p/nt/uassert"
	"gno.land/p/nt/urequire"
)

func TestSchemaNew(t *testing.T) {
	cases := []struct {
		name    string
		options []SchemaOption
		fields  []string
		strict  bool
	}{
		{
			name: "default",
		},
		{
			name:    "strict",
			options: []SchemaOption{Strict()},
			strict:  true,
		},
		{
			name: "with fields",
			options: []SchemaOption{
				WithField("foo"),
				WithField("bar"),
				WithDefaultField("baz", 42),
			},
			fields: []string{"foo", "bar", "baz"},
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			s := NewSchema("Foo", tc.options...)

			uassert.Equal(t, "Foo", s.Name())
			uassert.Equal(t, tc.strict, s.IsStrict())
			urequire.Equal(t, len(tc.fields), s.Size(), "field count")

			for i, name := range s.Fields() {
				uassert.Equal(t, tc.fields[i], name)
				uassert.True(t, s.HasField(name))
			}
		})
	}
}

func TestSchemaAddField(t *testing.T) {
	cases := []struct {
		name       string
		options    []SchemaOption
		fieldName  string
		fieldIndex int
		fields     []string
		success    bool
	}{
		{
			name:       "new only field",
			fieldName:  "foo",
			fieldIndex: 0,
			fields:     []string{"foo"},
			success:    true,
		},
		{
			name: "new existing fields",
			options: []SchemaOption{
				WithField("foo"),
				WithField("bar"),
			},
			fieldName:  "baz",
			fieldIndex: 2,
			fields:     []string{"foo", "bar", "baz"},
			success:    true,
		},
		{
			name:       "duplicated field",
			options:    []SchemaOption{WithField("foo")},
			fieldName:  "foo",
			fieldIndex: -1,
			fields:     []string{"foo"},
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			s := NewSchema("Foo", tc.options...)

			index, added := s.AddField(tc.fieldName, nil)

			if tc.success {
				uassert.Equal(t, tc.fieldIndex, index)
				uassert.True(t, added)
			} else {
				uassert.Equal(t, -1, index)
				uassert.False(t, added)
			}

			urequire.Equal(t, len(tc.fields), s.Size(), "field count")

			for i, name := range s.Fields() {
				uassert.Equal(t, tc.fields[i], name)
				uassert.True(t, s.HasField(name))
			}
		})
	}
}

func TestSchemaGetFieldIndex(t *testing.T) {
	s := NewSchema("Foo")
	s.AddField("foo", nil)
	s.AddField("bar", nil)
	s.AddField("baz", nil)

	uassert.Equal(t, 0, s.GetFieldIndex("foo"))
	uassert.Equal(t, 1, s.GetFieldIndex("bar"))
	uassert.Equal(t, 2, s.GetFieldIndex("baz"))

	uassert.Equal(t, -1, s.GetFieldIndex(""))
	uassert.Equal(t, -1, s.GetFieldIndex("unknown"))
}

func TestSchemaGetFieldName(t *testing.T) {
	s := NewSchema("Foo")
	s.AddField("foo", nil)
	s.AddField("bar", nil)
	s.AddField("baz", nil)

	name, found := s.GetFieldName(0)
	uassert.Equal(t, "foo", name)
	uassert.True(t, found)

	name, found = s.GetFieldName(1)
	uassert.Equal(t, "bar", name)
	uassert.True(t, found)

	name, found = s.GetFieldName(2)
	uassert.Equal(t, "baz", name)
	uassert.True(t, found)

	name, found = s.GetFieldName(404)
	uassert.Equal(t, "", name)
	uassert.False(t, found)
}

func TestSchemaGetDefault(t *testing.T) {
	s := NewSchema("Foo")
	s.AddField("foo", nil)
	s.AddField("bar", 42)

	_, found := s.GetDefault("foo")
	uassert.False(t, found)

	v, found := s.GetDefault("bar")
	uassert.True(t, found)

	got, ok := v.(int)
	urequire.True(t, ok, "default field value")
	uassert.Equal(t, 42, got)
}

func TestSchemaGetDefaultByIndex(t *testing.T) {
	s := NewSchema("Foo")
	s.AddField("foo", nil)
	s.AddField("bar", 42)

	_, found := s.GetDefaultByIndex(0)
	uassert.False(t, found)

	_, found = s.GetDefaultByIndex(404)
	uassert.False(t, found)

	v, found := s.GetDefaultByIndex(1)
	uassert.True(t, found)

	got, ok := v.(int)
	urequire.True(t, ok, "default field value")
	uassert.Equal(t, 42, got)
}

func TestSchemaRenameField(t *testing.T) {
	s := NewSchema("Foo")
	s.AddField("foo", nil)
	s.AddField("bar", nil)

	renamed := s.RenameField("foo", "bar")
	uassert.False(t, renamed)

	renamed = s.RenameField("", "baz")
	uassert.False(t, renamed)

	renamed = s.RenameField("foo", "")
	uassert.True(t, renamed)

	renamed = s.RenameField("", "foo")
	uassert.True(t, renamed)

	renamed = s.RenameField("foo", "foobar")
	uassert.True(t, renamed)

	urequire.Equal(t, 2, s.Size(), "field count")
	fields := []string{"foobar", "bar"}
	for i, name := range s.Fields() {
		uassert.Equal(t, fields[i], name)
		uassert.True(t, s.HasField(name))
	}
}

package datastore

import (
	"errors"
	"strings"

	"gno.land/p/moul/collection"
	"gno.land/p/nt/seqid"
)

// NewStorage creates a new records storage.
func NewStorage(name string, options ...StorageOption) Storage {
	s := Storage{
		name:       name,
		collection: collection.New(),
		schema:     NewSchema(strings.Title(name)),
	}

	for _, apply := range options {
		apply(&s)
	}
	return s
}

// Storage stores a collection of records.
//
// By default it searches records by record ID but it allows
// using custom user defined indexes for other record fields.
//
// When a storage is created it defines a default schema that
// keeps track of record fields. Storage can be optionally
// created with a user defined schema in cases where the number
// of fields has to be pre-defined or when new records must have
// one or more fields initialized to default values.
type Storage struct {
	name       string
	collection *collection.Collection
	schema     *Schema
}

// Name returns storage's name.
func (s Storage) Name() string {
	return s.name
}

// Collection returns the undelying collection used by the
// storage to store all records.
func (s Storage) Collection() *collection.Collection {
	return s.collection
}

// Schema returns the schema being used to track record fields.
func (s Storage) Schema() *Schema {
	return s.schema
}

// Size returns the number of records that the storage have.
func (s Storage) Size() int {
	return s.collection.GetIndex(collection.IDIndex).Size()
}

// NewRecord creates a new storage record.
//
// If a custom schema with default field values is assigned to
// storage it's used to assign initial default values when new
// records are created.
//
// Creating a new record doesn't assign an ID to it, a new ID
// is generated and assigned to the record when it's saved for
// the first time.
func (s Storage) NewRecord() Record {
	r := &record{
		schema:     s.schema,
		collection: s.collection,
	}

	// Assign default record values if the schema defines them
	for i, name := range s.schema.Fields() {
		if v, found := s.schema.GetDefaultByIndex(i); found {
			r.Set(name, v)
		}
	}
	return r
}

// Query returns a recordset that matches the query parameters.
// By default query selects records using the ID index.
//
// Example usage:
//
//	// Get 50 records starting from the one at position 100
//	rs, _ := storage.Query(
//		WithOffset(100),
//		WithSize(50),
//	)
//
//	// Iterate records to create a new slice
//	var records []Record
//	rs.Iterate(func (r Record) bool {
//		records = append(records, r)
//		return false
//	})
func (s Storage) Query(options ...QueryOption) (Recordset, error) {
	// Initialize the recordset for the query
	rs := recordset{
		query:   defaultQuery,
		records: s.collection.GetIndex(collection.IDIndex),
	}

	for _, apply := range options {
		if err := apply(&rs.query); err != nil {
			return nil, err
		}
	}

	indexName := rs.query.IndexName()
	if indexName != collection.IDIndex {
		// When using a custom index get the keys to get records from the ID index
		keys, err := s.getIndexRecordsKeys(indexName, rs.query.IndexKey())
		if err != nil {
			return nil, err
		}

		// Adjust the number of keys to match available query options
		if offset := rs.query.Offset(); offset > 0 {
			if offset > len(keys) {
				keys = nil
			} else {
				keys = keys[offset:]
			}
		}

		if size := rs.query.Size(); size > 0 && size < len(keys) {
			keys = keys[:size]
		}

		rs.keys = keys
		rs.size = len(keys)
	} else {
		// When using the default ID index init size with the total number of records
		rs.size = rs.records.Size()

		// Adjust recordset size to match available query options
		if offset := rs.query.Offset(); offset > 0 {
			if offset > rs.size {
				rs.size = 0
			} else {
				rs.size -= offset
			}
		}

		if size := rs.query.Size(); size > 0 && size < rs.size {
			rs.size = size
		}
	}

	return rs, nil
}

// MustQuery returns a recordset that matches the query parameters or panics on error.
// By default query selects records using the ID index.
//
// Example usage:
//
//	// Get 50 records starting from the one at position 100
//	var records []Record
//	storage.MustQuery(
//		WithOffset(100),
//		WithSize(50),
//	).Iterate(func (r Record) bool {
//		records = append(records, r)
//		return false
//	})
func (s Storage) MustQuery(options ...QueryOption) Recordset {
	rs, err := s.Query(options...)
	if err != nil {
		panic(err)
	}
	return rs
}

// Get returns the first record found for a key within a storage index.
//
// This is a convenience method to get a single record. A multi index will
// always return the first record value for the specified key in this case.
// To get multiple records create a query using a custom index and key value
// or use the underlying storage collection.
func (s Storage) Get(indexName, indexKey string) (_ Record, found bool) {
	iter := s.collection.Get(indexName, indexKey)
	if iter.Next() {
		return iter.Value().Obj.(Record), true
	}
	return nil, false
}

// GetByID returns a record whose ID matches the specified ID.
func (s Storage) GetByID(id uint64) (_ Record, found bool) {
	iter := s.collection.Get(collection.IDIndex, seqid.ID(id).String())
	if iter.Next() {
		return iter.Value().Obj.(Record), true
	}
	return nil, false
}

// Delete deletes a record from the storage.
func (s Storage) Delete(id uint64) bool {
	return s.collection.Delete(id)
}

func (s Storage) getIndexRecordsKeys(indexName, indexKey string) ([]string, error) {
	idx := s.collection.GetIndex(indexName)
	if idx == nil {
		return nil, errors.New("storage index for query not found: " + indexName)
	}

	var keys []string
	if v, found := idx.Get(indexKey); found {
		keys = castIfaceToRecordKeys(v)
		if keys == nil {
			return nil, errors.New("unexpected storage index key format")
		}
	}
	return keys, nil
}

func castIfaceToRecordKeys(v interface{}) []string {
	switch k := v.(type) {
	case []string:
		return k
	case string:
		return []string{k}
	}
	return nil
}

package datastore

// StorageOption configures storages.
type StorageOption func(*Storage)

// WithSchema assigns a schema to the storage.
func WithSchema(s *Schema) StorageOption {
	return func(st *Storage) {
		if s != nil {
			st.schema = s
		}
	}
}

// WithIndex assigns an index to the storage.
func WithIndex(i Index) StorageOption {
	return func(st *Storage) {
		st.collection.AddIndex(i.name, i.fn, i.options)
	}
}

package datastore

import (
	"strings"
	"testing"

	"gno.land/p/nt/uassert"
	"gno.land/p/nt/urequire"
)

func TestStorageDefaults(t *testing.T) {
	name := "foo"
	storage := NewStorage(name)

	uassert.Equal(t, name, storage.Name())
	uassert.NotEqual(t, nil, storage.Collection())
	uassert.Equal(t, 0, storage.Size())

	s := storage.Schema()
	uassert.NotEqual(t, nil, s)
	uassert.Equal(t, strings.Title(name), s.Name())
}

func TestStorageNewRecord(t *testing.T) {
	field := "status"
	defaultValue := "testing"
	s := NewSchema("Foo", WithDefaultField(field, defaultValue))
	storage := NewStorage("foo", WithSchema(s))

	r := storage.NewRecord()
	urequire.NotEqual(t, nil, r, "new record is not nil")
	uassert.Equal(t, uint64(0), r.ID())
	uassert.Equal(t, storage.Schema().Name(), r.Type())

	v, found := r.Get(field)
	urequire.True(t, found, "default value found")

	got, ok := v.(string)
	urequire.True(t, ok, "default value type")
	uassert.Equal(t, defaultValue, got)
}

func TestStorageQuery(t *testing.T) {
	index := NewIndex("tag", func(r Record) string {
		if v, found := r.Get("tag"); found {
			return v.(string)
		}
		return ""
	})

	cases := []struct {
		name    string
		options []QueryOption
		results []uint64
		setup   func() *Storage
		errMsg  string
	}{
		{
			name:    "default query",
			results: []uint64{1, 2},
			setup: func() *Storage {
				s := NewStorage("foo")
				s.NewRecord().Save()
				s.NewRecord().Save()
				return &s
			},
		},
		{
			name:    "with size",
			results: []uint64{1},
			options: []QueryOption{WithSize(1)},
			setup: func() *Storage {
				s := NewStorage("foo")
				s.NewRecord().Save()
				s.NewRecord().Save()
				return &s
			},
		},
		{
			name:    "with offset",
			results: []uint64{2},
			options: []QueryOption{WithOffset(1)},
			setup: func() *Storage {
				s := NewStorage("foo")
				s.NewRecord().Save()
				s.NewRecord().Save()
				return &s
			},
		},
		{
			name:    "with offset overflow",
			options: []QueryOption{WithOffset(4)},
			setup: func() *Storage {
				s := NewStorage("foo")
				s.NewRecord().Save()
				return &s
			},
		},
		{
			name:    "with size and offset",
			results: []uint64{2, 3},
			options: []QueryOption{WithSize(2), WithOffset(1)},
			setup: func() *Storage {
				s := NewStorage("foo")
				s.NewRecord().Save()
				s.NewRecord().Save()
				s.NewRecord().Save()
				s.NewRecord().Save()
				return &s
			},
		},
		{
			name:    "custom index",
			options: []QueryOption{UseIndex("tag", "A")},
			results: []uint64{1, 3},
			setup: func() *Storage {
				s := NewStorage("foo", WithIndex(index))

				r := s.NewRecord()
				r.Set("tag", "A")
				r.Save()

				r = s.NewRecord()
				r.Set("tag", "B")
				r.Save()

				r = s.NewRecord()
				r.Set("tag", "A")
				r.Save()

				return &s
			},
		},
		{
			name:    "custom index with offset",
			options: []QueryOption{UseIndex("tag", "B"), WithOffset(1)},
			results: []uint64{3, 4},
			setup: func() *Storage {
				s := NewStorage("foo", WithIndex(index))

				r := s.NewRecord()
				r.Set("tag", "B")
				r.Save()

				r = s.NewRecord()
				r.Set("tag", "A")
				r.Save()

				r = s.NewRecord()
				r.Set("tag", "B")
				r.Save()

				r = s.NewRecord()
				r.Set("tag", "B")
				r.Save()

				return &s
			},
		},
		{
			name:    "custom index with offset and size",
			options: []QueryOption{UseIndex("tag", "B"), WithOffset(1), WithSize(1)},
			results: []uint64{3},
			setup: func() *Storage {
				s := NewStorage("foo", WithIndex(index))

				r := s.NewRecord()
				r.Set("tag", "B")
				r.Save()

				r = s.NewRecord()
				r.Set("tag", "A")
				r.Save()

				r = s.NewRecord()
				r.Set("tag", "B")
				r.Save()

				r = s.NewRecord()
				r.Set("tag", "B")
				r.Save()

				return &s
			},
		},
		{
			name:    "custom index not found",
			options: []QueryOption{UseIndex("foo", "B")},
			setup: func() *Storage {
				s := NewStorage("foo")
				return &s
			},
			errMsg: "storage index for query not found: foo",
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			// Arrange
			storage := tc.setup()

			// Act
			rs, err := storage.Query(tc.options...)

			// Assert
			if tc.errMsg != "" {
				uassert.ErrorContains(t, err, tc.errMsg, "expect error")
				return
			}

			urequire.NoError(t, err, "expect no error")
			urequire.NotEqual(t, nil, rs, "new record is not nil")
			urequire.Equal(t, len(tc.results), rs.Size(), "expect query results count to match")

			var i int
			rs.Iterate(func(r Record) bool {
				urequire.Equal(t, tc.results[i], r.ID(), "expect result IDs to match")
				i++
				return false
			})
		})
	}
}

func TestStorageGet(t *testing.T) {
	index := NewIndex("name", func(r Record) string {
		if v, found := r.Get("name"); found {
			return v.(string)
		}
		return ""
	})

	cases := []struct {
		name     string
		key      string
		recordID uint64
		setup    func(*Storage)
	}{
		{
			name:     "single record",
			key:      "foobar",
			recordID: 1,
			setup: func(s *Storage) {
				r := s.NewRecord()
				r.Set("name", "foobar")
				r.Save()
			},
		},
		{
			name:     "two records",
			key:      "foobar",
			recordID: 1,
			setup: func(s *Storage) {
				r := s.NewRecord()
				r.Set("name", "foobar")
				r.Save()

				r = s.NewRecord()
				r.Set("name", "foobar")
				r.Save()

				r = s.NewRecord()
				r.Set("name", "extra")
				r.Save()
			},
		},
		{
			name: "record not found",
			key:  "unknown",
			setup: func(s *Storage) {
				r := s.NewRecord()
				r.Set("name", "foobar")
				r.Save()
			},
		},
		{
			name: "empty storage",
			key:  "foobar",
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			storage := NewStorage("foo", WithIndex(index))
			if tc.setup != nil {
				tc.setup(&storage)
			}

			r, found := storage.Get(index.Name(), tc.key)

			if tc.recordID == 0 {
				uassert.Equal(t, nil, r, "expect no record")
				uassert.False(t, found, "expect record not found")
				return
			}

			uassert.True(t, found, "expect record found")
			urequire.NotEqual(t, nil, r, "expect record to be found")
			uassert.Equal(t, tc.recordID, r.ID(), "expect ID to match")
		})
	}
}

func TestStorageGetByID(t *testing.T) {
	cases := []struct {
