package expect

import (
	"strings"

	"gno.land/p/nt/ufmt"
)

const defaultAssertFailMsg = "assert expectation failed"

// NewContext creates a new testing context.
func NewContext(t TestingT) Context {
	return Context{t: t}
}

// Context preserves the current testing context.
type Context struct {
	t       TestingT
	negated bool
	prefix  string
}

// T returns context's testing T instance.
func (c Context) T() TestingT {
	if c.t == nil {
		panic("expect: context is not initialized")
	}
	return c.t
}

// Prefix returns context's error prefix.
func (c Context) Prefix() string {
	return c.prefix
}

// IsNegated checks if current context negates current assert expectations.
func (c Context) IsNegated() bool {
	return c.negated
}

// CheckExpectation checks an assert expectation and calls a callback on fail.
// It returns true when the asserted expectation fails.
// Callback is called when a negated assertion succeeds or when non negated assertion fails.
func (c Context) CheckExpectation(success bool, cb func(Context) string) bool {
	failed := (c.negated && success) || (!c.negated && !success)
	if failed {
		msg := cb(c)
		if strings.TrimSpace(msg) == "" {
			msg = defaultAssertFailMsg
		}

		c.Fail(msg)
	}
	return failed
}

// Fail makes the current test fail with a custom message.
func (c Context) Fail(msg string, args ...any) {
	if c.prefix != "" {
		msg = c.prefix + " - " + msg
	}

	c.t.Fatalf(msg, args...)
}

// TestingT defines a minimal interface for `testing.T` instances.
type TestingT interface {
	Helper()
	Fatal(args ...any)
	Fatalf(format string, args ...any)
}

// MockTestingT creates a new testing mock that writes testing output to a string builder.
func MockTestingT(output *strings.Builder) TestingT {
	return &testingT{output}
}

type testingT struct{ buf *strings.Builder }

func (testingT) Helper()                          {}
func (t testingT) Fatal(args ...any)              { t.buf.WriteString(ufmt.Sprintln(args...)) }
func (t testingT) Fatalf(fmt string, args ...any) { t.buf.WriteString(ufmt.Sprintf(fmt+"\n", args...)) }

// Package expect provides testing support for packages and realms.
//
// The opinionated approach taken on this package for testing is to use function chaining and
// semanthics to hopefully make unit and file testing fun. Focus is not on speed as there are
// other packages that would run tests faster like the official `uassert` or `urequire` packages.
//
// Values can be asserted using the `Value()` function, for example:
//
//	func TestFoo(t *testing.T) {
//	  got := 42
//	  expect.Value(t, got).ToEqual(42)
//	  expect.Value(t, got).Not().ToEqual(0)
//
//	  expect.Value(t, "foo").ToEqual("foo")
//	  expect.Value(t, 42).AsInt().Not().ToBeGreaterThan(50)
//	  expect.Value(t, "TRUE").AsBoolean().ToBeTruthy()
//	}
//
// Functions can also be used to assert returned values, errors or panics.
//
// Package supports four type of functions:
//
//   - func()
//   - func() any
//   - func() error
//   - func() (any, error)
//
// Functions can be asserted using the `Func()` function, for example:
//
//	func TestFoo(t *testing.T) {
//	  expect.Func(t, func() {
//	    panic("Boom!")
//	  }).ToPanic().WithMessage("Boom!")
//
//	  wantErr := errors.New("Boom!")
//	  expect.Func(t, func() error {
//	    return wantErr
//	  }).ToFail().WithMessage("Boom!")
//
//	  expect.Func(t, func() error {
//	    return wantErr
//	  }).ToFail().WithError(wantErr)
//	}
package expect

package expect

import "gno.land/p/nt/ufmt"

// NewErrorChecker creates a new checker of errors.
func NewErrorChecker(ctx Context, err error) ErrorChecker {
	return ErrorChecker{ctx, err}
}

// ErrorChecker asserts error values.
type ErrorChecker struct {
	ctx Context
	err error
}

// Not negates the next called expectation.
func (c ErrorChecker) Not() ErrorChecker {
	c.ctx.negated = !c.ctx.negated
	return c
}

// WithMessage asserts that current error contains an expected message.
func (c ErrorChecker) WithMessage(msg string) {
	c.ctx.T().Helper()

	if c.err == nil {
		c.ctx.Fail("Expected an error with message\nGot: nil\nWant: %s", msg)
		return
	}

	NewMessageChecker(c.ctx, c.err.Error(), MessageTypeError).WithMessage(msg)
}

// WithError asserts that current error message is the same as an expected error.
func (c ErrorChecker) WithError(err error) {
	c.ctx.T().Helper()

	if c.err == nil {
		if err != nil {
			c.ctx.Fail("Expected an error\nGot: nil\nWant: %s", err.Error())
		}
		return
	}

	got := c.err.Error()
	c.ctx.CheckExpectation(got == err.Error(), func(ctx Context) string {
		if !ctx.IsNegated() {
			return ufmt.Sprintf("Expected errors to match\nGot: %s\nWant: %s", got, err.Error())
		}
		return ufmt.Sprintf("Expected errors to be different\nGot: %s", got)
	})
}

