// Package coinsort provides helpers to sort a slice of std.Coins using the
// classic sort.Sort API (without relying on sort.Slice).
//
// Usage examples:
//
//	coins := banker.GetCoins("g1....")
//
//	// Ascending by balance
//	coinsort.SortByBalance(coins)
//
//	// Custom order â largest balance first
//	coinsort.SortBy(coins, func(a, b std.Coin) bool {
//	    return a.Amount > b.Amount // descending
//	})
//
// Note: when getting std.Coins from the banker, it's sorted by denom by default.
package coinsort

import (
	"sort"
	"std"
)

type ByAmount struct{ std.Coins }

func (b ByAmount) Len() int           { return len(b.Coins) }
func (b ByAmount) Swap(i, j int)      { b.Coins[i], b.Coins[j] = b.Coins[j], b.Coins[i] }
func (b ByAmount) Less(i, j int) bool { return b.Coins[i].Amount < b.Coins[j].Amount }

// SortByBalance sorts c in ascending order by Amount.
//
//	coinsort.SortByBalance(myCoins)
func SortByBalance(c std.Coins) {
	sort.Sort(ByAmount{c})
}

// LessFunc defines the comparison function for SortBy. It must return true if
// 'a' should come before 'b'.

type LessFunc func(a, b std.Coin) bool

// customSorter adapts a LessFunc to sort.Interface so we can keep using
// sort.Sort (rather than sort.Slice).

type customSorter struct {
	coins std.Coins
	less  LessFunc
}

func (cs customSorter) Len() int      { return len(cs.coins) }
func (cs customSorter) Swap(i, j int) { cs.coins[i], cs.coins[j] = cs.coins[j], cs.coins[i] }
func (cs customSorter) Less(i, j int) bool {
	return cs.less(cs.coins[i], cs.coins[j])
}

// SortBy sorts c in place using the provided LessFunc.
//
// Example â descending by Amount:
//
//	coinsort.SortBy(coins, func(a, b std.Coin) bool {
//	    return a.Amount > b.Amount
//	})
func SortBy(c std.Coins, less LessFunc) {
	if less == nil {
		return // nothing to do; keep original order
	}
	sort.Sort(customSorter{coins: c, less: less})
}

package coinsort

import (
	"std"
	"testing"
)

func TestSortByBalance(t *testing.T) {
	coins := std.Coins{
		std.Coin{Denom: "b", Amount: 50},
		std.Coin{Denom: "c", Amount: 10},
		std.Coin{Denom: "a", Amount: 100},
	}

	expected := std.Coins{
		std.Coin{Denom: "c", Amount: 10},
		std.Coin{Denom: "b", Amount: 50},
		std.Coin{Denom: "a", Amount: 100},
	}

	SortByBalance(coins)

	for i := range coins {
		if coins[i] != expected[i] {
			t.Errorf("SortByBalance failed at index %d: got %+v, want %+v", i, coins[i], expected[i])
		}
	}
}

func TestSortByCustomDescendingAmount(t *testing.T) {
	coins := std.Coins{
		std.Coin{Denom: "a", Amount: 2},
		std.Coin{Denom: "b", Amount: 3},
		std.Coin{Denom: "c", Amount: 1},
	}

	expected := std.Coins{
		std.Coin{Denom: "b", Amount: 3},
		std.Coin{Denom: "a", Amount: 2},
		std.Coin{Denom: "c", Amount: 1},
	}

	SortBy(coins, func(a, b std.Coin) bool {
		return a.Amount > b.Amount // descending
	})

	for i := range coins {
		if coins[i] != expected[i] {
			t.Errorf("SortBy custom descending failed at index %d: got %+v, want %+v", i, coins[i], expected[i])
		}
	}
}

func TestSortByNilFunc(t *testing.T) {
	coins := std.Coins{
		std.Coin{Denom: "x", Amount: 5},
		std.Coin{Denom: "z", Amount: 20},
		std.Coin{Denom: "y", Amount: 10},
	}

	expected := std.Coins{
		std.Coin{Denom: "x", Amount: 5},
		std.Coin{Denom: "z", Amount: 20},
		std.Coin{Denom: "y", Amount: 10},
	}

	SortBy(coins, nil)

	// should stay the same
	for i := range coins {
		if coins[i] != expected[i] {
			t.Errorf("SortBy nil func failed at index %d: got %+v, want %+v", i, coins[i], expected[i])
		}
	}
}

// Package ctg is a simple utility package with helpers
// for bech32 address conversions.
package ctg

import (
	"std"

	"gno.land/p/nt/ufmt"
)

// ConvertCosmosToGno takes a Bech32 Cosmos address (prefix "cosmos")
// and returns the same address re-encoded with the gno.land prefix "g".
func ConvertCosmosToGno(addr string) (std.Address, error) {
	prefix, decoded, ok := std.DecodeBech32(std.Address(addr))
	if !ok {
		return "", ufmt.Errorf("bech32 decode failed")
	}

	if prefix != "cosmos" {
		return "", ufmt.Errorf("expected a cosmos address, got prefix %q", prefix)
	}

	return std.EncodeBech32("g", decoded), nil
}

// ConvertAnyToGno converts *any* valid Bech32 address to its gno.land form
// by preserving the underlying payload but replacing the prefix with "g".
// No prefix check is performed; invalid Bech32 input still returns an error.
func ConvertAnyToGno(addr string) (std.Address, error) {
	_, decoded, ok := std.DecodeBech32(std.Address(addr))
	if !ok {
		return "", ufmt.Errorf("bech32 decode failed")
	}
	return std.EncodeBech32("g", decoded), nil
}

// ConvertGnoToAny converts a gno.land address (prefixed with "g") to another Bech32
// prefix given by prefix. The function ensures the source address really
// is a gno.land address before proceeding.
//
// Example:
//
//	cosmosAddr, _ := ConvertGnoToAny("cosmos", "g1k98jx9...")
//	fmt.Println(cosmosAddr) // â cosmos1....
func ConvertGnoToAny(prefix string, addr std.Address) (string, error) {
	origPrefix, decoded, ok := std.DecodeBech32(addr)
	if !ok {
		return "", ufmt.Errorf("bech32 decode failed")
	}
	if origPrefix != "g" {
		return "", ufmt.Errorf("expected a gno address but got prefix %q", origPrefix)
	}
	return std.EncodeBech32(prefix, decoded).String(), nil
}

package ctg

import (
	"std"
	"testing"
)

func TestConvertKnownAddress(t *testing.T) {
	const (
		cosmosAddr = "cosmos1jg8mtutu9khhfwc4nxmuhcpftf0pajdh6svrgs"
		gnoAddr    = "g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5"
	)
	got, err := ConvertCosmosToGno(cosmosAddr)
	if err != nil {
		t.Fatalf("unexpected error: %v", err)
	}
	if got != gnoAddr {
		t.Fatalf("got %s, want %s", got, gnoAddr)
	}
}

func TestConvertCosmosToGno(t *testing.T) {
	decoded := [20]byte{
		0x01, 0x02, 0x03, 0x04, 0x05,
		0x06, 0x07, 0x08, 0x09, 0x0a,
		0x0b, 0x0c, 0x0d, 0x0e, 0x0f,
		0x10, 0x11, 0x12, 0x13, 0x14,
	}

	cosmosAddr := std.EncodeBech32("cosmos", decoded)
	wantGno := std.EncodeBech32("g", decoded)

	got, err := ConvertCosmosToGno(cosmosAddr.String())
	if err != nil {
		t.Fatalf("unexpected error: %v", err)
	}
	if got != wantGno {
		t.Fatalf("got %s, want %s", got, wantGno)
	}

	// invalid bech32
	if _, err := ConvertCosmosToGno("not-bech32"); err == nil {
		t.Fatalf("expected error for invalid bech32")
	}

	// wrong prefix
	gAddr := std.EncodeBech32("g", decoded)
	if _, err := ConvertCosmosToGno(gAddr.String()); err == nil {
		t.Fatalf("expected error for non-cosmos prefix")
	}
}

func TestConvertAnyToGno(t *testing.T) {
	payload := [20]byte{
		0x01, 0x02, 0x03, 0x04, 0x05,
		0x06, 0x07, 0x08, 0x09, 0x0a,
		0x0b, 0x0c, 0x0d, 0x0e, 0x0f,
		0x10, 0x11, 0x12, 0x13, 0x14,
	}

	tests := []struct {
		name    string
		input   std.Address
		want    std.Address
		wantErr bool
	}{
		{
			name:  "cosmosâg",
			input: std.EncodeBech32("cosmos", payload),
			want:  std.EncodeBech32("g", payload),
		},
		{
			name:  "osmoâg",
			input: std.EncodeBech32("osmo", payload),
			want:  std.EncodeBech32("g", payload),
		},
		{
			name:    "invalid bech32",
			input:   std.Address("xyz123"),
			wantErr: true,
		},
	}

	for _, tc := range tests {
		t.Run(tc.name, func(t *testing.T) {
			got, err := ConvertAnyToGno(tc.input.String())
			if tc.wantErr {
				if err == nil {
					t.Fatalf("expected error, got nil")
				}
				return
			}
			if err != nil {
				t.Fatalf("unexpected error: %v", err)
			}
			if got != tc.want {
				t.Fatalf("got %s, want %s", got, tc.want)
			}
		})
	}
}

func TestConvertGnoToAny(t *testing.T) {
	payload := [20]byte{
		0x01, 0x02, 0x03, 0x04, 0x05,
		0x06, 0x07, 0x08, 0x09, 0x0a,
		0x0b, 0x0c, 0x0d, 0x0e, 0x0f,
		0x10, 0x11, 0x12, 0x13, 0x14,
	}

	gno := std.EncodeBech32("g", payload)

	t.Run("gâcosmos", func(t *testing.T) {
		got, err := ConvertGnoToAny("cosmos", gno)
		if err != nil {
			t.Fatalf("unexpected error: %v", err)
		}
		if got != std.EncodeBech32("cosmos", payload).String() {
			t.Fatalf("conversion incorrect: %s", got)
		}
	})

	t.Run("gâfoobar", func(t *testing.T) {
		got, err := ConvertGnoToAny("foobar", gno)
		if err != nil {
			t.Fatalf("unexpected error: %v", err)
		}
		if got != std.EncodeBech32("foobar", payload).String() {
			t.Fatalf("conversion incorrect: %s", got)
		}
	})

	t.Run("gâosmo", func(t *testing.T) {
		got, err := ConvertGnoToAny("osmo", gno)
		if err != nil {
			t.Fatalf("unexpected error: %v", err)
		}
		if got != std.EncodeBech32("osmo", payload).String() {
			t.Fatalf("conversion incorrect: %s", got)
		}
	})

	t.Run("wrong source prefix", func(t *testing.T) {
		cosmos := std.EncodeBech32("cosmos", payload)
		if _, err := ConvertGnoToAny("g", cosmos); err == nil {
			t.Fatalf("expected error for non-g source prefix")
		}
	})

	t.Run("invalid bech32", func(t *testing.T) {
		if _, err := ConvertGnoToAny("cosmos", std.Address("nope")); err == nil {
			t.Fatalf("expected error for invalid bech32")
		}
	})
}

// Package pkgerr provides a custom error wrapper that prepends the realm link to your error
// This is useful for identifying the source package of the error.
//
// Usage:
//
// To wrap an error with realm and chain domain information, use the `New` function:
//
//	err := pkgerr.New("my error message") // in gno.land/r/leon/example
//	fmt.Println(err.Error()) // Output: "r/leon/example: my error message"
package pkgerr

import (
	"errors"
	"std"
	"strings"

	"gno.land/p/nt/ufmt"
)

// pkgErr is a custom error type that prepends the current
// realm link to the original error message.
type pkgErr struct {
	originalErr error
}

// New creates a new pkgErr with the given error. The returned error will include
// the current realm link in its message.
func New(msg string) error {
	return &pkgErr{originalErr: errors.New(msg)}
}

func Wrap(err error) error {
	if err == nil {
		return nil
	}

	return &pkgErr{originalErr: err}
}

func (e pkgErr) Unwrap() error {
	return e.originalErr
}

// Error implements the `error` interface for pkgErr.
func (e *pkgErr) Error() string {
	return ufmt.Sprintf("%s: %s",
		strings.TrimPrefix(std.CurrentRealm().PkgPath(), "gno.land/"),
		e.originalErr)
}

package pkgerr

import (
	"errors"
	"std"
	"strings"
	"testing"
)

const pkgPath = "gno.land/r/leon/test"

var prefix = strings.TrimPrefix(pkgPath, "gno.land/")

func TestNew(t *testing.T) {
	err := New("my error message")
	if err == nil {
		t.Fatal("Expected an error, got nil")
	}

	testing.SetRealm(std.NewCodeRealm(pkgPath))

	expected := prefix + ": my error message"

	if err.Error() != expected {
		t.Errorf("Expected error message %q, got %q", expected, err.Error())
	}
}

func TestWrap(t *testing.T) {
	testing.SetRealm(std.NewCodeRealm(pkgPath))

	originalErr := errors.New("original error")
	wrappedErr := Wrap(originalErr)
	if wrappedErr == nil {
		t.Fatal("Expected an error, got nil")
	}

	expected := prefix + ": original error"
	if wrappedErr.Error() != expected {
		t.Errorf("Expected error message %q, got %q", expected, wrappedErr.Error())
	}
}

func TestUnwrap(t *testing.T) {
	testing.SetRealm(std.NewCodeRealm(pkgPath))
	originalErr := errors.New("original error")
	wrappedErr := Wrap(originalErr)

	unwrappedErr := wrappedErr.(*pkgErr).Unwrap()
	if unwrappedErr != originalErr {
		t.Errorf("Expected unwrapped error %v, got %v", originalErr, unwrappedErr)
	}
}

func TestErrorMethod(t *testing.T) {
	testing.SetRealm(std.NewCodeRealm(pkgPath))
	originalErr := errors.New("original error")
	pkgErr := &pkgErr{originalErr: originalErr}

	expected := prefix + ": original error"
	if pkgErr.Error() != expected {
		t.Errorf("Expected error message %q, got %q", expected, pkgErr.Error())
	}
}

func TestWrapNilError(t *testing.T) {
	err := Wrap(nil)
	if err != nil {
		t.Errorf("Expected nil error, got %v", err)
	}
}

func TestNewWithEmptyMessage(t *testing.T) {
	testing.SetRealm(std.NewCodeRealm(pkgPath))
	err := New("")
	if err == nil {
		t.Fatal("Expected an error, got nil")
	}

	expected := prefix + ": "
	if err.Error() != expected {
		t.Errorf("Expected error message %q, got %q", expected, err.Error())
	}
}

// Package svgbtn provides utilities for generating SVG-styled buttons as Markdown image links.
//
// Buttons are rendered as SVG images with customizable size, colors, labels, and links.
// This package includes preconfigured styles such as Primary, Danger, Success, Small, Wide,
// Text-like, and Icon buttons, as well as a factory method for dynamic button creation.
//
// Example usage:
//
//	func Render(_ string) string {
//		btn := svgbtn.PrimaryButton(120, 40, "Click Me", "https://example.com")
//		return btn
//	}
//
// See more examples at gno.land/r/leon:buttons
//
// All buttons are returned as Markdown-compatible strings: [svg_data](link).
package svgbtn

import (
	"gno.land/p/demo/svg"
	"gno.land/p/nt/ufmt"
)

// Button creates a base SVG button with given size, colors, label, and link.
// - `width`, `height`: size in pixels
// - `btnColor`: background color (e.g. "#007BFF")
// - `textColor`: label color (e.g. "#FFFFFF")
// - `text`: visible button label
// - `link`: URL to wrap the image in markdown-style [svg](link)
func Button(width, height int, btnColor, textColor, text, link string) string {
	canvas := svg.NewCanvas(width, height).
		WithViewBox(0, 0, width, height).
		AddStyle("text", "font-family:sans-serif;font-size:14px;text-anchor:middle;dominant-baseline:middle;")

	bg := svg.NewRectangle(0, 0, width, height, btnColor)
	bg.RX = height / 5
	bg.RY = height / 5

	label := svg.NewText(width/2, height/2, text, textColor)

	canvas.Append(bg, label)

	return ufmt.Sprintf("[%s](%s)", canvas.Render(text), link)
}

// PrimaryButton renders a blue button with white text.
func PrimaryButton(width, height int, text, link string) string {
	return Button(width, height, "#007BFF", "#ffffff", text, link)
}

// DangerButton renders a red button with white text.
func DangerButton(width, height int, text, link string) string {
	return Button(width, height, "#DC3545", "#ffffff", text, link)
}

// SuccessButton renders a green button with white text.
func SuccessButton(width, height int, text, link string) string {
	return Button(width, height, "#28A745", "#ffffff", text, link)
}

// SmallButton renders a compact gray button with white text.
func SmallButton(width, height int, text, link string) string {
	return Button(width, height, "#6C757D", "#ffffff", text, link)
}

// WideButton renders a wider cyan button with white text.
func WideButton(width, height int, text, link string) string {
	return Button(width, height, "#17A2B8", "#ffffff", text, link)
}

// TextButton renders a white button with colored text, like a hyperlink.
func TextButton(width, height int, text, link string) string {
	return Button(width, height, "#ffffff", "#007BFF", text, link)
}

// IconButton renders a square button with an icon character (e.g. emoji).
func IconButton(width, height int, icon, link string) string {
	return Button(width, height, "#E0E0E0", "#000000", icon, link)
}

// ButtonFactory provides a named-style constructor for buttons.
// Supported kinds: "primary", "danger", "success", "small", "wide", "text", "icon".
func ButtonFactory(kind string, width, height int, text, link string) string {
	switch kind {
	case "primary":
		return PrimaryButton(width, height, text, link)
	case "danger":
		return DangerButton(width, height, text, link)
	case "success":
		return SuccessButton(width, height, text, link)
	case "small":
		return SmallButton(width, height, text, link)
	case "wide":
		return WideButton(width, height, text, link)
	case "text":
		return TextButton(width, height, text, link)
	case "icon":
		return IconButton(width, height, text, link)
	default:
		return PrimaryButton(width, height, text, link)
	}
}

package ascii

import (
	"strconv"
	"strings"

	"gno.land/p/moul/md"
)

type WAlignment string
type HAlignment string

const (
	// Width Alignment
	AlignWCenter WAlignment = "center"
	AlignLeft    WAlignment = "left"
	AlignRight   WAlignment = "right"

	// Height Alignment
	AlignHCenter HAlignment = "center"
	AlignTop     HAlignment = "top"
	AlignBottom  HAlignment = "bottom"
)

// padLine aligns text within a given width.
//
// Supports AlignLeft, AlignRight, and AlignWCenter alignment.
func padLine(line string, width int, align WAlignment, space string) string {
	padding := width - len(line)
	if width < len(line) {
		padding = 0
	}

	switch align {
	case AlignRight:
		return Repeat(space, padding) + line
	case AlignWCenter:
		left := padding / 2
		right := padding - left
		return Repeat(space, left) + line + Repeat(space, right)
	default: // AlignLeft
		return line + Repeat(space, padding)
	}
}

// padHeight pads lines vertically according to alignment.
//
// Supports AlignTop, AlignBottom, and AlignHCenter alignment.
func padHeight(lines []string, height int, align HAlignment) []string {
	padded := []string{}
	if height <= 0 {
		return lines
	}
	extra := height - len(lines)

	topPad := 0
	switch align {
	case AlignBottom:
		topPad = extra
	case AlignHCenter:
		topPad = extra / 2
	}

	for i := 0; i < topPad; i++ {
		padded = append(padded, "")
	}
	padded = append(padded, lines...)
	for len(padded) < height {
		padded = append(padded, "")
	}
	return padded
}

// Repeat returns repetition of a string n times.
func Repeat(char string, n int) string {
	if n <= 0 {
		return ""
	}
	return strings.Repeat(char, n)
}

// Box draws a single-line text in a simple box.
// If the string contains newlines, it falls back to FlexFrame function.
//
// Example:
//
//	Box("Hello World\n!")
//
// Gives:
//
//	+-------------+
//	| Hello World |
//	| !           |
//	+-------------+
func Box(text string) string {
	return FlexFrame(strings.Split(text, "\n"), AlignLeft)
}

// FlexFrame draws a frame with automatic width and alignment.
//
// Example:
//
//	FlexFrame([]string{"hello", "worldd", "!!"}, "right")
//
// Gives:
//
//	+-------+
//	|  hello |
//	| worldd |
//	|     !! |
//	+-------+
func FlexFrame(lines []string, align WAlignment) string {
	maxWidth := 0

	for _, line := range lines {
		if len(line) > maxWidth {
			maxWidth = len(line)
		}
	}

	top := "+" + Repeat("-", maxWidth+2) + "+\n"
	bottom := "+" + Repeat("-", maxWidth+2) + "+"

	body := ""
	for i := 0; i < len(lines); i++ {
		body += "| " + padLine(lines[i], maxWidth, align, " ") + " |\n"
	}

	return md.CodeBlock(top+body+bottom) + "\n"
}

// Frame draws a frame with specific width, height and alignment options.
//
// Example:
//
//	Frame([]string{"hello", "world", "!!"}, "center", 10, 5, "center")
//
// Gives:
//
//	+------------+
//	|            |
//	|   hello    |
//	|   world    |
//	|     !!     |
//	|            |
//	+------------+
func Frame(
	lines []string,
	wAlign WAlignment,
	width, height int,
	hAlign HAlignment,
) string {
	maxWidth := width
	for i := 0; i < len(lines); i++ {
		if len(lines[i]) > maxWidth {
			maxWidth = len(lines[i])
		}
	}

	if len(lines) > height {
		height = len(lines)
	}
	lines = padHeight(lines, height, hAlign)

	top := "+" + Repeat("-", maxWidth+2) + "+\n"
	bottom := "+" + Repeat("-", maxWidth+2) + "+"
	body := ""

	for _, line := range lines {
		body += "| " + padLine(line, maxWidth, wAlign, " ") + " |\n"
	}

	return md.CodeBlock(top+body+bottom) + "\n"
}

// FixedFrame draws a frame with a fixed width and height, truncating or wrapping content as needed.
// Width and height include the content area, not the frame borders.
//
// Example:
//
//	Frame([]string{"hello world!!"}, ascii.AlignWCenter, 10, 4, ascii.AlignHCenter)
//
// Gives:
//
//	+------------+
//	|            |
//	|   hello    |
//	|  world!!   |
//	|            |
//	+------------+
func FixedFrame(
	lines []string,
	wAlign WAlignment,
	width, height int,
	hAlign HAlignment,
) string {
	var wrapped []string
	if width < 0 {
		width = 0
	}
	if height < 0 {
		height = 0
	}

	for _, line := range lines {
		words := strings.Fields(line)
		current := ""
		for _, word := range words {
			if len(current)+len(word)+1 > width {
				wrapped = append(wrapped, current)
				current = word
			} else {
				if current == "" {
					current = word
				} else {
					current += " " + word
				}
			}
		}
		if current != "" {
			wrapped = append(wrapped, current)
		}
	}
	wrapped = padHeight(wrapped, height, hAlign)

	top := "+" + Repeat("-", width+2) + "+\n"
	bottom := "+" + Repeat("-", width+2) + "+"

	body := ""
	for i, line := range wrapped {
		if i == height {
			break
		}
		body += "| " + padLine(line, width, wAlign, " ") + " |\n"
	}

	return md.CodeBlock(top+body+bottom) + "\n"
}

// ProgressBar renders a visual progress bar, the size represents the number of chars in length for the bar.
//
// Example:
//
//	ProgressBar(2, 6, 10, true)
//
// Gives: [###-------] 33%
func ProgressBar(current int, total int, charSize int, displayPercent bool) string {
	if total == 0 {
		return PercentageBar(0, charSize, displayPercent)
	}
	percent := (current * 100) / total

	return PercentageBar(percent, charSize, displayPercent)
}

// PercentageBar renders a visual progress bar, the size represents the number of chars in length for the bar.
// This differs from ProgressBar in that it does not require a total value, takes a percentage directly.
//
// Example:
//
//	PercentageBar(50, 6, true)
//
// Gives: [###---] 50%
func PercentageBar(percent int, charSize int, displayPercent bool) string {
	fillLength := (percent * charSize) / 100
	emptyLength := charSize - fillLength

	filled := Repeat("#", fillLength)
	empty := Repeat("-", emptyLength)

	out := "[" + filled + empty + "]"
	if !displayPercent {
		return out
	}
	return out + " " + strconv.Itoa(percent) + "%"
}

// Grid renders a 2D grid of characters.
//
// Example:
//
//	Grid(3, 3, "x")
//
// Gives:
//
//	xxx
//	xxx
//	xxx
func Grid(rows int, cols int, fill string) string {
	out := ""
	if rows <= 0 || cols <= 0 {
		return out
	}

	for r := 0; r < rows; r++ {
		row := ""
		for c := 0; c < cols; c++ {
			row += fill
		}
		out += row + "\n"
	}
	return out
}

