/*
Package ascii provides a simple way to create ASCII components in Gno.

The ascii package allows you to create basic ASCII components like boxes and lines, and output the generated ASCII to a string in codeblock format when necessary.

Example:

	import "gno.land/p/lou/ascii"

	func Foo() string {
	    return ascii.Frame([]string{"Hello, World!"}, 20, 5, ascii.AlignWCenter, ascii.AlignHCenter)
	}

*/

package ascii // import "gno.land/p/lou/ascii"

/*
Package query provides a simple way to handle queries in your realm.

It includes helper functions to extract individual query parameters, convert the full query string into a map,
and update one or more parameters in a URL.

Example:

	import (
		"gno.land/p/lou/query"
		"gno.land/p/moul/md"
	)

	func Render(rawPath string) string {
		mode, err := query.GetQueryValueFromURL("mode", rawPath)
		if err != nil || mode == "" {
			mode = "grid" // default value
		}

		// Handle queryMode value
		listURL, _ := query.UpdateQueryValue(rawPath, "mode", "list")
		gridURL, _ := query.UpdateQueryValue(rawPath, "mode", "grid")

		out := md.Link("list mode", listURL) + "\n\n"
		out += md.Link("grid mode", gridURL) + "\n\n"
		return out
	}

*/

package query // import "gno.land/p/lou/query"

package query

import (
	"net/url"
)

// GetQueryValues returns all values for a given key in query parameters.
func GetQueryValues(params url.Values, key string) []string {
	return params[key]
}

// GetQueryFirstValue returns the first value for a given key in query parameters.
func GetQueryFirstValue(params url.Values, key string) string {
	if vals, ok := params[key]; ok && len(vals) > 0 {
		return vals[0]
	}
	return ""
}

// HasQueryKey checks if the given key exists in query parameters.
func HasQueryKey(params url.Values, key string) bool {
	_, exists := params[key]
	return exists
}

// GetQueryValueFromURL retrieves a single query value from the raw URL.
// If the URL is invalid or the parameter does not exist, it returns an empty string.
//
//	 value := ParseQueryParams("user", "https://example.com/?user=john")
//		// value == "john"
func GetQueryValueFromURL(key, rawPath string) (string, error) {
	u, err := url.Parse(rawPath)
	if err != nil {
		return "", err
	}
	return u.Query().Get(key), nil
}

// ParseQueryFirstValuesMap returns a map of the first values for all query parameters.
//
//	m := ParseQueryFirstValuesMap("https://example.com/?foo=1&bar=2")
//	// m == map[string]string{"foo": "1", "bar": "2"}
func ParseQueryFirstValuesMap(rawPath string) (map[string]string, error) {
	u, err := url.Parse(rawPath)
	if err != nil {
		return nil, err
	}

	result := make(map[string]string)
	for key, vals := range u.Query() {
		if len(vals) > 0 {
			result[key] = vals[0]
		}
	}
	return result, nil
}

// UpdateQueryValue sets a single query key to one value, replacing existing values.
//
//	url := UpdateQueryValue("https://example.com/?foo=1", "foo", "2")
//	// url == "https://example.com/?foo=2"
func UpdateQueryValue(rawPath, key, value string) (string, error) {
	return UpdateQueryValues(rawPath, key, []string{value})
}

// UpdateQueryValues sets a single query key to multiple values.
// Existing values are replaced, and new ones are added. Returns the modified URL as a string.
//
//	url := UpdateQueryValues("https://example.com/?foo=1", "foo", []string{"2", "3"})
//	// url == "https://example.com/?foo=2&foo=3"
func UpdateQueryValues(rawPath, key string, values []string) (string, error) {
	u, err := url.Parse(rawPath)
	if err != nil {
		return "", err
	}
	q := u.Query()
	q[key] = values
	u.RawQuery = q.Encode()
	return u.String(), nil
}

// UpdateQueryFirstValues sets multiple query parameters to single values.
// Existing parameters are overwritten, and new ones are added. Returns the modified URL as a string.
//
//	url := UpdateQueryFirstValues("https://example.com/?foo=1", map[string]string{"foo": "2", "bar": "3"})
//	// url == "https://example.com/?bar=3&foo=2"
func UpdateQueryFirstValues(rawPath string, updates map[string]string) (string, error) {
	u, err := url.Parse(rawPath)
	if err != nil {
		return "", err
	}
	q := u.Query()
	for k, v := range updates {
		q.Set(k, v)
	}
	u.RawQuery = q.Encode()
	return u.String(), nil
}

// UpdateQueryAllValues sets multiple query parameters, each with multiple values.
// Existing parameters are replaced, and new ones are added. Returns the modified URL as a string.
//
//	url := UpdateQueryAllValues("https://example.com/?foo=1", map[string][]string{"foo": {"2", "3"}, "bar": {"4"}})
//	// url == "https://example.com/?bar=4&foo=2&foo=3"
func UpdateQueryAllValues(rawPath string, updates map[string][]string) (string, error) {
	u, err := url.Parse(rawPath)
	if err != nil {
		return "", err
	}
	q := u.Query()
	for k, v := range updates {
		q[k] = v
	}
	u.RawQuery = q.Encode()
	return u.String(), nil
}

// SetQueries sets multiple query parameters in the URL.
// It replaces existing parameters and adds new ones, returning the modified URL as a string.
//
// url := SetQueries("https://example.com/?fa=1", map[string]string{"foo": "2", "bar": "3"})
// // url == "https://example.com/?bar=3&foo=2"
func SetQueries(rawPath string, queries map[string]string) (string, error) {
	u, err := url.Parse(rawPath)
	if err != nil {
		return "", err
	}
	q := u.Query()
	for k, v := range queries {
		q.Set(k, v)
	}
	u.RawQuery = q.Encode()
	return u.String(), nil
}

// SetQueriesMulti sets multiple query parameters with multiple values.
// It replaces existing parameters and adds new ones, returning the modified URL as a string.
//
// url := SetQueriesMulti("https://example.com/?fa=1", map[string][]string{"foo": {"2", "3"}, "bar": {"4"}})
// // url == "https://example.com/?bar=4&foo=2&foo=3"
func SetQueriesMulti(rawPath string, queries map[string][]string) (string, error) {
	u, err := url.Parse(rawPath)
	if err != nil {
		return "", err
	}
	q := u.Query()
	for k, v := range queries {
		q[k] = v
	}
	u.RawQuery = q.Encode()
	return u.String(), nil
}

// DeleteQuery removes a key from the query parameters.
//
//	url := DeleteQuery("https://example.com/?foo=1&bar=2", "foo")
//	// url == "https://example.com/?bar=2"
func DeleteQuery(rawPath, key string) (string, error) {
	u, err := url.Parse(rawPath)
	if err != nil {
		return "", err
	}
	q := u.Query()
	q.Del(key)
	u.RawQuery = q.Encode()
	return u.String(), nil
}

// ResetQuery clears all query parameters from the URL path.
//
//	url := ResetQuery("https://example.com/?foo=1&bar=2")
//	// url == "https://example.com/"
func ResetQuery(rawPath string) (string, error) {
	u, err := url.Parse(rawPath)
	if err != nil {
		return "", err
	}
	u.RawQuery = ""
	return u.String(), nil
}

package query

import (
	"net/url"
	"testing"

	"gno.land/p/nt/uassert"
	"gno.land/p/nt/urequire"
)

func TestUpdateQueryValue(t *testing.T) {
	raw := "/blog?foo=bar"
	updated, err := UpdateQueryValue(raw, "foo", "baz")
	urequire.NoError(t, err, "no error")
	uassert.Equal(t, "/blog?foo=baz", updated)
}

func TestUpdateQueryFirstValues(t *testing.T) {
	raw := "/blog?sort=alpha"
	updated, err := UpdateQueryFirstValues(raw, map[string]string{
		"sort":  "recent",
		"order": "asc",
	})
	urequire.NoError(t, err, "no error")
	expected1 := "/blog?order=asc&sort=recent"
	expected2 := "/blog?sort=recent&order=asc"
	if updated != expected1 && updated != expected2 {
		t.Errorf("got %s, expected %s or %s", updated, expected1, expected2)
	}
}

func TestGetQueryValueFromURL(t *testing.T) {
	raw := "/blog?view=grid&sort=alpha"
	val, err := GetQueryValueFromURL("sort", raw)
	urequire.NoError(t, err, "no error")
	uassert.Equal(t, "alpha", val)
}

func TestParseQueryFirstValuesMap(t *testing.T) {
	raw := "/blog?sort=alpha&order=desc"
	m, err := ParseQueryFirstValuesMap(raw)
	urequire.NoError(t, err, "no error")
	uassert.Equal(t, "alpha", m["sort"])
	uassert.Equal(t, "desc", m["order"])
}

func TestDeleteQuery(t *testing.T) {
	raw := "/blog?sort=alpha&mode=grid"
	deleted, err := DeleteQuery(raw, "sort")
	urequire.NoError(t, err, "no error")
	uassert.Equal(t, "/blog?mode=grid", deleted)
}

func TestMalformedURLHandling(t *testing.T) {
	_, err := ParseQueryFirstValuesMap(":::")
	urequire.Error(t, err)

	_, err = UpdateQueryValue(":::", "a", "b")
	urequire.Error(t, err)

	val, err := GetQueryValueFromURL("x", ":::")
	urequire.Error(t, err)
	urequire.Equal(t, "", val)
}

func TestGetQueryValues(t *testing.T) {
	values := url.Values{
		"foo": {"1", "2"},
	}
	got := GetQueryValues(values, "foo")
	uassert.Equal(t, "1", got[0])
	uassert.Equal(t, "2", got[1])
}

func TestGetQueryFirstValue(t *testing.T) {
	values := url.Values{
		"foo": {"1", "2"},
	}
	uassert.Equal(t, "1", GetQueryFirstValue(values, "foo"))
	uassert.Equal(t, "", GetQueryFirstValue(values, "bar")) // key doesn't exist
}

func TestHasQueryKey(t *testing.T) {
	values := url.Values{
		"foo": {"1"},
	}
	uassert.Equal(t, true, HasQueryKey(values, "foo"))
	uassert.Equal(t, false, HasQueryKey(values, "bar"))
}

func TestUpdateQueryValues(t *testing.T) {
	raw := "/blog?foo=bar"
	updated, err := UpdateQueryValues(raw, "foo", []string{"1", "2"})
	urequire.NoError(t, err, "no error")
	uassert.Equal(t, "/blog?foo=1&foo=2", updated)
}

func TestUpdateQueryAllValues(t *testing.T) {
	raw := "/blog?foo=1"
	updated, err := UpdateQueryAllValues(raw, map[string][]string{
		"foo": {"2", "3"},
		"bar": {"4"},
	})
	urequire.NoError(t, err, "no error")
	// order may vary
	expected1 := "/blog?bar=4&foo=2&foo=3"
	expected2 := "/blog?foo=2&foo=3&bar=4"
	if updated != expected1 && updated != expected2 {
		t.Errorf("got %s, expected %s or %s", updated, expected1, expected2)
	}
}

func TestSetQueries(t *testing.T) {
	raw := "/blog?foo=1"
	updated, err := SetQueries(raw, map[string]string{
		"foo": "2",
		"bar": "3",
	})
