	expected2 := "/blog?foo=2&bar=3"
	if updated != expected1 && updated != expected2 {
		t.Errorf("got %s, expected %s or %s", updated, expected1, expected2)
	}
}

func TestSetQueriesMulti(t *testing.T) {
