// Package addrset provides a specialized set data structure for managing unique Gno addresses.
//
// It is built on top of an AVL tree for efficient operations and maintains addresses in sorted order.
// This package is particularly useful when you need to:
//   - Track a collection of unique addresses (e.g., for whitelists, participants, etc.)
//   - Efficiently check address membership
//   - Support pagination when displaying addresses
//
// Example usage:
//
//	import (
//	    "std"
//	    "gno.land/p/moul/addrset"
//	)
//
//	func MyHandler() {
//	    // Create a new address set
//	    var set addrset.Set
//
//	    // Add some addresses
//	    addr1 := std.Address("g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5")
//	    addr2 := std.Address("g1sss5g0rkqr88k4u648yd5d3l9t4d8vvqwszqth")
//
//	    set.Add(addr1)  // returns true (newly added)
//	    set.Add(addr2)  // returns true (newly added)
//	    set.Add(addr1)  // returns false (already exists)
//
//	    // Check membership
//	    if set.Has(addr1) {
//	        // addr1 is in the set
//	    }
//
//	    // Get size
//	    size := set.Size()  // returns 2
//
//	    // Iterate with pagination (10 items per page, starting at offset 0)
//	    set.IterateByOffset(0, 10, func(addr std.Address) bool {
//	        // Process addr
//	        return false  // continue iteration
//	    })
//
//	    // Remove an address
//	    set.Remove(addr1)  // returns true (was present)
//	    set.Remove(addr1)  // returns false (not present)
//	}
package addrset

import (
	"std"

	"gno.land/p/nt/avl"
)

type Set struct {
	tree avl.Tree
}

// Add inserts an address into the set.
// Returns true if the address was newly added, false if it already existed.
func (s *Set) Add(addr std.Address) bool {
	return !s.tree.Set(string(addr), nil)
}

// Remove deletes an address from the set.
// Returns true if the address was found and removed, false if it didn't exist.
func (s *Set) Remove(addr std.Address) bool {
	_, removed := s.tree.Remove(string(addr))
	return removed
}

// Has checks if an address exists in the set.
func (s *Set) Has(addr std.Address) bool {
	return s.tree.Has(string(addr))
}

// Size returns the number of addresses in the set.
func (s *Set) Size() int {
	return s.tree.Size()
}

// IterateByOffset walks through addresses starting at the given offset.
// The callback should return true to stop iteration.
func (s *Set) IterateByOffset(offset int, count int, cb func(addr std.Address) bool) {
	s.tree.IterateByOffset(offset, count, func(key string, _ any) bool {
		return cb(std.Address(key))
	})
}

// ReverseIterateByOffset walks through addresses in reverse order starting at the given offset.
// The callback should return true to stop iteration.
func (s *Set) ReverseIterateByOffset(offset int, count int, cb func(addr std.Address) bool) {
	s.tree.ReverseIterateByOffset(offset, count, func(key string, _ any) bool {
		return cb(std.Address(key))
	})
}

// Tree returns the underlying AVL tree for advanced usage.
func (s *Set) Tree() avl.ITree {
	return &s.tree
}

package addrset

import (
	"std"
	"testing"

	"gno.land/p/nt/uassert"
)

func TestSet(t *testing.T) {
	addr1 := std.Address("addr1")
	addr2 := std.Address("addr2")
	addr3 := std.Address("addr3")

	tests := []struct {
		name    string
		actions func(s *Set)
		size    int
		has     map[std.Address]bool
		addrs   []std.Address // for iteration checks
	}{
		{
			name:    "empty set",
			actions: func(s *Set) {},
			size:    0,
			has:     map[std.Address]bool{addr1: false},
		},
		{
			name: "single address",
			actions: func(s *Set) {
				s.Add(addr1)
			},
			size: 1,
			has: map[std.Address]bool{
				addr1: true,
				addr2: false,
			},
			addrs: []std.Address{addr1},
		},
		{
			name: "multiple addresses",
			actions: func(s *Set) {
				s.Add(addr1)
				s.Add(addr2)
				s.Add(addr3)
			},
			size: 3,
			has: map[std.Address]bool{
				addr1: true,
				addr2: true,
				addr3: true,
			},
			addrs: []std.Address{addr1, addr2, addr3},
		},
		{
			name: "remove address",
			actions: func(s *Set) {
				s.Add(addr1)
				s.Add(addr2)
				s.Remove(addr1)
			},
			size: 1,
			has: map[std.Address]bool{
				addr1: false,
				addr2: true,
			},
			addrs: []std.Address{addr2},
		},
		{
			name: "duplicate adds",
			actions: func(s *Set) {
				uassert.True(t, s.Add(addr1))     // first add returns true
				uassert.False(t, s.Add(addr1))    // second add returns false
				uassert.True(t, s.Remove(addr1))  // remove existing returns true
				uassert.False(t, s.Remove(addr1)) // remove non-existing returns false
			},
			size: 0,
			has: map[std.Address]bool{
				addr1: false,
			},
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			var set Set

			// Execute test actions
			tt.actions(&set)

			// Check size
			uassert.Equal(t, tt.size, set.Size())

			// Check existence
			for addr, expected := range tt.has {
				uassert.Equal(t, expected, set.Has(addr))
			}

			// Check iteration if addresses are specified
			if tt.addrs != nil {
				collected := []std.Address{}
				set.IterateByOffset(0, 10, func(addr std.Address) bool {
					collected = append(collected, addr)
					return false
				})

				// Check length
				uassert.Equal(t, len(tt.addrs), len(collected))

				// Check each address
				for i, addr := range tt.addrs {
					uassert.Equal(t, addr, collected[i])
				}
			}
		})
	}
}

func TestSetIterationLimits(t *testing.T) {
	tests := []struct {
		name     string
		addrs    []std.Address
		offset   int
		limit    int
		expected int
	}{
		{
			name:     "zero offset full list",
			addrs:    []std.Address{"a1", "a2", "a3"},
			offset:   0,
			limit:    10,
			expected: 3,
		},
		{
			name:     "offset with limit",
			addrs:    []std.Address{"a1", "a2", "a3", "a4"},
			offset:   1,
			limit:    2,
			expected: 2,
		},
		{
			name:     "offset beyond size",
			addrs:    []std.Address{"a1", "a2"},
			offset:   3,
			limit:    1,
			expected: 0,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			var set Set
			for _, addr := range tt.addrs {
				set.Add(addr)
			}

			// Test forward iteration
			count := 0
			set.IterateByOffset(tt.offset, tt.limit, func(addr std.Address) bool {
				count++
				return false
			})
			uassert.Equal(t, tt.expected, count)

			// Test reverse iteration
			count = 0
			set.ReverseIterateByOffset(tt.offset, tt.limit, func(addr std.Address) bool {
				count++
				return false
			})
			uassert.Equal(t, tt.expected, count)
		})
	}
}

// Package once provides utilities for one-time execution patterns.
// It extends the concept of sync.Once with error handling and panic options.
package once

import (
	"errors"
)

// Once represents a one-time execution guard
type Once struct {
	done    bool
	err     error
	paniced bool
	value   any // stores the result of the execution
}

// New creates a new Once instance
func New() *Once {
	return &Once{}
}

// Do executes fn only once and returns nil on subsequent calls
func (o *Once) Do(fn func()) {
	if o.done {
		return
	}
	defer func() { o.done = true }()
	fn()
}

// DoErr executes fn only once and returns the same error on subsequent calls
func (o *Once) DoErr(fn func() error) error {
	if o.done {
		return o.err
	}
	defer func() { o.done = true }()
	o.err = fn()
	return o.err
}

// DoOrPanic executes fn only once and panics on subsequent calls
func (o *Once) DoOrPanic(fn func()) {
	if o.done {
		panic("once: multiple execution attempted")
	}
	defer func() { o.done = true }()
	fn()
}

// DoValue executes fn only once and returns its value, subsequent calls return the cached value
func (o *Once) DoValue(fn func() any) any {
	if o.done {
		return o.value
	}
	defer func() { o.done = true }()
	o.value = fn()
	return o.value
}

// DoValueErr executes fn only once and returns its value and error
// Subsequent calls return the cached value and error
func (o *Once) DoValueErr(fn func() (any, error)) (any, error) {
	if o.done {
		return o.value, o.err
	}
	defer func() { o.done = true }()
	o.value, o.err = fn()
	return o.value, o.err
}

// Reset resets the Once instance to its initial state
// This is mainly useful for testing purposes
func (o *Once) Reset() {
	o.done = false
	o.err = nil
	o.paniced = false
	o.value = nil
}

// IsDone returns whether the Once has been executed
func (o *Once) IsDone() bool {
	return o.done
}

// Error returns the error from the last execution if any
func (o *Once) Error() error {
	return o.err
}

var (
	ErrNotExecuted = errors.New("once: not executed yet")
)

// Value returns the stored value and an error if not executed yet
func (o *Once) Value() (any, error) {
	if !o.done {
		return nil, ErrNotExecuted
	}
	return o.value, nil
}

package once

import (
	"errors"
	"testing"
)

func TestOnce_Do(t *testing.T) {
	counter := 0
	once := New()

	increment := func() {
		counter++
	}

	// First call should execute
	once.Do(increment)
	if counter != 1 {
		t.Errorf("expected counter to be 1, got %d", counter)
	}

	// Second call should not execute
	once.Do(increment)
	if counter != 1 {
		t.Errorf("expected counter to still be 1, got %d", counter)
	}
}

func TestOnce_DoErr(t *testing.T) {
	once := New()
	expectedErr := errors.New("test error")

	fn := func() error {
		return expectedErr
	}

	// First call should return error
	if err := once.DoErr(fn); err != expectedErr {
		t.Errorf("expected error %v, got %v", expectedErr, err)
	}

	// Second call should return same error
	if err := once.DoErr(fn); err != expectedErr {
		t.Errorf("expected error %v, got %v", expectedErr, err)
	}
}

func TestOnce_DoOrPanic(t *testing.T) {
	once := New()
	executed := false

	fn := func() {
		executed = true
	}

	// First call should execute
	once.DoOrPanic(fn)
	if !executed {
		t.Error("function should have executed")
	}

	// Second call should panic
	defer func() {
		if r := recover(); r == nil {
			t.Error("expected panic on second execution")
		}
	}()
	once.DoOrPanic(fn)
}

func TestOnce_DoValue(t *testing.T) {
	once := New()
	expected := "test value"
	counter := 0

	fn := func() any {
		counter++
		return expected
	}

	// First call should return value
	if result := once.DoValue(fn); result != expected {
		t.Errorf("expected %v, got %v", expected, result)
	}

	// Second call should return cached value
	if result := once.DoValue(fn); result != expected {
		t.Errorf("expected %v, got %v", expected, result)
	}

	if counter != 1 {
		t.Errorf("function should have executed only once, got %d executions", counter)
	}
}

func TestOnce_DoValueErr(t *testing.T) {
	once := New()
	expectedVal := "test value"
	expectedErr := errors.New("test error")
	counter := 0

	fn := func() (any, error) {
		counter++
		return expectedVal, expectedErr
	}

	// First call should return value and error
	val, err := once.DoValueErr(fn)
	if val != expectedVal || err != expectedErr {
		t.Errorf("expected (%v, %v), got (%v, %v)", expectedVal, expectedErr, val, err)
	}

	// Second call should return cached value and error
	val, err = once.DoValueErr(fn)
	if val != expectedVal || err != expectedErr {
		t.Errorf("expected (%v, %v), got (%v, %v)", expectedVal, expectedErr, val, err)
	}

	if counter != 1 {
		t.Errorf("function should have executed only once, got %d executions", counter)
	}
}

func TestOnce_Reset(t *testing.T) {
	once := New()
	counter := 0

	fn := func() {
		counter++
	}

	once.Do(fn)
	if counter != 1 {
		t.Errorf("expected counter to be 1, got %d", counter)
	}

	once.Reset()
	once.Do(fn)
	if counter != 2 {
		t.Errorf("expected counter to be 2 after reset, got %d", counter)
	}
}

func TestOnce_IsDone(t *testing.T) {
	once := New()

	if once.IsDone() {
		t.Error("new Once instance should not be done")
	}

	once.Do(func() {})

	if !once.IsDone() {
		t.Error("Once instance should be done after execution")
	}
}

func TestOnce_Error(t *testing.T) {
	once := New()
	expectedErr := errors.New("test error")

	if err := once.Error(); err != nil {
		t.Errorf("expected nil error, got %v", err)
	}

	once.DoErr(func() error {
		return expectedErr
	})

	if err := once.Error(); err != expectedErr {
		t.Errorf("expected error %v, got %v", expectedErr, err)
	}
}

func TestOnce_Value(t *testing.T) {
	once := New()

	// Test unexecuted state
	val, err := once.Value()
	if err != ErrNotExecuted {
		t.Errorf("expected ErrNotExecuted, got %v", err)
	}
	if val != nil {
		t.Errorf("expected nil value, got %v", val)
	}

	// Test after execution
	expected := "test value"
	once.DoValue(func() any {
		return expected
	})

	val, err = once.Value()
	if err != nil {
		t.Errorf("expected nil error, got %v", err)
	}
	if val != expected {
		t.Errorf("expected value %v, got %v", expected, val)
	}
}

func TestOnce_DoValueErr_Panic_MarkedDone(t *testing.T) {
	once := New()
	count := 0
	fn := func() (any, error) {
		count++
		panic("panic")
	}
	var r any
	func() {
		defer func() { r = recover() }()
		once.DoValueErr(fn)
	}()
	if r == nil {
		t.Error("expected panic on first call")
	}
	if !once.IsDone() {
		t.Error("expected once to be marked as done after panic")
	}
	r = nil
	func() {
		defer func() { r = recover() }()
		once.DoValueErr(fn)
	}()
	if r != nil {
		t.Error("expected no panic on subsequent call")
	}
	if count != 1 {
		t.Errorf("expected count to be 1, got %d", count)
	}
}

// Package authz provides flexible authorization control for privileged actions.
//
// # Authorization Strategies
//
// The package supports multiple authorization strategies:
//   - Member-based: Single user or team of users
//   - Contract-based: Async authorization (e.g., via DAO)
//   - Auto-accept: Allow all actions
//   - Drop: Deny all actions
//
// Core Components
//
//   - Authority interface: Base interface implemented by all authorities
//   - Authorizer: Main wrapper object for authority management
//   - MemberAuthority: Manages authorized addresses
//   - ContractAuthority: Delegates to another contract
//   - AutoAcceptAuthority: Accepts all actions
//   - DroppedAuthority: Denies all actions
//
// Quick Start
//
//	// Initialize with contract deployer as authority
//	var member std.Address(...)
//	var auth = authz.NewWithMembers(member)
//
//	// Create functions that require authorization
//	func UpdateConfig(newValue string) error {
//		crossing()
//		return auth.DoByPrevious("update_config", func() error {
//			config = newValue
//			return nil
//		})
//	}
//
// See example_test.gno for more usage examples.
package authz

import (
	"errors"
	"std"
	"strings"

	"gno.land/p/moul/addrset"
	"gno.land/p/moul/once"
	"gno.land/p/nt/avl"
	"gno.land/p/nt/avl/rotree"
	"gno.land/p/nt/ufmt"
)

// Authorizer is the main wrapper object that handles authority management.
// It is configured with a replaceable Authority implementation.
type Authorizer struct {
	auth Authority
}

// Authority represents an entity that can authorize privileged actions.
// It is implemented by MemberAuthority, ContractAuthority, AutoAcceptAuthority,
// and DroppedAuthority.
type Authority interface {
	// Authorize executes a privileged action if the caller is authorized
	// Additional args can be provided for context (e.g., for proposal creation)
	Authorize(caller std.Address, title string, action PrivilegedAction, args ...any) error

	// String returns a human-readable description of the authority
	String() string
}

// PrivilegedAction defines a function that performs a privileged action.
type PrivilegedAction func() error

// PrivilegedActionHandler is called by contract-based authorities to handle
// privileged actions.
type PrivilegedActionHandler func(title string, action PrivilegedAction) error

// NewWithCurrent creates a new Authorizer with the auth realm's address as authority
func NewWithCurrent() *Authorizer {
	return &Authorizer{
		auth: NewMemberAuthority(std.CurrentRealm().Address()),
	}
}

// NewWithPrevious creates a new Authorizer with the previous realm's address as authority
func NewWithPrevious() *Authorizer {
	return &Authorizer{
		auth: NewMemberAuthority(std.PreviousRealm().Address()),
	}
}

// NewWithCurrent creates a new Authorizer with the auth realm's address as authority
func NewWithMembers(addrs ...std.Address) *Authorizer {
	return &Authorizer{
		auth: NewMemberAuthority(addrs...),
	}
}

// NewWithOrigin creates a new Authorizer with the origin caller's address as
// authority.
// This is typically used in the init() function.
func NewWithOrigin() *Authorizer {
	origin := std.OriginCaller()
	previous := std.PreviousRealm()
	if origin != previous.Address() {
		panic("NewWithOrigin() should be called from init() where std.PreviousRealm() is origin")
	}
	return &Authorizer{
		auth: NewMemberAuthority(origin),
	}
}

// NewWithAuthority creates a new Authorizer with a specific authority
func NewWithAuthority(authority Authority) *Authorizer {
	return &Authorizer{
		auth: authority,
	}
}

// Authority returns the auth authority implementation
func (a *Authorizer) Authority() Authority {
	return a.auth
}

// Transfer changes the auth authority after validation
func (a *Authorizer) Transfer(caller std.Address, newAuthority Authority) error {
	// Ask auth authority to validate the transfer
	return a.auth.Authorize(caller, "transfer_authority", func() error {
		a.auth = newAuthority
		return nil
	})
}

// DoByCurrent executes a privileged action by the auth realm.
func (a *Authorizer) DoByCurrent(title string, action PrivilegedAction, args ...any) error {
	current := std.CurrentRealm()
	caller := current.Address()
	return a.auth.Authorize(caller, title, action, args...)
}

// DoByPrevious executes a privileged action by the previous realm.
func (a *Authorizer) DoByPrevious(title string, action PrivilegedAction, args ...any) error {
	previous := std.PreviousRealm()
	caller := previous.Address()
	return a.auth.Authorize(caller, title, action, args...)
}

// String returns a string representation of the auth authority
func (a *Authorizer) String() string {
	authStr := a.auth.String()

	switch a.auth.(type) {
	case *MemberAuthority:
	case *ContractAuthority:
	case *AutoAcceptAuthority:
	case *droppedAuthority:
	default:
		// this way official "dropped" is different from "*custom*: dropped" (autoclaimed).
		return ufmt.Sprintf("custom_authority[%s]", authStr)
	}
	return authStr
}

// MemberAuthority is the default implementation using addrset for member
// management.
type MemberAuthority struct {
	members addrset.Set
}

func NewMemberAuthority(members ...std.Address) *MemberAuthority {
	auth := &MemberAuthority{}
	for _, addr := range members {
		auth.members.Add(addr)
	}
	return auth
}

func (a *MemberAuthority) Authorize(caller std.Address, title string, action PrivilegedAction, args ...any) error {
	if !a.members.Has(caller) {
		return errors.New("unauthorized")
	}

	if err := action(); err != nil {
		return err
	}
	return nil
}

func (a *MemberAuthority) String() string {
	addrs := []string{}
	a.members.Tree().Iterate("", "", func(key string, _ any) bool {
		addrs = append(addrs, key)
		return false
	})
	addrsStr := strings.Join(addrs, ",")
	return ufmt.Sprintf("member_authority[%s]", addrsStr)
}

// AddMember adds a new member to the authority
func (a *MemberAuthority) AddMember(caller std.Address, addr std.Address) error {
	return a.Authorize(caller, "add_member", func() error {
		a.members.Add(addr)
		return nil
	})
}

// AddMembers adds a list of members to the authority
func (a *MemberAuthority) AddMembers(caller std.Address, addrs ...std.Address) error {
	return a.Authorize(caller, "add_members", func() error {
		for _, addr := range addrs {
			a.members.Add(addr)
		}
		return nil
	})
}

// RemoveMember removes a member from the authority
func (a *MemberAuthority) RemoveMember(caller std.Address, addr std.Address) error {
	return a.Authorize(caller, "remove_member", func() error {
		a.members.Remove(addr)
		return nil
	})
}

// Tree returns a read-only view of the members tree
func (a *MemberAuthority) Tree() *rotree.ReadOnlyTree {
	tree := a.members.Tree().(*avl.Tree)
	return rotree.Wrap(tree, nil)
}

// Has checks if the given address is a member of the authority
func (a *MemberAuthority) Has(addr std.Address) bool {
	return a.members.Has(addr)
}

// ContractAuthority implements async contract-based authority
type ContractAuthority struct {
	contractPath    string
	contractAddr    std.Address
	contractHandler PrivilegedActionHandler
	proposer        Authority // controls who can create proposals
}

func NewContractAuthority(path string, handler PrivilegedActionHandler) *ContractAuthority {
	return &ContractAuthority{
		contractPath:    path,
		contractAddr:    std.DerivePkgAddr(path),
		contractHandler: handler,
		proposer:        NewAutoAcceptAuthority(), // default: anyone can propose
	}
}

// NewRestrictedContractAuthority creates a new contract authority with a
// proposer restriction.
func NewRestrictedContractAuthority(path string, handler PrivilegedActionHandler, proposer Authority) Authority {
	if path == "" {
		panic("contract path cannot be empty")
	}
	if handler == nil {
		panic("contract handler cannot be nil")
	}
	if proposer == nil {
		panic("proposer cannot be nil")
	}
	return &ContractAuthority{
		contractPath:    path,
		contractAddr:    std.DerivePkgAddr(path),
		contractHandler: handler,
		proposer:        proposer,
	}
}

func (a *ContractAuthority) Authorize(caller std.Address, title string, action PrivilegedAction, args ...any) error {
	if a.contractHandler == nil {
		return errors.New("contract handler is not set")
	}

	// setup a once instance to ensure the action is executed only once
	executionOnce := once.Once{}

	// Wrap the action to ensure it can only be executed by the contract
	wrappedAction := func() error {
		current := std.CurrentRealm().Address()
		if current != a.contractAddr {
			return errors.New("action can only be executed by the contract")
		}
		return executionOnce.DoErr(func() error {
			return action()
		})
	}

	// Use the proposer authority to control who can create proposals
	return a.proposer.Authorize(caller, title+"_proposal", func() error {
		if err := a.contractHandler(title, wrappedAction); err != nil {
			return err
		}
		return nil
	}, args...)
}

func (a *ContractAuthority) String() string {
	return ufmt.Sprintf("contract_authority[contract=%s]", a.contractPath)
}

// AutoAcceptAuthority implements an authority that accepts all actions
// AutoAcceptAuthority is a simple authority that automatically accepts all
// actions.
// It can be used as a proposer authority to allow anyone to create proposals.
type AutoAcceptAuthority struct{}

func NewAutoAcceptAuthority() *AutoAcceptAuthority {
	return &AutoAcceptAuthority{}
}

func (a *AutoAcceptAuthority) Authorize(caller std.Address, title string, action PrivilegedAction, args ...any) error {
	return action()
}

func (a *AutoAcceptAuthority) String() string {
	return "auto_accept_authority"
}

// droppedAuthority implements an authority that denies all actions
type droppedAuthority struct{}

func NewDroppedAuthority() Authority {
	return &droppedAuthority{}
}

func (a *droppedAuthority) Authorize(caller std.Address, title string, action PrivilegedAction, args ...any) error {
	return errors.New("dropped authority: all actions are denied")
}

func (a *droppedAuthority) String() string {
	return "dropped_authority"
}

package authz

import (
	"errors"
	"std"
	"strings"
	"testing"

	"gno.land/p/nt/testutils"
	"gno.land/p/nt/uassert"
)

func TestNewWithCurrent(t *testing.T) {
	alice := testutils.TestAddress("alice")
	testing.SetRealm(std.NewUserRealm(alice))

	auth := NewWithCurrent()

	// Check that the current authority is a MemberAuthority
	memberAuth, ok := auth.Authority().(*MemberAuthority)
	uassert.True(t, ok, "expected MemberAuthority")

	// Check that the caller is a member
	uassert.True(t, memberAuth.Has(alice), "caller should be a member")

	// Check string representation
	uassert.True(t, strings.Contains(auth.String(), alice.String()))
}

func TestNewWithAuthority(t *testing.T) {
	alice := testutils.TestAddress("alice")
	memberAuth := NewMemberAuthority(alice)

	auth := NewWithAuthority(memberAuth)

	// Check that the current authority is the one we provided
	uassert.True(t, auth.Authority() == memberAuth, "expected provided authority")
}

func TestAuthorizerAuthorize(t *testing.T) {
	alice := testutils.TestAddress("alice")
	testing.SetRealm(std.NewUserRealm(alice))

	auth := NewWithCurrent()

	// Test successful action with args
	executed := false
	args := []any{"test_arg", 123}
	err := auth.DoByCurrent("test_action", func() error {
		executed = true
		return nil
	}, args...)

	uassert.True(t, err == nil, "expected no error")
	uassert.True(t, executed, "action should have been executed")

	// Test unauthorized action with args
	testing.SetRealm(std.NewUserRealm(testutils.TestAddress("bob")))

	executed = false
	err = auth.DoByCurrent("test_action", func() error {
		executed = true
		return nil
	}, "unauthorized_arg")

	uassert.True(t, err != nil, "expected error")
	uassert.False(t, executed, "action should not have been executed")

	// Test action returning error
	testing.SetRealm(std.NewUserRealm(alice))
	expectedErr := errors.New("test error")

	err = auth.DoByCurrent("test_action", func() error {
		return expectedErr
	})

	uassert.True(t, err == expectedErr, "expected specific error")
}

func TestAuthorizerTransfer(t *testing.T) {
	alice := testutils.TestAddress("alice")
	testing.SetRealm(std.NewUserRealm(alice))

	auth := NewWithCurrent()

	// Test transfer to new member authority
	bob := testutils.TestAddress("bob")
	newAuth := NewMemberAuthority(bob)

	err := auth.Transfer(alice, newAuth)
	uassert.True(t, err == nil, "expected no error")
	uassert.True(t, auth.Authority() == newAuth, "expected new authority")

	// Test unauthorized transfer
	testing.SetRealm(std.NewUserRealm(bob)) // doesn't matter that it's bob
	carol := testutils.TestAddress("carol")

	err = auth.Transfer(carol, NewMemberAuthority(alice))
	uassert.True(t, err != nil, "expected error")

	// Test transfer to contract authority
	contractAuth := NewContractAuthority("gno.land/r/test", func(title string, action PrivilegedAction) error {
		return action()
	})

	err = auth.Transfer(bob, contractAuth)
	uassert.True(t, err == nil, "expected no error")
	uassert.True(t, auth.Authority() == contractAuth, "expected contract authority")
}

func TestAuthorizerTransferChain(t *testing.T) {
	alice := testutils.TestAddress("alice")
	testing.SetRealm(std.NewUserRealm(alice))

	// Create a chain of transfers
	auth := NewWithCurrent()

	// First transfer to a new member authority
	bob := testutils.TestAddress("bob")
	memberAuth := NewMemberAuthority(bob)

	err := auth.Transfer(alice, memberAuth)
	uassert.True(t, err == nil, "unexpected error in first transfer")

	// Then transfer to a contract authority
	contractAuth := NewContractAuthority("gno.land/r/test", func(title string, action PrivilegedAction) error {
		return action()
	})
	err = auth.Transfer(bob, contractAuth)
	uassert.True(t, err == nil, "unexpected error in second transfer")

	// Finally transfer to an auto-accept authority
	autoAuth := NewAutoAcceptAuthority()
	codeRealm := std.NewCodeRealm("gno.land/r/test")
	code := codeRealm.Address()
	testing.SetRealm(codeRealm)
