func slicesEqual(w1, w2 []string) bool {
	if len(w1) != len(w2) {
		return false
	}
	for i := 0; i < len(w1); i++ {
		if w1[0] != w2[0] {
			return false
		}
	}
	return true
}

func maxint8(a, b int8) int8 {
	if a > b {
		return a
	}
	return b
}

func reverseSlice(ss []string) {
	for i := 0; i < len(ss)/2; i++ {
		j := len(ss) - 1 - i
		ss[i], ss[j] = ss[j], ss[i]
	}
}

func TestNodeStructuralSharing(t *testing.T) {
	t.Run("unmodified paths remain shared", func(t *testing.T) {
		root := NewNode("B", 2)
		root, _ = root.Set("A", 1)
		root, _ = root.Set("C", 3)

		originalRight := root.rightNode
		newRoot, _ := root.Set("A", 10)

		if newRoot.rightNode != originalRight {
			t.Error("Unmodified right subtree should remain shared")
		}
	})

	t.Run("multiple modifications reuse shared structure", func(t *testing.T) {
		// Create initial tree
		root := NewNode("B", 2)
		root, _ = root.Set("A", 1)
		root, _ = root.Set("C", 3)

		// Store original nodes
		originalRight := root.rightNode

		// First modification
		mod1, _ := root.Set("A", 10)

		// Second modification
		mod2, _ := mod1.Set("C", 30)

		// Check sharing in first modification
		if mod1.rightNode != originalRight {
			t.Error("First modification should share unmodified right subtree")
		}

		// Check that second modification creates new right node
		if mod2.rightNode == originalRight {
			t.Error("Second modification should create new right node")
		}
	})
}

func TestNodeCopyOnWrite(t *testing.T) {
	t.Run("copy preserves structure", func(t *testing.T) {
		root := NewNode("B", 2)
		root, _ = root.Set("A", 1)
		root, _ = root.Set("C", 3)

		// Only copy non-leaf nodes
		if !root.IsLeaf() {
			copied := root._copy()
			if copied == root {
				t.Error("Copy should create new instance")
			}

			// Create temporary trees to use Equal method
			original := &Tree{node: root}
			copiedTree := &Tree{node: copied}
			if !original.Equal(copiedTree) {
				t.Error("Copied node should preserve structure")
			}
		}
	})

	t.Run("removal copy pattern", func(t *testing.T) {
		// Create a more complex tree to test removal
		root := NewNode("B", 2)
		root, _ = root.Set("A", 1)
		root, _ = root.Set("C", 3)
		root, _ = root.Set("D", 4) // Add this to ensure proper tree structure

		// Store references to original nodes
		originalRight := root.rightNode
		originalRightRight := originalRight.rightNode

		// Remove "A" which should only affect the left subtree
		newRoot, _, _, _ := root.Remove("A")

		// Verify right subtree remains unchanged and shared
		if newRoot.rightNode != originalRight {
			t.Error("Right subtree should remain shared during removal of left node")
		}

		// Also verify deeper nodes remain shared
		if newRoot.rightNode.rightNode != originalRightRight {
			t.Error("Deep right subtree should remain shared during removal")
		}

		// Verify original tree is unchanged
		if _, _, exists := root.Get("A"); !exists {
			t.Error("Original tree should remain unchanged")
		}
	})

	t.Run("copy leaf node panic", func(t *testing.T) {
		leaf := NewNode("A", 1)

		defer func() {
			if r := recover(); r == nil {
				t.Error("Expected panic when copying leaf node")
			}
		}()

		// This should panic with our specific message
		leaf._copy()
	})
}

func TestNodeEqual(t *testing.T) {
	tests := []struct {
		name     string
		node1    func() *Node
		node2    func() *Node
		expected bool
	}{
		{
			name:     "nil nodes",
			node1:    func() *Node { return nil },
			node2:    func() *Node { return nil },
			expected: true,
		},
		{
			name:     "one nil node",
			node1:    func() *Node { return NewNode("A", 1) },
			node2:    func() *Node { return nil },
			expected: false,
		},
		{
			name:     "single leaf nodes equal",
			node1:    func() *Node { return NewNode("A", 1) },
			node2:    func() *Node { return NewNode("A", 1) },
			expected: true,
		},
		{
			name:     "single leaf nodes different key",
			node1:    func() *Node { return NewNode("A", 1) },
			node2:    func() *Node { return NewNode("B", 1) },
			expected: false,
		},
		{
			name:     "single leaf nodes different value",
			node1:    func() *Node { return NewNode("A", 1) },
			node2:    func() *Node { return NewNode("A", 2) },
			expected: false,
		},
		{
			name: "complex trees equal",
			node1: func() *Node {
				n, _ := NewNode("B", 2).Set("A", 1)
				n, _ = n.Set("C", 3)
				return n
			},
			node2: func() *Node {
				n, _ := NewNode("B", 2).Set("A", 1)
				n, _ = n.Set("C", 3)
				return n
			},
			expected: true,
		},
		{
			name: "complex trees different structure",
			node1: func() *Node {
				// Create a tree with structure:
				//     B
				//    / \
				//   A   D
				n := NewNode("B", 2)
				n, _ = n.Set("A", 1)
				n, _ = n.Set("D", 4)
				return n
			},
			node2: func() *Node {
				// Create a tree with structure:
				//     C
				//    / \
				//   A   D
				n := NewNode("C", 3)
				n, _ = n.Set("A", 1)
				n, _ = n.Set("D", 4)
				return n
			},
			expected: false, // These trees should be different
		},
		{
			name: "complex trees same structure despite different insertion order",
			node1: func() *Node {
				n, _ := NewNode("B", 2).Set("A", 1)
				n, _ = n.Set("C", 3)
				return n
			},
			node2: func() *Node {
				n, _ := NewNode("A", 1).Set("B", 2)
				n, _ = n.Set("C", 3)
				return n
			},
			expected: true,
		},
		{
			name: "truly different structures",
			node1: func() *Node {
				n, _ := NewNode("B", 2).Set("A", 1)
				return n // Tree with just two nodes
			},
			node2: func() *Node {
				n, _ := NewNode("B", 2).Set("C", 3)
				return n // Different two-node tree
			},
			expected: false,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			node1 := tt.node1()
			node2 := tt.node2()
			result := node1.Equal(node2)
			if result != tt.expected {
				t.Errorf("Expected Equal to return %v for %s", tt.expected, tt.name)
				println("\nComparison failed:")
				println("Tree 1:")
				printTree(node1, 0)
				println("Tree 2:")
				printTree(node2, 0)
			}
		})
	}
}

// Helper function to print tree structure
func printTree(node *Node, level int) {
	if node == nil {
		return
	}
	indent := strings.Repeat("  ", level)
	println(fmt.Sprintf("%sKey: %s, Value: %v, Height: %d, Size: %d",
		indent, node.key, node.value, node.height, node.size))
	printTree(node.leftNode, level+1)
	printTree(node.rightNode, level+1)
}

// Package cow provides a Copy-on-Write (CoW) AVL tree implementation.
// This is a fork of gno.land/p/nt/avl that adds CoW functionality
// while maintaining the original AVL tree interface and properties.
//
// Copy-on-Write creates a copy of a data structure only when it is modified,
// while still presenting the appearance of a full copy. When a tree is cloned,
// it initially shares all its nodes with the original tree. Only when a
// modification is made to either the original or the clone are new nodes created,
// and only along the path from the root to the modified node.
//
// Key features:
//   - O(1) cloning operation
//   - Minimal memory usage through structural sharing
//   - Full AVL tree functionality (self-balancing, ordered operations)
//   - Thread-safe for concurrent reads of shared structures
//
// While the CoW mechanism handles structural copying automatically, users need
// to consider how to handle the values stored in the tree:
//
//  1. Simple Values (int, string, etc.):
//     - These are copied by value automatically
//     - No additional handling needed
//
//  2. Complex Values (structs, pointers):
//     - Only the reference is copied by default
//     - Users must implement their own deep copy mechanism if needed
//
// Example:
//
//	// Create original tree
//	original := cow.NewTree()
//	original.Set("key1", "value1")
//
//	// Create a clone - O(1) operation
//	clone := original.Clone()
//
//	// Modify clone - only affected nodes are copied
//	clone.Set("key1", "modified")
//
//	// Original remains unchanged
//	val, _ := original.Get("key1") // Returns "value1"
package cow

type IterCbFn func(key string, value any) bool

//----------------------------------------
// Tree

// The zero struct can be used as an empty tree.
type Tree struct {
	node *Node
}

// NewTree creates a new empty AVL tree.
func NewTree() *Tree {
	return &Tree{
		node: nil,
	}
}

// Size returns the number of key-value pair in the tree.
func (tree *Tree) Size() int {
	return tree.node.Size()
}

// Has checks whether a key exists in the tree.
// It returns true if the key exists, otherwise false.
func (tree *Tree) Has(key string) (has bool) {
	return tree.node.Has(key)
}

// Get retrieves the value associated with the given key.
// It returns the value and a boolean indicating whether the key exists.
func (tree *Tree) Get(key string) (value any, exists bool) {
	_, value, exists = tree.node.Get(key)
	return
}

// GetByIndex retrieves the key-value pair at the specified index in the tree.
// It returns the key and value at the given index.
func (tree *Tree) GetByIndex(index int) (key string, value any) {
	return tree.node.GetByIndex(index)
}

// Set inserts a key-value pair into the tree.
// If the key already exists, the value will be updated.
// It returns a boolean indicating whether the key was newly inserted or updated.
func (tree *Tree) Set(key string, value any) (updated bool) {
	newnode, updated := tree.node.Set(key, value)
	tree.node = newnode
	return updated
}

// Remove removes a key-value pair from the tree.
// It returns the removed value and a boolean indicating whether the key was found and removed.
func (tree *Tree) Remove(key string) (value any, removed bool) {
	newnode, _, value, removed := tree.node.Remove(key)
	tree.node = newnode
	return value, removed
}

// Iterate performs an in-order traversal of the tree within the specified key range.
// It calls the provided callback function for each key-value pair encountered.
// If the callback returns true, the iteration is stopped.
func (tree *Tree) Iterate(start, end string, cb IterCbFn) bool {
	return tree.node.TraverseInRange(start, end, true, true,
		func(node *Node) bool {
			return cb(node.Key(), node.Value())
		},
	)
}

// ReverseIterate performs a reverse in-order traversal of the tree within the specified key range.
// It calls the provided callback function for each key-value pair encountered.
// If the callback returns true, the iteration is stopped.
func (tree *Tree) ReverseIterate(start, end string, cb IterCbFn) bool {
	return tree.node.TraverseInRange(start, end, false, true,
		func(node *Node) bool {
			return cb(node.Key(), node.Value())
		},
	)
}

// IterateByOffset performs an in-order traversal of the tree starting from the specified offset.
// It calls the provided callback function for each key-value pair encountered, up to the specified count.
// If the callback returns true, the iteration is stopped.
func (tree *Tree) IterateByOffset(offset int, count int, cb IterCbFn) bool {
	return tree.node.TraverseByOffset(offset, count, true, true,
		func(node *Node) bool {
			return cb(node.Key(), node.Value())
		},
	)
}

// ReverseIterateByOffset performs a reverse in-order traversal of the tree starting from the specified offset.
// It calls the provided callback function for each key-value pair encountered, up to the specified count.
// If the callback returns true, the iteration is stopped.
func (tree *Tree) ReverseIterateByOffset(offset int, count int, cb IterCbFn) bool {
	return tree.node.TraverseByOffset(offset, count, false, true,
		func(node *Node) bool {
			return cb(node.Key(), node.Value())
		},
	)
}

// Equal returns true if the two trees contain the same key-value pairs.
// WARNING: This is an expensive operation that recursively traverses the entire tree structure.
// It should only be used in tests or when absolutely necessary.
func (tree *Tree) Equal(other *Tree) bool {
	if tree == nil || other == nil {
		return tree == other
	}
	return tree.node.Equal(other.node)
}

// Clone creates a shallow copy of the tree
func (tree *Tree) Clone() *Tree {
	if tree == nil {
		return nil
	}
	return &Tree{
		node: tree.node,
	}
}

