func TestCopyOnWrite(t *testing.T) {
	// Create original tree
	original := NewTree()
	original.Set("A", 1)
	original.Set("B", 2)
	original.Set("C", 3)

	// Create a clone
	clone := original.Clone()

	// Modify clone
	clone.Set("B", 20)
	clone.Set("D", 4)

	// Verify original is unchanged
	if val, _ := original.Get("B"); val != 2 {
		t.Errorf("Original tree was modified: expected B=2, got B=%v", val)
	}
	if original.Has("D") {
		t.Error("Original tree was modified: found key D")
	}

	// Verify clone has new values
	if val, _ := clone.Get("B"); val != 20 {
		t.Errorf("Clone not updated: expected B=20, got B=%v", val)
	}
	if val, _ := clone.Get("D"); val != 4 {
		t.Errorf("Clone not updated: expected D=4, got D=%v", val)
	}
}

func TestCopyOnWriteEdgeCases(t *testing.T) {
	t.Run("nil tree clone", func(t *testing.T) {
		var original *Tree
		clone := original.Clone()
		if clone != nil {
			t.Error("Expected nil clone from nil tree")
		}
	})

	t.Run("empty tree clone", func(t *testing.T) {
		original := NewTree()
		clone := original.Clone()

		// Modify clone
		clone.Set("A", 1)

		if original.Size() != 0 {
			t.Error("Original empty tree was modified")
		}
		if clone.Size() != 1 {
			t.Error("Clone was not modified")
		}
	})

	t.Run("multiple clones", func(t *testing.T) {
		original := NewTree()
		original.Set("A", 1)
		original.Set("B", 2)

		// Create multiple clones
		clone1 := original.Clone()
		clone2 := original.Clone()
		clone3 := clone1.Clone()

		// Modify each clone differently
		clone1.Set("A", 10)
		clone2.Set("B", 20)
		clone3.Set("C", 30)

		// Check original remains unchanged
		if val, _ := original.Get("A"); val != 1 {
			t.Errorf("Original modified: expected A=1, got A=%v", val)
		}
		if val, _ := original.Get("B"); val != 2 {
			t.Errorf("Original modified: expected B=2, got B=%v", val)
		}

		// Verify each clone has correct values
		if val, _ := clone1.Get("A"); val != 10 {
			t.Errorf("Clone1 incorrect: expected A=10, got A=%v", val)
		}
		if val, _ := clone2.Get("B"); val != 20 {
			t.Errorf("Clone2 incorrect: expected B=20, got B=%v", val)
		}
		if val, _ := clone3.Get("C"); val != 30 {
			t.Errorf("Clone3 incorrect: expected C=30, got C=%v", val)
		}
	})

	t.Run("clone after removal", func(t *testing.T) {
		original := NewTree()
		original.Set("A", 1)
		original.Set("B", 2)
		original.Set("C", 3)

		// Remove a node and then clone
		original.Remove("B")
		clone := original.Clone()

		// Modify clone
		clone.Set("B", 20)

		// Verify original state
		if original.Has("B") {
			t.Error("Original tree should not have key B")
		}

		// Verify clone state
		if val, _ := clone.Get("B"); val != 20 {
			t.Errorf("Clone incorrect: expected B=20, got B=%v", val)
		}
	})

	t.Run("concurrent modifications", func(t *testing.T) {
		original := NewTree()
		original.Set("A", 1)
		original.Set("B", 2)

		clone1 := original.Clone()
		clone2 := original.Clone()

		// Modify same key in different clones
		clone1.Set("B", 20)
		clone2.Set("B", 30)

		// Each clone should have its own value
		if val, _ := clone1.Get("B"); val != 20 {
			t.Errorf("Clone1 incorrect: expected B=20, got B=%v", val)
		}
		if val, _ := clone2.Get("B"); val != 30 {
			t.Errorf("Clone2 incorrect: expected B=30, got B=%v", val)
		}
	})

	t.Run("deep tree modifications", func(t *testing.T) {
		original := NewTree()
		// Create a deeper tree
		keys := []string{"M", "F", "T", "B", "H", "P", "Z"}
		for _, k := range keys {
			original.Set(k, k)
		}

		clone := original.Clone()

		// Modify a deep node
		clone.Set("H", "modified")

		// Check original remains unchanged
		if val, _ := original.Get("H"); val != "H" {
			t.Errorf("Original modified: expected H='H', got H=%v", val)
		}

		// Verify clone modification
		if val, _ := clone.Get("H"); val != "modified" {
			t.Errorf("Clone incorrect: expected H='modified', got H=%v", val)
		}
	})

	t.Run("rebalancing test", func(t *testing.T) {
		original := NewTree()
		// Insert nodes that will cause rotations
		keys := []string{"A", "B", "C", "D", "E"}
		for _, k := range keys {
			original.Set(k, k)
		}

		clone := original.Clone()

		// Add more nodes to clone to trigger rebalancing
		clone.Set("F", "F")
		clone.Set("G", "G")

		// Verify original structure remains unchanged
		originalKeys := collectKeys(original)
		expectedOriginal := []string{"A", "B", "C", "D", "E"}
		if !slicesEqual(originalKeys, expectedOriginal) {
			t.Errorf("Original tree structure changed: got %v, want %v", originalKeys, expectedOriginal)
		}

		// Verify clone has all keys
		cloneKeys := collectKeys(clone)
		expectedClone := []string{"A", "B", "C", "D", "E", "F", "G"}
		if !slicesEqual(cloneKeys, expectedClone) {
			t.Errorf("Clone tree structure incorrect: got %v, want %v", cloneKeys, expectedClone)
		}
	})

	t.Run("value mutation test", func(t *testing.T) {
		type MutableValue struct {
			Data string
		}

		original := NewTree()
		mutable := &MutableValue{Data: "original"}
		original.Set("key", mutable)

		clone := original.Clone()

		// Modify the mutable value
		mutable.Data = "modified"

		// Both original and clone should see the modification
		// because we're not deep copying values
		origVal, _ := original.Get("key")
		cloneVal, _ := clone.Get("key")

		if origVal.(*MutableValue).Data != "modified" {
			t.Error("Original value not modified as expected")
		}
		if cloneVal.(*MutableValue).Data != "modified" {
			t.Error("Clone value not modified as expected")
		}
	})
}

// Helper function to collect all keys in order
func collectKeys(tree *Tree) []string {
	var keys []string
	tree.Iterate("", "", func(key string, _ any) bool {
		keys = append(keys, key)
		return false
	})
	return keys
}

// Package realmpath is a lightweight Render.path parsing and link generation
// library with an idiomatic API, closely resembling that of net/url.
//
// This package provides utilities for parsing request paths and query
// parameters, allowing you to extract path segments and manipulate query
// values.
//
// Example usage:
//
//	import "gno.land/p/moul/realmpath"
//
//	func Render(path string) string {
//	    // Parsing a sample path with query parameters
//	    path = "hello/world?foo=bar&baz=foobar"
//	    req := realmpath.Parse(path)
//
//	    // Accessing parsed path and query parameters
//	    println(req.Path)             // Output: hello/world
//	    println(req.PathPart(0))      // Output: hello
//	    println(req.PathPart(1))      // Output: world
//	    println(req.Query.Get("foo")) // Output: bar
//	    println(req.Query.Get("baz")) // Output: foobar
//
//	    // Rebuilding the URL
//	    println(req.String())         // Output: /r/current/realm:hello/world?baz=foobar&foo=bar
//	}
package realmpath

import (
	"net/url"
	"std"
	"strings"
)

var chainDomain = std.ChainDomain()

// Request represents a parsed request.
type Request struct {
	Path  string     // The path of the request
	Query url.Values // The parsed query parameters
	Realm string     // The realm associated with the request
}

// Parse takes a raw path string and returns a Request object.
// It splits the path into its components and parses any query parameters.
func Parse(rawPath string) *Request {
	// Split the raw path into path and query components
	path, query := splitPathAndQuery(rawPath)

	// Parse the query string into url.Values
	queryValues, _ := url.ParseQuery(query)

	return &Request{
		Path:  path,        // Set the path
		Query: queryValues, // Set the parsed query values
	}
}

// PathParts returns the segments of the path as a slice of strings.
// It trims leading and trailing slashes and splits the path by slashes.
func (r *Request) PathParts() []string {
	return strings.Split(strings.Trim(r.Path, "/"), "/")
}

// PathPart returns the specified part of the path.
// If the index is out of bounds, it returns an empty string.
func (r *Request) PathPart(index int) string {
	parts := r.PathParts() // Get the path segments
	if index < 0 || index >= len(parts) {
		return "" // Return empty if index is out of bounds
	}
	return parts[index] // Return the specified path part
}

// String rebuilds the URL from the path and query values.
// If the Realm is not set, it automatically retrieves the current realm path.
func (r *Request) String() string {
	// Automatically set the Realm if it is not already defined
	if r.Realm == "" {
		r.Realm = std.CurrentRealm().PkgPath() // Get the current realm path
	}

	// Rebuild the path using the realm and path parts
	relativePkgPath := strings.TrimPrefix(r.Realm, chainDomain) // Trim the chain domain prefix
	reconstructedPath := relativePkgPath + ":" + strings.Join(r.PathParts(), "/")

	// Rebuild the query string
	queryString := r.Query.Encode() // Encode the query parameters
	if queryString != "" {
		return reconstructedPath + "?" + queryString // Return the full URL with query
	}
	return reconstructedPath // Return the path without query parameters
}

func splitPathAndQuery(rawPath string) (string, string) {
	if idx := strings.Index(rawPath, "?"); idx != -1 {
		return rawPath[:idx], rawPath[idx+1:] // Split at the first '?' found
	}
	return rawPath, "" // No query string present
}

package realmpath_test

import (
	"net/url"
	"std"
	"testing"

	"gno.land/p/moul/realmpath"
	"gno.land/p/nt/uassert"
	"gno.land/p/nt/urequire"
)

func TestExample(t *testing.T) {
	cd := std.ChainDomain()
	testing.SetRealm(std.NewCodeRealm(cd + "/r/lorem/ipsum"))

	// initial parsing
	path := "hello/world?foo=bar&baz=foobar"
	req := realmpath.Parse(path)
	urequire.False(t, req == nil, "req should not be nil")
	uassert.Equal(t, req.Path, "hello/world")
	uassert.Equal(t, req.Query.Get("foo"), "bar")
	uassert.Equal(t, req.Query.Get("baz"), "foobar")
	uassert.Equal(t, req.String(), "/r/lorem/ipsum:hello/world?baz=foobar&foo=bar")

	// alter query
	req.Query.Set("hey", "salut")
	uassert.Equal(t, req.String(), "/r/lorem/ipsum:hello/world?baz=foobar&foo=bar&hey=salut")

	// alter path
	req.Path = "bye/ciao"
	uassert.Equal(t, req.String(), "/r/lorem/ipsum:bye/ciao?baz=foobar&foo=bar&hey=salut")
}

func TestParse(t *testing.T) {
	cd := std.ChainDomain()
	testing.SetRealm(std.NewCodeRealm(cd + "/r/lorem/ipsum"))

	tests := []struct {
		rawPath        string
		realm_XXX      string // optional
		expectedPath   string
		expectedQuery  url.Values
		expectedString string
	}{
		{
			rawPath:      "hello/world?foo=bar&baz=foobar",
			expectedPath: "hello/world",
			expectedQuery: url.Values{
				"foo": []string{"bar"},
				"baz": []string{"foobar"},
			},
			expectedString: "/r/lorem/ipsum:hello/world?baz=foobar&foo=bar",
		},
		{
			rawPath:      "api/v1/resource?search=test&limit=10",
			expectedPath: "api/v1/resource",
			expectedQuery: url.Values{
				"search": []string{"test"},
				"limit":  []string{"10"},
			},
			expectedString: "/r/lorem/ipsum:api/v1/resource?limit=10&search=test",
		},
		{
			rawPath:        "singlepath",
			expectedPath:   "singlepath",
			expectedQuery:  url.Values{},
			expectedString: "/r/lorem/ipsum:singlepath",
		},
		{
			rawPath:        "path/with/trailing/slash/",
			expectedPath:   "path/with/trailing/slash/",
			expectedQuery:  url.Values{},
			expectedString: "/r/lorem/ipsum:path/with/trailing/slash",
		},
		{
			rawPath:        "emptyquery?",
			expectedPath:   "emptyquery",
			expectedQuery:  url.Values{},
			expectedString: "/r/lorem/ipsum:emptyquery",
		},
		{
			rawPath:      "path/with/special/characters/?key=val%20ue&anotherKey=with%21special%23chars",
			expectedPath: "path/with/special/characters/",
			expectedQuery: url.Values{
				"key":        []string{"val ue"},
				"anotherKey": []string{"with!special#chars"},
			},
			expectedString: "/r/lorem/ipsum:path/with/special/characters?anotherKey=with%21special%23chars&key=val+ue",
		},
		{
			rawPath:      "path/with/empty/key?keyEmpty&=valueEmpty",
			expectedPath: "path/with/empty/key",
			expectedQuery: url.Values{
				"keyEmpty": []string{""},
				"":         []string{"valueEmpty"},
			},
			expectedString: "/r/lorem/ipsum:path/with/empty/key?=valueEmpty&keyEmpty=",
		},
		{
			rawPath:      "path/with/multiple/empty/keys?=empty1&=empty2",
			expectedPath: "path/with/multiple/empty/keys",
			expectedQuery: url.Values{
				"": []string{"empty1", "empty2"},
			},
			expectedString: "/r/lorem/ipsum:path/with/multiple/empty/keys?=empty1&=empty2",
		},
		{
			rawPath:      "path/with/percent-encoded/%20space?query=hello%20world",
			expectedPath: "path/with/percent-encoded/%20space", // XXX: should we decode?
			expectedQuery: url.Values{
				"query": []string{"hello world"},
			},
			expectedString: "/r/lorem/ipsum:path/with/percent-encoded/%20space?query=hello+world",
		},
		{
			rawPath:      "path/with/very/long/query?key1=value1&key2=value2&key3=value3&key4=value4&key5=value5&key6=value6",
			expectedPath: "path/with/very/long/query",
			expectedQuery: url.Values{
				"key1": []string{"value1"},
				"key2": []string{"value2"},
				"key3": []string{"value3"},
				"key4": []string{"value4"},
				"key5": []string{"value5"},
				"key6": []string{"value6"},
			},
			expectedString: "/r/lorem/ipsum:path/with/very/long/query?key1=value1&key2=value2&key3=value3&key4=value4&key5=value5&key6=value6",
		},
		{
			rawPath:      "custom/realm?foo=bar&baz=foobar",
			realm_XXX:    cd + "/r/foo/bar",
			expectedPath: "custom/realm",
			expectedQuery: url.Values{
				"foo": []string{"bar"},
				"baz": []string{"foobar"},
			},
			expectedString: "/r/foo/bar:custom/realm?baz=foobar&foo=bar",
		},
	}

	for _, tt := range tests {
		t.Run(tt.rawPath, func(t *testing.T) {
			req := realmpath.Parse(tt.rawPath)
			req.Realm = tt.realm_XXX // set optional realm
			urequire.False(t, req == nil, "req should not be nil")
			uassert.Equal(t, req.Path, tt.expectedPath)
			urequire.Equal(t, len(req.Query), len(tt.expectedQuery))
			uassert.Equal(t, req.Query.Encode(), tt.expectedQuery.Encode())
			// XXX: uassert.Equal(t, req.Query, tt.expectedQuery)
			uassert.Equal(t, req.String(), tt.expectedString)
		})
	}
}

// Package debug provides utilities for logging and displaying debug information
// within Gno realms. It supports conditional rendering of logs and metadata,
// toggleable via query parameters.
//
// Key Features:
// - Log collection and display using Markdown formatting.
// - Metadata display for realm path, address, and height.
// - Collapsible debug section for cleaner presentation.
// - Query-based debug toggle using `?debug=1`.
package debug

import (
	"std"
	"time"

	"gno.land/p/moul/md"
	"gno.land/p/moul/mdtable"
	"gno.land/p/moul/realmpath"
	"gno.land/p/nt/ufmt"
)

// Debug encapsulates debug information, including logs and metadata.
type Debug struct {
	Logs         []string
	HideMetadata bool
}

// Log appends a new line of debug information to the Logs slice.
func (d *Debug) Log(line string) {
	d.Logs = append(d.Logs, line)
}

// Render generates the debug content as a collapsible Markdown section.
// It conditionally renders logs and metadata if enabled via the `?debug=1` query parameter.
func (d Debug) Render(path string) string {
	if realmpath.Parse(path).Query.Get("debug") != "1" {
		return ""
	}

	var content string

	if d.Logs != nil {
		content += md.H3("Logs")
		content += md.BulletList(d.Logs)
	}

	if !d.HideMetadata {
		content += md.H3("Metadata")
		table := mdtable.Table{
			Headers: []string{"Key", "Value"},
		}
		table.Append([]string{"`std.CurrentRealm().PkgPath()`", string(std.CurrentRealm().PkgPath())})
		table.Append([]string{"`std.CurrentRealm().Address()`", string(std.CurrentRealm().Address())})
		table.Append([]string{"`std.PreviousRealm().PkgPath()`", string(std.PreviousRealm().PkgPath())})
		table.Append([]string{"`std.PreviousRealm().Address()`", string(std.PreviousRealm().Address())})
		table.Append([]string{"`std.ChainHeight()`", ufmt.Sprintf("%d", std.ChainHeight())})
		table.Append([]string{"`time.Now().Format(time.RFC3339)`", time.Now().Format(time.RFC3339)})
		content += table.String()
	}

	if content == "" {
		return ""
	}

	return md.CollapsibleSection("debug", content)
}

// Render displays metadata about the current realm but does not display logs.
// This function uses a default Debug struct with metadata enabled and no logs.
func Render(path string) string {
	return Debug{}.Render(path)
}

// IsEnabled checks if the `?debug=1` query parameter is set in the given path.
// Returns true if debugging is enabled, otherwise false.
func IsEnabled(path string) bool {
	req := realmpath.Parse(path)
	return req.Query.Get("debug") == "1"
}

// ToggleURL modifies the given path's query string to toggle the `?debug=1` parameter.
// If debugging is currently enabled, it removes the parameter.
// If debugging is disabled, it adds the parameter.
func ToggleURL(path string) string {
	req := realmpath.Parse(path)
	if IsEnabled(path) {
		req.Query.Del("debug")
	} else {
		req.Query.Add("debug", "1")
	}
	return req.String()
}

// Package dynreplacer provides a simple template engine for handling dynamic
// content replacement. It is similar to strings.Replacer but with lazy
// execution of replacements, making it more optimization-friendly in several
// cases. While strings.Replacer requires all replacement values to be computed
// upfront, dynreplacer only executes the callback functions for placeholders
// that actually exist in the template, avoiding unnecessary computations.
//
// The package ensures efficient, non-recursive replacement of placeholders in a
// single pass. This lazy evaluation approach is particularly beneficial when:
// - Some replacement values are expensive to compute
// - Not all placeholders are guaranteed to be present in the template
// - Templates are reused with different content
//
// Example usage:
//
//	r := dynreplacer.New(
//	    dynreplacer.Pair{":name:", func() string { return "World" }},
//	    dynreplacer.Pair{":greeting:", func() string { return "Hello" }},
//	)
//	result := r.Replace("Hello :name:!") // Returns "Hello World!"
//
// The replacer caches computed values, so subsequent calls with the same
// placeholder will reuse the cached value instead of executing the callback
// again:
//
//	r := dynreplacer.New()
//	r.RegisterCallback(":expensive:", func() string { return "computed" })
//	r.Replace("Value1: :expensive:") // Computes the value
//	r.Replace("Value2: :expensive:") // Uses cached value
//	r.ClearCache()                   // Force re-computation on next use
package dynreplacer

import (
	"strings"
)

// Replacer manages dynamic placeholders, their associated functions, and cached
// values.
type Replacer struct {
	callbacks    map[string]func() string
	cachedValues map[string]string
}

// Pair represents a placeholder and its callback function
type Pair struct {
	Placeholder string
	Callback    func() string
}

// New creates a new Replacer instance with optional initial replacements.
// It accepts pairs where each pair consists of a placeholder string and
// its corresponding callback function.
//
// Example:
//
//	New(
//	    Pair{":name:", func() string { return "World" }},
//	    Pair{":greeting:", func() string { return "Hello" }},
//	)
func New(pairs ...Pair) *Replacer {
	r := &Replacer{
		callbacks:    make(map[string]func() string),
		cachedValues: make(map[string]string),
	}

	for _, pair := range pairs {
		r.RegisterCallback(pair.Placeholder, pair.Callback)
	}

	return r
}

// RegisterCallback associates a placeholder with a function to generate its
// content.
func (r *Replacer) RegisterCallback(placeholder string, callback func() string) {
	r.callbacks[placeholder] = callback
}

// Replace processes the given layout, replacing placeholders with cached or
// newly computed values.
func (r *Replacer) Replace(layout string) string {
	replacements := []string{}

	// Check for placeholders and compute/retrieve values
	hasReplacements := false
	for placeholder, callback := range r.callbacks {
		if strings.Contains(layout, placeholder) {
			value, exists := r.cachedValues[placeholder]
			if !exists {
				value = callback()
				r.cachedValues[placeholder] = value
			}
			replacements = append(replacements, placeholder, value)
			hasReplacements = true
		}
	}

	// If no replacements were found, return the original layout
	if !hasReplacements {
		return layout
	}

	// Create a strings.Replacer with all computed replacements
	replacer := strings.NewReplacer(replacements...)
	return replacer.Replace(layout)
}

// ClearCache clears all cached values, forcing re-computation on next Replace.
func (r *Replacer) ClearCache() {
	r.cachedValues = make(map[string]string)
}

package dynreplacer

import (
	"strings"
	"testing"

	"gno.land/p/nt/uassert"
)

func TestNew(t *testing.T) {
	tests := []struct {
		name  string
		pairs []Pair
	}{
		{
			name:  "empty constructor",
			pairs: []Pair{},
		},
		{
			name: "single pair",
			pairs: []Pair{
				{":name:", func() string { return "World" }},
			},
		},
		{
			name: "multiple pairs",
			pairs: []Pair{
				{":greeting:", func() string { return "Hello" }},
				{":name:", func() string { return "World" }},
			},
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			r := New(tt.pairs...)
			uassert.True(t, r.callbacks != nil, "callbacks map should be initialized")
			uassert.True(t, r.cachedValues != nil, "cachedValues map should be initialized")

			// Verify all callbacks were registered
			for _, pair := range tt.pairs {
				_, exists := r.callbacks[pair.Placeholder]
				uassert.True(t, exists, "callback should be registered for "+pair.Placeholder)
			}
		})
	}
}

func TestReplace(t *testing.T) {
	tests := []struct {
		name     string
		layout   string
		setup    func(*Replacer)
		expected string
	}{
		{
			name:     "empty layout",
			layout:   "",
			setup:    func(r *Replacer) {},
			expected: "",
		},
		{
			name:   "single replacement",
			layout: "Hello :name:!",
			setup: func(r *Replacer) {
				r.RegisterCallback(":name:", func() string { return "World" })
			},
			expected: "Hello World!",
		},
		{
			name:   "multiple replacements",
			layout: ":greeting: :name:!",
			setup: func(r *Replacer) {
				r.RegisterCallback(":greeting:", func() string { return "Hello" })
				r.RegisterCallback(":name:", func() string { return "World" })
			},
			expected: "Hello World!",
		},
		{
			name:   "no recursive replacement",
			layout: ":outer:",
			setup: func(r *Replacer) {
				r.RegisterCallback(":outer:", func() string { return ":inner:" })
				r.RegisterCallback(":inner:", func() string { return "content" })
			},
			expected: ":inner:",
		},
		{
			name:   "unused callbacks",
			layout: "Hello :name:!",
			setup: func(r *Replacer) {
				r.RegisterCallback(":name:", func() string { return "World" })
				r.RegisterCallback(":unused:", func() string { return "Never Called" })
			},
			expected: "Hello World!",
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			r := New()
			tt.setup(r)
			result := r.Replace(tt.layout)
			uassert.Equal(t, tt.expected, result)
		})
	}
}

func TestCaching(t *testing.T) {
	r := New()
	callCount := 0
	r.RegisterCallback(":expensive:", func() string {
		callCount++
		return "computed"
	})

	layout := "Value: :expensive:"

	// First call should compute
	result1 := r.Replace(layout)
	uassert.Equal(t, "Value: computed", result1)
	uassert.Equal(t, 1, callCount)

	// Second call should use cache
	result2 := r.Replace(layout)
	uassert.Equal(t, "Value: computed", result2)
	uassert.Equal(t, 1, callCount)

	// After clearing cache, should recompute
	r.ClearCache()
	result3 := r.Replace(layout)
	uassert.Equal(t, "Value: computed", result3)
	uassert.Equal(t, 2, callCount)
}

func TestComplexExample(t *testing.T) {
	layout := `
		# Welcome to gno.land

		## Blog
		:latest-blogposts:

		## Events
		:next-events:

		## Awesome Gno
		:awesome-gno:
	`

	r := New(
		Pair{":latest-blogposts:", func() string { return "Latest blog posts content here" }},
		Pair{":next-events:", func() string { return "Upcoming events listed here" }},
		Pair{":awesome-gno:", func() string { return ":latest-blogposts: (This should NOT be replaced again)" }},
	)

	result := r.Replace(layout)

	// Check that original placeholders are replaced
	uassert.True(t, !strings.Contains(result, ":latest-blogposts:\n"), "':latest-blogposts:' placeholder should be replaced")
	uassert.True(t, !strings.Contains(result, ":next-events:\n"), "':next-events:' placeholder should be replaced")
	uassert.True(t, !strings.Contains(result, ":awesome-gno:\n"), "':awesome-gno:' placeholder should be replaced")

	// Check that the replacement content is present
	uassert.True(t, strings.Contains(result, "Latest blog posts content here"), "Blog posts content should be present")
	uassert.True(t, strings.Contains(result, "Upcoming events listed here"), "Events content should be present")
	uassert.True(t, strings.Contains(result, ":latest-blogposts: (This should NOT be replaced again)"),
		"Nested placeholder should not be replaced")
}

func TestEdgeCases(t *testing.T) {
	tests := []struct {
		name     string
		layout   string
		setup    func(*Replacer)
		expected string
	}{
		{
			name:   "empty string placeholder",
			layout: "Hello :",
			setup: func(r *Replacer) {
				r.RegisterCallback("", func() string { return "World" })
			},
			expected: "WorldHWorldeWorldlWorldlWorldoWorld World:World",
		},
		{
			name:   "overlapping placeholders",
			layout: "Hello :name::greeting:",
			setup: func(r *Replacer) {
				r.RegisterCallback(":name:", func() string { return "World" })
				r.RegisterCallback(":greeting:", func() string { return "Hi" })
				r.RegisterCallback(":name::greeting:", func() string { return "Should not match" })
			},
			expected: "Hello WorldHi",
		},
		{
			name:   "replacement order",
			layout: ":a::b::c:",
			setup: func(r *Replacer) {
				r.RegisterCallback(":c:", func() string { return "3" })
				r.RegisterCallback(":b:", func() string { return "2" })
				r.RegisterCallback(":a:", func() string { return "1" })
			},
			expected: "123",
		},
		{
			name:   "special characters in placeholders",
			layout: "Hello :$name#123:!",
			setup: func(r *Replacer) {
				r.RegisterCallback(":$name#123:", func() string { return "World" })
			},
			expected: "Hello World!",
		},
		{
			name:   "multiple occurrences of same placeholder",
			layout: ":name: and :name: again",
			setup: func(r *Replacer) {
				callCount := 0
				r.RegisterCallback(":name:", func() string {
					callCount++
					return "World"
				})
			},
			expected: "World and World again",
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			r := New()
			tt.setup(r)
			result := r.Replace(tt.layout)
			uassert.Equal(t, tt.expected, result)
		})
	}
}

// Package errs provides utilities for combining multiple errors.
// This is a simplified version of https://github.com/uber-go/multierr (MIT License),
// adapted for the Gno programming language with a focus on core functionality
// and idiomatic usage patterns.
//
// Example usage:
//
//	err1 := doSomething()
//	err2 := doSomethingElse()
//	if err := errs.Combine(err1, err2); err != nil {
//	    return err // Returns combined errors or single error
//	}
package errs

import (
	"strings"
)

// multiError represents multiple errors combined into one.
type multiError struct {
	errors []error
}

// Error implements the error interface by returning a single-line representation
// of all contained errors, separated by semicolons.
func (m *multiError) Error() string {
	if m == nil || len(m.errors) == 0 {
		return ""
	}

	var b strings.Builder
	first := true
	for _, err := range m.errors {
		if first {
			first = false
		} else {
			b.WriteString("; ")
		}
		b.WriteString(err.Error())
	}
	return b.String()
}

// String returns a multi-line representation of the error.
func (m *multiError) String() string {
	if m == nil || len(m.errors) == 0 {
		return ""
	}

	var b strings.Builder
	b.WriteString("the following errors occurred:")
	for _, err := range m.errors {
		b.WriteString("\n -  ")
		b.WriteString(err.Error())
	}
	return b.String()
}

// Unwrap returns the slice of underlying errors contained in this multiError.
// Returns nil if the receiver is nil.
func (m *multiError) Unwrap() []error {
	if m == nil {
		return nil
	}
	return m.errors
}

// Errors extracts the underlying errors from an error interface.
// If the error is a multiError, it returns its contained errors.
// If the error is nil, returns nil.
// If the error is a regular error, returns a slice containing just that error.
func Errors(err error) []error {
	if err == nil {
		return nil
	}

	if merr, ok := err.(*multiError); ok {
		return merr.Unwrap()
	}

	return []error{err}
}

// Combine merges multiple errors into a single error efficiently.
// It handles several cases:
//   - If all input errors are nil, returns nil
//   - If there's exactly one non-nil error, returns that error directly
//   - If there are multiple non-nil errors, returns a multiError containing them
//   - Flattens any *multiError in the input
func Combine(errs ...error) error {
	nonNil := make([]error, 0, len(errs))
	for _, err := range errs {
		if err == nil {
			continue
		}
		if m, ok := err.(*multiError); ok && m != nil {
			nonNil = append(nonNil, m.Unwrap()...)
		} else {
			nonNil = append(nonNil, err)
		}
	}

	switch len(nonNil) {
	case 0:
		return nil
	case 1:
		return nonNil[0]
	default:
		return &multiError{errors: nonNil}
	}
}

