	}

	empty := &multiError{errors: nil}
	if got := empty.Unwrap(); got != nil {
		t.Errorf("Unwrap on empty: expected nil, got %v", got)
	}

	single := &multiError{errors: []error{newError("foo")}}
	got := single.Unwrap()
	if len(got) != 1 || got[0].Error() != "foo" {
		t.Errorf("Unwrap on single: expected [foo], got %v", got)
	}

	multi := &multiError{errors: []error{newError("foo"), newError("bar")}}
	got = multi.Unwrap()
	if len(got) != 2 || got[0].Error() != "foo" || got[1].Error() != "bar" {
		t.Errorf("Unwrap on multi: expected [foo bar], got %v", got)
	}
}

// Package fifo implements a fixed-size FIFO (First-In-First-Out) list data structure
// using a singly-linked list. The implementation prioritizes storage efficiency by minimizing
// storage operations - each add/remove operation only updates 1-2 pointers, regardless of
// list size.
//
// Key features:
// - Fixed-size with automatic removal of oldest entries when full
// - Support for both prepend (add at start) and append (add at end) operations
// - Constant storage usage through automatic pruning
// - O(1) append operations and latest element access
// - Iterator support for sequential access
// - Dynamic size adjustment via SetMaxSize
//
// This implementation is optimized for frequent updates, as insertions and deletions only
// require updating 1-2 pointers. However, random access operations are O(n) as they require
// traversing the list. For use cases where writes are rare, a slice-based
// implementation might be more suitable.
//
// The linked list structure is equally efficient for storing both small values (like pointers)
// and larger data structures, as each node maintains a single next-pointer regardless of the
// stored value's size.
//
// Example usage:
//
//	list := fifo.New(3)        // Create a new list with max size 3
//	list.Append("a")           // List: [a]
//	list.Append("b")           // List: [a b]
//	list.Append("c")           // List: [a b c]
//	list.Append("d")           // List: [b c d] (oldest element "a" was removed)
//	latest := list.Latest()    // Returns "d"
//	all := list.Entries()      // Returns ["b", "c", "d"]
package fifo

// node represents a single element in the linked list
type node struct {
	value any
	next  *node
}

// List represents a fixed-size FIFO list
type List struct {
	head    *node
	tail    *node
	size    int
	maxSize int
}

// New creates a new FIFO list with the specified maximum size
func New(maxSize int) *List {
	return &List{
		maxSize: maxSize,
	}
}

// Prepend adds a new entry at the start of the list. If the list exceeds maxSize,
// the last entry is automatically removed.
func (l *List) Prepend(entry any) {
	if l.maxSize == 0 {
		return
	}

	newNode := &node{value: entry}

	if l.head == nil {
		l.head = newNode
		l.tail = newNode
		l.size = 1
		return
	}

	newNode.next = l.head
	l.head = newNode

	if l.size < l.maxSize {
		l.size++
		return
	}

	// Remove last element by traversing to second-to-last
	if l.size == 1 {
		// Special case: if size is 1, just update both pointers
		l.head = newNode
		l.tail = newNode
		newNode.next = nil
		return

	}

	// Find second-to-last node
	current := l.head
	for current.next != l.tail {
		current = current.next
	}
	current.next = nil
	l.tail = current

}

// Append adds a new entry at the end of the list. If the list exceeds maxSize,
// the first entry is automatically removed.
func (l *List) Append(entry any) {
	if l.maxSize == 0 {
		return
	}

	newNode := &node{value: entry}

	if l.head == nil {
		l.head = newNode
		l.tail = newNode
		l.size = 1
		return
	}

	l.tail.next = newNode
	l.tail = newNode

	if l.size < l.maxSize {
		l.size++
	} else {
		l.head = l.head.next
	}
}

// Get returns the entry at the specified index.
// Index 0 is the oldest entry, Size()-1 is the newest.
func (l *List) Get(index int) any {
	if index < 0 || index >= l.size {
		return nil
	}

	current := l.head
	for i := 0; i < index; i++ {
		current = current.next
	}
	return current.value
}

// Size returns the current number of entries in the list
func (l *List) Size() int {
	return l.size
}

// MaxSize returns the maximum size configured for this list
func (l *List) MaxSize() int {
	return l.maxSize
}

// Entries returns all current entries as a slice
func (l *List) Entries() []any {
	entries := make([]any, l.size)
	current := l.head
	for i := 0; i < l.size; i++ {
		entries[i] = current.value
		current = current.next
	}
	return entries
}

// Iterator returns a function that can be used to iterate over the entries
// from oldest to newest. Returns nil when there are no more entries.
func (l *List) Iterator() func() any {
	current := l.head
	return func() any {
		if current == nil {
			return nil
		}
		value := current.value
		current = current.next
		return value
	}
}

// Latest returns the most recent entry.
// Returns nil if the list is empty.
func (l *List) Latest() any {
	if l.tail == nil {
		return nil
	}
	return l.tail.value
}

// SetMaxSize updates the maximum size of the list.
// If the new maxSize is smaller than the current size,
// the oldest entries are removed to fit the new size.
func (l *List) SetMaxSize(maxSize int) {
	if maxSize < 0 {
		maxSize = 0
	}

	// If new maxSize is smaller than current size,
	// remove oldest entries until we fit
	if maxSize < l.size {
		// Special case: if new maxSize is 0, clear the list
		if maxSize == 0 {
			l.head = nil
			l.tail = nil
			l.size = 0
		} else {
			// Keep the newest entries by moving head forward
			diff := l.size - maxSize
			for i := 0; i < diff; i++ {
				l.head = l.head.next
			}
			l.size = maxSize
		}
	}

	l.maxSize = maxSize
}

// Delete removes the element at the specified index.
// Returns true if an element was removed, false if the index was invalid.
func (l *List) Delete(index int) bool {
	if index < 0 || index >= l.size {
		return false
	}

	// Special case: deleting the only element
	if l.size == 1 {
		l.head = nil
		l.tail = nil
		l.size = 0
		return true
	}

	// Special case: deleting first element
	if index == 0 {
		l.head = l.head.next
		l.size--
		return true
	}

	// Find the node before the one to delete
	current := l.head
	for i := 0; i < index-1; i++ {
		current = current.next
	}

	// Special case: deleting last element
	if index == l.size-1 {
		l.tail = current
		current.next = nil
	} else {
		current.next = current.next.next
	}

	l.size--
	return true
}

// Package fp provides functional programming utilities for Gno, enabling
// transformations, filtering, and other operations on slices of any.
//
// Example of chaining operations:
//
//	numbers := []any{1, 2, 3, 4, 5, 6}
//
//	// Define predicates, mappers and reducers
//	isEven := func(v any) bool { return v.(int)%2 == 0 }
//	double := func(v any) any { return v.(int) * 2 }
//	sum := func(a, b any) any { return a.(int) + b.(int) }
//
//	// Chain operations: filter even numbers, double them, then sum
//	evenNums := Filter(numbers, isEven)        // [2, 4, 6]
//	doubled := Map(evenNums, double)           // [4, 8, 12]
//	result := Reduce(doubled, sum, 0)          // 24
//
//	// Alternative: group by even/odd, then get even numbers
//	byMod2 := func(v any) any { return v.(int) % 2 }
//	grouped := GroupBy(numbers, byMod2)        // {0: [2,4,6], 1: [1,3,5]}
//	evens := grouped[0]                        // [2,4,6]
package fp

// Mapper is a function type that maps an element to another element.
type Mapper func(any) any

// Predicate is a function type that evaluates a condition on an element.
type Predicate func(any) bool

// Reducer is a function type that reduces two elements to a single value.
type Reducer func(any, any) any

// Filter filters elements from the slice that satisfy the given predicate.
//
// Example:
//
//	numbers := []any{-1, 0, 1, 2}
//	isPositive := func(v any) bool { return v.(int) > 0 }
//	result := Filter(numbers, isPositive) // [1, 2]
func Filter(values []any, fn Predicate) []any {
	result := []any{}
	for _, v := range values {
		if fn(v) {
			result = append(result, v)
		}
	}
	return result
}

// Map applies a function to each element in the slice.
//
// Example:
//
//	numbers := []any{1, 2, 3}
//	toString := func(v any) any { return fmt.Sprintf("%d", v) }
//	result := Map(numbers, toString) // ["1", "2", "3"]
func Map(values []any, fn Mapper) []any {
	result := make([]any, len(values))
	for i, v := range values {
		result[i] = fn(v)
	}
	return result
}

// Reduce reduces a slice to a single value by applying a function.
//
// Example:
//
//	numbers := []any{1, 2, 3, 4}
//	sum := func(a, b any) any { return a.(int) + b.(int) }
//	result := Reduce(numbers, sum, 0) // 10
func Reduce(values []any, fn Reducer, initial any) any {
	acc := initial
	for _, v := range values {
		acc = fn(acc, v)
	}
	return acc
}

// FlatMap maps each element to a collection and flattens the results.
//
// Example:
//
//	words := []any{"hello", "world"}
//	split := func(v any) any {
//	    chars := []any{}
//	    for _, c := range v.(string) {
//	        chars = append(chars, string(c))
//	    }
//	    return chars
//	}
//	result := FlatMap(words, split) // ["h","e","l","l","o","w","o","r","l","d"]
func FlatMap(values []any, fn Mapper) []any {
	result := []any{}
	for _, v := range values {
		inner := fn(v).([]any)
		result = append(result, inner...)
	}
	return result
}

// All returns true if all elements satisfy the predicate.
//
// Example:
//
//	numbers := []any{2, 4, 6, 8}
//	isEven := func(v any) bool { return v.(int)%2 == 0 }
//	result := All(numbers, isEven) // true
func All(values []any, fn Predicate) bool {
	for _, v := range values {
		if !fn(v) {
			return false
		}
	}
	return true
}

// Any returns true if at least one element satisfies the predicate.
//
// Example:
//
//	numbers := []any{1, 3, 4, 7}
//	isEven := func(v any) bool { return v.(int)%2 == 0 }
//	result := Any(numbers, isEven) // true (4 is even)
func Any(values []any, fn Predicate) bool {
	for _, v := range values {
		if fn(v) {
			return true
		}
	}
	return false
}

// None returns true if no elements satisfy the predicate.
//
// Example:
//
//	numbers := []any{1, 3, 5, 7}
//	isEven := func(v any) bool { return v.(int)%2 == 0 }
//	result := None(numbers, isEven) // true (no even numbers)
func None(values []any, fn Predicate) bool {
	for _, v := range values {
		if fn(v) {
			return false
		}
	}
	return true
}

// Chunk splits a slice into chunks of the given size.
//
// Example:
//
//	numbers := []any{1, 2, 3, 4, 5}
//	result := Chunk(numbers, 2) // [[1,2], [3,4], [5]]
func Chunk(values []any, size int) [][]any {
	if size <= 0 {
		return nil
	}
	var chunks [][]any
	for i := 0; i < len(values); i += size {
		end := i + size
		if end > len(values) {
			end = len(values)
		}
		chunks = append(chunks, values[i:end])
	}
	return chunks
}

// Find returns the first element that satisfies the predicate and a boolean indicating if an element was found.
//
// Example:
//
//	numbers := []any{1, 2, 3, 4}
//	isEven := func(v any) bool { return v.(int)%2 == 0 }
//	result, found := Find(numbers, isEven) // 2, true
func Find(values []any, fn Predicate) (any, bool) {
	for _, v := range values {
		if fn(v) {
			return v, true
		}
	}
	return nil, false
}

// Reverse reverses the order of elements in a slice.
//
// Example:
//
//	numbers := []any{1, 2, 3}
//	result := Reverse(numbers) // [3, 2, 1]
func Reverse(values []any) []any {
	result := make([]any, len(values))
	for i, v := range values {
		result[len(values)-1-i] = v
	}
	return result
}

// Zip combines two slices into a slice of pairs. If the slices have different lengths,
// extra elements from the longer slice are ignored.
//
// Example:
//
//	a := []any{1, 2, 3}
//	b := []any{"a", "b", "c"}
//	result := Zip(a, b) // [[1,"a"], [2,"b"], [3,"c"]]
func Zip(a, b []any) [][2]any {
	length := min(len(a), len(b))
	result := make([][2]any, length)
	for i := 0; i < length; i++ {
		result[i] = [2]any{a[i], b[i]}
	}
	return result
}

// Unzip splits a slice of pairs into two separate slices.
//
// Example:
//
//	pairs := [][2]any{{1,"a"}, {2,"b"}, {3,"c"}}
//	numbers, letters := Unzip(pairs) // [1,2,3], ["a","b","c"]
func Unzip(pairs [][2]any) ([]any, []any) {
	a := make([]any, len(pairs))
	b := make([]any, len(pairs))
	for i, pair := range pairs {
		a[i] = pair[0]
		b[i] = pair[1]
	}
	return a, b
}

// GroupBy groups elements based on a key returned by a Mapper.
//
// Example:
//
//	numbers := []any{1, 2, 3, 4, 5, 6}
//	byMod3 := func(v any) any { return v.(int) % 3 }
//	result := GroupBy(numbers, byMod3) // {0: [3,6], 1: [1,4], 2: [2,5]}
func GroupBy(values []any, fn Mapper) map[any][]any {
	result := make(map[any][]any)
	for _, v := range values {
		key := fn(v)
		result[key] = append(result[key], v)
	}
	return result
}

// Flatten flattens a slice of slices into a single slice.
//
// Example:
//
//	nested := [][]any{{1,2}, {3,4}, {5}}
//	result := Flatten(nested) // [1,2,3,4,5]
func Flatten(values [][]any) []any {
	result := []any{}
	for _, v := range values {
		result = append(result, v...)
	}
	return result
}

// Helper functions
func min(a, b int) int {
	if a < b {
		return a
	}
	return b
}

package fp

import (
	"fmt"
	"testing"
)

func TestMap(t *testing.T) {
	tests := []struct {
		name     string
		input    []any
		fn       func(any) any
		expected []any
	}{
		{
			name:     "multiply numbers by 2",
			input:    []any{1, 2, 3},
			fn:       func(v any) any { return v.(int) * 2 },
			expected: []any{2, 4, 6},
		},
		{
			name:     "empty slice",
			input:    []any{},
			fn:       func(v any) any { return v.(int) * 2 },
			expected: []any{},
		},
		{
			name:     "convert numbers to strings",
			input:    []any{1, 2, 3},
			fn:       func(v any) any { return fmt.Sprintf("%d", v.(int)) },
			expected: []any{"1", "2", "3"},
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			result := Map(tt.input, tt.fn)
			if !equalSlices(result, tt.expected) {
				t.Errorf("Map failed, expected %v, got %v", tt.expected, result)
			}
		})
	}
}

func TestFilter(t *testing.T) {
	tests := []struct {
		name     string
		input    []any
		fn       func(any) bool
		expected []any
	}{
		{
			name:     "filter even numbers",
			input:    []any{1, 2, 3, 4},
			fn:       func(v any) bool { return v.(int)%2 == 0 },
			expected: []any{2, 4},
		},
		{
			name:     "empty slice",
			input:    []any{},
			fn:       func(v any) bool { return v.(int)%2 == 0 },
			expected: []any{},
		},
		{
			name:     "no matches",
			input:    []any{1, 3, 5},
			fn:       func(v any) bool { return v.(int)%2 == 0 },
			expected: []any{},
		},
		{
			name:     "all matches",
			input:    []any{2, 4, 6},
			fn:       func(v any) bool { return v.(int)%2 == 0 },
			expected: []any{2, 4, 6},
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			result := Filter(tt.input, tt.fn)
			if !equalSlices(result, tt.expected) {
				t.Errorf("Filter failed, expected %v, got %v", tt.expected, result)
