		},
		{
			name:     "empty slice",
			input:    []any{},
			fn:       func(v any) any { return v.(int) % 2 },
			expected: map[any][]any{},
		},
		{
			name:  "single group",
			input: []any{2, 4, 6},
			fn:    func(v any) any { return v.(int) % 2 },
			expected: map[any][]any{
				0: {2, 4, 6},
			},
		},
		{
			name:  "group by type",
			input: []any{1, "a", 2, "b", true},
			fn: func(v any) any {
				switch v.(type) {
				case int:
					return "int"
				case string:
					return "string"
				default:
					return "other"
				}
			},
			expected: map[any][]any{
				"int":    {1, 2},
				"string": {"a", "b"},
				"other":  {true},
			},
		},
	}

	for _, tt := range tests {
