// compareStringInterfaceMaps compares two map[string]any for equality
func compareStringInterfaceMaps(a, b map[string]any) bool {
	if len(a) != len(b) {
		return false
	}
	for k, v1 := range a {
		v2, ok := b[k]
		if !ok {
			return false
		}
		// Compare values based on their type
		switch val1 := v1.(type) {
		case string:
			val2, ok := v2.(string)
			if !ok || val1 != val2 {
				return false
			}
		case int:
			val2, ok := v2.(int)
			if !ok || val1 != val2 {
				return false
			}
		case float64:
			val2, ok := v2.(float64)
			if !ok || val1 != val2 {
				return false
			}
		case bool:
			val2, ok := v2.(bool)
			if !ok || val1 != val2 {
				return false
			}
		case []any:
			val2, ok := v2.([]any)
			if !ok || len(val1) != len(val2) {
				return false
			}
			for i := range val1 {
				if val1[i] != val2[i] {
					return false
				}
			}
		case map[string]any:
			val2, ok := v2.(map[string]any)
			if !ok || !compareStringInterfaceMaps(val1, val2) {
				return false
			}
		default:
			return false
		}
	}
	return true
}

func TestToMapStringInterface(t *testing.T) {
	tests := []struct {
		name     string
		input    any
		expected map[string]any
		wantErr  bool
	}{
		{
			name: "map[string]any",
			input: map[string]any{
				"key1": "value1",
				"key2": 42,
			},
			expected: map[string]any{
				"key1": "value1",
				"key2": 42,
			},
			wantErr: false,
		},
		{
			name: "map[string]string",
			input: map[string]string{
				"key1": "value1",
				"key2": "value2",
			},
			expected: map[string]any{
				"key1": "value1",
				"key2": "value2",
			},
			wantErr: false,
		},
		{
			name: "map[string]int",
			input: map[string]int{
				"key1": 1,
				"key2": 2,
			},
			expected: map[string]any{
				"key1": 1,
				"key2": 2,
			},
			wantErr: false,
		},
		{
			name: "map[string]float64",
			input: map[string]float64{
				"key1": 1.1,
				"key2": 2.2,
			},
			expected: map[string]any{
				"key1": 1.1,
				"key2": 2.2,
			},
			wantErr: false,
		},
		{
			name: "map[string]bool",
			input: map[string]bool{
				"key1": true,
				"key2": false,
			},
			expected: map[string]any{
				"key1": true,
				"key2": false,
			},
			wantErr: false,
		},
		{
			name: "map[string][]string",
			input: map[string][]string{
				"key1": {"a", "b"},
				"key2": {"c", "d"},
			},
			expected: map[string]any{
				"key1": []any{"a", "b"},
				"key2": []any{"c", "d"},
			},
			wantErr: false,
		},
		{
			name: "nested map[string]map[string]string",
			input: map[string]map[string]string{
				"key1": {"nested1": "value1"},
				"key2": {"nested2": "value2"},
			},
			expected: map[string]any{
				"key1": map[string]any{"nested1": "value1"},
				"key2": map[string]any{"nested2": "value2"},
			},
			wantErr: false,
		},
		{
			name:     "unsupported type",
			input:    42, // not a map
			expected: nil,
			wantErr:  true,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			got, err := ToMapStringInterface(tt.input)
			if (err != nil) != tt.wantErr {
				t.Errorf("ToMapStringInterface() error = %v, wantErr %v", err, tt.wantErr)
				return
			}
			if !tt.wantErr {
				if !compareStringInterfaceMaps(got, tt.expected) {
					t.Errorf("ToMapStringInterface() = %v, expected %v", got, tt.expected)
				}
			}
		})
	}
}

