func TestToMapStringInterfaceErrors(t *testing.T) {
	_, err := ToMapStringInterface(42)
	if err == nil || !strings.Contains(err.Error(), "unsupported map type") {
		t.Errorf("Expected error containing 'unsupported map type', got %v", err)
	}
}

// compareIntInterfaceMaps compares two map[int]any for equality
func compareIntInterfaceMaps(a, b map[int]any) bool {
	if len(a) != len(b) {
		return false
	}
	for k, v1 := range a {
		v2, ok := b[k]
		if !ok {
			return false
		}
		// Compare values based on their type
		switch val1 := v1.(type) {
		case string:
			val2, ok := v2.(string)
			if !ok || val1 != val2 {
				return false
			}
		case int:
			val2, ok := v2.(int)
			if !ok || val1 != val2 {
				return false
			}
		case float64:
			val2, ok := v2.(float64)
			if !ok || val1 != val2 {
				return false
			}
		case bool:
			val2, ok := v2.(bool)
			if !ok || val1 != val2 {
				return false
			}
		case []any:
			val2, ok := v2.([]any)
			if !ok || len(val1) != len(val2) {
				return false
			}
			for i := range val1 {
				if val1[i] != val2[i] {
					return false
				}
			}
		case map[string]any:
			val2, ok := v2.(map[string]any)
			if !ok || !compareStringInterfaceMaps(val1, val2) {
				return false
			}
		default:
			return false
		}
	}
	return true
}

func TestToMapIntInterface(t *testing.T) {
	tests := []struct {
		name     string
		input    any
		expected map[int]any
		wantErr  bool
	}{
		{
			name: "map[int]any",
			input: map[int]any{
				1: "value1",
				2: 42,
			},
			expected: map[int]any{
				1: "value1",
				2: 42,
			},
			wantErr: false,
		},
		{
			name: "map[int]string",
			input: map[int]string{
				1: "value1",
				2: "value2",
			},
			expected: map[int]any{
				1: "value1",
				2: "value2",
			},
			wantErr: false,
		},
		{
			name: "map[int]int",
			input: map[int]int{
				1: 10,
				2: 20,
			},
			expected: map[int]any{
				1: 10,
				2: 20,
			},
			wantErr: false,
		},
		{
			name: "map[int]float64",
			input: map[int]float64{
				1: 1.1,
				2: 2.2,
			},
			expected: map[int]any{
				1: 1.1,
				2: 2.2,
			},
			wantErr: false,
		},
		{
			name: "map[int]bool",
			input: map[int]bool{
				1: true,
				2: false,
			},
			expected: map[int]any{
				1: true,
				2: false,
			},
			wantErr: false,
		},
		{
			name: "map[int][]string",
			input: map[int][]string{
				1: {"a", "b"},
				2: {"c", "d"},
			},
			expected: map[int]any{
				1: []any{"a", "b"},
				2: []any{"c", "d"},
			},
			wantErr: false,
		},
		{
			name: "map[int]map[string]any",
			input: map[int]map[string]any{
				1: {"nested1": "value1"},
				2: {"nested2": "value2"},
			},
			expected: map[int]any{
				1: map[string]any{"nested1": "value1"},
				2: map[string]any{"nested2": "value2"},
			},
			wantErr: false,
		},
		{
			name:     "unsupported type",
			input:    42, // not a map
			expected: nil,
			wantErr:  true,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			got, err := ToMapIntInterface(tt.input)
			if (err != nil) != tt.wantErr {
				t.Errorf("ToMapIntInterface() error = %v, wantErr %v", err, tt.wantErr)
				return
			}
			if !tt.wantErr {
				if !compareIntInterfaceMaps(got, tt.expected) {
					t.Errorf("ToMapIntInterface() = %v, expected %v", got, tt.expected)
				}
			}
		})
	}
}

