func TestIterator(t *testing.T) {
	tests := []struct {
		name      string
		values    []any
		start     int
		end       int
		expected  []Entry
		wantStop  bool
		stopAfter int // stop after N elements, -1 for no stop
	}{
		{
			name:      "empty list",
			values:    []any{},
			start:     0,
			end:       10,
			expected:  []Entry{},
			stopAfter: -1,
		},
		{
			name:      "nil list",
			values:    nil,
			start:     0,
			end:       0,
			expected:  []Entry{},
			stopAfter: -1,
		},
		{
			name:   "single element forward",
			values: []any{42},
			start:  0,
			end:    0,
			expected: []Entry{
				{Index: 0, Value: 42},
			},
			stopAfter: -1,
		},
		{
			name:   "multiple elements forward",
			values: []any{1, 2, 3, 4, 5},
			start:  0,
			end:    4,
			expected: []Entry{
				{Index: 0, Value: 1},
				{Index: 1, Value: 2},
				{Index: 2, Value: 3},
				{Index: 3, Value: 4},
				{Index: 4, Value: 5},
			},
			stopAfter: -1,
		},
		{
			name:   "multiple elements reverse",
			values: []any{1, 2, 3, 4, 5},
			start:  4,
			end:    0,
			expected: []Entry{
				{Index: 4, Value: 5},
				{Index: 3, Value: 4},
				{Index: 2, Value: 3},
				{Index: 1, Value: 2},
				{Index: 0, Value: 1},
			},
			stopAfter: -1,
		},
		{
			name:   "partial range forward",
			values: []any{1, 2, 3, 4, 5},
			start:  1,
			end:    3,
			expected: []Entry{
				{Index: 1, Value: 2},
				{Index: 2, Value: 3},
				{Index: 3, Value: 4},
			},
			stopAfter: -1,
		},
		{
			name:   "partial range reverse",
			values: []any{1, 2, 3, 4, 5},
			start:  3,
			end:    1,
			expected: []Entry{
				{Index: 3, Value: 4},
				{Index: 2, Value: 3},
				{Index: 1, Value: 2},
			},
			stopAfter: -1,
		},
		{
			name:      "stop iteration early",
			values:    []any{1, 2, 3, 4, 5},
			start:     0,
			end:       4,
			wantStop:  true,
			stopAfter: 2,
			expected: []Entry{
				{Index: 0, Value: 1},
				{Index: 1, Value: 2},
			},
		},
		{
			name:   "negative start",
			values: []any{1, 2, 3},
			start:  -1,
			end:    2,
			expected: []Entry{
				{Index: 0, Value: 1},
				{Index: 1, Value: 2},
				{Index: 2, Value: 3},
			},
			stopAfter: -1,
		},
		{
			name:   "negative end",
			values: []any{1, 2, 3},
			start:  0,
			end:    -2,
			expected: []Entry{
				{Index: 0, Value: 1},
			},
			stopAfter: -1,
		},
		{
			name:      "start beyond size",
			values:    []any{1, 2, 3},
			start:     5,
			end:       6,
			expected:  []Entry{},
			stopAfter: -1,
		},
		{
			name:   "end beyond size",
			values: []any{1, 2, 3},
			start:  0,
			end:    5,
			expected: []Entry{
				{Index: 0, Value: 1},
				{Index: 1, Value: 2},
				{Index: 2, Value: 3},
			},
			stopAfter: -1,
		},
		{
			name:   "with deleted elements",
			values: []any{1, 2, nil, 4, 5},
			start:  0,
			end:    4,
			expected: []Entry{
				{Index: 0, Value: 1},
				{Index: 1, Value: 2},
				{Index: 3, Value: 4},
				{Index: 4, Value: 5},
			},
			stopAfter: -1,
		},
		{
			name:   "with deleted elements reverse",
			values: []any{1, nil, 3, nil, 5},
			start:  4,
			end:    0,
			expected: []Entry{
				{Index: 4, Value: 5},
				{Index: 2, Value: 3},
				{Index: 0, Value: 1},
			},
			stopAfter: -1,
		},
		{
			name:      "start equals end",
			values:    []any{1, 2, 3},
			start:     1,
			end:       1,
			expected:  []Entry{{Index: 1, Value: 2}},
			stopAfter: -1,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			list := New()
			list.Append(tt.values...)

			var result []Entry
			stopped := list.Iterator(tt.start, tt.end, func(index int, value any) bool {
				result = append(result, Entry{Index: index, Value: value})
				return tt.stopAfter >= 0 && len(result) >= tt.stopAfter
			})

			uassert.Equal(t, len(result), len(tt.expected), "comparing length")

			for i := range result {
				uassert.Equal(t, result[i].Index, tt.expected[i].Index, "comparing index")
				uassert.Equal(t, typeutil.ToString(result[i].Value), typeutil.ToString(tt.expected[i].Value), "comparing value")
			}

			uassert.Equal(t, stopped, tt.wantStop, "comparing stopped")
		})
	}
}

func TestLargeListAppendGetAndDelete(t *testing.T) {
	l := New()
	size := 100

	// Append values from 0 to 99
	for i := 0; i < size; i++ {
		l.Append(i)
		val := l.Get(i)
		uassert.Equal(t, i, val)
	}

	// Verify size
	uassert.Equal(t, size, l.Size())
	uassert.Equal(t, size, l.TotalSize())

	// Get and verify each value
	for i := 0; i < size; i++ {
		val := l.Get(i)
		uassert.Equal(t, i, val)
	}

	// Get and verify each value
	for i := 0; i < size; i++ {
		err := l.Delete(i)
		uassert.Equal(t, nil, err)
	}

	// Verify size
	uassert.Equal(t, 0, l.Size())
	uassert.Equal(t, size, l.TotalSize())

	// Get and verify each value
	for i := 0; i < size; i++ {
		val := l.Get(i)
		uassert.Equal(t, nil, val)
	}
}

func TestEdgeCases(t *testing.T) {
	tests := []struct {
		name string
		test func(t *testing.T)
	}{
		{
			name: "nil list operations",
