				uassert.Equal(t, 0, l.Size())
				uassert.Equal(t, 0, l.TotalSize())
				uassert.Equal(t, nil, l.Get(0))
				err := l.Delete(0)
				uassert.Equal(t, ErrOutOfBounds.Error(), err.Error())
			},
		},
		{
			name: "delete empty indices slice",
			test: func(t *testing.T) {
				l := New()
				l.Append(1)
				err := l.Delete()
				uassert.Equal(t, nil, err)
				uassert.Equal(t, 1, l.Size())
			},
		},
		{
			name: "append nil values",
			test: func(t *testing.T) {
				l := New()
				l.Append(nil, nil)
				uassert.Equal(t, 2, l.Size())
				uassert.Equal(t, nil, l.Get(0))
				uassert.Equal(t, nil, l.Get(1))
			},
		},
		{
			name: "delete same index multiple times",
			test: func(t *testing.T) {
				l := New()
				l.Append(1, 2, 3)
				err := l.Delete(1)
				uassert.Equal(t, nil, err)
				err = l.Delete(1)
				uassert.Equal(t, ErrDeleted.Error(), err.Error())
			},
		},
		{
			name: "iterator with all deleted elements",
			test: func(t *testing.T) {
				l := New()
				l.Append(1, 2, 3)
				l.Delete(0, 1, 2)
				var count int
				l.Iterator(0, 2, func(index int, value any) bool {
					count++
					return false
				})
				uassert.Equal(t, 0, count)
			},
		},
		{
			name: "append after delete",
			test: func(t *testing.T) {
				l := New()
				l.Append(1, 2)
				l.Delete(1)
				l.Append(3)
				uassert.Equal(t, 2, l.Size())
				uassert.Equal(t, 3, l.TotalSize())
				uassert.Equal(t, 1, l.Get(0))
				uassert.Equal(t, nil, l.Get(1))
				uassert.Equal(t, 3, l.Get(2))
			},
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			tt.test(t)
		})
	}
}

func TestIteratorByOffset(t *testing.T) {
	tests := []struct {
		name     string
		values   []any
		offset   int
		count    int
		expected []Entry
		wantStop bool
	}{
		{
			name:     "empty list",
			values:   []any{},
			offset:   0,
			count:    5,
			expected: []Entry{},
			wantStop: false,
		},
		{
			name:   "positive count forward iteration",
			values: []any{1, 2, 3, 4, 5},
			offset: 1,
			count:  2,
			expected: []Entry{
				{Index: 1, Value: 2},
				{Index: 2, Value: 3},
			},
			wantStop: false,
		},
		{
			name:   "negative count backward iteration",
			values: []any{1, 2, 3, 4, 5},
			offset: 3,
			count:  -2,
			expected: []Entry{
				{Index: 3, Value: 4},
				{Index: 2, Value: 3},
			},
			wantStop: false,
		},
		{
			name:   "count exceeds available elements forward",
			values: []any{1, 2, 3},
			offset: 1,
			count:  5,
			expected: []Entry{
				{Index: 1, Value: 2},
				{Index: 2, Value: 3},
			},
			wantStop: false,
		},
		{
			name:   "count exceeds available elements backward",
			values: []any{1, 2, 3},
			offset: 1,
			count:  -5,
			expected: []Entry{
				{Index: 1, Value: 2},
				{Index: 0, Value: 1},
			},
			wantStop: false,
		},
		{
			name:     "zero count",
			values:   []any{1, 2, 3},
			offset:   0,
			count:    0,
			expected: []Entry{},
			wantStop: false,
		},
		{
			name:   "negative offset",
			values: []any{1, 2, 3},
			offset: -1,
			count:  2,
			expected: []Entry{
				{Index: 0, Value: 1},
				{Index: 1, Value: 2},
			},
			wantStop: false,
		},
		{
			name:   "offset beyond size",
			values: []any{1, 2, 3},
			offset: 5,
			count:  -2,
			expected: []Entry{
				{Index: 2, Value: 3},
				{Index: 1, Value: 2},
			},
			wantStop: false,
		},
		{
			name:   "with deleted elements",
			values: []any{1, nil, 3, nil, 5},
			offset: 0,
			count:  3,
			expected: []Entry{
				{Index: 0, Value: 1},
				{Index: 2, Value: 3},
				{Index: 4, Value: 5},
			},
			wantStop: false,
		},
		{
			name:   "early stop in forward iteration",
			values: []any{1, 2, 3, 4, 5},
			offset: 0,
			count:  5,
			expected: []Entry{
				{Index: 0, Value: 1},
				{Index: 1, Value: 2},
			},
			wantStop: true, // The callback will return true after 2 elements
		},
		{
			name:   "early stop in backward iteration",
			values: []any{1, 2, 3, 4, 5},
			offset: 4,
			count:  -5,
			expected: []Entry{
				{Index: 4, Value: 5},
				{Index: 3, Value: 4},
			},
			wantStop: true, // The callback will return true after 2 elements
		},
		{
			name:     "nil list",
			values:   nil,
			offset:   0,
			count:    5,
			expected: []Entry{},
			wantStop: false,
		},
		{
			name:   "single element forward",
			values: []any{1},
			offset: 0,
			count:  5,
			expected: []Entry{
				{Index: 0, Value: 1},
			},
			wantStop: false,
		},
		{
			name:   "single element backward",
			values: []any{1},
			offset: 0,
			count:  -5,
			expected: []Entry{
				{Index: 0, Value: 1},
			},
			wantStop: false,
		},
		{
			name:     "all deleted elements",
			values:   []any{nil, nil, nil},
			offset:   0,
			count:    3,
			expected: []Entry{},
			wantStop: false,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			list := New()
			list.Append(tt.values...)

			var result []Entry
			var cb IterCbFn
			if tt.wantStop {
				cb = func(index int, value any) bool {
					result = append(result, Entry{Index: index, Value: value})
					return len(result) >= 2 // Stop after 2 elements for early stop tests
				}
			} else {
				cb = func(index int, value any) bool {
					result = append(result, Entry{Index: index, Value: value})
					return false
				}
			}

			stopped := list.IteratorByOffset(tt.offset, tt.count, cb)

			uassert.Equal(t, len(tt.expected), len(result), "comparing length")
			for i := range result {
				uassert.Equal(t, tt.expected[i].Index, result[i].Index, "comparing index")
				uassert.Equal(t, typeutil.ToString(tt.expected[i].Value), typeutil.ToString(result[i].Value), "comparing value")
			}
			uassert.Equal(t, tt.wantStop, stopped, "comparing stopped")
		})
	}
}

func TestMustDelete(t *testing.T) {
	tests := []struct {
		name        string
		setup       func() *List
		indices     []int
		shouldPanic bool
		panicMsg    string
	}{
		{
			name: "successful delete",
			setup: func() *List {
				l := New()
				l.Append(1, 2, 3)
				return l
			},
			indices:     []int{1},
			shouldPanic: false,
		},
		{
			name: "out of bounds",
			setup: func() *List {
				l := New()
				l.Append(1)
				return l
			},
			indices:     []int{1},
			shouldPanic: true,
			panicMsg:    ErrOutOfBounds.Error(),
		},
		{
			name: "already deleted",
			setup: func() *List {
				l := New()
				l.Append(1)
				l.Delete(0)
				return l
			},
			indices:     []int{0},
			shouldPanic: true,
			panicMsg:    ErrDeleted.Error(),
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			l := tt.setup()
			if tt.shouldPanic {
				defer func() {
					r := recover()
					if r == nil {
						t.Error("Expected panic but got none")
					}
					err, ok := r.(error)
					if !ok {
						t.Errorf("Expected error but got %v", r)
					}
					uassert.Equal(t, tt.panicMsg, err.Error())
				}()
			}
			l.MustDelete(tt.indices...)
			if tt.shouldPanic {
				t.Error("Expected panic")
			}
		})
	}
}

