			start:    0,
			end:      10,
			expected: []Entry{},
		},
		{
			name:   "single element",
			values: []any{42},
			start:  0,
			end:    0,
			expected: []Entry{
				{Index: 0, Value: 42},
			},
		},
		{
			name:   "multiple elements forward",
			values: []any{1, 2, 3, 4, 5},
			start:  1,
			end:    3,
			expected: []Entry{
				{Index: 1, Value: 2},
				{Index: 2, Value: 3},
				{Index: 3, Value: 4},
			},
		},
		{
			name:   "multiple elements reverse",
			values: []any{1, 2, 3, 4, 5},
			start:  3,
			end:    1,
			expected: []Entry{
				{Index: 3, Value: 4},
				{Index: 2, Value: 3},
				{Index: 1, Value: 2},
			},
		},
		{
			name:   "with deleted elements",
			values: []any{1, nil, 3, nil, 5},
			start:  0,
			end:    4,
			expected: []Entry{
				{Index: 0, Value: 1},
				{Index: 2, Value: 3},
				{Index: 4, Value: 5},
			},
		},
