// Package chonk provides a simple way to store arbitrarily large strings
// in a linked list across transactions for efficient storage and retrieval.
// A Chonk support three operations: Add, Flush, and Scanner.
// - Add appends a string to the Chonk.
// - Flush clears the Chonk.
// - Scanner is used to iterate over the chunks in the Chonk.
package chonk

// Chonk is a linked list string storage and
// retrieval system for fine bois.
type Chonk struct {
	first *chunk
	last  *chunk
}

// chunk is a linked list node for Chonk
type chunk struct {
	text string
	next *chunk
}

// New creates a reference to a new Chonk
func New() *Chonk {
	return &Chonk{}
}

// Add appends a string to the Chonk. If the Chonk is empty,
// the string will be the first and last chunk. Otherwise,
// the string will be appended to the end of the Chonk.
func (c *Chonk) Add(text string) {
	next := &chunk{text: text}
	if c.first == nil {
		c.first = next
		c.last = next
		return
	}
	c.last.next = next
	c.last = next
}

// Flush clears the Chonk by setting the first and last
// chunks to nil. This will allow the garbage collector to
// free the memory used by the Chonk.
func (c *Chonk) Flush() {
	c.first = nil
	c.last = nil
}

// Scanner returns a new Scanner for the Chonk. The Scanner
// is used to iterate over the chunks in the Chonk.
func (c *Chonk) Scanner() *Scanner {
	return &Scanner{
		next: c.first,
	}
}

// Scanner is a simple string scanner for Chonk. It is used
// to iterate over the chunks in a Chonk from first to last.
type Scanner struct {
	current *chunk
	next    *chunk
}

// Scan advances the scanner to the next chunk. It returns
// true if there is a next chunk, and false if there is not.
func (s *Scanner) Scan() bool {
	if s.next != nil {
		s.current = s.next
		s.next = s.next.next
		return true
	}
	return false
}

// Text returns the current chunk. It is only valid to call
// this method after a call to Scan returns true. Expected usage:
//
//		scanner := chonk.Scanner()
//			for scanner.Scan() {
//	    		fmt.Println(scanner.Text())
//			}
func (s *Scanner) Text() string {
	return s.current.text
}

package chonk

import (
	"testing"
)

func TestChonk(t *testing.T) {
	t.Parallel()
	c := New()
	testTable := []struct {
		name   string
		chunks []string
	}{
		{
			name:   "empty",
			chunks: []string{},
		},
		{
			name:   "single chunk",
			chunks: []string{"a"},
		},
		{
			name:   "multiple chunks",
			chunks: []string{"a", "b", "c", "d", "e", "f", "g", "h", "i", "j"},
		},
		{
			name:   "multiline chunks",
			chunks: []string{"1a\nb\nc\n\n", "d\ne\nf", "g\nh\ni", "j\nk\nl\n\n\n\n"},
		},
		{
			name:   "empty",
			chunks: []string{},
		},
	}
	testChonk := func(t *testing.T, c *Chonk, chunks []string) {
		for _, chunk := range chunks {
			c.Add(chunk)
		}
		scanner := c.Scanner()
		i := 0
		for scanner.Scan() {
			if scanner.Text() != chunks[i] {
				t.Errorf("expected %s, got %s", chunks[i], scanner.Text())
			}
			i++
		}
	}
	for _, test := range testTable {
		t.Run(test.name, func(t *testing.T) {
			testChonk(t, c, test.chunks)
			c.Flush()
		})
	}
}

package needle

import (
	"bytes"
	"crypto/sha256"
	"errors"
)

const (
	// HashLength is the length in bytes of the hash prefix in any message
	HashLength = 32
	// PayloadLength is the length of the remaining bytes of the message.
	PayloadLength = 160
	// NeedleLength is the number of bytes required for a valid needle.
	NeedleLength = HashLength + PayloadLength
)

// Needle is a container for a 160 byte payload
// and a 32 byte sha256 hash of the payload.
type Needle struct {
	hash    [HashLength]byte
	payload [PayloadLength]byte
}

var (
	// ErrorInvalidHash is an error for in invalid hash
	ErrorInvalidHash = errors.New("invalid hash")
	// ErrorByteSliceLength is an error for an invalid byte slice length passed in to New or FromBytes
	ErrorByteSliceLength = errors.New("invalid byte slice length")
)

// New creates a Needle used for submitting a payload to a Haystack sever. It takes a Payload
// byte slice that is 160 bytes in length and returns a reference to a
// Needle and an error. The purpose of this function is to make it
// easy to create a new Needle from a payload. This function handles creating a sha256
// hash of the payload, which is used by the Needle to submit to a haystack server.
func New(p []byte) (*Needle, error) {
	if len(p) != PayloadLength {
		return nil, ErrorByteSliceLength
	}
	var n Needle
	sum := sha256.Sum256(p)
	copy(n.hash[:], sum[:])
	copy(n.payload[:], p)
	return &n, nil
}

// FromBytes is intended convert raw bytes (from UDP or storage) into a Needle.
// It takes a byte slice and expects it to be exactly the length of NeedleLength.
// The byte slice should consist of the first 32 bytes being the sha256 hash of the
// payload and the payload bytes. This function verifies the length of the byte slice,
// copies the bytes into a private [192]byte array, and validates the Needle. It returns
// a reference to a Needle and an error.
func FromBytes(b []byte) (*Needle, error) {
	if len(b) != NeedleLength {
		return nil, ErrorByteSliceLength
	}
	var n Needle
	copy(n.hash[:], b[:HashLength])
	copy(n.payload[:], b[HashLength:])
	if err := n.validate(); err != nil {
		return nil, err
	}
	return &n, nil
}

// Hash returns a copy of the bytes of the sha256 256 hash of the Needle payload.
func (n *Needle) Hash() []byte {
	return n.Bytes()[:HashLength]
}

// Payload returns a byte slice of the Needle payload
func (n *Needle) Payload() []byte {
	return n.Bytes()[HashLength:]
}

// Bytes returns a byte slice of the entire 192 byte hash + payload
func (n *Needle) Bytes() []byte {
	b := make([]byte, NeedleLength)
	copy(b, n.hash[:])
	copy(b[HashLength:], n.payload[:])
	return b
}

// validate checks that a Needle has a valid hash, it returns either nil or an error.
func (n *Needle) validate() error {
	if hash := sha256.Sum256(n.Payload()); !bytes.Equal(n.Hash(), hash[:]) {
		return ErrorInvalidHash
	}
	return nil
}

package needle

import (
	"bytes"
	"crypto/sha256"
	"encoding/hex"
	"testing"
)

func TestNeedle(t *testing.T) {
	t.Parallel()
	t.Run("Bytes", func(t *testing.T) {
		t.Parallel()
		p, _ := hex.DecodeString("")
		n, _ := New(p)
		b := n.Bytes()
		b[0], b[1], b[2], b[3] = 0, 0, 0, 0
		if bytes.Equal(n.Bytes(), b) {
			t.Error("mutating Bytes() changed needle bytes")
		}
	})
	t.Run("Payload", func(t *testing.T) {
		t.Parallel()
		p, _ := hex.DecodeString("")
		n, _ := New(p)
		payload := n.Payload()
		if !bytes.Equal(p, payload) {
			t.Error("payload imported by New does not match needle.Payload()")
		}
		payload[0] = 0
		pl := n.Payload()
		if bytes.Equal(pl, payload) {
			t.Error("mutating Payload() changed needle payload")
		}
	})
	t.Run("Hash", func(t *testing.T) {
		t.Parallel()
		p, _ := hex.DecodeString("")
		n, _ := New(p)
		hash := n.Hash()
		h := sha256.Sum256(p)
		if !bytes.Equal(h[:], hash) {
			t.Error("exported hash is invalid")
		}
		hash[0] = 0
		h2 := n.Hash()
		if bytes.Equal(h2, hash) {
			t.Error("mutating Hash() changed needle hash")
		}
	})
}

