// Package mgroup is a simple managed group managing ownership and membership
// for authorization in gno realms. The ManagedGroup struct is used to manage
// the owner, backup owners, and members of a group. The owner is the primary
// owner of the group and can add and remove backup owners and members. Backup
// owners can claim ownership of the group. This is meant to provide backup
// accounts for the owner in case the owner account is lost or compromised.
// Members are used to authorize actions across realms.
package mgroup

import (
	"errors"
	"std"

	"gno.land/p/nt/avl"
	"gno.land/p/nt/ownable"
)

var (
	ErrCannotRemoveOwner = errors.New("mgroup: cannot remove owner")
	ErrNotBackupOwner    = errors.New("mgroup: not a backup owner")
	ErrNotMember         = errors.New("mgroup: not a member")
	ErrInvalidAddress    = errors.New("mgroup: address is invalid")
)

type ManagedGroup struct {
	owner        *ownable.Ownable
	backupOwners *avl.Tree
	members      *avl.Tree
}

// New creates a new ManagedGroup with the owner set to the provided address.
// The owner is automatically added as a backup owner and member of the group.
func New(ownerAddress std.Address) *ManagedGroup {
	g := &ManagedGroup{
		owner:        ownable.NewWithAddress(ownerAddress),
		backupOwners: avl.NewTree(),
		members:      avl.NewTree(),
	}
	err := g.addBackupOwner(ownerAddress)
	if err != nil {
		panic(err)
	}
	err = g.addMember(ownerAddress)
	if err != nil {
		panic(err)
	}
	return g
}

// AddBackupOwner adds a backup owner to the group by std.Address.
// If the caller is not the owner, an error is returned.
func (g *ManagedGroup) AddBackupOwner(addr std.Address) error {
	if !g.owner.OwnedByPrevious() {
		return ownable.ErrUnauthorized
	}
	return g.addBackupOwner(addr)
}

func (g *ManagedGroup) addBackupOwner(addr std.Address) error {
	if !addr.IsValid() {
		return ErrInvalidAddress
	}
	g.backupOwners.Set(addr.String(), struct{}{})
	return nil
}

// RemoveBackupOwner removes a backup owner from the group by std.Address.
// The owner cannot be removed. If the caller is not the owner, an error is returned.
func (g *ManagedGroup) RemoveBackupOwner(addr std.Address) error {
	if !g.owner.OwnedByPrevious() {
		return ownable.ErrUnauthorized
	}
	if !addr.IsValid() {
		return ErrInvalidAddress
	}
	if addr == g.Owner() {
		return ErrCannotRemoveOwner
	}
	g.backupOwners.Remove(addr.String())
	return nil
}

// ClaimOwnership allows a backup owner to claim ownership of the group.
// If the caller is not a backup owner, an error is returned.
// The caller is automatically added as a member of the group.
func (g *ManagedGroup) ClaimOwnership() error {
	caller := std.PreviousRealm().Address()
	// already owner, skip
	if caller == g.Owner() {
		return nil
	}
	if !g.IsBackupOwner(caller) {
		return ErrNotMember
	}
	g.owner = ownable.NewWithAddress(caller)
	err := g.addMember(caller)
	return err
}

// AddMember adds a member to the group by std.Address.
// If the caller is not the owner, an error is returned.
func (g *ManagedGroup) AddMember(addr std.Address) error {
	if !g.owner.OwnedByPrevious() {
		return ownable.ErrUnauthorized
	}
	return g.addMember(addr)
}

func (g *ManagedGroup) addMember(addr std.Address) error {
	if !addr.IsValid() {
		return ErrInvalidAddress
	}
	g.members.Set(addr.String(), struct{}{})
	return nil
}

// RemoveMember removes a member from the group by std.Address.
// The owner cannot be removed. If the caller is not the owner,
// an error is returned.
func (g *ManagedGroup) RemoveMember(addr std.Address) error {
	if !g.owner.OwnedByPrevious() {
		return ownable.ErrUnauthorized
	}
	if !addr.IsValid() {
		return ErrInvalidAddress
	}
	if addr == g.Owner() {
		return ErrCannotRemoveOwner
	}
	g.members.Remove(addr.String())
	return nil
}

// MemberCount returns the number of members in the group.
func (g *ManagedGroup) MemberCount() int {
	return g.members.Size()
}

// BackupOwnerCount returns the number of backup owners in the group.
func (g *ManagedGroup) BackupOwnerCount() int {
	return g.backupOwners.Size()
}

// IsMember checks if an address is a member of the group.
func (g *ManagedGroup) IsMember(addr std.Address) bool {
	return g.members.Has(addr.String())
}

// IsBackupOwner checks if an address is a backup owner in the group.
func (g *ManagedGroup) IsBackupOwner(addr std.Address) bool {
	return g.backupOwners.Has(addr.String())
}

// Owner returns the owner of the group.
func (g *ManagedGroup) Owner() std.Address {
	return g.owner.Owner()
}

// BackupOwners returns a slice of all backup owners in the group, using the underlying
// avl.Tree to iterate over the backup owners. If you have a large group, you may
// want to use BackupOwnersWithOffset to iterate over backup owners in chunks.
func (g *ManagedGroup) BackupOwners() []string {
	return g.BackupOwnersWithOffset(0, g.BackupOwnerCount())
}

// Members returns a slice of all members in the group, using the underlying
// avl.Tree to iterate over the members. If you have a large group, you may
// want to use MembersWithOffset to iterate over members in chunks.
func (g *ManagedGroup) Members() []string {
	return g.MembersWithOffset(0, g.MemberCount())
}

// BackupOwnersWithOffset returns a slice of backup owners in the group, using the underlying
// avl.Tree to iterate over the backup owners. The offset and count parameters allow you
// to iterate over backup owners in chunks to support patterns such as pagination.
func (g *ManagedGroup) BackupOwnersWithOffset(offset, count int) []string {
	return sliceWithOffset(g.backupOwners, offset, count)
}

// MembersWithOffset returns a slice of members in the group, using the underlying
// avl.Tree to iterate over the members. The offset and count parameters allow you
// to iterate over members in chunks to support patterns such as pagination.
func (g *ManagedGroup) MembersWithOffset(offset, count int) []string {
	return sliceWithOffset(g.members, offset, count)
}

// sliceWithOffset is a helper function to iterate over an avl.Tree with an offset and count.
func sliceWithOffset(t *avl.Tree, offset, count int) []string {
	var result []string
	t.IterateByOffset(offset, count, func(k string, _ any) bool {
		if k == "" {
			return true
		}
		result = append(result, k)
		return false
	})
	return result
}

package mgroup

import (
	"std"
	"testing"

	"gno.land/p/nt/avl"
	"gno.land/p/nt/ownable"
	"gno.land/p/nt/uassert"
)

func TestManagedGroup(t *testing.T) {
	u1 := std.NewCodeRealm("gno.land/r/test/test/u1")
	u2 := std.NewCodeRealm("gno.land/r/test/test/u2")
	u3 := std.NewCodeRealm("gno.land/r/test/test/u3")
	u4 := std.NewCodeRealm("gno.land/r/test/test/u4")

	// testUsingPreviousRealm sets the realm to "gno.land/r/test/test"
	// and uses the SetRealm in the test as the previous caller
	testUsingPreviousRealm := func(fn func()) {
		testing.SetRealm(std.NewCodeRealm("gno.land/r/test/test"))
		fn()
	}

	t.Run("AddBackupOwner", func(t *testing.T) {
		g := New(u1.Address())

		testing.SetRealm(u1)
		testUsingPreviousRealm(func() {
			if err := g.AddBackupOwner(u2.Address()); err != nil {
				t.Errorf("expected nil, got %v", err.Error())
			}
			// ensure invalid address is caught
			var badAddr std.Address
			if err := g.AddBackupOwner(badAddr); err != ErrInvalidAddress {
				t.Errorf("expected %v, got %v", ErrInvalidAddress.Error(), err.Error())
			}
		})

		testing.SetRealm(u3)
		testUsingPreviousRealm(func() {
			// an address that is not the owner should not be allowed to add a backup owner
			if err := g.AddBackupOwner(u4.Address()); err != ownable.ErrUnauthorized {
				t.Errorf("expected %v, got %v", ownable.ErrUnauthorized, err.Error())
			}
		})

	})
	t.Run("RemoveBackupOwner", func(t *testing.T) {
		g := New(u1.Address())

		testing.SetRealm(u1)
		testUsingPreviousRealm(func() {
			g.AddBackupOwner(u2.Address())
			if err := g.RemoveBackupOwner(u2.Address()); err != nil {
				t.Errorf("expected nil, got %v", err.Error())
			}
			// running this twice should not error.
			if err := g.RemoveBackupOwner(u2.Address()); err != nil {
				t.Errorf("expected nil, got %v", err.Error())
			}
			var badAddr std.Address
			if err := g.RemoveBackupOwner(badAddr); err != ErrInvalidAddress {
				t.Errorf("expected %v, got %v", ErrInvalidAddress.Error(), err.Error())
			}
			if err := g.RemoveBackupOwner(u1.Address()); err != ErrCannotRemoveOwner {
				t.Errorf("expected %v, got %v", ErrCannotRemoveOwner.Error(), err.Error())
			}
		})

		testing.SetRealm(u3)
		testUsingPreviousRealm(func() {
			g.AddBackupOwner(u2.Address())
			if err := g.RemoveBackupOwner(u2.Address()); err != ownable.ErrUnauthorized {
				t.Errorf("expected %v, got %v", ownable.ErrUnauthorized, err.Error())
			}
		})
	})

	t.Run("ClaimOwnership", func(t *testing.T) {
		g := New(u1.Address())

		testing.SetRealm(u1)
		testUsingPreviousRealm(func() {
			g.AddBackupOwner(u2.Address())
		})
		testing.SetRealm(u2)
		testUsingPreviousRealm(func() {
			if err := g.ClaimOwnership(); err != nil {
				t.Errorf("expected nil, got %v", err.Error())
			}
			if g.Owner() != u2.Address() {
				t.Errorf("expected %v, got %v", u2, g.Owner())
			}
			if !g.IsMember(u2.Address()) {
				t.Errorf("expected %v to be a member", u2)
			}
			if err := g.ClaimOwnership(); err != nil {
				t.Errorf("expected nil, got %v", err.Error())
			}
		})
		testing.SetRealm(u3)
		testUsingPreviousRealm(func() {
			if err := g.ClaimOwnership(); err != ErrNotMember {
				t.Errorf("expected %v, got %v", ErrNotMember.Error(), err.Error())
			}
		})
	})

	t.Run("AddMember", func(t *testing.T) {
		g := New(u1.Address())
		testing.SetRealm(u1)
		testUsingPreviousRealm(func() {
			if err := g.AddMember(u2.Address()); err != nil {
				t.Errorf("expected nil, got %v", err.Error())
			}
			if !g.IsMember(u2.Address()) {
				t.Errorf("expected %v to be a member", u2)
			}
			var badAddr std.Address
			if err := g.AddMember(badAddr); err != ErrInvalidAddress {
				t.Errorf("expected %v, got %v", ErrInvalidAddress, err.Error())
			}
		})
		testing.SetRealm(u3)
		testUsingPreviousRealm(func() {
			if err := g.AddMember(u4.Address()); err != ownable.ErrUnauthorized {
				t.Errorf("expected %v, got %v", ownable.ErrUnauthorized, err.Error())
			}
		})
	})

	t.Run("RemoveMember", func(t *testing.T) {
		g := New(u1.Address())

		testing.SetRealm(u1)
		testUsingPreviousRealm(func() {
			g.AddMember(u2.Address())
			if err := g.RemoveMember(u2.Address()); err != nil {
				t.Errorf("expected nil, got %v", err.Error())
			}
			if g.IsMember(u2.Address()) {
				t.Errorf("expected %v to not be a member", u2)
			}
			// running this twice should not error
			if err := g.RemoveMember(u2.Address()); err != nil {
				t.Errorf("expected nil, got %v", err.Error())
			}
			var badAddr std.Address
			if err := g.RemoveMember(badAddr); err != ErrInvalidAddress {
				t.Errorf("expected %v, got %v", ErrInvalidAddress.Error(), err.Error())
			}
			if err := g.RemoveMember(u1.Address()); err != ErrCannotRemoveOwner {
				t.Errorf("expected %v, got %v", ErrCannotRemoveOwner.Error(), err.Error())
			}
		})

		testing.SetRealm(u2)
		testUsingPreviousRealm(func() {
			if err := g.RemoveMember(u3.Address()); err != ownable.ErrUnauthorized {
				t.Errorf("expected %v, got %v", ownable.ErrUnauthorized.Error(), err.Error())
			}
		})
	})

	t.Run("MemberCount", func(t *testing.T) {
		g := New(u1.Address())
		testing.SetRealm(u1)
		testUsingPreviousRealm(func() {
			uassert.Equal(t, 1, g.MemberCount())
			g.AddMember(u2.Address())
			uassert.Equal(t, 2, g.MemberCount())
			g.AddMember(u3.Address())
			uassert.Equal(t, 3, g.MemberCount())
			g.RemoveMember(u2.Address())
			uassert.Equal(t, 2, g.MemberCount())
		})
	})

	t.Run("BackupOwnerCount", func(t *testing.T) {
		g := New(u1.Address())
		testing.SetRealm(u1)
		testUsingPreviousRealm(func() {
			uassert.Equal(t, 1, g.BackupOwnerCount())
			g.AddBackupOwner(u2.Address())
			uassert.Equal(t, 2, g.BackupOwnerCount())
			g.AddBackupOwner(u3.Address())
			uassert.Equal(t, 3, g.BackupOwnerCount())
			g.RemoveBackupOwner(u2.Address())
			uassert.Equal(t, 2, g.BackupOwnerCount())
		})
	})

	t.Run("IsMember", func(t *testing.T) {
		g := New(u1.Address())
		testing.SetRealm(u1)
		testUsingPreviousRealm(func() {
			if !g.IsMember(u1.Address()) {
				t.Errorf("expected %v to be a member", u1)
			}
			if g.IsMember(u2.Address()) {
				t.Errorf("expected %v to not be a member", u2)
			}
			g.AddMember(u2.Address())
			if !g.IsMember(u2.Address()) {
				t.Errorf("expected %v to be a member", u2)
			}
		})
	})
	t.Run("IsBackupOwner", func(t *testing.T) {
		g := New(u1.Address())
		testing.SetRealm(u1)
		testUsingPreviousRealm(func() {
			if !g.IsBackupOwner(u1.Address()) {
				t.Errorf("expected %v to be a backup owner", u1)
			}
			if g.IsBackupOwner(u2.Address()) {
				t.Errorf("expected %v to not be a backup owner", u2)
			}
			g.AddBackupOwner(u2.Address())
			if !g.IsBackupOwner(u2.Address()) {
				t.Errorf("expected %v to be a backup owner", u2)
			}
		})
	})

	t.Run("Owner", func(t *testing.T) {
		g := New(u1.Address())
		testing.SetRealm(u1)
		testUsingPreviousRealm(func() {
			if g.Owner() != u1.Address() {
				t.Errorf("expected %v, got %v", u1, g.Owner())
			}
			g.AddBackupOwner(u2.Address())
			if g.Owner() != u1.Address() {
				t.Errorf("expected %v, got %v", u1, g.Owner())
			}
		})
		testing.SetRealm(u2)
		testUsingPreviousRealm(func() {
			g.ClaimOwnership()
			if g.Owner() != u2.Address() {
				t.Errorf("expected %v, got %v", u2, g.Owner())
			}
		})
	})
	t.Run("BackupOwners", func(t *testing.T) {
		g := New(u1.Address())
		testing.SetRealm(u1)
		testUsingPreviousRealm(func() {
			g.AddBackupOwner(u2.Address())
			g.AddBackupOwner(u3.Address())
			owners := g.BackupOwners()
			if len(owners) != 3 {
				t.Errorf("expected 3, got %v", len(owners))
			}
			if owners[0] != u1.Address().String() {
				t.Errorf("expected %v, got %v", u1, owners[0])
			}
			if owners[1] != u2.Address().String() {
				t.Errorf("expected %v, got %v", u2, owners[1])
			}
			if owners[2] != u3.Address().String() {
				t.Errorf("expected %v, got %v", u3, owners[1])
			}
		})
	})
	t.Run("Members", func(t *testing.T) {
		g := New(u1.Address())
		testing.SetRealm(u1)
		testUsingPreviousRealm(func() {
			g.AddMember(u2.Address())
			g.AddMember(u3.Address())
			members := g.Members()
			if len(members) != 3 {
				t.Errorf("expected 3, got %v", len(members))
			}
			if members[0] != u1.Address().String() {
				t.Errorf("expected %v, got %v", u1, members[0])
			}
			if members[1] != u2.Address().String() {
				t.Errorf("expected %v, got %v", u2, members[1])
			}
			if members[2] != u3.Address().String() {
				t.Errorf("expected %v, got %v", u3, members[1])
			}
		})
	})
}

func TestSliceWithOffset(t *testing.T) {
	testTable := []struct {
		name          string
		slice         []string
		offset        int
		count         int
		expected      []string
		expectedCount int
	}{
		{
			name:          "empty",
			slice:         []string{},
			offset:        0,
			count:         0,
			expected:      []string{},
			expectedCount: 0,
		},
		{
			name:          "single",
			slice:         []string{"a"},
			offset:        0,
			count:         1,
			expected:      []string{"a"},
			expectedCount: 1,
		},
		{
			name:          "single offset",
			slice:         []string{"a"},
			offset:        1,
			count:         1,
			expected:      []string{},
			expectedCount: 0,
		},
		{
			name:          "multiple",
			slice:         []string{"a", "b", "c", "d", "e", "f", "g", "h", "i", "j"},
			offset:        0,
			count:         10,
			expected:      []string{"a", "b", "c", "d", "e", "f", "g", "h", "i", "j"},
			expectedCount: 10,
		},
		{
			name:          "multiple offset",
			slice:         []string{"a", "b", "c", "d", "e", "f", "g", "h", "i", "j"},
			offset:        5,
			count:         5,
			expected:      []string{"f", "g", "h", "i", "j"},
			expectedCount: 5,
		},
		{
			name:          "multiple offset end",
			slice:         []string{"a", "b", "c", "d", "e", "f", "g", "h", "i", "j"},
			offset:        10,
			count:         5,
			expected:      []string{},
			expectedCount: 0,
		},
		{
			name:          "multiple offset past end",
			slice:         []string{"a", "b", "c", "d", "e", "f", "g", "h", "i", "j"},
			offset:        11,
			count:         5,
			expected:      []string{},
			expectedCount: 0,
		},
		{
			name:          "multiple offset count past end",
			slice:         []string{"a", "b", "c", "d", "e", "f", "g", "h", "i", "j"},
			offset:        5,
			count:         20,
			expected:      []string{"f", "g", "h", "i", "j"},
			expectedCount: 5,
		},
	}
	for _, test := range testTable {
		t.Run(test.name, func(t *testing.T) {
			tree := avl.NewTree()
			for _, s := range test.slice {
				tree.Set(s, struct{}{})
			}
			slice := sliceWithOffset(tree, test.offset, test.count)
			if len(slice) != test.expectedCount {
				t.Errorf("expected %v, got %v", test.expectedCount, len(slice))
			}
		})
	}
}

// Package rolist provides a read-only wrapper for list.List with safe value transformation.
//
// It is useful when you want to expose a read-only view of a list while ensuring that
// the sensitive data cannot be modified.
//
// Example:
//
//	// Define a user structure with sensitive data
//	type User struct {
//	    Name     string
//	    Balance  int
//	    Internal string // sensitive field
//	}
//
//	// Create and populate the original list
//	var privateList list.List
//	privateList.Append(&User{
//	    Name:     "Alice",
//	    Balance:  100,
//	    Internal: "sensitive",
//	})
//
//	// Create a safe transformation function that copies the struct
//	// while excluding sensitive data
//	makeEntrySafeFn := func(v any) any {
//	    u := v.(*User)
//	    return &User{
//	        Name:     u.Name,
//	        Balance:  u.Balance,
//	        Internal: "", // omit sensitive data
//	    }
//	}
//
//	// Create a read-only view of the list
//	publicList := rolist.Wrap(privateList, makeEntrySafeFn)
//
//	// Safely access the data
//	value := publicList.Get(0)
//	user := value.(*User)
//	// user.Name == "Alice"
//	// user.Balance == 100
//	// user.Internal == "" (sensitive data is filtered)
package rolist

import (
	"gno.land/p/nt/avl/list"
	"gno.land/p/nt/avl/rotree"
)

// IReadOnlyList defines the read-only operations available on a list.
type IReadOnlyList interface {
	Len() int
	Get(index int) any
	Slice(startIndex, endIndex int) []any
	ForEach(fn func(index int, value any) bool)
}

// ReadOnlyList wraps a list.List and provides read-only access.
type ReadOnlyList struct {
	list            *list.List
	makeEntrySafeFn func(any) any
}

// Verify interface implementations
var (
	_ IReadOnlyList = (*ReadOnlyList)(nil)
	_ IReadOnlyList = (interface{ list.IList })(nil) // is subset of list.IList
)

// Wrap creates a new ReadOnlyList from an existing list.List and a safety transformation function.
// If makeEntrySafeFn is nil, values will be returned as-is without transformation.
func Wrap(list *list.List, makeEntrySafeFn func(any) any) *ReadOnlyList {
	return &ReadOnlyList{
		list:            list,
		makeEntrySafeFn: makeEntrySafeFn,
	}
}

// getSafeValue applies the makeEntrySafeFn if it exists, otherwise returns the original value
func (rol *ReadOnlyList) getSafeValue(value any) any {
	if rol.makeEntrySafeFn == nil {
		return value
	}
	return rol.makeEntrySafeFn(value)
}

// Len returns the number of elements in the list.
func (rol *ReadOnlyList) Len() int {
	return rol.list.Len()
}

// Get returns the value at the specified index, converted to a safe format.
// Returns nil if index is out of bounds.
func (rol *ReadOnlyList) Get(index int) any {
	value := rol.list.Get(index)
	if value == nil {
		return nil
	}
	return rol.getSafeValue(value)
}

// Slice returns a slice of values from startIndex (inclusive) to endIndex (exclusive),
// with all values converted to a safe format.
func (rol *ReadOnlyList) Slice(startIndex, endIndex int) []any {
	values := rol.list.Slice(startIndex, endIndex)
	if values == nil {
		return nil
	}

	result := make([]any, len(values))
	for i, v := range values {
		result[i] = rol.getSafeValue(v)
	}
	return result
}

// ForEach iterates through all elements in the list, providing safe versions of the values.
func (rol *ReadOnlyList) ForEach(fn func(index int, value any) bool) {
	rol.list.ForEach(func(index int, value any) bool {
		return fn(index, rol.getSafeValue(value))
	})
}

// Tree returns a read-only pointer to the underlying AVL tree.
func (rol *ReadOnlyList) Tree() *rotree.ReadOnlyTree {
	return rol.list.Tree()
}

package rolist

import (
	"testing"

	"gno.land/p/nt/avl/list"
)

func TestExample(t *testing.T) {
	// User represents our internal data structure
	type User struct {
		ID       string
		Name     string
		Balance  int
		Internal string // sensitive internal data
	}

	// Create and populate the original list
	l := &list.List{}
	l.Append(
		&User{
			ID:       "1",
			Name:     "Alice",
			Balance:  100,
			Internal: "sensitive_data_1",
		},
		&User{
			ID:       "2",
			Name:     "Bob",
			Balance:  200,
			Internal: "sensitive_data_2",
		},
	)

	// Define a makeEntrySafeFn that:
	// 1. Creates a defensive copy of the User struct
	// 2. Omits sensitive internal data
	makeEntrySafeFn := func(v any) any {
		originalUser := v.(*User)
		return &User{
			ID:       originalUser.ID,
			Name:     originalUser.Name,
			Balance:  originalUser.Balance,
			Internal: "", // Omit sensitive data
		}
	}

	// Create a read-only view of the list
	roList := Wrap(l, makeEntrySafeFn)

	// Test retrieving and verifying a user
	t.Run("Get User", func(t *testing.T) {
		// Get user from read-only list
		value := roList.Get(0)
		if value == nil {
			t.Fatal("User at index 0 not found")
		}

		user := value.(*User)

		// Verify user data is correct
		if user.Name != "Alice" || user.Balance != 100 {
			t.Errorf("Unexpected user data: got name=%s balance=%d", user.Name, user.Balance)
		}

		// Verify sensitive data is not exposed
		if user.Internal != "" {
			t.Error("Sensitive data should not be exposed")
		}

		// Verify it's a different instance than the original
		originalUser := l.Get(0).(*User)
		if user == originalUser {
			t.Error("Read-only list should return a copy, not the original pointer")
		}
	})

	// Test slice functionality
	t.Run("Slice Users", func(t *testing.T) {
		users := roList.Slice(0, 2)
		if len(users) != 2 {
			t.Fatalf("Expected 2 users, got %d", len(users))
		}

		for _, v := range users {
			user := v.(*User)
			if user.Internal != "" {
				t.Error("Sensitive data exposed in slice")
			}
		}
	})

	// Test ForEach functionality
	t.Run("ForEach Users", func(t *testing.T) {
		count := 0
		roList.ForEach(func(index int, value any) bool {
			user := value.(*User)
			if user.Internal != "" {
				t.Error("Sensitive data exposed during iteration")
			}
			count++
			return false
		})

		if count != 2 {
			t.Errorf("Expected 2 users, got %d", count)
		}
	})
}

func TestNilMakeEntrySafeFn(t *testing.T) {
	// Create a list with some test data
	l := &list.List{}
	originalValue := []int{1, 2, 3}
	l.Append(originalValue)

	// Create a ReadOnlyList with nil makeEntrySafeFn
	roList := Wrap(l, nil)

	// Test that we get back the original value
	value := roList.Get(0)
	if value == nil {
		t.Fatal("Value not found")
	}

	// Verify it's the exact same slice (not a copy)
	retrievedSlice := value.([]int)
	if &retrievedSlice[0] != &originalValue[0] {
		t.Error("Expected to get back the original slice reference")
	}
}

func TestReadOnlyList(t *testing.T) {
	// Example of a makeEntrySafeFn that appends "_readonly" to demonstrate transformation
	makeEntrySafeFn := func(value any) any {
		return value.(string) + "_readonly"
	}

	l := &list.List{}
	l.Append("value1", "value2", "value3")

	roList := Wrap(l, makeEntrySafeFn)

	tests := []struct {
		name     string
		index    int
		expected any
	}{
		{"ExistingIndex0", 0, "value1_readonly"},
		{"ExistingIndex1", 1, "value2_readonly"},
		{"NonExistingIndex", 3, nil},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			value := roList.Get(tt.index)
			if value != tt.expected {
				t.Errorf("For index %d, expected %v, got %v", tt.index, tt.expected, value)
			}
		})
	}
}

package combinederr

import "strings"

// CombinedError is a combined execution error
type CombinedError struct {
	errors []error
}

// Error returns the combined execution error
func (e *CombinedError) Error() string {
	if len(e.errors) == 0 {
		return ""
	}

	var sb strings.Builder

	for _, err := range e.errors {
		sb.WriteString(err.Error() + "; ")
	}

	// Remove the last semicolon and space
	result := sb.String()

	return result[:len(result)-2]
}

// Add adds a new error to the execution error
func (e *CombinedError) Add(err error) {
	if err == nil {
		return
	}

	e.errors = append(e.errors, err)
}

// Size returns a
func (e *CombinedError) Size() int {
	return len(e.errors)
}

package commondao

import (
	"errors"
	"std"

	"gno.land/p/nt/avl/list"
	"gno.land/p/nt/seqid"
)

// PathSeparator is the separator character used in DAO paths.
const PathSeparator = "/"

var (
	ErrInvalidVoteChoice    = errors.New("invalid vote choice")
	ErrNotMember            = errors.New("account is not a member of the DAO")
	ErrOverflow             = errors.New("next ID overflows uint64")
	ErrProposalFailed       = errors.New("proposal failed to pass")
	ErrProposalNotFound     = errors.New("proposal not found")
	ErrVotingDeadlineNotMet = errors.New("voting deadline not met")
	ErrVotingDeadlinePassed = errors.New("voting deadline has passed")
)

// CommonDAO defines a DAO.
type CommonDAO struct {
	id                         uint64
	slug                       string
	name                       string
	description                string
	parent                     *CommonDAO
	children                   list.IList
	members                    MemberStorage
	genID                      seqid.ID
	activeProposals            ProposalStorage
	finishedProposals          ProposalStorage
	deleted                    bool // Soft delete
	disableVotingDeadlineCheck bool
}

// New creates a new common DAO.
func New(options ...Option) *CommonDAO {
	dao := &CommonDAO{
		children:          &list.List{},
		members:           NewMemberStorage(),
		activeProposals:   NewProposalStorage(),
		finishedProposals: NewProposalStorage(),
	}
	for _, apply := range options {
		apply(dao)
	}
	return dao
}

// ID returns DAO's unique identifier.
func (dao CommonDAO) ID() uint64 {
	return dao.id
}

// Slug returns DAO's URL slug.
func (dao CommonDAO) Slug() string {
	return dao.slug
}

// Name returns DAO's name.
func (dao CommonDAO) Name() string {
	return dao.name
}

// Description returns DAO's description.
func (dao CommonDAO) Description() string {
	return dao.description
}

// Path returns the full path to the DAO.
// Paths are normally used when working with hierarchical
// DAOs and is created by concatenating DAO slugs.
func (dao CommonDAO) Path() string {
	// NOTE: Path could be a value but there might be use cases where dynamic path is useful (?)
	parent := dao.Parent()
	if parent != nil {
		prefix := parent.Path()
		if prefix != "" {
			return prefix + PathSeparator + dao.slug
		}
	}
	return dao.slug
}

// Parent returns the parent DAO.
// Null can be returned when DAO has no parent assigned.
func (dao CommonDAO) Parent() *CommonDAO {
	return dao.parent
}

// Children returns a list with the direct DAO children.
// Each item in the list is a reference to a CommonDAO instance.
func (dao CommonDAO) Children() list.IList {
	return dao.children
}

// TopParent returns the topmost parent DAO.
// The top parent is the root of the DAO tree.
func (dao *CommonDAO) TopParent() *CommonDAO {
	parent := dao.Parent()
	if parent != nil {
		return parent.TopParent()
	}
	return dao
}

// Members returns the list of DAO members.
func (dao CommonDAO) Members() MemberStorage {
	return dao.members
}

// ActiveProposals returns active DAO proposals.
func (dao CommonDAO) ActiveProposals() ProposalStorage {
	return dao.activeProposals
}

// FinishedProposalsi returns finished DAO proposals.
func (dao CommonDAO) FinishedProposals() ProposalStorage {
	return dao.finishedProposals
}

// IsDeleted returns true when DAO has been soft deleted.
func (dao CommonDAO) IsDeleted() bool {
	return dao.deleted
}

// SetDeleted changes DAO's soft delete flag.
func (dao *CommonDAO) SetDeleted(deleted bool) {
	dao.deleted = deleted
}

// Propose creates a new DAO proposal.
func (dao *CommonDAO) Propose(creator std.Address, d ProposalDefinition) (*Proposal, error) {
	id, ok := dao.genID.TryNext()
	if !ok {
		return nil, ErrOverflow
	}

	p, err := NewProposal(uint64(id), creator, d)
	if err != nil {
		return nil, err
	}

	dao.activeProposals.Add(p)
	return p, nil
}

// MustPropose creates a new DAO proposal or panics on error.
func (dao *CommonDAO) MustPropose(creator std.Address, d ProposalDefinition) *Proposal {
	p, err := dao.Propose(creator, d)
	if err != nil {
		panic(err)
	}
	return p
}

// GetProposal returns a proposal or nil when proposal is not found.
func (dao CommonDAO) GetProposal(proposalID uint64) *Proposal {
	p := dao.activeProposals.Get(proposalID)
	if p != nil {
		return p
	}
	return dao.finishedProposals.Get(proposalID)
}

// Vote submits a new vote for a proposal.
//
// By default votes are only allowed to members of the DAO when the proposal is active,
// and within the voting period. No votes are allowed once the voting deadline passes.
// DAO deadline checks can optionally be disabled using the `DisableVotingDeadlineCheck` option.
func (dao *CommonDAO) Vote(member std.Address, proposalID uint64, c VoteChoice, reason string) error {
	if !dao.Members().Has(member) {
		return ErrNotMember
	}

	p := dao.activeProposals.Get(proposalID)
	if p == nil {
		return ErrProposalNotFound
	}

	if !dao.disableVotingDeadlineCheck && p.HasVotingDeadlinePassed() {
		return ErrVotingDeadlinePassed
	}

	if !p.IsVoteChoiceValid(c) {
		return ErrInvalidVoteChoice
	}

	p.record.AddVote(Vote{
		Address: member,
		Choice:  c,
		Reason:  reason,
	})
	return nil
}

// Tally counts votes and validates if a proposal passes.
func (dao *CommonDAO) Tally(proposalID uint64) (passes bool, _ error) {
	p := dao.activeProposals.Get(proposalID)
	if p == nil {
		return false, ErrProposalNotFound
	}

	if p.Status() != StatusActive {
		return false, ErrStatusIsNotActive
	}

	if err := dao.checkProposalPasses(p); err != nil {
		// Don't return an error if proposal failed to pass when tallying
		if err == ErrProposalFailed {
			return false, nil
		}
		return false, err
	}
	return true, nil
}

// Execute executes a proposal.
//
// By default active proposals can only be executed after their voting deadline passes.
// DAO deadline checks can optionally be disabled using the `DisableVotingDeadlineCheck` option.
func (dao *CommonDAO) Execute(proposalID uint64) error {
	p := dao.activeProposals.Get(proposalID)
	if p == nil {
		return ErrProposalNotFound
	}

	if p.Status() != StatusActive {
		return ErrStatusIsNotActive
	}

	if !dao.disableVotingDeadlineCheck && !p.HasVotingDeadlinePassed() {
		return ErrVotingDeadlineNotMet
	}

	// From this point any error results in a proposal failure and successful execution
	err := p.Validate()

	if err == nil {
		err = dao.checkProposalPasses(p)
	}

	if err == nil {
		// Execute proposal only if it's executable
		if e, ok := p.Definition().(Executable); ok {
			err = e.Execute(cross)
		}
	}

	// Proposal fails if there is any error during validation and execution process
	if err != nil {
		p.status = StatusFailed
		p.statusReason = err.Error()
	} else {
		p.status = StatusPassed
	}

	// Whichever the outcome of the validation, tallying
	// and execution consider the proposal finished.
	dao.activeProposals.Remove(p.id)
	dao.finishedProposals.Add(p)
	return nil
}

func (dao *CommonDAO) checkProposalPasses(p *Proposal) error {
	record := p.VotingRecord().Readonly()
	members := NewMemberSet(dao.Members())
	passes, err := p.Definition().Tally(record, members)
	if err != nil {
		return err
	}

	if !passes {
		return ErrProposalFailed
	}
	return nil
}

package commondao

import "std"

// Option configures the CommonDAO.
type Option func(*CommonDAO)

// WithID assigns a unique identifier to the DAO.
func WithID(id uint64) Option {
	return func(dao *CommonDAO) {
		dao.id = id
	}
}

// WithName assigns a name to the DAO.
func WithName(name string) Option {
	return func(dao *CommonDAO) {
		dao.name = name
	}
}

// WithDescription assigns a description to the DAO.
func WithDescription(description string) Option {
	return func(dao *CommonDAO) {
		dao.description = description
	}
}

// WithSlug assigns a URL slug to the DAO.
func WithSlug(slug string) Option {
	return func(dao *CommonDAO) {
		dao.slug = slug
	}
}

// WithParent assigns a parent DAO.
func WithParent(p *CommonDAO) Option {
	return func(dao *CommonDAO) {
		dao.parent = p
	}
}

// WithChildren assigns one or more direct child SubDAOs to the DAO.
func WithChildren(children ...*CommonDAO) Option {
	return func(dao *CommonDAO) {
		for _, subDAO := range children {
			dao.children.Append(subDAO)
		}
	}
}

// WithMember assigns a member to the DAO.
func WithMember(addr std.Address) Option {
	return func(dao *CommonDAO) {
		dao.members.Add(addr)
	}
}

// WithMemberStorage assigns a custom member storage to the DAO.
// An empty member storage is used by default if the specified storage is nil.
func WithMemberStorage(s MemberStorage) Option {
	return func(dao *CommonDAO) {
		if s == nil {
			s = NewMemberStorage()
		}
		dao.members = s
	}
}

// WithActiveProposalStorage assigns a custom storage for active proposals.
// A default empty proposal storage is used when the custopm storage is nil.
// Custom storage implementations can be used to store proposals in a different location.
func WithActiveProposalStorage(s ProposalStorage) Option {
	return func(dao *CommonDAO) {
		if s == nil {
			s = NewProposalStorage()
		}
		dao.activeProposals = s
	}
}

// WithFinishedProposalStorage assigns a custom storage for finished proposals.
// A default empty proposal storage is used when the custopm storage is nil.
// Custom storage implementations can be used to store proposals in a different location.
func WithFinishedProposalStorage(s ProposalStorage) Option {
	return func(dao *CommonDAO) {
		if s == nil {
			s = NewProposalStorage()
		}
		dao.finishedProposals = s
	}
}

// DisableVotingDeadlineCheck disables voting deadline check when voting or executing proposals.
// By default CommonDAO checks that the proposal voting deadline has not been met when a new vote
// is submitted, before registering the vote, and on proposal execution it also checks that voting
// deadline has been met before executing a proposal.
//
// Disabling these checks can be useful in different use cases, moving the responsibility to check
// the deadline to the commondao package caller. One example where this could be useful would be
// in case where 100% or a required number of members of a DAO vote on a proposal and reach consensus
// before the deadline is met, otherwise proposal would have to wait until deadline to be executed.
func DisableVotingDeadlineCheck() Option {
	return func(dao *CommonDAO) {
		dao.disableVotingDeadlineCheck = true
	}
}

