func TestCommonDAOPropose(t *testing.T) {
	cases := []struct {
		name    string
		setup   func() *CommonDAO
		creator std.Address
		def     ProposalDefinition
		err     error
	}{
		{
			name:    "success",
			setup:   func() *CommonDAO { return New() },
			creator: "g1w4ek2u33ta047h6lta047h6lta047h6ldvdwpn",
			def:     testPropDef{},
		},
		{
			name:  "nil definition",
			setup: func() *CommonDAO { return New() },
			err:   ErrProposalDefinitionRequired,
		},
		{
