package validators

import (
	"errors"
	"std"
)

// ValsetProtocol defines the validator set protocol (PoA / PoS / PoC / ?)
type ValsetProtocol interface {
	// AddValidator adds a new validator to the validator set.
	// If the validator is already present, the method should error out
	//
	// TODO: This API is not ideal -- the address should be derived from
	// the public key, and not be passed in as such, but currently Gno
	// does not support crypto address derivation
	AddValidator(address_XXX std.Address, pubKey string, power uint64) (Validator, error)

	// RemoveValidator removes the given validator from the set.
	// If the validator is not present in the set, the method should error out
	RemoveValidator(address_XXX std.Address) (Validator, error)

	// IsValidator returns a flag indicating if the given
	// bech32 address is part of the validator set
	IsValidator(address_XXX std.Address) bool

	// GetValidator returns the validator using the given address
	GetValidator(address_XXX std.Address) (Validator, error)

	// GetValidators returns the currently active validator set
	GetValidators() []Validator
}

// Validator represents a single chain validator
type Validator struct {
	Address     std.Address // bech32 address
	PubKey      string      // bech32 representation of the public key
	VotingPower uint64
}

const (
	ValidatorAddedEvent   = "ValidatorAdded"   // emitted when a validator was added to the set
	ValidatorRemovedEvent = "ValidatorRemoved" // emitted when a validator was removed from the set
)

var (
	// ErrValidatorExists is returned when the validator is already in the set
	ErrValidatorExists = errors.New("validator already exists")

	// ErrValidatorMissing is returned when the validator is not in the set
	ErrValidatorMissing = errors.New("validator doesn't exist")
)

package poa

import "gno.land/p/sys/validators"

type Option func(*PoA)

// WithInitialSet sets the initial PoA validator set
func WithInitialSet(validators []validators.Validator) Option {
	return func(p *PoA) {
		for _, validator := range validators {
			p.validators.Set(validator.Address.String(), validator)
		}
	}
}

package poa

import (
	"errors"
	"std"

	"gno.land/p/nt/avl"
	"gno.land/p/sys/validators"
)

var ErrInvalidVotingPower = errors.New("invalid voting power")

// PoA specifies the Proof of Authority validator set, with simple add / remove constraints.
//
// To add:
// - proposed validator must not be part of the set already
// - proposed validator voting power must be > 0
//
// To remove:
// - proposed validator must be part of the set already
type PoA struct {
	validators *avl.Tree // std.Address -> validators.Validator
}

// NewPoA creates a new empty Proof of Authority validator set
func NewPoA(opts ...Option) *PoA {
	// Create the empty set
	p := &PoA{
		validators: avl.NewTree(),
	}

	// Apply the options
	for _, opt := range opts {
		opt(p)
	}

	return p
}

func (p *PoA) AddValidator(address_XXX std.Address, pubKey string, power uint64) (validators.Validator, error) {
	// Validate that the operation is a valid call.
	// Check if the validator is already in the set
	if p.IsValidator(address_XXX) {
		return validators.Validator{}, validators.ErrValidatorExists
	}

	// Make sure the voting power > 0
	if power == 0 {
		return validators.Validator{}, ErrInvalidVotingPower
	}

	v := validators.Validator{
		Address:     address_XXX,
		PubKey:      pubKey, // TODO: in the future, verify the public key
		VotingPower: power,
	}

	// Add the validator to the set
	p.validators.Set(address_XXX.String(), v)

	return v, nil
}

func (p *PoA) RemoveValidator(address_XXX std.Address) (validators.Validator, error) {
	// Validate that the operation is a valid call
	// Fetch the validator
	validator, err := p.GetValidator(address_XXX)
	if err != nil {
		return validators.Validator{}, err
	}

	// Remove the validator from the set
	p.validators.Remove(address_XXX.String())

	return validator, nil
}

func (p *PoA) IsValidator(address_XXX std.Address) bool {
	_, exists := p.validators.Get(address_XXX.String())

	return exists
}

func (p *PoA) GetValidator(address_XXX std.Address) (validators.Validator, error) {
	validatorRaw, exists := p.validators.Get(address_XXX.String())
	if !exists {
		return validators.Validator{}, validators.ErrValidatorMissing
	}

	validator := validatorRaw.(validators.Validator)

	return validator, nil
}

func (p *PoA) GetValidators() []validators.Validator {
	vals := make([]validators.Validator, 0, p.validators.Size())

	p.validators.Iterate("", "", func(_ string, value any) bool {
		validator := value.(validators.Validator)
		vals = append(vals, validator)

		return false
	})

	return vals
}

package poa

import (
	"testing"

	"gno.land/p/nt/testutils"
	"gno.land/p/nt/uassert"
	"gno.land/p/nt/urequire"
	"gno.land/p/sys/validators"

	"gno.land/p/nt/ufmt"
)

// generateTestValidators generates a dummy validator set
func generateTestValidators(count int) []validators.Validator {
	vals := make([]validators.Validator, 0, count)

	for i := 0; i < count; i++ {
		val := validators.Validator{
			Address:     testutils.TestAddress(ufmt.Sprintf("%d", i)),
			PubKey:      "public-key",
			VotingPower: 1,
		}

		vals = append(vals, val)
	}

	return vals
}

func TestPoA_AddValidator_Invalid(t *testing.T) {
	t.Parallel()

	t.Run("validator already in set", func(t *testing.T) {
		t.Parallel()

		var (
			proposalAddress = testutils.TestAddress("caller")
			proposalKey     = "public-key"

			initialSet = generateTestValidators(1)
		)

		initialSet[0].Address = proposalAddress
		initialSet[0].PubKey = proposalKey

		// Create the protocol with an initial set
		p := NewPoA(WithInitialSet(initialSet))

		// Attempt to add the validator
		_, err := p.AddValidator(proposalAddress, proposalKey, 1)
		uassert.ErrorIs(t, err, validators.ErrValidatorExists)
	})

	t.Run("invalid voting power", func(t *testing.T) {
		t.Parallel()

		var (
			proposalAddress = testutils.TestAddress("caller")
			proposalKey     = "public-key"
		)

		// Create the protocol with no initial set
		p := NewPoA()

		// Attempt to add the validator
		_, err := p.AddValidator(proposalAddress, proposalKey, 0)
		uassert.ErrorIs(t, err, ErrInvalidVotingPower)
	})
}

func TestPoA_AddValidator(t *testing.T) {
	t.Parallel()

	var (
		proposalAddress = testutils.TestAddress("caller")
		proposalKey     = "public-key"
	)

	// Create the protocol with no initial set
	p := NewPoA()

	// Attempt to add the validator
	_, err := p.AddValidator(proposalAddress, proposalKey, 1)
	uassert.NoError(t, err)

	// Make sure the validator is added
	if !p.IsValidator(proposalAddress) || p.validators.Size() != 1 {
		t.Fatal("address is not validator")
	}
}

func TestPoA_RemoveValidator_Invalid(t *testing.T) {
	t.Parallel()

	t.Run("proposed removal not in set", func(t *testing.T) {
		t.Parallel()

		var (
			proposalAddress = testutils.TestAddress("caller")
			initialSet      = generateTestValidators(1)
		)

		initialSet[0].Address = proposalAddress

		// Create the protocol with an initial set
		p := NewPoA(WithInitialSet(initialSet))

		// Attempt to remove the validator
		_, err := p.RemoveValidator(testutils.TestAddress("totally random"))
		uassert.ErrorIs(t, err, validators.ErrValidatorMissing)
	})
}

func TestPoA_RemoveValidator(t *testing.T) {
	t.Parallel()

	var (
		proposalAddress = testutils.TestAddress("caller")
		initialSet      = generateTestValidators(1)
	)

	initialSet[0].Address = proposalAddress

	// Create the protocol with an initial set
	p := NewPoA(WithInitialSet(initialSet))

	// Attempt to remove the validator
	_, err := p.RemoveValidator(proposalAddress)
	urequire.NoError(t, err)

	// Make sure the validator is removed
	if p.IsValidator(proposalAddress) || p.validators.Size() != 0 {
		t.Fatal("address is validator")
	}
}

func TestPoA_GetValidator(t *testing.T) {
	t.Parallel()

	t.Run("validator not in set", func(t *testing.T) {
		t.Parallel()

		// Create the protocol with no initial set
		p := NewPoA()

		// Attempt to get the voting power
		_, err := p.GetValidator(testutils.TestAddress("caller"))
		uassert.ErrorIs(t, err, validators.ErrValidatorMissing)
	})

	t.Run("validator fetched", func(t *testing.T) {
		t.Parallel()

		var (
			address_XXX = testutils.TestAddress("caller")
			pubKey      = "public-key"
			votingPower = uint64(10)

			initialSet = generateTestValidators(1)
		)

		initialSet[0].Address = address_XXX
		initialSet[0].PubKey = pubKey
		initialSet[0].VotingPower = votingPower

		// Create the protocol with an initial set
		p := NewPoA(WithInitialSet(initialSet))

		// Get the validator
		val, err := p.GetValidator(address_XXX)
		urequire.NoError(t, err)

		// Validate the address
		if val.Address != address_XXX {
			t.Fatal("invalid address")
		}

		// Validate the voting power
		if val.VotingPower != votingPower {
			t.Fatal("invalid voting power")
		}

		// Validate the public key
		if val.PubKey != pubKey {
			t.Fatal("invalid public key")
		}
	})
}

func TestPoA_GetValidators(t *testing.T) {
	t.Parallel()

	t.Run("empty set", func(t *testing.T) {
		t.Parallel()

		// Create the protocol with no initial set
		p := NewPoA()

		// Attempt to get the voting power
		vals := p.GetValidators()

		if len(vals) != 0 {
			t.Fatal("validator set is not empty")
		}
	})

	t.Run("validator set fetched", func(t *testing.T) {
		t.Parallel()

		initialSet := generateTestValidators(10)

		// Create the protocol with an initial set
		p := NewPoA(WithInitialSet(initialSet))

		// Get the validator set
		vals := p.GetValidators()

		if len(vals) != len(initialSet) {
			t.Fatal("returned validator set mismatch")
		}

		for _, val := range vals {
			for _, initialVal := range initialSet {
				if val.Address != initialVal.Address {
					continue
				}

				// Validate the voting power
				uassert.Equal(t, val.VotingPower, initialVal.VotingPower)

				// Validate the public key
				uassert.Equal(t, val.PubKey, initialVal.PubKey)
			}
		}
	})
}

package treasury

import (
	"errors"
	"std"

	"gno.land/p/aeddi/panictoerr"
)

var ErrNoStdBankerProvided = errors.New("no std banker provided")

// CoinsBanker is a Banker that sends std.Coins.
type CoinsBanker struct {
	owner  std.Address // The address of this coins banker owner.
	banker std.Banker  // The underlying std banker, must be a BankerTypeRealmSend.
}

var _ Banker = (*CoinsBanker)(nil)

// ID implements Banker.
func (CoinsBanker) ID() string {
	return "Coins"
}

// Send implements Banker.
func (cb *CoinsBanker) Send(p Payment) error {
	if std.CurrentRealm().Address() != cb.owner {
		return ErrCurrentRealmIsNotOwner
	}
	// Check if payment is of type coinsPayment.
	payment, ok := p.(coinsPayment)
	if !ok {
		return ErrInvalidPaymentType
	}

	// Send the coins.
	return panictoerr.PanicToError(func() {
		cb.banker.SendCoins(cb.owner, payment.toAddress, payment.coins)
	})
}

// Balances implements Banker.
func (cb *CoinsBanker) Balances() []Balance {
	// Get the coins from the banker.
	coins := cb.banker.GetCoins(cb.owner)

	// Convert std.Coins to []Balance.
	balances := make([]Balance, len(coins))
	for i := range coins {
		balances[i] = Balance{
			Denom:  coins[i].Denom,
			Amount: coins[i].Amount,
		}
	}

	return balances
}

// Address implements Banker.
func (cb *CoinsBanker) Address() string {
	return cb.owner.String()
}

// NewCoinsBanker creates a new CoinsBanker with the current Realm's address
// as the owner.
func NewCoinsBanker(banker std.Banker) (*CoinsBanker, error) {
	owner := std.CurrentRealm().Address()

	return NewCoinsBankerWithOwner(owner, banker)
}

// NewCoinsBankerWithOwner creates a new CoinsBanker with the given address.
func NewCoinsBankerWithOwner(owner std.Address, banker std.Banker) (*CoinsBanker, error) {
	if owner == "" {
		return nil, ErrNoOwnerProvided
	}

	// NOTE: Should we add methods to std.Banker to check both its type and the
	// associated Realm for this kind of case?
	// For example:
	// if banker.Type() != std.BankerTypeRealmSend { panic("banker must be of type std.BankerTypeRealmSend") }
	// if banker.Realm().Address() != owner { panic("banker must be owned by the given owner address") }
	if banker == nil {
		return nil, ErrNoStdBankerProvided
	}

	return &CoinsBanker{
		owner:  owner,
		banker: banker,
	}, nil
}

// coinsPayment represents a payment that is issued by a CoinsBanker.
type coinsPayment struct {
	coins     std.Coins   // The coins being sent.
	toAddress std.Address // The recipient of the payment.
}

var _ Payment = (*coinsPayment)(nil)

// BankerID implements Payment.
func (coinsPayment) BankerID() string {
	return CoinsBanker{}.ID()
}

// String implements Payment.
func (cp coinsPayment) String() string {
	return cp.coins.String() + " to " + cp.toAddress.String()
}

// NewCoinsPayment creates a new coinsPayment.
func NewCoinsPayment(coins std.Coins, toAddress std.Address) Payment {
	return coinsPayment{
		coins:     coins,
		toAddress: toAddress,
	}
}

