func TestBitwise_And(t *testing.T) {
	tests := []struct {
		x, y, want string
	}{
		{"5", "1", "1"},  // 0101 & 0001 = 0001
		{"-1", "1", "1"}, // 1111 & 0001 = 0001
		{"-5", "3", "3"}, // 1111...1011 & 0000...0011 = 0000...0011
		{MAX_UINT256, MAX_UINT256, MAX_UINT256},
		{TWO_POW_128, TWO_POW_128_MINUS_1, "0"}, // 2^128 & (2^128 - 1) = 0
