package ownable2step

import (
	"std"
)

const OwnershipTransferEvent = "OwnershipTransfer"

// Ownable2Step is a two-step ownership transfer package
// It allows the current owner to set a new owner and the new owner will need to accept the ownership before it is transferred
// XXX Implement using Ownable instead of replicating it.
type Ownable2Step struct {
	owner        std.Address
	pendingOwner std.Address
}

func New() *Ownable2Step {
	return &Ownable2Step{
		owner:        std.CurrentRealm().Address(),
		pendingOwner: "",
	}
}

func NewWithOrigin() *Ownable2Step {
	origin := std.OriginCaller()
	previous := std.PreviousRealm()
	if origin != previous.Address() {
		panic("NewWithOrigin() should be called from init() where std.PreviousRealm() is origin")
	}
	return &Ownable2Step{
		owner: origin,
	}
}

func NewWithAddress(addr std.Address) *Ownable2Step {
	return &Ownable2Step{
		owner:        addr,
		pendingOwner: "",
	}
}

// TransferOwnership initiate the transfer of the ownership to a new address by setting the PendingOwner
func (o *Ownable2Step) TransferOwnership(newOwner std.Address) error {
	if !o.OwnedByCurrent() {
		return ErrUnauthorized
	}
	if !newOwner.IsValid() {
		return ErrInvalidAddress
	}

	o.pendingOwner = newOwner
	return nil
}

// AcceptOwnership accepts the pending ownership transfer
func (o *Ownable2Step) AcceptOwnership() error {
	if o.pendingOwner.String() == "" {
		return ErrNoPendingOwner
	}
	if std.CurrentRealm().Address() != o.pendingOwner {
		return ErrPendingUnauthorized
	}

	o.owner = o.pendingOwner
	o.pendingOwner = ""

	return nil
}

// DropOwnership removes the owner, effectively disabling any owner-related actions
// Top-level usage: disables all only-owner actions/functions,
// Embedded usage: behaves like a burn functionality, removing the owner from the struct
func (o *Ownable2Step) DropOwnership() error {
	if !o.OwnedByCurrent() {
		return ErrUnauthorized
	}

	prevOwner := o.owner
	o.owner = ""
	o.pendingOwner = ""

	std.Emit(
		OwnershipTransferEvent,
		"from", prevOwner.String(),
		"to", "",
	)

	return nil
}

// Owner returns the owner address from Ownable
func (o *Ownable2Step) Owner() std.Address {
	return o.owner
}

// PendingOwner returns the pending owner address from Ownable2Step
func (o *Ownable2Step) PendingOwner() std.Address {
	return o.pendingOwner
}

// OwnedByCurrent checks if the caller of the function is the Realm's owner
func (o *Ownable2Step) OwnedByCurrent() bool {
	return std.CurrentRealm().Address() == o.owner
}

// AssertOwnedByCurrent panics if the caller is not the owner
func (o *Ownable2Step) AssertOwnedByCurrent() {
	if std.CurrentRealm().Address() != o.owner {
		panic(ErrUnauthorized)
	}
}

// OwnedByPrevious checks if the caller of the function is the Realm's owner
func (o *Ownable2Step) OwnedByPrevious() bool {
	return std.PreviousRealm().Address() == o.owner
}

// AssertOwnedByPrevious panics if the caller is not the owner
func (o *Ownable2Step) AssertOwnedByPrevious() {
	if std.PreviousRealm().Address() != o.owner {
		panic(ErrUnauthorized)
	}
}

// from www.youtube.com/watch?v=d-Eq6x1yssU
// Levenshtein distance is an algorithm to detect how close 2 words are.
// For example, "Float" and "Boats" have a levenstein distance of 3.
// Why ? Because you need 3 steps to go from Boats to Float
// - We start with the word (Boats)
// - Remove the s (Boat)
// - Insert the l (Bloat)
// - Substitute b with f (Float)
package levenshtein

import (
	"strconv"

	"gno.land/p/nt/avl"
)

// Distance computes the Levenshtein distance between two strings.
//
// The Levenshtein distance is a measure of the similarity between two strings,
// defined as the minimum number of single-character edits (insertions,
// deletions, or substitutions) required to change one string into the other.
//
// For example, the Levenshtein distance between "Float" and "Boats" is 3:
//   - Remove 's' from "Boats" â "Boat"
//   - Insert 'l' â "Bloat"
//   - Substitute 'b' with 'f' â "Float"
//
// This implementation uses a memory-efficient dynamic programming approach
// that computes the distance in O(len(a) * len(b)) time and O(min(len(a), len(b))) space.
func Distance(a, b string) uint {
	// make sure a is the longest
	if len(a) < len(b) {
		a, b = b, a
	}

	// init first row
	line := make([]uint, len(b)+1)
	for j := range line {
		line[j] = uint(j)
	}

	// main loop
	for i := 1; i <= len(a); i++ {
		prev := line[0]
		line[0] = uint(i)
		for j := 1; j <= len(b); j++ {
			old := line[j]
			if a[i-1] == b[j-1] {
				line[j] = prev
			} else {
				line[j] = min3(
					line[j-1]+1, // insertion
					line[j]+1,   // deletion
					prev+1,      // substitution
				)
			}
			prev = old
		}
	}
	return line[len(b)]
}

// Levenshteinable is an interface for types that can be compared using
// Levenshtein distance. It provides the LString method, which returns
// the string representation used for comparison.
//
// Note: This differs from the standard String() method, which may be used
// for display or debugging.
type Levenshteinable interface {
	LString() string
}

// PickFromString returns the Levenshteinable object from the slice xs
// that is closest in Levenshtein distance to the input string xStr.
//
// It returns the best match along with its distance. If xs is empty,
// it returns (nil, 0).
//
// This function runs in O(n) time, where n is the length of the slice.
func PickFromString(xStr string, xs []Levenshteinable) (Levenshteinable, uint) {
	// check for empty list
	if len(xs) == 0 {
		return nil, 0
	}

	// initialize
	bestDistance := Distance(xStr, xs[0].LString())
	best := xs[0]

	// loop through objects
	for n := 1; n < len(xs); n++ {
		dist := Distance(xStr, xs[n].LString())
		if dist < bestDistance {
			bestDistance = dist
			best = xs[n]
		}
	}
	return best, bestDistance
}

// Pick returns the Levenshteinable object from the slice xs that is
// closest in Levenshtein distance to the input object x.
//
// This is a convenience wrapper around PickFromString(x.LString(), xs).
// It runs in O(n) time.
func Pick(x Levenshteinable, xs []Levenshteinable) (Levenshteinable, uint) {
	return PickFromString(x.LString(), xs)
}

func padLeftNum(n uint, width int) string {
	s := strconv.FormatUint(uint64(n), 10)
	for len(s) < width {
		s = "0" + s
	}
	return s
}

// SeveralPickFromString returns the n closest Levenshteinable objects
// to the input string xStr from the slice xs, using Levenshtein distance.
//
// If xs is empty, it returns nil. If n is 0, it returns an empty slice.
//
// The result is not guaranteed to be sorted by distance.
// This function runs in O(n) time using an AVL tree for efficient selection.
func SeveralPickFromString(xStr string, xs []Levenshteinable, n uint) []Levenshteinable {
	// error casing
	if len(xs) == 0 {
		return nil
	}
	if n == 0 {
		return []Levenshteinable{}
	}

	tree := avl.NewTree()
	maxKey := ""

	for k, v := range xs {
		// calculate distance and create unique key
		d := Distance(xStr, v.LString())
		// ufmt.Sprintf("%016u:%d", d, k)
		key := padLeftNum(d, 6) + ":" + strconv.Itoa(k) // no sprintf ( ;-;)
		tree.Set(key, v)

		// remove the element with the greater key
		if uint(tree.Size()) > n {
			tree.ReverseIterate("", "", func(k string, _ any) bool {
				maxKey = k
				return true
			})
			tree.Remove(maxKey)
		}
	}

	// create the list
	out := make([]Levenshteinable, 0, tree.Size())
	tree.Iterate("", "", func(k string, v any) bool {
		out = append(out, v.(Levenshteinable))
		return false
	})
	return out
}

// SeveralPick returns the n closest Levenshteinable objects to the
// input object x from the slice xs, using Levenshtein distance.
//
// This is a convenience wrapper around SeveralPickFromString(x.LString(), xs, n).
// It runs in O(n) time.
func SeveralPick(x Levenshteinable, xs []Levenshteinable, n uint) []Levenshteinable {
	return SeveralPickFromString(x.LString(), xs, n)
}

func min3(a, b, c uint) uint {
	min := a
	if b < min {
		min = b
	}
	if c < min {
		min = c
	}
	return min
}

package levenshtein

import "testing"

func TestExample(t *testing.T) {
	if Distance("float", "boats") != 3 {
		t.Fatal("expected float vs boats to be 3")
	}

	if Distance("kitten", "sitting") != 3 {
		t.Fatal("expected kitten vs sitting to be 3")
	}

	if Distance("foo", "foo") != 0 {
		t.Fatal("expected foo and foo to be 0 (the same)")
	}
}

type person struct {
	name     string
	lastName string
}

func (p person) LString() string {
	return p.name + " " + p.lastName
}

func TestWithMyPhoneContact(t *testing.T) {
	contacts := []Levenshteinable{
		person{"maman", ""},
		person{"papa", ""},
		person{"paul", "parisot"},
		person{"leonardo", "dicaprio"},
		person{"jenifer", "lawrence"},
		person{"jae", "kwon"},
		person{"justin", "bieber"},
	}
	who, _ := PickFromString("pau parosit", contacts)
	// println(who.LString(), n)
	if who.LString() != "paul parisot" {
		t.Fatal("expected to be paul parisot !")
	}

	who, _ = PickFromString("jkwo0n", contacts)
	// println(who.LString(), n)
	if who.LString() != "jae kwon" {
		t.Fatal("expected to be jae kwon !")
	}

	who, _ = PickFromString("paulparis", contacts)
	// println(who, n)
}

func TestWithAVL(t *testing.T) {
	contacts := []Levenshteinable{
		person{"maman", ""},
		person{"papa", ""},
		person{"paul", "parisot"},
		person{"leonardo", "dicaprio"},
		person{"jenifer", "lawrence"},
		person{"jae", "kwon"},
		person{"justin", "bieber"},
	}
	list := SeveralPickFromString("paulparis", contacts, 3)
	if len(list) != 3 {
		t.Fatal("expected 3 elements, got ", len(list))
	}
	if list[0] != contacts[2] {
		t.Fatal("expected first element to be paul parisot")
	}
	if list[1] != contacts[1] {
		t.Fatal("expected second to be papa")
	}
}

func TestWhitespaces(t *testing.T) {
	if Distance("hello world", "helloworld") != 1 {
		t.Fatal("'helloworld' vs 'hello world' doesnt have 1 distance")
	}
	if Distance("hello  world", "hello world") != 1 {
		t.Fatal("'hello  world' vs 'hello world' doesnt have 1 distance")
	}
	if Distance("hello world", "hello  world") != 1 {
		t.Fatal("'hello world' vs 'hello  world' doesnt have 1 distance")
	}
}

package daocond

import (
	"gno.land/p/nt/avl"
)

type BallotAVL struct {
	tree *avl.Tree
}

func NewBallot() Ballot {
	return &BallotAVL{tree: avl.NewTree()}
}

// Implements Ballot interface
func (b *BallotAVL) Vote(voter string, vote Vote) {
	b.tree.Set(voter, vote)
}

// Implements Ballot interface
func (b *BallotAVL) Get(voter string) Vote {
	vote, ok := b.tree.Get(voter)
	if !ok {
		return VoteAbstain
	}
	return vote.(Vote)
}

// Implements Ballot interface
func (b *BallotAVL) Total() int {
	return b.tree.Size()
}

// Implements Ballot interface
func (b *BallotAVL) Iterate(fn func(voter string, vote Vote) bool) {
	b.tree.Iterate("", "", func(key string, value interface{}) bool {
		v, ok := value.(Vote)
		if !ok {
			return false
		}
		return fn(key, v)
	})
}

package daocond

import (
	"strings"
)

type andCond struct {
	conditions []Condition
}

func And(conditions ...Condition) Condition {
	if len(conditions) < 2 {
		panic("at least two conditions are required")
	}
	return &andCond{conditions: conditions}
}

// Eval implements Condition.
func (c *andCond) Eval(ballot Ballot) bool {
	for _, condition := range c.conditions {
		if !condition.Eval(ballot) {
			return false
		}
	}
	return true
}

// Signal implements Condition.
func (c *andCond) Signal(ballot Ballot) float64 {
	totalSignal := 0.0
	for _, condition := range c.conditions {
		totalSignal += condition.Signal(ballot)
	}
	return totalSignal / float64(len(c.conditions))
}

// Render implements Condition.
func (c *andCond) Render() string {
	renders := []string{}
	for _, condition := range c.conditions {
		renders = append(renders, condition.Render())
	}
	return "[" + strings.Join(renders, " AND ") + "]"
}

// RenderWithVotes implements Condition.
func (c *andCond) RenderWithVotes(ballot Ballot) string {
	renders := []string{}
	for _, condition := range c.conditions {
		renders = append(renders, condition.RenderWithVotes(ballot))
	}
	return "[" + strings.Join(renders, " AND ") + "]"
}

var _ Condition = (*andCond)(nil)

package daocond

import (
	"errors"
	"math"
	"strconv"
	"strings"

	"gno.land/p/nt/ufmt"
)

type gnolovDaoCondThreshold struct {
	threshold            float64
	roles                []string
	hasRoleFn            func(memberId string, role string) bool
	usersWithRoleCountFn func(role string) uint32
}

var roleWeights = []float64{3.0, 2.0, 1.0}

// This is our implementation of the govdao codition following Jae Kwon's specification: https://gist.github.com/jaekwon/918ad325c4c8f7fb5d6e022e33cb7eb3
func GnoloveDAOCondThreshold(threshold float64, roles []string, hasRoleFn func(memberId string, role string) bool, usersWithRoleCountFn func(role string) uint32) Condition {
	if threshold <= 0 || threshold > 1 {
		panic(errors.New("invalid threshold"))
	}
	if usersWithRoleCountFn == nil {
		panic(errors.New("nil usersWithRoleCountFn"))
	}
	if hasRoleFn == nil {
		panic(errors.New("nil hasRoleFn"))
	}
	if len(roles) > 3 {
		panic("the gnolove dao condition handles at most 3 roles")
	}
	return &gnolovDaoCondThreshold{
		threshold:            threshold,
		roles:                roles,
		hasRoleFn:            hasRoleFn,
		usersWithRoleCountFn: usersWithRoleCountFn,
	}
}

// Eval implements Condition.
func (c *gnolovDaoCondThreshold) Eval(ballot Ballot) bool {
	return c.yesRatio(ballot) >= c.threshold
}

// Signal implements Condition.
func (c *gnolovDaoCondThreshold) Signal(ballot Ballot) float64 {
	return math.Min(c.yesRatio(ballot)/c.threshold, 1)
}

// Render implements Condition.
func (c *gnolovDaoCondThreshold) Render() string {
	rolePowers := []string{}
	for i, role := range c.roles {
		weight := strconv.FormatFloat(roleWeights[i], 'f', 2, 64) // ufmt.Sprintf("%.2f", ...) is not working
		rolePowers = append(rolePowers, ufmt.Sprintf("%s => %s power", role, weight))
	}
	return ufmt.Sprintf("%g%% of total voting power | %s", c.threshold*100, strings.Join(rolePowers, " | "))
}

// RenderWithVotes implements Condition.
func (c *gnolovDaoCondThreshold) RenderWithVotes(ballot Ballot) string {
	vPowers, totalPower := c.computeVotingPowers()
	rolePowers := []string{}
	for _, role := range c.roles {
		weight := strconv.FormatFloat(vPowers[role], 'f', 2, 64) // ufmt.Sprintf("%.2f", ...) is not working
		rolePowers = append(rolePowers, ufmt.Sprintf("%s => %s power", role, weight))
	}
	s := ""
	s += ufmt.Sprintf("%g%% of total voting power | %s\n\n", c.threshold*100, strings.Join(rolePowers, " | "))
	s += ufmt.Sprintf("Threshold needed: %g%% of total voting power\n\n", c.threshold*100)
	s += ufmt.Sprintf("Yes: %d/%d\n\n", c.yesRatio(ballot), totalPower)
	s += ufmt.Sprintf("Voting power needed: %g%% of total voting power\n\n", c.threshold*totalPower)
	return s
}

var _ Condition = (*gnolovDaoCondThreshold)(nil)

func (c *gnolovDaoCondThreshold) yesRatio(ballot Ballot) float64 {
	var totalYes float64
	votingPowersByTier, totalPower := c.computeVotingPowers()
	// Case when there are zero T1s
	if totalPower == 0.0 {
		return totalPower
	}

	ballot.Iterate(func(voter string, vote Vote) bool {
		if vote != VoteYes {
			return false
		}
		tier := c.getUserRole(voter)
		totalYes += votingPowersByTier[tier]
		return false
	})
	return totalYes / totalPower
}

func (c *gnolovDaoCondThreshold) getUserRole(userID string) string {
	for _, role := range c.roles {
		if c.hasRoleFn(userID, role) {
			return role
		}
	}
	panic("No role found for user")
}

func (c *gnolovDaoCondThreshold) computeVotingPowers() (map[string]float64, float64) {
	votingPowers := make(map[string]float64)
	totalPower := 0.0
	countsMembersPerRole := make(map[string]float64)

	for _, role := range c.roles {
		countsMembersPerRole[role] = float64(c.usersWithRoleCountFn(role))
	}

	for i, role := range c.roles {
		if i == 0 {
			votingPowers[role] = roleWeights[0] // Highest tier always gets max power (3.0)
		} else {
			votingPowers[role] = computePower(countsMembersPerRole[c.roles[0]], countsMembersPerRole[role], roleWeights[i])
		}
		totalPower += votingPowers[role] * countsMembersPerRole[role]
	}

	return votingPowers, totalPower
}

// max power here is the number of votes each tier gets when we have
// the same number of member on each tier
// T2 = 2.0 and T1 = 1.0 with the ration T1/Tn
// we compute the actual ratio
func computePower(T1, Tn, maxPower float64) float64 {
	// If there are 0 Tn (T2, T3) just return the max power
	// we could also return 0.0 as voting power
	if Tn <= 0.0 {
		return maxPower
	}

	computedPower := (T1 / Tn) * maxPower
	if computedPower >= maxPower {
		// If computed power is bigger than the max, this happens if Tn is lower than T1
		// cap the max power to max power.
		return maxPower
	}
	return computedPower
}

package daocond

import (
	"testing"

	"gno.land/p/nt/ufmt"
	"gno.land/p/nt/urequire"
)

/*
Example 1:
T1 100 members --> 300 VP, 3 votes per member
T2 100 members --> 200 VP, 2 votes per member
T3 100 members --> 100 VP, 1 votes per member
Example 2:

T1 100 members --> 300 VP, 3 votes per member
T2 50 members --> 100 VP, 2 votes per member *
T3 10 members --> 10 VP, 1 votes per member *
Example 3:

T1 100 members --> 300 VP, 3 votes per member
T2 200 members --> 200 VP, 1 votes per member *
T3 100 members --> 100 VP, 1 votes per member
Example 4:

T1 100 members --> 300 VP, 3 votes per member
T2 200 members --> 200 VP, 1 votes per member *
T3 1000 members --> 100 VP, 0.1 votes per member
*/
func TestComputeVotingPowers(t *testing.T) {
	type gnoloveDaoComposition struct {
		t1s                int
		t2s                int
		t3s                int
		abstainT3          bool
		expectedTotalPower float64
		expectedPowers     map[string]float64
	}
	tests := map[string]gnoloveDaoComposition{
		"example 1": {
			t1s:                100,
			t2s:                100,
			t3s:                100,
			abstainT3:          false,
			expectedTotalPower: 600,
			expectedPowers: map[string]float64{
				"T1": 3.0,
				"T2": 2.0,
				"T3": 1.0,
			},
		},
		"example 2": {
			t1s:                100,
			t2s:                50,
			t3s:                10,
			abstainT3:          false,
			expectedTotalPower: 410,
			expectedPowers: map[string]float64{
				"T1": 3.0,
				"T2": 2.0,
				"T3": 1.0,
			},
		},
		"example 3": {
			t1s:                100,
			t2s:                200,
			t3s:                100,
			abstainT3:          false,
			expectedTotalPower: 600,
			expectedPowers: map[string]float64{
				"T1": 3.0,
				"T2": 1.0,
				"T3": 1.0,
			},
		},
		"example 4": {
			t1s:                100,
			t2s:                200,
			t3s:                1000,
			abstainT3:          false,
			expectedTotalPower: 600,
			expectedPowers: map[string]float64{
				"T1": 3.0,
				"T2": 1.0,
				"T3": 0.1,
			},
		},
		"0 -T1s": {
			t1s:                0,
			t2s:                100,
			t3s:                100,
			abstainT3:          false,
			expectedTotalPower: 0,
			expectedPowers: map[string]float64{
				"T1": 3.0,
				"T2": 0.0,
				"T3": 0.0,
			},
		},
		"100 T1, 1 T2, 1 T3": {
			t1s:                100,
			t2s:                1,
			t3s:                1,
			abstainT3:          false,
			expectedTotalPower: 303,
			expectedPowers: map[string]float64{
				"T1": 3.0,
				"T2": 2.0,
				"T3": 1.0,
			},
		},
		"T3 Abstaining": {
			t1s:                100,
			t2s:                100,
			t3s:                100,
			expectedTotalPower: 500,
			abstainT3:          true,
			expectedPowers: map[string]float64{
				"T1": 3.0,
				"T2": 2.0,
			},
		},
	}

	for name, composition := range tests {
		t.Run(name, func(t *testing.T) {
			dao := newMockDAO()
			for i := 0; i < composition.t1s; i++ {
				dao.addUser(ufmt.Sprintf("%d_T1", i), []string{"T1"})
			}
			for i := 0; i < composition.t2s; i++ {
				dao.addUser(ufmt.Sprintf("%d_T2", i), []string{"T2"})
			}
			for i := 0; i < composition.t3s; i++ {
				dao.addUser(ufmt.Sprintf("%d_T3", i), []string{"T3"})
			}

			roles := []string{"T1", "T2"}
			if !composition.abstainT3 {
				roles = append(roles, "T3")
			}

			cond := &gnolovDaoCondThreshold{
				threshold:            0.6,
				hasRoleFn:            dao.hasRole,
				roles:                roles,
				usersWithRoleCountFn: dao.usersWithRoleCount,
			}
			votingPowers, totalPower := cond.computeVotingPowers()
			for tier, expectedPower := range composition.expectedPowers {
				if votingPowers[tier] != expectedPower {
					t.Fail()
				}
			}

			if totalPower != composition.expectedTotalPower {
				t.Fail()
			}
		})
	}
}

func TestEval(t *testing.T) {
	type gnoloveDaoVotes struct {
		votesT1      []Vote
		votesT2      []Vote
		votesT3      []Vote
		threshold    float64
		expectedEval bool
		expectedYes  float64
		abstainT3    bool
		panic        bool
	}
	tests := map[string]gnoloveDaoVotes{
		"2/6% Yes": { //0.3333
			votesT1:      []Vote{VoteNo},                             // 3 voting power
			votesT2:      []Vote{VoteYes, VoteYes, VoteYes, VoteYes}, // 2 voting power combined
			votesT3:      []Vote{VoteNo},
			expectedEval: false,
			abstainT3:    false,
			threshold:    0.45,
			expectedYes:  2.0 / 6.0,
			panic:        false,
		},
		"50% Yes": {
			votesT1:      []Vote{VoteNo},                             // 3 voting power
			votesT2:      []Vote{VoteYes, VoteYes, VoteYes, VoteYes}, // 2 voting power combined
			votesT3:      []Vote{VoteYes},
			expectedEval: true,
			abstainT3:    false,
			threshold:    0.45,
			expectedYes:  3.0 / 6.0,
			panic:        false,
		},
		"several T2 & T3": {
			votesT1: []Vote{VoteNo, VoteNo}, // 6 voting power
			// 10 T2 total power (2/3) powerT1 = 4, 4/10 0.4 each
			votesT2: []Vote{VoteYes, VoteYes, VoteYes, VoteYes, VoteYes, VoteYes, VoteYes, VoteYes, VoteYes, VoteYes},
			// 4 T3 total power (1/3) powerT1 = 2, 2/4 0.5 each
			votesT3:      []Vote{VoteYes, VoteNo, VoteYes, VoteNo},
			expectedEval: false,
			abstainT3:    false,
			threshold:    0.42, //total power = 6+4+2 T3yes = 1, T2yes = 4 T1yes = 0 totalYes = 0.41666666666 (5/12)
			expectedYes:  5.0 / 12.0,
			panic:        false,
		},
		"several T2 & T3 eval true": {
			votesT1: []Vote{VoteNo, VoteNo}, // 6 voting power
			// 10 T2 total power (2/3) powerT1 = 4, 4/10 0.4 each
			votesT2: []Vote{VoteYes, VoteYes, VoteYes, VoteYes, VoteYes, VoteYes, VoteYes, VoteYes, VoteYes, VoteYes},
			// 4 T3 total power (1/3) powerT1 = 2, 2/4 0.5 each
			votesT3:      []Vote{VoteYes, VoteYes, VoteYes, VoteNo},
			expectedEval: true,
			abstainT3:    false,
			threshold:    0.42, //total power = 6+4+2 T3yes = 1.5, T2yes = 4 T1yes = 0 totalYes = 0.45833333333 (5.5/12)
			expectedYes:  5.5 / 12.0,
			panic:        false,
		},
		"only T1s": {
			votesT1:      []Vote{VoteYes, VoteNo}, // 6 voting power
			expectedEval: false,
			abstainT3:    false,
			threshold:    0.6,
			expectedYes:  3.0 / 6.0,
			panic:        false,
		},
		"only T3s": { // as T2 & T3 power is capped as power of T1, in this case the power will be 0 everywhere
			votesT3:      []Vote{VoteYes, VoteYes, VoteYes, VoteYes}, // voting power = 0, 0 each
			expectedEval: false,
			abstainT3:    false,
			threshold:    0.6,
			expectedYes:  0.0,
		},
		"T3 abstaining": {
			votesT1:      []Vote{VoteYes, VoteNo}, // 6 voting power
			votesT2:      []Vote{VoteYes, VoteYes},
			votesT3:      []Vote{},
			expectedEval: true,
			abstainT3:    true,
			threshold:    0.6,
			expectedYes:  7.0 / 10.0,
			panic:        false,
		},
		"T3 abstaining w/ votes": {
			votesT1:      []Vote{VoteYes, VoteNo}, // 6 voting power
			votesT2:      []Vote{VoteYes, VoteYes},
			votesT3:      []Vote{VoteYes, VoteNo},
			expectedEval: true,
			abstainT3:    true,
			threshold:    0.6,
			expectedYes:  7.0 / 10.0,
			panic:        true, // a user with T3 when T3 is abstaining should panic
		},
	}

	for name, tdata := range tests {
		t.Run(name, func(t *testing.T) {
			if tdata.panic {
				defer func() {
					if r := recover(); r == nil {
						t.Errorf("The code did not panic")
					}
				}()
			}
			votes := map[string]Vote{}
			dao := newMockDAO()
			for i, vote := range tdata.votesT1 {
				userID := ufmt.Sprintf("%d_T1", i)
				dao.addUser(userID, []string{"T1"})
				votes[userID] = vote
			}

			for i, vote := range tdata.votesT2 {
				userID := ufmt.Sprintf("%d_T2", i)
				dao.addUser(userID, []string{"T2"})
				votes[userID] = vote
			}

			for i, vote := range tdata.votesT3 {
				userID := ufmt.Sprintf("%d_T3", i)
				dao.addUser(userID, []string{"T3"})
				votes[userID] = vote
			}
			ballot := NewBallot()
			for userId, vote := range votes {
				ballot.Vote(userId, vote)
			}
			roles := []string{"T1", "T2"}
			if !tdata.abstainT3 {
				roles = append(roles, "T3")
			}
			cond := GnoloveDAOCondThreshold(tdata.threshold, roles, dao.hasRole, dao.usersWithRoleCount)
			// Get percent of total yes
			urequire.Equal(t, tdata.expectedEval, cond.Eval(ballot))
		})
	}
}

type mockDAO struct {
	members map[string][]string
	roles   map[string][]string
}

func newMockDAO() *mockDAO {
	return &mockDAO{
		members: map[string][]string{},
		roles:   map[string][]string{}, // roles to users
	}
}

func (m *mockDAO) addUser(memberId string, roles []string) {
	m.members[memberId] = roles
	for _, memberRole := range roles {
		m.roles[memberRole] = append(m.roles[memberRole], memberId)
	}
}
func (m *mockDAO) usersWithRoleCount(role string) uint32 {
	return uint32(len(m.roles[role]))
}

func (m *mockDAO) hasRole(memberId string, role string) bool {
	roles, ok := m.members[memberId]
	if !ok {
		return false
	}
	for _, memberRole := range roles {
		if memberRole == role {
			return true
		}
	}
	return false
}

package daocond

import (
	"errors"
	"math"

	"gno.land/p/nt/ufmt"
)

type membersThresholdCond struct {
	isMemberFn     func(memberId string) bool
	membersCountFn func() uint64
	threshold      float64
}

func MembersThreshold(threshold float64, isMemberFn func(memberId string) bool, membersCountFn func() uint64) Condition {
	if threshold <= 0 || threshold > 1 {
		panic(errors.New("invalid threshold"))
	}
	if isMemberFn == nil {
		panic(errors.New("nil isMemberFn"))
	}
	if membersCountFn == nil {
		panic(errors.New("nil membersCountFn"))
	}
	return &membersThresholdCond{
		threshold:      threshold,
		isMemberFn:     isMemberFn,
		membersCountFn: membersCountFn,
	}
}

// Eval implements Condition.
func (c *membersThresholdCond) Eval(ballot Ballot) bool {
	return c.yesRatio(ballot) >= c.threshold
}

// Signal implements Condition.
func (c *membersThresholdCond) Signal(ballot Ballot) float64 {
	return math.Min(c.yesRatio(ballot)/c.threshold, 1)
}

// Render implements Condition.
func (c *membersThresholdCond) Render() string {
	return ufmt.Sprintf("%g%% of members", c.threshold*100)
}

// RenderWithVotes implements Condition.
func (c *membersThresholdCond) RenderWithVotes(ballot Ballot) string {
	s := ""
	s += ufmt.Sprintf("to meet the condition, %g%% of members must vote yes\n\n", c.threshold*100)
	s += ufmt.Sprintf("Yes: %d/%d = %g%%\n\n", c.totalYes(ballot), c.membersCountFn(), c.yesRatio(ballot)*100)
	return s
}

var _ Condition = (*membersThresholdCond)(nil)

func (c *membersThresholdCond) yesRatio(ballot Ballot) float64 {
	return float64(c.totalYes(ballot)) / float64(c.membersCountFn())
}

func (c *membersThresholdCond) totalYes(ballot Ballot) uint64 {
	totalYes := uint64(0)
	ballot.Iterate(func(voter string, vote Vote) bool {
		if vote == VoteYes && c.isMemberFn(voter) {
			totalYes += 1
		}
		return false
	})
	return totalYes
}

package daocond

import (
	"math"
	"strings"
)

type orCond struct {
	conditions []Condition
}

func Or(conditions ...Condition) Condition {
	if len(conditions) < 2 {
		panic("at least two conditions are required")
	}
	return &orCond{conditions: conditions}
}

// Eval implements Condition.
func (c *orCond) Eval(ballot Ballot) bool {
	for _, condition := range c.conditions {
		if condition.Eval(ballot) {
			return true
		}
	}
	return false
}

// Signal implements Condition.
func (c *orCond) Signal(ballot Ballot) float64 {
	maxSignal := 0.0
	for _, condition := range c.conditions {
		maxSignal = math.Max(maxSignal, condition.Signal(ballot))
	}
	return maxSignal
}

// Render implements Condition.
func (c *orCond) Render() string {
	renders := []string{}
	for _, condition := range c.conditions {
		renders = append(renders, condition.Render())
	}
	return "[" + strings.Join(renders, " OR ") + "]"
}

// RenderWithVotes implements Condition.
func (c *orCond) RenderWithVotes(ballot Ballot) string {
	renders := []string{}
	for _, condition := range c.conditions {
		renders = append(renders, condition.RenderWithVotes(ballot))
	}
	return "[" + strings.Join(renders, " OR ") + "]"
}

var _ Condition = (*orCond)(nil)

package daocond

import (
	"errors"
	"math"

	"gno.land/p/nt/ufmt"
)

type roleCountCond struct {
	hasRoleFn func(memberId string, role string) bool
	count     uint64
	role      string
}

func RoleCount(count uint64, role string, hasRoleFn func(memberId string, role string) bool) Condition {
	if count == 0 {
		panic(errors.New("count must be greater than 0"))
	}
	if role == "" {
		panic(errors.New("role must not be empty"))
	}
	if hasRoleFn == nil {
		panic(errors.New("nil hasRoleFn"))
	}
	return &roleCountCond{
		count:     count,
		hasRoleFn: hasRoleFn,
		role:      role,
	}
}

// Eval implements Condition.
func (c *roleCountCond) Eval(ballot Ballot) bool {
	return c.totalYes(ballot) >= c.count
}

// Signal implements Condition.
func (c *roleCountCond) Signal(ballot Ballot) float64 {
	return math.Min(float64(c.totalYes(ballot))/float64(c.count), 1)
}

// Render implements Condition.
func (c *roleCountCond) Render() string {
	return ufmt.Sprintf("%d %s", c.count, c.role)
}

// RenderWithVotes implements Condition.
func (c *roleCountCond) RenderWithVotes(ballot Ballot) string {
	s := ""
	s += ufmt.Sprintf("to meet the condition, %d members with role %s must vote yes\n\n", c.count, c.role)
	s += ufmt.Sprintf("Yes: %d/%d\n\n", c.totalYes(ballot), c.count)
	return s
}

var _ Condition = (*roleCountCond)(nil)

func (c *roleCountCond) totalYes(ballot Ballot) uint64 {
	totalYes := uint64(0)
	ballot.Iterate(func(voter string, vote Vote) bool {
		if vote == VoteYes && c.hasRoleFn(voter, c.role) {
			totalYes += 1
		}
		return false
	})
	return totalYes
}

package daocond

import (
