// Package piechart provides functionality to render a pie chart as an SVG image.
// It takes a list of PieSlice objects, each representing a slice of the pie with a value,
// color, and label, and generates an SVG representation of the pie chart.
package piechart

import (
	"math"

	"gno.land/p/demo/svg"
	"gno.land/p/nt/ufmt"
	"gno.land/p/sunspirit/md"
)

type PieSlice struct {
	Value float64
	Color string
	Label string
}

// Render creates an SVG pie chart from given slices (value, color and label).
// It returns an img svg markup as a string, including a markdown header if a non-empty title is provided.
func Render(slices []PieSlice, title string) string {
	// Validate input slices length
	if len(slices) == 0 {
		return "\npiechart fails: no data provided"
	}

	const (
		canvasWidth  = 500
		canvasHeight = 200
		centerX      = 100.0
		centerY      = 100.0
		radius       = 80.0
		legendX      = 210
		legendStartY = 30
		lineHeight   = 26
		squareSize   = 16
		fontSize     = 16
	)

	canvas := svg.NewCanvas(canvasWidth, canvasHeight)

	// Sum all values to compute slices proportions
	var total float64
	for _, s := range slices {
		total += s.Value
	}

	// Draw pie slices and legend in one pass
	startAngle := -math.Pi / 2
	for i, s := range slices {
		if s.Value > 0 {
			// --- PIE SLICE ---
			// Calculate angle span for current slice
			angle := (s.Value / total) * 2 * math.Pi
			endAngle := startAngle + angle

			// Compute start and end points on the circle circumference
			cosStart, sinStart := math.Cos(startAngle), math.Sin(startAngle)
			cosEnd, sinEnd := math.Cos(endAngle), math.Sin(endAngle)
			x1 := centerX + radius*cosStart
			y1 := centerY + radius*sinStart
			x2 := centerX + radius*cosEnd
			y2 := centerY + radius*sinEnd

			// Determine if the arc should be a large arc (> 180 degrees) (Arc direction)
			largeArcFlag := 0
			if angle > math.Pi {
				largeArcFlag = 1
			}

			// Build the SVG path for the pie slice
			path := ufmt.Sprintf(
				"M%.2f,%.2f L%.2f,%.2f A%.2f,%.2f 0 %d 1 %.2f,%.2f Z",
				centerX, centerY, x1, y1, radius, radius, largeArcFlag, x2, y2,
			)

			// Colored slice
			canvas.Append(svg.Path{
				D:    path,
				Fill: s.Color,
			})

			startAngle = endAngle
		}

		// --- LEGEND ---
		y := legendStartY + i*lineHeight
		// Colored square representing slice color
		canvas.Append(svg.Rectangle{
			X:      legendX,
			Y:      y - squareSize/2,
			Width:  squareSize,
			Height: squareSize,
			Fill:   s.Color,
		})

		// Legend text showing label, value and percentage
		text := ufmt.Sprintf("%s: %.0f (%.1f%%)", s.Label, s.Value, s.Value*100/total)
		canvas.Append(svg.Text{
			X:    legendX + squareSize + 8,
			Y:    y + fontSize/3,
			Text: text,
			Fill: "#54595D",
			Attr: svg.BaseAttrs{
				Style: ufmt.Sprintf("font-family:'Inter var',sans-serif;font-size:%dpx;", fontSize),
			},
		})
	}

	if title == "" {
		return canvas.Render("Pie Chart")
	}
	return md.H2(title) + canvas.Render("Pie Chart "+title)
}

package piechart

import (
	"strings"
	"testing"
)

func TestRender(t *testing.T) {
	title := "Test"
	slices := []PieSlice{
		{Value: 10, Color: "#00FF00", Label: "Green"},
		{Value: 20, Color: "#FFFF00", Label: "Yellow"},
		{Value: 30, Color: "#FF0000", Label: "Red"},
	}

	result := Render(slices, title)

	// Check if the result contains the expected image SVG structure
	if !strings.Contains(result, "data:image/svg+xml;base64,") {
		t.Errorf("Expected result to contain data:image/svg+xml;base64, got %s", result)
	}

	// Check if the result contains the title
	if !strings.Contains(result, title) {
		t.Errorf("SVG does not contain the title %q", title)
	}

	// Check if the result contains non-empty slices
	emptySlices := []PieSlice{}
	emptyResult := Render(emptySlices, title)
	expectedErr := "\npiechart fails: no data provided"
	if emptyResult != expectedErr {
		t.Errorf("Expected exact error for empty slices: %q, got %q", expectedErr, emptyResult)
	}
}

package tablesort

import (
	"net/url"
	"strings"

	"gno.land/p/mason/md"
	"gno.land/p/nt/ufmt"
)

// Table holds the headings and rows for rendering.
// Each row must have the same number of cells as there are headings.
type Table struct {
	Headings []string   // ["A", "B", "C"]
	Rows     [][]string // [["a1","b1","c1"], ["a2","b2","c2"], ...]
}

// Render generates a Markdown table from a Table struct with sortable columns based on URL params.
// paramPrefix is an optional prefix for in the URL to identify the tablesort Renders (e.g. "members-").
func Render(u *url.URL, table *Table, paramPrefix string) string {
	direction := ""
	currentHeading := ""
	if h := u.Query().Get(paramPrefix + "sort-asc"); h != "" {
		direction = "asc"
		currentHeading = h
	} else if h := u.Query().Get(paramPrefix + "sort-desc"); h != "" {
		direction = "desc"
		currentHeading = h
	}

	var sb strings.Builder

	// Find the index of the column to sort
	colIndex := -1
	for i, h := range table.Headings {
		if h == currentHeading {
			colIndex = i
			break
		}
	}

	// Sort rows if necessary
	if colIndex != -1 {
		SortRows(table.Rows, colIndex, direction == "asc")
	}

	// Build header
	sb.WriteString(buildHeader(u, table.Headings, currentHeading, direction, paramPrefix))
	sb.WriteString("\n")

	numCols := len(table.Headings)

	// Build rows
	for i, row := range table.Rows {
		// Validate row length
		if len(row) != numCols {
			return "tablesort fails: row " + ufmt.Sprintf("%d", i+1) + " has " +
				ufmt.Sprintf("%d", len(row)) + " cells, expected " +
				ufmt.Sprintf("%d", numCols) + ", because there are " + ufmt.Sprintf("%d", numCols) + " columns.\n"
		}

		sb.WriteString("|")
		for _, cell := range row {
			sb.WriteString(" " + cell + " |")
		}
		sb.WriteString("\n")
	}

	return sb.String()
}

// buildHeader builds the Markdown header row with clickable links and arrows
func buildHeader(u *url.URL, headings []string, currentHeading, direction string, paramPrefix string) string {
	var sb strings.Builder
	sb.WriteString("|")
	for _, h := range headings {
		arrow := ""
		if h == currentHeading {
			if direction == "asc" {
				arrow = " â"
			} else if direction == "desc" {
				arrow = " â"
			}
		}

		// Build URL for the header link with toggle logic
		newURL := *u
		q := newURL.Query()
		if h == currentHeading {
			// Toggle sort direction
			if direction == "asc" {
				q.Del(paramPrefix + "sort-asc")
				q.Set(paramPrefix+"sort-desc", h)
			} else {
				q.Del(paramPrefix + "sort-desc")
				q.Set(paramPrefix+"sort-asc", h)
			}
		} else {
			// First click defaults to descending
			q.Del(paramPrefix + "sort-asc")
			q.Set(paramPrefix+"sort-desc", h)
		}
		newURL.RawQuery = q.Encode()
		link := md.Link(h+arrow, newURL.String())
		sb.WriteString(" " + link + " |")
	}

	sb.WriteString("\n|")
	for range headings {
		sb.WriteString(" --- |")
	}
	return sb.String()
}

// Package tablesort provides functionality to render a Markdown table with sortable columns.
// It allows users to click on column headers to sort the table in ascending or descending sort direction.
// The sorting state is managed via URL query parameters.
// It displays an error if the table is malformed (e.g. rows with missing cells).
// Multiple tablesort can be rendered on the same page by using a paramPrefix for each Render (See the Render function).
package tablesort

import (
	"sort"
)

// rowSorter implements sort.Interface for sorting rows by a specific column.
type rowSorter struct {
	rows      [][]string
	colIndex  int
	ascending bool
}

func (rs rowSorter) Len() int {
	return len(rs.rows)
}

func (rs rowSorter) Less(i, j int) bool {
	iCell := rs.rows[i][rs.colIndex]
	jCell := rs.rows[j][rs.colIndex]
	if rs.ascending {
		return iCell < jCell
	}
	return iCell > jCell
}

func (rs rowSorter) Swap(i, j int) {
	rs.rows[i], rs.rows[j] = rs.rows[j], rs.rows[i]
}

// SortRows sorts the rows slice by a given column index and direction
func SortRows(rows [][]string, colIndex int, ascending bool) {
	sort.Sort(rowSorter{rows, colIndex, ascending})
}

package tablesort

import (
	"net/url"
	"strings"
	"testing"
)

func TestRender(t *testing.T) {
	// --- Case 1: Testing Render with a Table with "Role" column sorted descending and param prefix "members-"
	table := &Table{
		Headings: []string{"Tier", "Member", "Role"},
		Rows: [][]string{
			{"T1", "g11111", "finance-officer"},
			{"T2", "g22222", "developer"},
			{"T3", "g33333", "developer"},
		},
	}

	u, _ := url.Parse("/test?members-sort-desc=Tier")
	md := Render(u, table, "members-")

	expected := "| [Tier â](/test?members-sort-asc=Tier) | [Member](/test?members-sort-desc=Member) | [Role](/test?members-sort-desc=Role) |\n" +
		"| --- | --- | --- |\n" +
		"| T3 | g33333 | developer |\n" +
		"| T2 | g22222 | developer |\n" +
		"| T1 | g11111 | finance-officer |\n"

	// Trim spaces for comparison
	md = strings.TrimSpace(md)
	expected = strings.TrimSpace(expected)

	if md != expected {
		t.Errorf("Render() output mismatch.\nExpected:\n%s\nGot:\n%s", expected, md)
	}

	// --- Case 2: Testing Render with an invalid Table (row with missing cell)
	table = &Table{
		Headings: []string{"Tier", "Member", "Role"},
		Rows: [][]string{
			{"T1", "g11111"}, // Missing the "Role" cell
		},
	}

	md = Render(u, table, "")
	expected = "tablesort fails: row 1 has 2 cells, expected 3, because there are 3 columns.\n"

	if md != expected {
		t.Errorf("Expected error message:\n%s\nGot:\n%s", expected, md)
	}

	// --- Case 3: Testing SortRows
	rows := [][]string{
		{"T1", "g11111", "finance-officer"},
		{"T2", "g22222", "developer"},
		{"T3", "g33333", "developer"},
	}

	SortRows(rows, 2, false) // Sort by "Role" descending
	expected = "finance-officer"

	if rows[0][2] != expected {
		t.Errorf("SortRows() failed. Expected first role: %s, got: %s", expected, rows[0][2])
	}
}

// Package urlfilter provides functionality to filter items based on URL query parameters.
// It is designed to work with an avl.Tree structure where each key represents a filter
// and each value is an avl.Tree containing items associated with that filter.
package urlfilter

import (
	"net/url"
	"strings"

	"gno.land/p/nt/avl"
	"gno.land/p/nt/ufmt"
	"gno.land/p/sunspirit/md"
)

// ApplyFilters filters items based on the "filter" query parameter in the given URL
// and generates a Markdown representation of all available filters.
//
// Expected `items` structure:
//   - `items` is an *avl.Tree where each key is a filter name (e.g., "T1", "size:XL", "on_sale")
//     and each value is an *avl.Tree containing the items for that filter.
//   - Each item tree uses:
//     Key   (string): Unique item identifier
//     Value (any)   : Optional associated item data
//
// Example:
//
//	// Build the main filters tree
//	filters := avl.NewTree()
//
//	// Subtree for filter "T1"
//	t1Items := avl.NewTree()
//	t1Items.Set("item1", nil)
//	t1Items.Set("item2", nil)
//	filters.Set("T1", t1Items)
//
//	// URL with active filter "T1"
//	u, _ := url.Parse("/shop?filter=T1")
//
//	mdFilters, items := ApplyFilters(u, filters, "filter")
//
//	// mdFilters	â Markdown links for toggling filters
//	// items    	â AVL tree containing the filtered items
func ApplyFilters(u *url.URL, items *avl.Tree, paramName string) (string, *avl.Tree) {
	active := parseFilterMap(u.Query(), paramName)
	allFilters := make([]string, 0)
	resultTree := avl.NewTree()

	// Iterate over each filter group in the items tree
	items.Iterate("", "", func(filterKey string, subtree interface{}) bool {
		allFilters = append(allFilters, filterKey)

		tree, ok := subtree.(*avl.Tree)
		if !ok {
			return false
		}

		// Add items to result if there are no active filters
		// or if the current filter is active
		tree.Iterate("", "", func(itemKey string, _ interface{}) bool {
			if len(active) == 0 || active[filterKey] {
				resultTree.Set(itemKey, filterKey)
			}
			return false
		})
		return false
	})

	// Build Markdown links for toggling each filter
	var sb strings.Builder
	for _, f := range allFilters {
		q := toggleFilterQuery(active, f, allFilters, paramName)
		urlStr := buildURL(u.Path, q)
		sb.WriteString(ufmt.Sprintf(" | %v ", md.Link(formatLabel(f, active[f]), urlStr)))
	}

	return sb.String(), resultTree
}

// buildURL returns a path + query string, omitting the "?" if no query exists.
func buildURL(path string, query url.Values) string {
	if enc := query.Encode(); enc != "" {
		return path + "?" + enc
	}
	return path
}

// parseFilterMap reads the "filter" query parameter and converts it into a map
// where keys are filter names and values are true for active filters.
//
// Example:
//
//	"filter=T1,T2" -> map[string]bool{"T1": true, "T2": true}
func parseFilterMap(query url.Values, paramName string) map[string]bool {
	filterStr := strings.TrimSpace(query.Get(paramName))
	if filterStr == "" {
		return map[string]bool{}
	}
	m := make(map[string]bool)
	for _, f := range strings.Split(filterStr, ",") {
		if f = strings.TrimSpace(f); f != "" {
			m[f] = true
		}
	}
	return m
}

// toggleFilterQuery returns a new query string with the given filter toggled.
// - If the filter is currently active, it will be removed.
// - If it is inactive, it will be added.
// The order of filters follows the `all` list for consistency.
func toggleFilterQuery(active map[string]bool, toggled string, all []string, paramName string) url.Values {
	newFilters := []string{}
	for _, f := range all {
		if f == toggled {
			if !active[f] { // Add if it was inactive
				newFilters = append(newFilters, f)
			}
		} else if active[f] { // Keep other active filters
			newFilters = append(newFilters, f)
		}
	}
	q := url.Values{}
	if len(newFilters) > 0 {
		q.Set(paramName, strings.Join(newFilters, ","))
	}
	return q
}

// formatLabel returns the Markdown-formatted label for a filter,
// showing active filters in bold (**filter**) and inactive filters
// with strikethrough (~~filter~~).
func formatLabel(name string, active bool) string {
	if active {
		return md.Bold(name)
	}
	return md.Strikethrough(name)
}

package urlfilter

import (
	"net/url"
	"strings"
	"testing"

	"gno.land/p/nt/avl"
)

func buildTestTree() *avl.Tree {
	root := avl.NewTree()

	// keyF1 subtree
	t1 := avl.NewTree()
	t1.Set("key1_1", nil)
	t1.Set("key1_2", nil)
	root.Set("keyF1", t1)

	// keyF2 subtree
	t2 := avl.NewTree()
	t2.Set("key2_1", nil)
	t2.Set("key2_2", nil)
	root.Set("keyF2", t2)

	return root
}

func TestApplyFilters(t *testing.T) {
	TreeParent := buildTestTree()

	// --- Case 1: No filter selected
	u, _ := url.Parse("/test")
	mdFilters, items := ApplyFilters(u, TreeParent, "filter")
	expectedMarkdown := " | [~~keyF1~~](/test?filter=keyF1)  | [~~keyF2~~](/test?filter=keyF2) "
	if mdFilters != expectedMarkdown {
		t.Errorf("Expected Markdown %q, got %q", expectedMarkdown, mdFilters)
	}
	// All items should be present
	count := 0
	items.Iterate("", "", func(k string, _ interface{}) bool {
		count++
		return false
	})
	if count != 4 {
		t.Errorf("Expected 4 items, got %d", count)
	}

	// Try using ApplyFilters with a different name.
	withCustom, _ := ApplyFilters(u, TreeParent, "custom-param-name")
	if !strings.Contains(withCustom, "custom-param-name=keyF1") {
		t.Errorf("Expected 'custom-param-name' parameter in Markdown, got %q", mdFilters)
	}

	// --- Case 2: One filter active (keyF1)
	u, _ = url.Parse("/test?filter=keyF1")
	mdFilters, items = ApplyFilters(u, TreeParent, "filter")
	expectedMarkdown = " | [**keyF1**](/test)  | [~~keyF2~~](/test?filter=keyF1%2CkeyF2) "
	if mdFilters != expectedMarkdown {
		t.Errorf("Expected Markdown %q, got %q", expectedMarkdown, mdFilters)
	}
	// Only keyF1 items should be present
	keys := map[string]bool{}
	items.Iterate("", "", func(k string, _ interface{}) bool {
		keys[k] = true
		return false
	})
	if len(keys) != 2 || !keys["key1_1"] || !keys["key1_2"] {
		t.Errorf("Unexpected items in filtered result: %#v", keys)
	}

	// --- Case 3: Multiple filters active (keyF1, keyF2)
	u, _ = url.Parse("/test?filter=keyF1,keyF2")
	mdFilters, items = ApplyFilters(u, TreeParent, "filter")
	// Both filters should be bold, no remove query for last one
	if items.Size() != 4 {
		t.Errorf("Expected 4 items, got %d", items.Size())
	}

	// --- Case 4: Filter not existing
	u, _ = url.Parse("/test?filter=unknown")
	mdFilters, items = ApplyFilters(u, TreeParent, "filter")
	// No matching items
	if items.Size() != 0 {
		t.Errorf("Expected 0 items for unknown filter, got %d", items.Size())
	}
}

// Package genesis provides a way to store and access dynamic variables that are
// set during the genesis or block0 initialization phase. This package
// demonstrates an important aspect of p/ packages in Gno: while they are
// pure packages, they can have mutable state during initialization (init phase)
// before becoming read-only.
//
// Future improvements:
// When Gno supports something similar to "go build -X", this package could be
