package accesscontrol

import (
	"std"

	"gno.land/p/nt/avl"
	"gno.land/p/nt/ownable"
)

const (
	RoleCreatedEvent   = "RoleCreated"
	RoleGrantedEvent   = "RoleGranted"
	RoleRevokedEvent   = "RoleRevoked"
	RoleRenouncedEvent = "RoleRenounced"
	RoleSetEvent       = "RoleSet"
)

// Role struct to store role information
type Role struct {
	Name    string
	Holders *avl.Tree // std.Address -> struct{}
	Ownable *ownable.Ownable
}

// Roles struct to store all Roles information
type Roles struct {
	Roles       []*Role
	UserToRoles avl.Tree // std.Address -> []*Role
	Ownable     *ownable.Ownable
}

func validRoleName(name string) error {
	if len(name) > 30 || name == "" {
		return ErrNameRole
	}
	return nil
}

// NewRole creates a new instance of Role
func NewRole(name string, admin std.Address) (*Role, error) {
	if err := validRoleName(name); err != nil {
		return nil, ErrNameRole
	}

	return &Role{
		Name:    name,
		Holders: avl.NewTree(),
		Ownable: ownable.NewWithAddress(admin),
	}, nil
}

// CreateRole create a new role within the realm
func (rs *Roles) CreateRole(name string) (*Role, error) {
	if err := validRoleName(name); err != nil {
		return nil, ErrNameRole
	}

	if !rs.Ownable.OwnedByCurrent() {
		return nil, ErrNotOwner
	}

	for _, role := range rs.Roles {
		if role.Name == name {
			return nil, ErrRoleSameName
		}
	}

	role, err := NewRole(name, rs.Ownable.Owner())
	if err != nil {
		return nil, err
	}

	rs.Roles = append(rs.Roles, role)

	std.Emit(
		RoleCreatedEvent,
		"roleName", name,
		"sender", rs.Ownable.Owner().String(),
	)

	return role, nil
}

// HasAccount check if an account has a specific role
func (r *Role) HasAccount(account std.Address) bool {
	return r.Holders.Has(account.String())
}

// FindRole searches for a role by its name
func (rs *Roles) FindRole(name string) (*Role, error) {
	for _, role := range rs.Roles {
		if role.Name == name {
			return role, nil
		}
	}

	return nil, ErrRoleNotFound
}

// GrantRole grants a role to an account
func (rs *Roles) GrantRole(name string, account std.Address) error {
	r, err := rs.FindRole(name)
	if err != nil {
		return ErrRoleNotFound
	}

	if !r.Ownable.OwnedByCurrent() {
		return ErrNotOwner
	}

	r.Holders.Set(account.String(), struct{}{})

	// Add in UserToRoles
	roles, found := rs.UserToRoles.Get(account.String())
	if !found {
		roles = []*Role{}
	}
	roles = append(roles.([]*Role), r)
	rs.UserToRoles.Set(account.String(), roles)

	std.Emit(
		RoleGrantedEvent,
		"roleName", r.Name,
		"account", account.String(),
		"sender", std.CurrentRealm().Address().String(),
	)

	return nil
}

// RevokeRole revokes a role from an account
func (rs *Roles) RevokeRole(name string, account std.Address) error {
	r, err := rs.FindRole(name)
	if err != nil {
		return ErrRoleNotFound
	}

	if !r.Ownable.OwnedByCurrent() {
		return ErrNotOwner
	}

	r.Holders.Remove(account.String())

	// Remove in UserToRoles
	roles, found := rs.UserToRoles.Get(account.String())
	if found {
		updatedRoles := []*Role{}
		for _, role := range roles.([]*Role) {
			if role != r {
				updatedRoles = append(updatedRoles, role)
			}
		}
		rs.UserToRoles.Set(account.String(), updatedRoles)
	}

	std.Emit(
		RoleRevokedEvent,
		"roleName", r.Name,
		"account", account.String(),
		"sender", std.CurrentRealm().Address().String(),
	)

	return nil
}

// RenounceRole allows an account to renounce a role it holds
func (rs *Roles) RenounceRole(name string) error {
	r, err := rs.FindRole(name)
	if err != nil {
		return ErrRoleNotFound
	}

	caller := std.OriginCaller()

	if !r.HasAccount(caller) {
		return ErrAccountNotRole
	}

	r.Holders.Remove(caller.String())

	std.Emit(
		RoleRenouncedEvent,
		"roleName", r.Name,
		"account", caller.String(),
		"sender", caller.String(),
	)

	return nil
}

// SetRoleAdmin transfers the ownership of the role to a new administrator
func (r *Role) SetRoleAdmin(admin std.Address) error {
	if err := r.Ownable.TransferOwnership(admin); err != nil {
		return err
	}

	std.Emit(
		RoleSetEvent,
		"roleName", r.Name,
		"newAdminRole", r.Ownable.Owner().String(),
	)

	return nil
}

// Package accesscontrol implements a role-based access control (RBAC) system for Gno applications.
// It provides functionality to create, assign, revoke, and transfer roles.
//
// # Usage
//
// Import the `gno.land/p/demo/accesscontrol` package to manage roles within your Gno realm. You can create roles,
// assign them to users, revoke them, and transfer role ownership.
//
// Roles can be created by the contract owner using `CreateRole`. Each role is uniquely identified by its name.
//
//	CreateRole("editor")
//
// Use `GrantRole` to assign a role to a user, and `RevokeRole` to remove a role from a user.
//
//	GrantRole("editor", userAddress)
//
//	RevokeRole("editor", userAddress)
//
// Users can renounce their roles using `RenounceRole`, voluntarily removing themselves from a role.
//
//	RenounceRole("editor")
//
// You can look up a role by name with `FindRole`.
//
// FindRole("editor")
//
// Role ownership can be transferred using `SetRoleAdmin`.
//
// SetRoleAdmin(newAdminAddress)
//
// Key events
// - `RoleCreatedEvent`: Triggered when a new role is created
// Key includes:
// `roleName` (name of the role)
// `sender` (address of the sender)
//
// - `RoleGrantedEvent`: Triggered when a role is granted to an account
// Key includes:
// `roleName` (name of the role)
// `account` (address of the account)
// `sender` (address of the sender)
//
// - `RoleRevokedEvent`: Triggered when a role is revoked from an account
// Key includes:
// `roleName` (name of the role)
// `account` (address of the account)
// `sender` (address of the sender)
//
// - `RoleRenouncedEvent`: Triggered when a role is renounced by an account
// Key includes:
// `roleName` (name of the role)
// `account` (address of the account)
//
// - `RoleSetEvent`: Triggered when a role's administrator is set or changed
// Key includes:
// `roleName` (name of the role)
// `newAdmin` (address of the new administrator)
// `sender` (address of the sender)
package accesscontrol

// Package snowflake provides utility functions for generating unique identifiers
// (Snowflake IDs) based on a custom implementation inspired by the Snowflake algorithm (https://pkg.go.dev/github.com/godruoyi/go-snowflake#section-sourcefiles).
// The Snowflake IDs generated by this package ensure uniqueness and are suitable for
// distributed systems. The package offers a mechanism to convert these IDs
// to standard SnowflakeID format strings, making them compatible with systems
// requiring UUIDs.
// Add import "gno.land/p/demo/entropy" to use the entropy package. For of the pseudo-random number generator.
// TimestampLength is 42-bit.
// 42 it's the maximum value that can be returned "2^42", It's represent 139 years (2^42 secondes)
// SequenBits allows for 4096 unique IDs to be generated per millisecond.

package snowflake

import (
	"encoding/binary"
	"encoding/hex"
	"std"
	"time"

	"gno.land/p/demo/entropy"
	"gno.land/p/nt/ufmt"
)

const (
	TimestampBits uint8  = 42
	MachineIDBits uint64 = 10
	SequenceBits  uint64 = 12
	MaxSequence   uint16 = 1<<SequenceBits - 1
	MaxTimestamp  uint64 = 1<<TimestampBits - 1
	MaxMachineID  uint64 = 1<<MachineIDBits - 1

	machineIDMoveLength = SequenceBits
	timestampMoveLength = MachineIDBits + SequenceBits

	startTime uint64 = 1226354400000 // 10 Nov 2008 23:00:00 UTC in milliseconds (Snowflake epoch)
)

// SnowflakeID struct for generating unique Snowflake-based identifiers.
type SnowflakeID struct {
	machineID     uint64
	sequence      uint16
	startTime     uint64
	lastTimestamp uint64
	entropy       *entropy.Instance
}

func NewSnowflakeID() *SnowflakeID {
	s := &SnowflakeID{
		startTime: startTime,
		entropy:   entropy.New(),
	}
	s.SetMachineID()
	return s
}

// GenerateID generates a unique identifier based on the Snowflake algorithm.
// It combines the current timestamp, machine ID, and a sequence number to ensure uniqueness
// across distributed systems.
func (s *SnowflakeID) GenerateID() uint64 {
	current := s.getTimenow()

	if current == s.lastTimestamp {
		s.incrementSequence()
	} else {
		s.sequence = 0
		s.lastTimestamp = current
	}

	elapsedTime := uint64(current-s.startTime) % MaxTimestamp

	id := (uint64(elapsedTime) << timestampMoveLength) |
		(uint64(s.machineID) << machineIDMoveLength) |
		uint64(s.incrementSequence())
	return id
}

func (s *SnowflakeID) incrementSequence() uint16 {
	random := uint16(s.entropy.Value())
	s.sequence = (s.sequence + 1 + random) & MaxSequence
	return s.sequence
}

func (s *SnowflakeID) getTimenow() uint64 {
	return uint64(time.Now().UnixMilli())
}

// SetMachineID sets the machine ID based on the callerâs address.
func (s *SnowflakeID) SetMachineID() {
	caller := std.PreviousRealm().Address() // Retrieve the callerâs address
	machineID := uint64(0)
	for _, c := range caller.String() {
		machineID += uint64(c)
	}
	machineID %= MaxMachineID
	s.machineID = machineID
}

func SnowflakeIDToUUIDString(id uint64) string {
	bytes := make([]byte, 16)

	// Copy transformed ID into the second half of the array
	copy(bytes[8:], uint64ToBytes(id))

	// Use bits from the Snowflake ID to generate the first half of the SnowflakeID
	bytes[0] = byte(id >> 60)
	bytes[1] = byte(id >> 52)
	bytes[2] = byte(id >> 44)
	bytes[3] = byte(id >> 36)
	bytes[4] = byte(id >> 28)
	bytes[5] = byte(id >> 20)
	bytes[6] = byte(id >> 12)
	bytes[7] = byte(id >> 4)

	// set the version and variant bits according to SnowflakeID specification.
	bytes[6] = (bytes[6] & 0x0f) | 0x40 // version 4 (random)
	bytes[8] = (bytes[8] & 0x3f) | 0x80 // variant 1 (RFC 4122)

	hexStr := hex.EncodeToString(bytes)
	return ufmt.Sprintf("%s-%s-%s-%s-%s",
		hexStr[0:8],
		hexStr[8:12],
		hexStr[12:16],
		hexStr[16:20],
		hexStr[20:],
	)
}

func uint64ToBytes(i uint64) []byte {
	buf := make([]byte, 8)
	binary.BigEndian.PutUint64(buf, i)
	return buf
}

package snowflake

import (
	"testing"

	"gno.land/p/nt/uassert"
)

func TestID(t *testing.T) {
	u := NewSnowflakeID()
	id1 := u.GenerateID()
	id2 := u.GenerateID()
	uuid1 := SnowflakeIDToUUIDString(id1)
	uuid2 := SnowflakeIDToUUIDString(id2)

	uassert.NotEqual(t, uuid1, uuid2)

	idMap := make(map[uint64]bool)
	uuidMap := make(map[string]bool)

	for i := 0; i < 101; i++ {
		id := u.GenerateID()
		if _, exists := idMap[id]; exists {
			t.Errorf("Duplicate ID found: %d", id)
		}
		idMap[id] = true

		uuidStr := SnowflakeIDToUUIDString(id)
		if _, exists := uuidMap[uuidStr]; exists {
			t.Errorf("Duplicate UUID found: %s", uuidStr)
		}
		uuidMap[uuidStr] = true
		t.Logf("Generated UUID %d: %s", i, uuidStr)
	}
}

func TestGetEntropy(t *testing.T) {
	u := NewSnowflakeID()
	initialEntropyValue := u.entropy.Value()

	for i := 0; i < 10; i++ {
		u.GenerateID()
	}

	uassert.NotEqual(t, initialEntropyValue, u.entropy.Value())
}

