//----------------------------------------
// Realm (package) state

var (
	gGroups       avl.Tree // id -> *Group
	gGroupsCtr    int      // increments Group.id
	gGroupsByName avl.Tree // name -> *Group
)

//----------------------------------------
// Constants

var reName = regexp.MustCompile(`^[a-z]+[_a-z0-9]{2,29}$`)

package groups

import (
	"std"
	"strconv"
	"time"
)

type MemberID uint64

type Member struct {
	id          MemberID
	address_XXX std.Address
	weight      int
	metadata    string
	createdAt   time.Time
}

func (mid MemberID) String() string {
	return strconv.Itoa(int(mid))
}

func (member *Member) getMemberStr() string {
	memberDataStr := ""
	memberDataStr += "\t\t\t[" + memberIDKey(member.id) + ", " + member.address_XXX.String() + ", " + strconv.Itoa(member.weight) + ", " + member.metadata + ", " + member.createdAt.String() + "],\n\n"
	return memberDataStr
}

package groups

import (
	"std"
	"strconv"
	"strings"

	"gno.land/r/sys/users"
)

//----------------------------------------
// private utility methods
// XXX ensure these cannot be called from public.

func getGroup(gid GroupID) *Group {
	gidkey := groupIDKey(gid)
	group_, exists := gGroups.Get(gidkey)
	if !exists {
		panic("group id (" + gid.String() + ") does not exists")
	}
	group := group_.(*Group)
	return group
}

func incGetGroupID() GroupID {
	gGroupsCtr++
	return GroupID(gGroupsCtr)
}

func padLeft(str string, length int) string {
	if len(str) >= length {
		return str
	}
	return strings.Repeat(" ", length-len(str)) + str
}

func padZero(u64 uint64, length int) string {
	str := strconv.Itoa(int(u64))
	if len(str) >= length {
		return str
	}
	return strings.Repeat("0", length-len(str)) + str
}

func groupIDKey(gid GroupID) string {
	return padZero(uint64(gid), 10)
}

func memberIDKey(mid MemberID) string {
	return padZero(uint64(mid), 10)
}

func indentBody(indent string, body string) string {
	lines := strings.Split(body, "\n")
	res := ""
	for i, line := range lines {
		if i > 0 {
			res += "\n"
		}
		res += indent + line
	}
	return res
}

// NOTE: length must be greater than 3.
func summaryOf(str string, length int) string {
	lines := strings.SplitN(str, "\n", 2)
	line := lines[0]
	if len(line) > length {
		line = line[:(length-3)] + "..."
	} else if len(lines) > 1 {
		// len(line) <= 80
		line = line + "..."
	}
	return line
}

func displayAddressMD(addr std.Address) string {
	user := users.ResolveAddress(addr)
	if user == nil {
		return "[" + addr.String() + "](/u/" + addr.String() + ")"
	}
	return "[@" + user.Name() + "](/u/" + user.Name() + ")"
}

func usernameOf(addr std.Address) string {
	user := users.ResolveAddress(addr)
	if user == nil {
		panic("user not found")
	}
	return user.Name()
}

func isValidPermission(perm Permission) bool {
	return perm == EditPermission || perm == DeletePermission
}

package groups

import (
	"std"

	"gno.land/r/sys/users"
)

//----------------------------------------
// Public facing functions

func GetGroupIDFromName(name string) (GroupID, bool) {
	groupI, exists := gGroupsByName.Get(name)
	if !exists {
		return 0, false
	}
	return groupI.(*Group).id, true
}

func CreateGroup(cur realm, name string) GroupID {
	std.AssertOriginCall()
	caller := std.OriginCaller()
	usernameOf(caller)
	url := "/r/demo/groups:" + name
	group := newGroup(url, name, caller)
	gidkey := groupIDKey(group.id)
	gGroups.Set(gidkey, group)
	gGroupsByName.Set(name, group)
	return group.id
}

func AddMember(cur realm, gid GroupID, address_XXX string, weight int, metadata string) MemberID {
	std.AssertOriginCall()
	caller := std.OriginCaller()
	usernameOf(caller)
	group := getGroup(gid)
	if !group.HasPermission(caller, EditPermission) {
		panic("unauthorized to edit group")
	}
	user := users.ResolveAddress(std.Address(address_XXX))
	if user == nil {
		panic("unknown address " + address_XXX)
	}
	mid := group.lastMemberID
	member := group.newMember(mid, std.Address(address_XXX), weight, metadata)
	midkey := memberIDKey(mid)
	group.members.Set(midkey, member)
	mid++
	group.lastMemberID = mid
	return member.id
}

func DeleteGroup(cur realm, gid GroupID) {
	std.AssertOriginCall()
	caller := std.OriginCaller()
	group := getGroup(gid)
	if !group.HasPermission(caller, DeletePermission) {
		panic("unauthorized to delete group")
	}
	group.deleteGroup()
}

func DeleteMember(cur realm, gid GroupID, mid MemberID) {
	std.AssertOriginCall()
	caller := std.OriginCaller()
	group := getGroup(gid)
	if !group.HasPermission(caller, DeletePermission) {
		panic("unauthorized to delete member")
	}
	group.deleteMember(mid)
}

package groups

import (
	"strings"
)

//----------------------------------------
// Render functions

func RenderGroup(gid GroupID) string {
	group := getGroup(gid)
	if group == nil {
		return "missing Group"
	}
	return group.RenderGroup()
}

func Render(path string) string {
	if path == "" {
		str := "List of all Groups:\n\n"
		gGroups.Iterate("", "", func(key string, value any) bool {
			group := value.(*Group)
			str += " * [" + group.name + "](" + group.url + ")\n"
			return false
		})
		return str
	}
	parts := strings.Split(path, "/")
	if len(parts) == 1 {
		// /r/demo/groups:Group_NAME
		name := parts[0]
		groupI, exists := gGroupsByName.Get(name)
		if !exists {
			return "Group does not exist: " + name
		}
		return groupI.(*Group).RenderGroup()
	} else {
		return "unrecognized path " + path
	}
}

