const (
	minSize     = 1
	defaultSize = 20
	maxSize     = 100
)

func Draw(size int) string {
	if size < minSize || size > maxSize {
		panic("invalid millipede size")
	}
	paddings := []string{"  ", " ", "", " ", "  ", "   ", "    ", "    ", "   "}
	var b strings.Builder
	b.WriteString("    ââ ââ\n")
	for i := 0; i < size; i++ {
		b.WriteString(paddings[i%9] + "ââ(âââ)ââ\n")
	}
	return b.String()
}

func Render(path string) string {
	size := defaultSize

	path = strings.TrimSpace(path)
	if path != "" {
		var err error
		size, err = strconv.Atoi(path)
		if err != nil {
			panic(err)
		}
	}

	output := "```\n" + Draw(size) + "```\n"
	if size > minSize {
		output += ufmt.Sprintf("[%d](/r/demo/art/millipede:%d)< ", size-1, size-1)
	}
	if size < maxSize {
		output += ufmt.Sprintf(" >[%d](/r/demo/art/millipede:%d)", size+1, size+1)
	}
	return output
}

