// The Test20 token allows anyone to mint any amount of tokens to any address, making
// it unsuitable for production use. The primary goal of this package is to facilitate
// testing and experimentation without any security measures or restrictions.
//
//	WARNING: This token is highly insecure and should not be used in any
//	 production environment. It is intended solely for testing and
//	 educational purposes.
package test20

import (
	"gno.land/p/demo/tokens/grc20"
	"gno.land/r/demo/defi/grc20reg"
)

var Token, PrivateLedger = grc20.NewToken("Test20", "TST", 4)

func init() {
	grc20reg.Register(cross, Token, "")
}

// Package atomicswap implements a hash time-locked contract (HTLC) for atomic swaps
// between native coins (ugnot) or GRC20 tokens.
//
// An atomic swap allows two parties to exchange assets in a trustless way, where
// either both transfers happen or neither does. The process works as follows:
//
//  1. Alice wants to swap with Bob. She generates a secret and creates a swap with
//     Bob's address and the hash of the secret (hashlock).
//
//  2. Bob can claim the assets by providing the correct secret before the timelock expires.
//     The secret proves Bob knows the preimage of the hashlock.
//
// 3. If Bob doesn't claim in time, Alice can refund the assets back to herself.
//
// Example usage for native coins:
//
//	// Alice creates a swap with 1000ugnot for Bob
//	secret := "mysecret"
//	hashlock := hex.EncodeToString(sha256.Sum256([]byte(secret)))
//	id, _ := atomicswap.NewCoinSwap(bobAddr, hashlock) // -send 1000ugnot
//
//	// Bob claims the swap by providing the secret
//	atomicswap.Claim(id, "mysecret")
//
// Example usage for GRC20 tokens:
//
//	// Alice approves the swap contract to spend her tokens
//	token.Approve(swapAddr, 1000)
//
//	// Alice creates a swap with 1000 tokens for Bob
//	id, _ := atomicswap.NewGRC20Swap(bobAddr, hashlock, "gno.land/r/demo/token")
//
//	// Bob claims the swap by providing the secret
//	atomicswap.Claim(id, "mysecret")
//
// If Bob doesn't claim in time (default 1 week), Alice can refund:
//
//	atomicswap.Refund(id)
package atomicswap

import (
	"std"
	"strconv"
	"time"

	"gno.land/p/demo/tokens/grc20"
	"gno.land/p/nt/avl"
	"gno.land/p/nt/ufmt"
	"gno.land/r/demo/defi/grc20reg"
)

const defaultTimelockDuration = 7 * 24 * time.Hour // 1w

var (
	swaps   avl.Tree // id -> *Swap
	counter int
)

// NewCoinSwap creates a new atomic swap contract for native coins.
// It uses a default timelock duration.
func NewCoinSwap(cur realm, recipient std.Address, hashlock string) (int, *Swap) {
	timelock := time.Now().Add(defaultTimelockDuration)
	return NewCustomCoinSwap(cur, recipient, hashlock, timelock)
}

// NewGRC20Swap creates a new atomic swap contract for grc20 tokens.
// It uses gno.land/r/demo/defi/grc20reg to lookup for a registered token.
func NewGRC20Swap(cur realm, recipient std.Address, hashlock string, tokenRegistryKey string) (int, *Swap) {
	timelock := time.Now().Add(defaultTimelockDuration)
	token := grc20reg.MustGet(tokenRegistryKey)
	return NewCustomGRC20Swap(cur, recipient, hashlock, timelock, token)
}

// NewCoinSwapWithTimelock creates a new atomic swap contract for native coin.
// It allows specifying a custom timelock duration.
// It is not callable with `gnokey maketx call`, but can be imported by another contract or `gnokey maketx run`.
func NewCustomCoinSwap(cur realm, recipient std.Address, hashlock string, timelock time.Time) (int, *Swap) {
	sender := std.PreviousRealm().Address()
	sent := std.OriginSend()
	require(len(sent) != 0, "at least one coin needs to be sent")

	// Create the swap
	sendFn := func(cur realm, to std.Address) {
		banker := std.NewBanker(std.BankerTypeRealmSend)
		pkgAddr := std.CurrentRealm().Address()
		banker.SendCoins(pkgAddr, to, sent)
	}
	amountStr := sent.String()
	swap := newSwap(sender, recipient, hashlock, timelock, amountStr, sendFn)

	counter++
	id := strconv.Itoa(counter)
	swaps.Set(id, swap)
	return counter, swap
}

// NewCustomGRC20Swap creates a new atomic swap contract for grc20 tokens.
// It is not callable with `gnokey maketx call`, but can be imported by another contract or `gnokey maketx run`.
func NewCustomGRC20Swap(cur realm, recipient std.Address, hashlock string, timelock time.Time, token *grc20.Token) (int, *Swap) {
	sender := std.PreviousRealm().Address()
	curAddr := std.CurrentRealm().Address()

	allowance := token.Allowance(sender, curAddr)
	require(allowance > 0, "no allowance")

	userTeller := token.RealmTeller()
	err := userTeller.TransferFrom(sender, curAddr, allowance)
	require(err == nil, "cannot retrieve tokens from allowance")

	amountStr := ufmt.Sprintf("%d%s", allowance, token.GetSymbol())
	sendFn := func(cur realm, to std.Address) {
		err := userTeller.Transfer(to, allowance)
		require(err == nil, "cannot transfer tokens")
	}

	swap := newSwap(sender, recipient, hashlock, timelock, amountStr, sendFn)

	counter++
	id := strconv.Itoa(counter)
	swaps.Set(id, swap)

	return counter, swap
}

// Claim loads a registered swap and tries to claim it.
func Claim(cur realm, id int, secret string) {
	swap := mustGet(id)
	swap.Claim(secret)
}

// Refund loads a registered swap and tries to refund it.
func Refund(cur realm, id int) {
	swap := mustGet(id)
	swap.Refund()
}

// Render returns a list of swaps (simplified) for the homepage, and swap details when specifying a swap ID.
func Render(path string) string {
	if path == "" { // home
		output := ""
		size := swaps.Size()
		max := 10
		swaps.ReverseIterateByOffset(size-max, max, func(key string, value any) bool {
			swap := value.(*Swap)
			output += ufmt.Sprintf("- %s: %s -(%s)> %s - %s\n",
				key, swap.sender, swap.amountStr, swap.recipient, swap.Status())
			return false
		})
		return output
	} else { // by id
		swap, ok := swaps.Get(path)
		if !ok {
			return "404"
		}
		return swap.(*Swap).String()
	}
}

// require checks a condition and panics with a message if the condition is false.
func require(check bool, msg string) {
	if !check {
		panic(msg)
	}
}

// mustGet retrieves a swap by its id or panics.
func mustGet(id int) *Swap {
	key := strconv.Itoa(id)
	swap, ok := swaps.Get(key)
	if !ok {
		panic("unknown swap ID")
	}
	return swap.(*Swap)
}

package atomicswap

import (
	"crypto/sha256"
	"encoding/hex"
	"std"
	"time"

	"gno.land/p/nt/ufmt"
)

// Swap represents an atomic swap contract.
type Swap struct {
	sender    std.Address
	recipient std.Address
	hashlock  string
	timelock  time.Time
	claimed   bool
	refunded  bool
	amountStr string
	sendFn    func(cur realm, to std.Address)
}

func newSwap(
	sender std.Address,
	recipient std.Address,
	hashlock string,
	timelock time.Time,
	amountStr string,
	sendFn func(realm, std.Address),
) *Swap {
	require(time.Now().Before(timelock), "timelock must be in the future")
	require(hashlock != "", "hashlock must not be empty")
	return &Swap{
		recipient: recipient,
		sender:    sender,
		hashlock:  hashlock,
		timelock:  timelock,
		claimed:   false,
		refunded:  false,
		sendFn:    sendFn,
		amountStr: amountStr,
	}
}

// Claim allows the recipient to claim the funds if they provide the correct preimage.
func (s *Swap) Claim(preimage string) {
	require(!s.claimed, "already claimed")
	require(!s.refunded, "already refunded")
	require(std.PreviousRealm().Address() == s.recipient, "unauthorized")

	hashlock := sha256.Sum256([]byte(preimage))
	hashlockHex := hex.EncodeToString(hashlock[:])
	require(hashlockHex == s.hashlock, "invalid preimage")

	s.claimed = true
	s.sendFn(cross, s.recipient)
}

// Refund allows the sender to refund the funds after the timelock has expired.
func (s *Swap) Refund() {
	require(!s.claimed, "already claimed")
	require(!s.refunded, "already refunded")
	require(std.PreviousRealm().Address() == s.sender, "unauthorized")
	require(time.Now().After(s.timelock), "timelock not expired")

	s.refunded = true
	s.sendFn(cross, s.sender)
}

func (s Swap) Status() string {
	switch {
	case s.refunded:
		return "refunded"
	case s.claimed:
		return "claimed"
	case s.TimeRemaining() < 0:
		return "expired"
	default:
		return "active"
	}
}

func (s Swap) TimeRemaining() time.Duration {
	remaining := time.Until(s.timelock)
	if remaining < 0 {
		return 0
	}
	return remaining
}

// String returns the current state of the swap.
func (s Swap) String() string {
	return ufmt.Sprintf(
		"- status: %s\n- sender: %s\n- recipient: %s\n- amount: %s\n- hashlock: %s\n- timelock: %s\n- remaining: %s",
		s.Status(), s.sender, s.recipient, s.amountStr, s.hashlock, s.timelock.Format(time.RFC3339), s.TimeRemaining().String(),
	)
}

