package shifumi

import (
	"errors"
	"std"
	"strconv"

	"gno.land/p/nt/avl"
	"gno.land/p/nt/seqid"

	"gno.land/r/sys/users"
)

const (
	empty = iota
	rock
	paper
	scissors
	last
)

type game struct {
	player1, player2 std.Address // shifumi is a 2 players game
	move1, move2     int         // can be empty, rock, paper, or scissors
}

var games avl.Tree
var id seqid.ID

func (g *game) play(player std.Address, move int) error {
	if !(move > empty && move < last) {
		return errors.New("invalid move")
	}
	if player != g.player1 && player != g.player2 {
		return errors.New("invalid player")
	}
	if player == g.player1 && g.move1 == empty {
		g.move1 = move
		return nil
	}
	if player == g.player2 && g.move2 == empty {
		g.move2 = move
		return nil
	}
	return errors.New("already played")
}

func (g *game) winner() int {
	if g.move1 == empty || g.move2 == empty {
		return -1
	}
	if g.move1 == g.move2 {
		return 0
	}
	if g.move1 == rock && g.move2 == scissors ||
		g.move1 == paper && g.move2 == rock ||
		g.move1 == scissors && g.move2 == paper {
		return 1
	}
	return 2
}

// NewGame creates a new game where player1 is the caller and player2 the argument.
// A new game index is returned.
func NewGame(_ realm, player std.Address) int {
	games.Set(id.Next().String(), &game{player1: std.PreviousRealm().Address(), player2: player})
	return int(id)
}

// Play executes a move for the game at index idx, where move can be:
// 1 (rock), 2 (paper), 3 (scissors).
func Play(_ realm, idx, move int) {
	v, ok := games.Get(seqid.ID(idx).String())
	if !ok {
		panic("game not found")
	}
	if err := v.(*game).play(std.PreviousRealm().Address(), move); err != nil {
		panic(err)
	}
}

func Render(path string) string {
	mov1 := []string{"", " ð¤  ", " ð«±  ", " ð  "}
	mov2 := []string{"", " ð¤  ", " ð«²  ", " ð  "}
	win := []string{"pending", "draw", "player1", "player2"}

	output := `# ð  â  âï¸  Shifumi
Actions:
* [NewGame](shifumi$help&func=NewGame) opponentAddress
* [Play](shifumi$help&func=Play) gameIndex move (1=rock, 2=paper, 3=scissors)

 game  | player1 |     | player2 |       | win 
 --- | --- | --- | --- | --- | ---
`
	// Output the 100 most recent games.
	maxGames := 100
	for n := int(id); n > 0 && int(id)-n < maxGames; n-- {
		v, ok := games.Get(seqid.ID(n).String())
		if !ok {
			continue
		}
		g := v.(*game)
		output += strconv.Itoa(n) + " | " +
			shortName(g.player1) + " | " + mov1[g.move1] + " | " +
			shortName(g.player2) + " | " + mov2[g.move2] + " | " +
			win[g.winner()+1] + "\n"
	}
	return output
}

func shortName(addr std.Address) string {
	user := users.ResolveAddress(addr)
	if user != nil {
		return user.Name()
	}
	if len(addr) < 10 {
		return string(addr)
	}
	return string(addr)[:10] + "..."
}

// eval realm is capable of evaluating 32-bit integer
// expressions as they would appear in Go. For example:
// /r/demo/math_eval:(4+12)/2-1+11*15
package eval

import (
	evalint32 "gno.land/p/demo/math_eval/int32"
	"gno.land/p/nt/ufmt"
)

func Render(p string) string {
	if len(p) == 0 {
		return `
evaluates 32-bit integer expressions. for example:

[(4+12)/2-1+11*15](/r/demo/math_eval:(4+12)/2-1+11*15)

`
	}
	expr, err := evalint32.Parse(p)
	if err != nil {
		return err.Error()
	}
	res, err := evalint32.Eval(expr, nil)
	if err != nil {
		return err.Error()
	}

	return ufmt.Sprintf("%s = %d", p, res)
}

