					md.BulletItem("Deeply nested"),
					"    ",
				),
			"  ",
		)

	output += md.H3("Nested Lists") +
		md.LanguageCodeBlock("go", nestedCode) +
		"\nResult:\n" +
		nestedResult + "\n"

	return output
}

