// Package coins provides simple helpers to retrieve information about coins
// on the Gno.land blockchain.
//
// The primary goal of this realm is to allow users to check their token balances without
// relying on external tools or services. This is particularly valuable for new networks
// that aren't yet widely supported by public explorers or wallets. By using this realm,
// users can always access their balance information directly through the gnodev.
//
// While currently focused on basic balance checking functionality, this realm could
// potentially be extended to support other banker-related workflows in the future.
// However, we aim to keep it minimal and focused on its core purpose.
//
// This is a "Render-only realm" - it exposes only a Render function as its public
// interface and doesn't maintain any state of its own. This pattern allows for
// simple, stateless information retrieval directly through the blockchain's
// rendering capabilities.
package coins

import (
	"net/url"
	"std"
	"strconv"
	"strings"

	"gno.land/p/leon/coinsort"
	"gno.land/p/leon/ctg"
	"gno.land/p/moul/md"
	"gno.land/p/moul/mdtable"
	"gno.land/p/nt/mux"
	"gno.land/p/nt/ufmt"

	"gno.land/r/sys/users"
)

var router *mux.Router

func init() {
	router = mux.NewRouter()

	router.HandleFunc("", func(res *mux.ResponseWriter, req *mux.Request) {
		res.Write(renderHomepage())
	})

	router.HandleFunc("balances", func(res *mux.ResponseWriter, req *mux.Request) {
		res.Write(renderBalances(req))
	})

	router.HandleFunc("convert/{address}", func(res *mux.ResponseWriter, req *mux.Request) {
		res.Write(renderConvertedAddress(req.GetVar("address")))
	})

	// Coin info
	router.HandleFunc("supply/{denom}", func(res *mux.ResponseWriter, req *mux.Request) {
		// banker := std.NewBanker(std.BankerTypeReadonly)
		// res.Write(renderAddressBalance(banker, denom, denom))
		res.Write("The total supply feature is coming soon.")
	})

	router.NotFoundHandler = func(res *mux.ResponseWriter, req *mux.Request) {
		res.Write("# 404\n\nThat page was not found. Would you like to [**go home**?](/r/gnoland/coins)")
	}
}

func Render(path string) string {
	return router.Render(path)
}

func renderHomepage() string {
	return strings.Replace(`# Gno.land Coins Explorer

This is a simple, readonly realm that allows users to browse native coin balances. Check your coin balance below!

<gno-form path="balances">
	<gno-input name="address" type="text" placeholder="Valid bech32 address (e.g. g1..., cosmos1..., osmo1...)" />
	<gno-input name="coin" type="text" placeholder="Coin (e.g. ugnot)"" />
</gno-form>

Here are a few more ways to use this app:

- ~/r/gnoland/coins:balances?address=g1...~ - show full list of coin balances of an address
	- [Example](/r/gnoland/coins:balances?address=g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5)
- ~/r/gnoland/coins:balances?address=g1...&coin=ugnot~ - shows the balance of an address for a specific coin
	- [Example](/r/gnoland/coins:balances?address=g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5&coin=ugnot)
- ~/r/gnoland/coins:convert/<bech32_addr>~ - convert a bech32 address to a Gno address
	- [Example](/r/gnoland/coins:convert/cosmos1jg8mtutu9khhfwc4nxmuhcpftf0pajdh6svrgs)
- ~/r/gnoland/coins:supply/<denom>~ - shows the total supply of denom
	- Coming soon!

`, "~", "`", -1)
}

func renderBalances(req *mux.Request) string {
	out := "# Balances\n\n"

	input := req.Query.Get("address")
	coin := req.Query.Get("coin")

	if input == "" && coin == "" {
		out += "Please input a valid address and coin denomination.\n\n"
		return out
	}

	if input == "" {
		out += "Please input a valid bech32 address.\n\n"
		return out
	}

	originalInput := input
	var wasConverted bool

	// Try to validate or convert
	if !std.Address(input).IsValid() {
		addr, err := ctg.ConvertAnyToGno(input)
		if err != nil {
			return out + ufmt.Sprintf("Tried converting `%s` to a Gno address but failed. Please try with a valid bech32 address.\n\n", input)
		}
		input = addr.String()
		wasConverted = true
	}

	if wasConverted {
		out += ufmt.Sprintf("> [!NOTE]\n>  Automatically converted `%s` to its Gno equivalent.\n\n", originalInput)
	}

	banker := std.NewBanker(std.BankerTypeReadonly)
	balances := banker.GetCoins(std.Address(input))

	if len(balances) == 0 {
		out += "This address currently has no coins."
		return out
	}

	if coin != "" {
		return renderSingleCoinBalance(coin, input, originalInput, wasConverted)
	}

	user, _ := users.ResolveAny(input)
	name := "`" + input + "`"
	if user != nil {
		name = user.RenderLink("")
	}

	out += ufmt.Sprintf("This page shows full coin balances of %s at block #%d\n\n",
		name, std.ChainHeight())

	// Determine sorting
	if getSortField(req) == "balance" {
		coinsort.SortByBalance(balances)
	}

	// Create table
	denomColumn := renderSortLink(req, "denom", "Denomination")
	balanceColumn := renderSortLink(req, "balance", "Balance")
	table := mdtable.Table{
		Headers: []string{denomColumn, balanceColumn},
	}

	if isSortReversed(req) {
		for _, b := range balances {
			table.Append([]string{b.Denom, strconv.Itoa(int(b.Amount))})
		}
	} else {
		for i := len(balances) - 1; i >= 0; i-- {
			table.Append([]string{balances[i].Denom, strconv.Itoa(int(balances[i].Amount))})
		}
	}

	out += table.String() + "\n\n"
	return out
}

func renderSingleCoinBalance(denom, addr, origInput string, wasConverted bool) string {
	out := "# Coin balance\n\n"
	banker := std.NewBanker(std.BankerTypeReadonly)

	if wasConverted {
		out += ufmt.Sprintf("> [!NOTE]\n>  Automatically converted `%s` to its Gno equivalent.\n\n", origInput)
	}

	user, _ := users.ResolveAny(addr)
	name := "`" + addr + "`"
	if user != nil {
		name = user.RenderLink("")
	}

	out += ufmt.Sprintf("%s has `%d%s` at block #%d\n\n",
		name, banker.GetCoins(std.Address(addr)).AmountOf(denom), denom, std.ChainHeight())

	out += "[View full balance list for this address](/r/gnoland/coins:balances?address=" + addr + ")"

	return out
}

func renderConvertedAddress(addr string) string {
	out := "# Address converter\n\n"

	gnoAddress, err := ctg.ConvertAnyToGno(addr)
	if err != nil {
		out += err.Error()
		return out
	}

	user, _ := users.ResolveAny(gnoAddress.String())
	name := "`" + gnoAddress.String() + "`"
	if user != nil {
		name = user.RenderLink("")
	}

	out += ufmt.Sprintf("`%s` on Cosmos matches %s on gno.land.\n\n", addr, name)
	out += "[[View `ugnot` balance for this address]](/r/gnoland/coins:balances?address=" + gnoAddress.String() + "&coin=ugnot) - "
	out += "[[View full balance list for this address]](/r/gnoland/coins:balances?address=" + gnoAddress.String() + ")"
	return out
}

// Helper functions for sorting and pagination
func getSortField(req *mux.Request) string {
	field := req.Query.Get("sort")
	switch field {
	case "denom", "balance":
		return field
	}
	return "denom"
}

func isSortReversed(req *mux.Request) bool {
	return req.Query.Get("order") != "asc"
}

func renderSortLink(req *mux.Request, field, label string) string {
	currentField := getSortField(req)
	currentOrder := req.Query.Get("order")

	newOrder := "desc"
	if field == currentField && currentOrder != "asc" {
		newOrder = "asc"
	}

	query := make(url.Values)
	for k, vs := range req.Query {
		query[k] = append([]string(nil), vs...)
	}

	query.Set("sort", field)
	query.Set("order", newOrder)

	if field == currentField {
		if currentOrder == "asc" {
			label += " â"
		} else {
			label += " â"
		}
	}

	return md.Link(label, "?"+query.Encode())
}

package coins

import (
	"std"
	"strings"
	"testing"

	"gno.land/p/leon/ctg"
	"gno.land/p/nt/testutils"
	"gno.land/p/nt/ufmt"
)

func TestBalanceChecker(t *testing.T) {
	denom1 := "testtoken1"
	denom2 := "testtoken2"
	addr1 := testutils.TestAddress("user1")
	addr2 := testutils.TestAddress("user2")

	coinsRealm := std.NewCodeRealm("gno.land/r/gnoland/coins")
	testing.SetRealm(coinsRealm)

	testing.IssueCoins(addr1, std.NewCoins(std.NewCoin(denom1, 1000000)))
	testing.IssueCoins(addr2, std.NewCoins(std.NewCoin(denom1, 501)))

	testing.IssueCoins(addr2, std.NewCoins(std.NewCoin(denom2, 12345)))

	gnoAddr, _ := ctg.ConvertCosmosToGno("cosmos1s2v4tdskccx2p3yyvzem4mw5nn5fprwcku77hr")
	osmoAddr := "osmo1fl48vsnmsdzcv85q5d2q4z5ajdha8yu3aq6l09"
	gnoAddr1, _ := ctg.ConvertAnyToGno(osmoAddr)

	testing.IssueCoins(gnoAddr1, std.NewCoins(std.NewCoin(denom2, 12345)))

	tests := []struct {
		name      string
		path      string
		contains  string
		wantPanic bool
	}{
		{
			name:     "homepage",
			path:     "",
			contains: "# Gno.land Coins Explorer",
		},
		// TODO: not supported yet
		// {
		// 	name:     "total supply",
		// 	path:     denom,
		// 	expected: "Balance: 1500000testtoken",
		// },
		{
			name:     "addr1's coin balance",
			path:     ufmt.Sprintf("balances?address=%s&coin=%s", addr1.String(), denom1),
			contains: ufmt.Sprintf("`%s` has `%d%s`", addr1.String(), 1000000, denom1),
		},
		{
			name:     "addr2's full balances",
			path:     ufmt.Sprintf("balances?address=%s", addr2.String()),
			contains: ufmt.Sprintf("This page shows full coin balances of `%s` at block", addr2.String()),
		},
		{
			name: "addr2's full balances",
			path: ufmt.Sprintf("balances?address=%s", addr2.String()),
			contains: `| testtoken1 | 501 |
| testtoken2 | 12345 |`,
		},
		{
			name:     "addr2's coin balance",
			path:     ufmt.Sprintf("balances?address=%s&coin=%s", addr2.String(), denom1),
			contains: ufmt.Sprintf("`%s` has `%d%s`", addr2.String(), 501, denom1),
		},
		{
			name:     "cosmos addr conversion",
			path:     "convert/cosmos1s2v4tdskccx2p3yyvzem4mw5nn5fprwcku77hr",
			contains: ufmt.Sprintf("`cosmos1s2v4tdskccx2p3yyvzem4mw5nn5fprwcku77hr` on Cosmos matches `%s`", gnoAddr),
		},
		{
			name:     "balances bech32 auto convert",
			path:     ufmt.Sprintf("balances?address=%s&coin=%s", osmoAddr, denom1),
			contains: "Automatically converted `osmo1fl48vsnmsdzcv85q5d2q4z5ajdha8yu3aq6l09`",
		},
		{
			name:     "single coin balance bech32 auto convert",
			path:     ufmt.Sprintf("balances?address=%s", osmoAddr),
			contains: "Automatically converted `osmo1fl48vsnmsdzcv85q5d2q4z5ajdha8yu3aq6l09`",
		},
		{
			name:      "no addr",
			path:      "balances?address=",
			contains:  "Please input a valid address",
			wantPanic: false,
		},
		{
			name:      "no addr",
			path:      "balances?address=&coin=",
			contains:  "Please input a valid address and coin denomination.",
			wantPanic: false,
		},
		{
			name:      "invalid path",
			path:      "invalid",
			contains:  "404",
			wantPanic: false,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if tt.wantPanic {
				defer func() {
					if r := recover(); r == nil {
						t.Errorf("expected panic for %s", tt.name)
					}
				}()
			}

			result := Render(tt.path)
			if !tt.wantPanic {
				if !strings.Contains(result, tt.contains) {
					t.Errorf("expected %s to contain %s", result, tt.contains)
				}
			}
		})
	}
}

package faucet

import (
	"errors"
	"std"
)

func AdminSetInPause(cur realm, inPause bool) string {
	if err := assertIsAdmin(); err != nil {
		return err.Error()
	}
	gInPause = inPause
	return ""
}

func AdminSetMessage(cur realm, message string) string {
	if err := assertIsAdmin(); err != nil {
		return err.Error()
	}
	gMessage = message
	return ""
}

func AdminSetTransferLimit(cur realm, amount int64) string {
	if err := assertIsAdmin(); err != nil {
		return err.Error()
	}
	gLimit = std.NewCoin("ugnot", amount)
	return ""
}

func AdminSetAdminAddr(cur realm, addr std.Address) string {
	if err := assertIsAdmin(); err != nil {
		return err.Error()
	}
	gAdminAddr = addr
	return ""
}

func AdminAddController(cur realm, addr std.Address) string {
	if err := assertIsAdmin(); err != nil {
		return err.Error()
	}

	size := gControllers.Size()

	if size >= gControllersMaxSize {
		return "can not add more controllers than allowed"
	}

	if gControllers.Has(addr.String()) {
		return addr.String() + " exists, no need to add."
	}

	gControllers.Set(addr.String(), addr)

	return ""
}

func AdminRemoveController(cur realm, addr std.Address) string {
	if err := assertIsAdmin(); err != nil {
		return err.Error()
	}

	if !gControllers.Has(addr.String()) {
		return addr.String() + " is not on the controller list"
	}

	_, ok := gControllers.Remove(addr.String())

	// it not should happen.
	// we will check anyway to prevent issues in the underline implementation.

	if !ok {
		return addr.String() + " is not on the controller list"
	}

	return ""
}

func assertIsAdmin() error {
	caller := std.PreviousRealm().Address()
	if caller != gAdminAddr {
		return errors.New("restricted for admin")
	}
	return nil
}

package faucet

import (
	"errors"
	"std"

	"gno.land/p/nt/avl"
	"gno.land/p/nt/ufmt"
)

var (
	// configurable by admin.
	gAdminAddr          std.Address = std.Address("g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5")
	gControllers                    = avl.NewTree()
	gControllersMaxSize             = 10 // limit it to 10
	gInPause                        = false
	gMessage                        = "# Community Faucet.\n\n"

	// internal vars, for stats.
	gTotalTransferred std.Coins
	gTotalTransfers   = uint(0)

	// per request limit, 350 gnot
	gLimit std.Coin = std.NewCoin("ugnot", 350_000_000)
)

func Transfer(cur realm, to std.Address, send int64) string {
	if err := assertIsController(); err != nil {
		return err.Error()
	}

	if gInPause {
		return errors.New("faucet in pause").Error()
	}

	// limit the per request
	if send > gLimit.Amount {
		return errors.New("Per request limit " + gLimit.String() + " exceed").Error()
	}
	sendCoins := std.Coins{std.NewCoin("ugnot", send)}

	gTotalTransferred = gTotalTransferred.Add(sendCoins)
	gTotalTransfers++

	banker := std.NewBanker(std.BankerTypeRealmSend)
	pkgaddr := std.CurrentRealm().Address()
	banker.SendCoins(pkgaddr, to, sendCoins)
	return ""
}

func GetPerTransferLimit() int64 {
	return gLimit.Amount
}

func bankerAddr(cur realm) std.Address {
	return std.CurrentRealm().Address()
}

func Render(_ string) string {
	banker := std.NewBanker(std.BankerTypeReadonly)
	balance := banker.GetCoins(bankerAddr(cross))

	output := gMessage
	if gInPause {
		output += "Status: inactive.\n"
	} else {
		output += "Status: active.\n"
	}
	output += ufmt.Sprintf("Balance: %s.\n", balance.String())
	output += ufmt.Sprintf("Total transfers: %s (in %d times).\n\n", gTotalTransferred.String(), gTotalTransfers)

	output += "Package address: " + std.CurrentRealm().Address().String() + "\n\n"
	output += ufmt.Sprintf("Admin: %s\n\n ", gAdminAddr.String())
	output += ufmt.Sprintf("Controllers:\n\n ")

	for i := 0; i < gControllers.Size(); i++ {
		_, v := gControllers.GetByIndex(i)
		output += ufmt.Sprintf("%s  ", v.(std.Address))
	}

	output += "\n\n"
	output += ufmt.Sprintf("Per request limit: %s\n\n", gLimit.String())

	return output
}

func assertIsController() error {
	caller := std.PreviousRealm().Address()
	ok := gControllers.Has(caller.String())
	if !ok {
		return errors.New(caller.String() + " is not on the controller list")
	}
	return nil
}

