package tokenhub

import (
	"std"
	"strings"

	"gno.land/p/demo/tokens/grc1155"
	"gno.land/p/demo/tokens/grc20"
	"gno.land/p/demo/tokens/grc721"
	"gno.land/p/nt/ufmt"
	"gno.land/r/demo/defi/grc20reg"
	"gno.land/r/sys/users"
)

// GetUserTokenBalances returns a string of all the grc20 tokens the user owns
func GetUserTokenBalances(userNameOrAddress string) string {
	return getTokenBalances(userNameOrAddress, false)
}

// GetUserTokenBalancesNonZero returns a string of all the grc20 tokens the user owns, but only the ones that have a balance greater than 0
func GetUserTokenBalancesNonZero(userNameOrAddress string) string {
	return getTokenBalances(userNameOrAddress, true)
}

// GetUserNFTBalances returns a string of all the NFTs the user owns
func GetUserNFTBalances(userNameOrAddress string) string {
	return getNFTBalances(userNameOrAddress)
}

// GetUserMultiTokenBalances returns a string of all the multi-tokens the user owns
func GetUserMultiTokenBalances(userNameOrAddress string) string {
	return getMultiTokenBalances(userNameOrAddress, false)
}

// GetUserMultiTokenBalancesNonZero returns a string of all the multi-tokens the user owns, but only the ones that have a balance greater than 0
func GetUserMultiTokenBalancesNonZero(userNameOrAddress string) string {
	return getMultiTokenBalances(userNameOrAddress, true)
}

// GetToken returns a token instance for a given key
func GetToken(key string) *grc20.Token {
	return grc20reg.Get(key)
}

// MustGetToken returns a token instance for a given key, panics if the token is not found
func MustGetToken(key string) *grc20.Token {
	return grc20reg.MustGet(key)
}

// GetNFT returns an NFT instance for a given key
func GetNFT(key string) grc721.IGRC721 {
	nftGetter, ok := registeredNFTs.Get(key)
	if !ok {
		return nil
	}
	return (nftGetter.(grc721.NFTGetter))()
}

// MustGetNFT returns an NFT instance for a given key, panics if the NFT is not found
func MustGetNFT(key string) grc721.IGRC721 {
	nftGetter := GetNFT(key)
	if nftGetter == nil {
		panic("unknown NFT: " + key)
	}
	return nftGetter
}

// GetMultiToken returns a multi-token instance for a given key
func GetMultiToken(key string) grc1155.IGRC1155 {
	info, ok := registeredMTs.Get(key)
	if !ok {
		return nil
	}
	mt := info.(GRC1155TokenInfo).Collection
	return mt()
}

// MustGetMultiToken returns a multi-token instance for a given key, panics if the multi-token is not found
func MustGetMultiToken(key string) grc1155.IGRC1155 {
	info := GetMultiToken(key)
	if info == nil {
		panic("unknown multi-token: " + key)
	}
	return info
}

// GetAllNFTs returns a string of all the NFTs registered
func GetAllNFTs() string {
	var out string
	registeredNFTs.Iterate("", "", func(key string, value interface{}) bool {
		out += ufmt.Sprintf("NFT:%s,", key)
		return false
	})
	return out
}

// GetAllTokens returns a string of all the tokens registered
func GetAllTokens() string {
	var out string
	grc20reg.GetRegistry().Iterate("", "", func(key string, value interface{}) bool {
		out += "Token:" + key + ","
		return false
	})
	return out
}

// GetAllTokenWithDetails returns a string of all the tokens registered with their details
func GetAllTokenWithDetails() string {
	var out string
	grc20reg.GetRegistry().Iterate("", "", func(key string, value interface{}) bool {
		token := value.(*grc20.Token)
		out += ufmt.Sprintf("Token:%s,Name:%s,Symbol:%s,Decimals:%d;", key, token.GetName(), token.GetSymbol(), token.GetDecimals())
		return false
	})
	return out
}

// GetAllMultiTokens returns a string of all the multi-tokens registered
func GetAllMultiTokens() string {
	var out string
	registeredMTs.Iterate("", "", func(key string, value interface{}) bool {
		out += "MultiToken:" + key + ","
		return false
	})
	return out
}

// GetAllRegistered returns a string of all the registered tokens, NFTs and multi-tokens
func GetAllRegistered() string {
	return GetAllNFTs() + GetAllTokens() + GetAllMultiTokens()
}

// getNFTBalances returns a string of all the NFTs the user owns
func getNFTBalances(input string) string {
	addr := getAddressForUsername(input)
	if !addr.IsValid() {
		panic("invalid address or username: " + input)
	}
	var out string

	registeredNFTs.Iterate("", "", func(key string, value interface{}) bool {
		nftGetter := value.(grc721.NFTGetter)
		nft := nftGetter()
		key_parts := strings.Split(key, ".")
		owner, err := nft.OwnerOf(grc721.TokenID(key_parts[len(key_parts)-1]))
		if err == nil && addr == owner { // show only the nfts owner owns
			out += "NFT:" + key + ","
		}
		return false
	})

	return out
}

// getTokenBalances returns a string of all the tokens the user owns
func getTokenBalances(input string, nonZero bool) string {

	addr := getAddressForUsername(input)
	if !addr.IsValid() {
		panic("invalid address or username: " + input)
	}
	var out string
	grc20reg.GetRegistry().Iterate("", "", func(key string, value interface{}) bool {

		token := value.(*grc20.Token)
		balance := token.BalanceOf(addr)
		if !nonZero || balance > 0 {
			out += ufmt.Sprintf("Token:%s:%d,", key, balance)
		}
		return false
	})

	return out
}

// getMultiTokenBalances returns a string of all the multi-tokens the user owns
func getMultiTokenBalances(input string, nonZero bool) string {
	addr := getAddressForUsername(input)
	if !addr.IsValid() {
		panic("invalid address or username: " + input)
	}
	var out string

	registeredMTs.Iterate("", "", func(key string, value interface{}) bool {
		info := value.(GRC1155TokenInfo)
		mt := info.Collection()
		balance, err := mt.BalanceOf(addr, grc1155.TokenID(info.TokenID))
		if err == nil {
			if !nonZero || balance > 0 {
				out += ufmt.Sprintf("MultiToken:%s:%d,", key, balance)
			}
		}
		return false
	})

	return out
}

// getAddressForUsername returns an address for a given username or address
func getAddressForUsername(addrOrName string) std.Address {
	addr := std.Address(addrOrName)
	if addr.IsValid() {
		return addr
	}

	if userData, _ := users.ResolveName(addrOrName); userData != nil {
		return userData.Addr()
	}

	return ""
}

package tokenhub

import (
	"std"

	"gno.land/p/demo/tokens/grc20"
	"gno.land/p/nt/fqname"
	"gno.land/r/demo/defi/grc20reg"

	"gno.land/p/demo/tokens/grc1155"
	"gno.land/p/demo/tokens/grc721"
	"gno.land/p/nt/avl"

	"gno.land/r/leon/hor"
)

type GRC1155TokenInfo struct {
	Collection grc1155.MultiTokenGetter
	TokenID    string
}

var (
	registeredTokens = avl.NewTree() // rlmPath[.slug] -> *grc20.Token
	registeredNFTs   = avl.NewTree() // rlmPath[.slug] -> grc721.NFTGetter
	registeredMTs    = avl.NewTree() // rlmPath[.slug] -> GRC1155TokenInfo
)

const pageSize = 10

func init() {
	hor.Register(cross, "Token Hub", "Registry for tokens and NFTs on gno.land")
}

// RegisterToken is a function that uses gno.land/r/demo/defi/grc20reg to register a token
// It uses the slug to construct a key and then registers the token in the registry
// The logic is the same as in grc20reg, but it's done here so the key path is callers pkgpath and not of this realm
// After doing so, the token hub realm uses grc20reg's registry as a read-only avl.Tree
//
// Note: register token returns the key path that can be used to retrieve the token
func RegisterToken(cur realm, token *grc20.Token, slug string) string {
	rlmPath := std.PreviousRealm().PkgPath()
	key := fqname.Construct(rlmPath, slug)

	grc20reg.Register(cross, token, key)

	return fqname.Construct(std.CurrentRealm().PkgPath(), key)
}

// RegisterNFT is a function that registers an NFT in an avl.Tree
func RegisterNFT(cur realm, nftGetter grc721.NFTGetter, collection string, tokenId string) error {
	nft := nftGetter()
	_, ok := nft.(grc721.IGRC721CollectionMetadata)
	if !ok {
		return ErrNFTNotMetadata
	}

	nftOwner, err := nft.OwnerOf(grc721.TokenID(tokenId))

	if err != nil {
		return err
	}
	if !nftOwner.IsValid() {
		return ErrNFTtokIDNotExists
	}

	rlmPath := std.PreviousRealm().PkgPath()
	key := rlmPath + "." + collection + "." + tokenId

	if registeredNFTs.Has(key) {
		return ErrNFTAlreadyRegistered
	}

	registeredNFTs.Set(key, nftGetter)
	return nil
}

// RegisterMultiToken is a function that registers a multi-token in an avl.Tree
// The avl.Tree value is a struct defined in this realm. It contains not only the getter (like other token types) but also the tokenID
func RegisterMultiToken(cur realm, mtGetter grc1155.MultiTokenGetter, tokenID string) error {
	rlmPath := std.PreviousRealm().PkgPath()
	key := rlmPath + "." + tokenID

	if registeredMTs.Has(key) {
		return ErrMTAlreadyRegistered
	}

	registeredMTs.Set(key, GRC1155TokenInfo{
		Collection: mtGetter,
		TokenID:    tokenID,
	})
	return nil
}

package calculator

import (
	"net/url"
	"strconv"
	"strings"

	"gno.land/p/moul/md"
	"gno.land/p/moul/mdtable"
	"gno.land/p/moul/realmpath"
	"gno.land/r/leon/hor"
)

type Node struct {
	value string // Value of the current node
	left  *Node
	right *Node
}

const (
	specialCharacters             = "+-*/."
	specialCharactersWithoutMinus = "+*/."
	topPriority                   = "*/"
	lowPriority                   = "+-"
	realmPath                     = "/r/miko/calculator"
)

var (
	val        float64
	displayVal string

	operationMap = map[string]func(left float64, right float64) float64{
		"+": func(left float64, right float64) float64 { return left + right },
		"-": func(left float64, right float64) float64 { return left - right },
		"*": func(left float64, right float64) float64 { return left * right },
		"/": func(left float64, right float64) float64 {
			if right == 0 {
				panic("Division by 0 is forbidden")
			}
			return left / right
		},
	}
)

func init() {
	hor.Register(cross, "Miko's calculator", "")
}

func evaluateValidity(line string) (bool, string) {
	if len(line) == 0 {
		return false, "Invalid empty input"
	} // edge case empty line
	if strings.Index(specialCharactersWithoutMinus, string(line[0])) != -1 ||
		strings.Index(specialCharacters, string(line[len(line)-1])) != -1 {
		return false, "Invalid equation"
	} // edge case special character at begining or end

	isPriorSpecial := false
	countParenthesis := 0

	for i := 0; i < len(line); i++ {
		if line[i] == '(' {
			countParenthesis += 1
			continue
		}
		if line[i] == ')' {
			if isPriorSpecial == true {
				return false, "Invalid equation"
			}
			countParenthesis -= 1
			isPriorSpecial = false
			continue
		}
		if strings.Index(specialCharacters, string(line[i])) != -1 {
			if isPriorSpecial && !(line[i] == '-' && i < (len(line)-1) && line[i+1] >= '0' && line[i+1] <= '9') { // if we have two subsequent operator and the second one isn't a - before a number (negative number)
				return false, "Invalid equation"
			}
			isPriorSpecial = true
			continue
		}
		if line[i] < '0' || line[i] > '9' {
			return false, "Invalid character encountered "
		}
		isPriorSpecial = false
	}

	if countParenthesis != 0 {
		return false, "Invalid equation"
	}
	return true, ""
}

func searchForPriority(priorityList string, line string) *Node {
	countParenthesis := 0
	for iPrio := 0; iPrio < len(priorityList); iPrio++ {
		for idx := 0; idx < len(line); idx++ {
			if line[idx] == '(' {
				countParenthesis += 1
			}
			if line[idx] == ')' {
				countParenthesis -= 1
			}
			if countParenthesis == 0 && line[idx] == priorityList[iPrio] &&
				!(line[idx] == '-' && (idx == 0 || strings.Index(specialCharacters, string(line[idx-1])) != -1)) { // - is not a substract sign if at the begining or after another sign
				return &Node{string(line[idx]), createTree(line[:idx]), createTree(line[idx+1:])}
			}

		}
	}
	return nil
}

// checks if the expression in line is contained in one big parenthesis
func isInOneParenthesis(line string) bool {
	if line[0] != '(' || line[len(line)-1] != ')' {
		return false
	}
	countParenthesis := 1
	for i := 1; i < len(line)-1; i++ {
		if line[i] == '(' {
			countParenthesis += 1
		}
		if line[i] == ')' {
			countParenthesis -= 1
		}
		if countParenthesis == 0 {
			return false
		}
	}
	return true
}

func createTree(line string) *Node {
	if isInOneParenthesis(line) {
		return createTree(line[1 : len(line)-1])
	}
	node := searchForPriority(lowPriority, line) // we put the lowest priority at the top of the tree, these operations will be executed last
	if node != nil {
		return node
	}
	node = searchForPriority(topPriority, line)
	if node != nil {
		return node
	}

	// if this code is reached, the only value possible in line is a number
	return &Node{line, nil, nil}
}

func readTree(tree *Node) float64 {
	operation, exists := operationMap[tree.value]

	if exists { // check if the current node is an operator
		return operation(readTree(tree.left), readTree(tree.right))
	}

	parsedValue, _ := strconv.ParseFloat(tree.value, 64)

	return parsedValue
}

// expression is the equation you want to solve (p replaces the + symbol)
// exemple: 2p4/2
func ComputeResult(expression string) string {
	valid, errString := evaluateValidity(expression)

	if !valid { // If a basic error is encountered, return the expression without the = at the end and display the same expression
		println(errString) // display error for debug
		return expression
	}
	tree := createTree(expression)
	val = readTree(tree)
	displayVal = strconv.FormatFloat(val, 'g', 6, 64)
	return displayVal
}

func removeLast(path string) string {
	lenPath := len(path)
	if lenPath > 0 {
		path = path[:lenPath-1]
	}
	return path
}

func createTable(req *realmpath.Request, query url.Values, expression string) mdtable.Table {
	line := make([]string, 0, 4)
	query.Set("expression", "")
	line = append(line, md.Link("res", req.String()))
	for _, str := range []string{"(", ")"} {
		query.Set("expression", expression+str)
		line = append(line, md.Link(str, req.String()))
	}
	query.Set("expression", removeLast(expression))
	line = append(line, md.Link("del", req.String())) // req and del are two special cases
	table := mdtable.Table{
		Headers: line,
	}
	line = []string{}
	for _, c := range "789+456-123*0.=/" {
		query.Set("expression", expression+string(c))
		line = append(line, md.Link(string(c), req.String()))
		if len(line) == 4 {
			table.Append(line)
			line = []string{}
		}
	}
	return table
}

func Render(path string) string {
	req := realmpath.Parse(path)
	query := req.Query
	expression := query.Get("expression")

	if expression == "" {
		displayVal = "0"
	} else {
		if expression[len(expression)-1] == '=' {
			expression = ComputeResult(expression[:len(expression)-1])
		} else {
			displayVal = expression
		}
	}
	out := md.H1("Calculator page")
	out += md.H3("Have you ever wanted to do maths but never actually found a calculator ?")
	out += md.H3("Do I have the realm for you...")
	out += "---------------\n"
	out += md.H2("Result: " + displayVal)

	table := createTable(req, query, expression)
	out += table.String()
	return out
}

package calculator

import "testing"

func Test_Addition(t *testing.T) {
	// Increment the value
	value := ComputeResult("1+1")
	// Verify the value is equal to 2
	if value != "2" {
		t.Fatalf("1 + 1 is not equal to 2")
	}
}

func Test_Subtraction(t *testing.T) {
	// Increment the value
	value := ComputeResult("1-1")
	// Verify the value is equal to 0
	if value != "0" {
		t.Fatalf("1 - 1 is not equal to 0")
	}
}

func Test_Multiplication(t *testing.T) {
	// Increment the value
	value := ComputeResult("1*4")
	// Verify the value is equal to 4
	if value != "4" {
		t.Fatalf("1 * 4 is not equal to 4")
	}
}

func Test_Division(t *testing.T) {
	// Increment the value
	value := ComputeResult("4/2")
	// Verify the value is equal to 2
	if value != "2" {
		t.Fatalf("4 / 2 is not equal to 2")
	}
}

func Test_AdditionDecimal(t *testing.T) {
	// Increment the value
	value := ComputeResult("1.2+1.3")
	// Verify the value is equal to 2.5
	if value != "2.5" {
		t.Fatalf("1.2 + 1.3 is not equal to 2.5")
	}
}

func Test_SubtractionDecimal(t *testing.T) {
	// Increment the value
	value := ComputeResult("1.3-1.2")
	// Verify the value is equal to 0.1
	if value != "0.1" {
		t.Fatalf("1.3 - 1.2 is not equal to 0.1")
	}
}

func Test_MultiplicationDecimal(t *testing.T) {
	// Increment the value
	value := ComputeResult("3*1.5")
	// Verify the value is equal to 4.5
	if value != "4.5" {
		t.Fatalf("3 * 1.5 is not equal to 4.5")
	}
}

func Test_DivisionDecimal(t *testing.T) {
