}

type testCommandSleep struct {
	dur time.Duration
}

func (tc *testCommandSleep) Run(t *testing.T, bufs map[string]string) {
	os.Sleep(tc.dur)
}

type testChecker struct {
	fn    func(t *testing.T, bufs map[string]string, tc *testChecker)
	tf    func(*testing.T, string, ...interface{})
	bufp  string
	preds []string
}

func (*testChecker) Checker() {}
func (tc *testChecker) Run(t *testing.T, bufs map[string]string) {
	tc.fn(t, bufs, tc)
}

func parseCommandTest(t *testing.T, command string) (funcs []testCommandRunner, testName string) {
	lines := strings.Split(command, "\n")
	atoi := func(s string) int {
		n, err := strconv.Atoi(s)
		checkErr(err)
		return n
	}
	// used to detect whether to auto-add a panic checker
	var hasPanicChecker bool
	panicChecker := func(lineNum int, testName string) testCommandRunner {
		return testCommandChecker{testCommandFunc(
			func(t *testing.T, bufs map[string]string) {
				if bufs["panic"] != "" {
					t.Fatalf("%s:%d: buffer \"panic\" is not empty (%q)", testName, lineNum, bufs["panic"])
				}
			},
		)}
	}

	for lineNum, line := range lines {
		flds := strings.Fields(line)
		if len(flds) == 0 {
			continue
		}
		command, checker := flds, ([]string)(nil)
		for idx, fld := range flds {
			if strings.HasPrefix(fld, "#") {
				command, checker = flds[:idx], flds[idx:]
				break
			}
		}
		var cmd string
		if len(command) > 0 {
			cmd = command[0]

			// there is a new command; if hasPanicChecker == false,
			// it means the previous command did not have a panic checker.
			// add it.
			if !hasPanicChecker && len(funcs) > 0 {
				// no lineNum+1 because it was the previous line
				funcs = append(funcs, panicChecker(lineNum, testName))
			}
		}
		switch cmd {
		case "": // move on
		case "newgame":
			w, b := white, black
			var seconds, incr int
			switch len(command) {
			case 1:
			case 5:
				incr = atoi(command[4])
				fallthrough
			case 4:
				seconds = atoi(command[3])
				fallthrough
			case 3:
				w, b = std.Address("g1"+command[1]), std.Address("g1"+command[2])
			default:
				panic("invalid newgame command " + line)
			}
			funcs = append(funcs,
				&testCommandNewGame{w, b, seconds, incr},
			)
		case "move":
			if len(command) != 3 {
				panic("invalid move command " + line)
			}
			if len(command[2]) < 4 || len(command[2]) > 5 {
				panic("invalid lan move " + command[2])
			}
			from, to := command[2][:2], command[2][2:4]
			var promotion chess.Piece
			if len(command[2]) == 5 {
				promotion = chess.PieceFromChar(command[2][4])
				if promotion == chess.PieceEmpty {
					panic("invalid piece for promotion: " + string(command[2][4]))
				}
			}
			funcs = append(funcs, &testCommandMove{
				addr:      std.Address("g1" + command[1]),
				from:      from,
				to:        to,
				promotion: promotion,
			})
		case "abort":
			funcs = append(funcs, newTestCommandColorID(Abort, "abort", command[1]))
		case "draw":
			funcs = append(funcs, newTestCommandColorID(Draw, "draw", command[1]))
		case "drawoffer":
			funcs = append(funcs, newTestCommandColorID(DrawOffer, "drawoffer", command[1]))
		case "timeout":
			funcs = append(funcs, newTestCommandColorID(ClaimTimeout, "timeout", command[1]))
		case "resign":
			funcs = append(funcs, newTestCommandColorID(Resign, "resign", command[1]))
		case "game":
			if len(command) > 2 {
				panic("invalid game command " + line)
			}
			tc := &testCommandGame{}
			if len(command) == 2 {
				tc.idWanted = command[1]
			}
			funcs = append(funcs, tc)
		case "player":
			if len(command) != 2 {
				panic("invalid player command " + line)
			}
			funcs = append(funcs, &testCommandPlayer{"g1" + command[1]})
		case "name":
			testName = strings.Join(command[1:], " ")
		case "copy":
			if len(command) > 3 || len(command) < 2 {
				panic("invalid copy command " + line)
			}
			tc := &testCommandCopy{dst: command[1], src: "result"}
			if len(command) == 3 {
				tc.src = command[2]
			}
			funcs = append(funcs, tc)
		case "sleep":
			if len(command) != 2 {
				panic("invalid sleep command " + line)
			}
			funcs = append(funcs, &testCommandSleep{
				time.Duration(atoi(command[1])) * time.Second,
			})
		default:
			panic("invalid command " + cmd)
		}

		if len(checker) == 0 {
			continue
		}
		if len(checker) == 1 {
			panic("no checker specified " + line)
		}

		bufp := checker[0]
		useFatal := false
		if len(bufp) > 1 && bufp[1] == '!' {
			bufp = bufp[2:]
			useFatal = true
		} else {
			bufp = bufp[1:]
		}
		if bufp == "" {
			bufp = "result"
		}
		if bufp == "panic" && !hasPanicChecker {
			hasPanicChecker = true
		}
		tf := func(ln int, testName string, useFatal bool) func(*testing.T, string, ...interface{}) {
			return func(t *testing.T, s string, v ...interface{}) {
				fn := t.Errorf
				if useFatal {
					fn = t.Fatalf
				}
				fn("%s:%d: "+s, append([]interface{}{testName, ln}, v...)...)
			}
		}(lineNum+1, testName, useFatal)

		switch checker[1] {
		case "empty":
			if len(checker) != 2 {
				panic("invalid empty checker " + line)
			}
			funcs = append(funcs, &testChecker{
				fn: func(t *testing.T, bufs map[string]string, tc *testChecker) {
					if bufs[tc.bufp] != "" {
						tc.tf(t, "buffer %q is not empty (%v)", tc.bufp, bufs[tc.bufp])
					}
				},
				tf:   tf,
				bufp: bufp,
			})
		case "equal":
			pred := strings.Join(checker[2:], " ")
			funcs = append(funcs, &testChecker{
				fn: func(t *testing.T, bufs map[string]string, tc *testChecker) {
					exp := tc.preds[0]
					if exp[0] == '#' {
						exp = bufs[exp[1:]]
					}
					if bufs[tc.bufp] != exp {
						tc.tf(t, "buffer %q: want %v got %v", tc.bufp, exp, bufs[tc.bufp])
					}
				},
				tf:    tf,
				bufp:  bufp,
				preds: []string{pred},
			})
		case "contains":
			preds := checker[2:]
			if len(preds) == 0 {
				break
			}
			funcs = append(funcs, &testChecker{
				fn: func(t *testing.T, bufs map[string]string, tc *testChecker) {
					for _, pred := range tc.preds {
						if !strings.Contains(bufs[tc.bufp], pred) {
							tc.tf(t, "buffer %q: %v does not contain %v", tc.bufp, bufs[tc.bufp], pred)
						}
					}
				},
				tf:    tf,
				bufp:  bufp,
				preds: preds,
			})
		case "containssp":
			pred := strings.Join(checker[2:], " ")
			if pred == "" {
				panic("invalid contanssp checker " + line)
			}
			funcs = append(funcs, &testChecker{
				fn: func(t *testing.T, bufs map[string]string, tc *testChecker) {
					if !strings.Contains(bufs[tc.bufp], tc.preds[0]) {
						tc.tf(t, "buffer %q: %v does not contain %v", tc.bufp, bufs[tc.bufp], tc.preds[0])
					}
				},
				tf:    tf,
				bufp:  bufp,
				preds: []string{pred},
			})
		default:
			panic("invalid checker " + checker[1])
		}
	}
	if !hasPanicChecker {
		funcs = append(funcs, panicChecker(len(lines), testName))
	}
	return
}

func runCommandTest(t *testing.T, command string) {
	funcs, testName := parseCommandTest(t, command)

	t.Run(testName, func(t *testing.T) {
		cleanup()
		bufs := make(map[string]string, 3)
		for _, f := range funcs {
			if _, ok := f.(interface{ Checker() }); ok {
				f.Run(t, bufs)
			} else {
				catchPanic(f, t, bufs)
			}
		}
	})
}

func catchPanic(tc testCommandRunner, t *testing.T, bufs map[string]string) {
	// XXX: should prefer testing.Recover, but see: https://github.com/gnolang/gno/issues/1650
	e := revive(func() { tc.Run(t, bufs) })
	if e == nil {
		bufs["panic"] = ""
		return
	}
	bufs["result"] = ""
	bufs["panic"] = fmt.Sprint(e)
}

// this file concerns mostly with "discovery"; ie. finding information
// about a user's chess playing and previous games

package chess

import (
	"bytes"
	"sort"
	"std"
	"strconv"
	"strings"

	"gno.land/p/morgan/chess/glicko2"
	"gno.land/p/nt/avl"
	"gno.land/r/sys/users"
)

type Category byte

const (
	// blanks are reserved for future use (bullet and classic)
	_ Category = iota
	Blitz
	Rapid
	_
	Correspondence
	CategoryMax
)

var categoryString = [CategoryMax]string{
	Blitz:          "blitz",
	Rapid:          "rapid",
	Correspondence: "correspondence",
}

var categoryList = [...]Category{Blitz, Rapid, Correspondence}

func (c Category) String() string {
	if c > CategoryMax || categoryString[c] == "" {
		panic("invalid category")
	}
	return categoryString[c]
}

func CategoryFromString(s string) Category {
	for i, cs := range categoryString {
		if s == cs {
			return Category(i)
		}
	}
	panic("invalid category")
}

func (tc *TimeControl) Category() Category {
	// https://lichess.org/faq#time-controls
	if tc == nil {
		return Correspondence
	}

	totalTime := tc.Seconds + tc.Increment*40
	switch {
	case tc.Seconds <= 0 || tc.Increment < 0:
		// should not happen
		return Correspondence
	case totalTime < 60*8:
		return Blitz
	default:
		return Rapid
	}
}

// realm state
var (
	playerStore   avl.Tree // std.Address -> *Player
	leaderboard   [CategoryMax]leaderboardType
	playerRatings [CategoryMax][]*glicko2.PlayerRating
)

func GetPlayer(player string) string {
	addr := parsePlayer(player)
	v, ok := playerStore.Get(addr.String())
	if !ok {
		panic("player not found")
	}
	b, err := v.(*Player).MarshalJSON()
	checkErr(err)
	return string(b)
}

// Player contains game-related player information.
type Player struct {
	Address      std.Address
	CategoryInfo [CategoryMax]CategoryInfo
}

type CategoryInfo struct {
	Wins, Losses, Draws int
	*glicko2.PlayerRating
}

// Score for determining leaderboards.
func (p Player) Score(cat Category) float64 {
	return p.CategoryInfo[cat].Rating
}

// Leaderboard position, 0 indexed.
// Dynamically calculated to avoid having to shift positions when LB changes.
func (p Player) LeaderboardPosition(cat Category) int {
	pos, ok := leaderboard[cat].find(p.Score(cat), p.Address)
	if !ok {
		return -1
	}
	return pos
}

func (g *Game) saveResult() {
	w, b := getPlayer(g.White), getPlayer(g.Black)

	cat := g.Time.Category()

	// Get numeric result for glicko2.
	var result float64
	switch g.Winner {
	case WinnerWhite:
		w.CategoryInfo[cat].Wins++
		b.CategoryInfo[cat].Losses++
		result = 1
	case WinnerBlack:
		w.CategoryInfo[cat].Losses++
		b.CategoryInfo[cat].Wins++
		result = 0
	case WinnerDraw:
		w.CategoryInfo[cat].Draws++
		b.CategoryInfo[cat].Draws++
		result = 0.5
	default:
		return // TODO: maybe panic
	}

	// Call glicko 2 rating calculator.
	owr, obr := w.CategoryInfo[cat].Rating, b.CategoryInfo[cat].Rating
	glicko2.UpdateRatings(playerRatings[cat], []glicko2.RatingScore{{
		White: g.White,
		Black: g.Black,
		Score: result,
	}})

	// Save in playerStore.
	playerStore.Set(w.Address.String(), w)
	playerStore.Set(b.Address.String(), b)
	leaderboard[cat], _ = leaderboard[cat].push(g.White, owr, w.CategoryInfo[cat].Rating)
	leaderboard[cat], _ = leaderboard[cat].push(g.Black, obr, b.CategoryInfo[cat].Rating)
}

func getPlayer(addr std.Address) *Player {
	praw, ok := playerStore.Get(addr.String())
	if ok {
		return praw.(*Player)
	}
	p := new(Player)
	p.Address = addr
	for _, cat := range categoryList {
		pr := glicko2.NewPlayerRating(addr)
		p.CategoryInfo[cat] = CategoryInfo{
			PlayerRating: pr,
		}
		playerRatings[cat] = append(playerRatings[cat], pr)
	}
	playerStore.Set(addr.String(), p)
	return p
}

type lbEntry struct {
	addr  std.Address
	score float64
}

type leaderboardType []lbEntry

// find performs binary search on leaderboard to find the first
// position where score appears, or anything lesser than it.
// Additionally, if addr is given, it finds the position where the given address appears.
// The second return parameter returns whether the address was found.
//
// The index will be 0 if the score is higher than any other on the leaderboard,
// and len(leaderboards) if it is lower than any other.
func (lb leaderboardType) find(score float64, addr std.Address) (int, bool) {
	i := sort.Search(len(lb), func(i int) bool {
		return lb[i].score <= score
	})

	if addr == "" || i == len(lb) {
		return i, false
	}

	for j := 0; i+j < len(lb) && lb[i+j].score == score; j++ {
		if lb[i+j].addr == addr {
			return i + j, true
		}
	}

	return i, false
}

// push adds or modifies the player's position in the leaderboard.
// the new leaderboard, and the new position of the player in the leaderboard is returned (0-indexed)
func (lb leaderboardType) push(player std.Address, oldScore, newScore float64) (leaderboardType, int) {
	// determine where the player is, currently
	oldPos, found := lb.find(oldScore, player)
	if found && (oldScore == newScore) {
		return lb, oldPos
	}

	// determine where to place the player next.
	newPos, _ := lb.find(newScore, "")

	var n leaderboardType
	switch {
	case !found:
		n = append(leaderboardType{}, lb[:newPos]...)
		n = append(n, lbEntry{player, newScore})
		n = append(n, lb[newPos:]...)

	case oldPos == newPos:
		n = lb
		n[newPos] = lbEntry{player, newScore}
	case oldPos > newPos:
		n = append(leaderboardType{}, lb[:newPos]...)
		n = append(n, lbEntry{player, newScore})
		n = append(n, lb[newPos:oldPos]...)
		n = append(n, lb[oldPos+1:]...)
	default: // oldPos < newPos
		n = append(leaderboardType{}, lb[:oldPos]...)
		n = append(n, lb[oldPos+1:newPos]...)
		n = append(n, lbEntry{player, newScore})
		n = append(n, lb[newPos:]...)
	}
	return n, newPos
}

// Leaderboard returns a list of all users, ordered by their position in the leaderboard.
// category is one of blitz, rapid or correspondence.
func Leaderboard(category string) string {
	cat := CategoryFromString(category)
	var buf bytes.Buffer
	buf.WriteByte('[')
	for idx, entry := range leaderboard[cat] {
		p, _ := playerStore.Get(entry.addr.String())
		d, err := p.(*Player).MarshalJSON()
		checkErr(err)
		buf.Write(d)
		if idx != len(leaderboard[cat])-1 {
			buf.WriteByte(',')
		}
	}
	buf.WriteByte(']')
	return buf.String()
}

// ListGames provides game listing functionality, with filter-based search functionality.
//
// available filters:
//
//	player:<player> white:<player> black:<player> finished:bool
//	limit:int id<cmp>int sort:asc/desc
//	<cmp>: '<' or '>'
//	<player>: either a bech32 address, "@user" (r/demo/users), or "caller"
func ListGames(filters string) string {
	ft := parseFilters(filters)
	results := make([]*Game, 0, ft.limit)
	cb := func(g *Game) (stop bool) {
		if !ft.valid(g) {
			return false
		}
		results = append(results, g)
		return len(results) >= ft.limit
	}

	// iterate over user2games array if we have one;
	// if we don't, iterate over games.
	if ft.u2gAddr != "" {
		v, ok := user2Games.Get(ft.u2gAddr.String())
		if !ok {
			return "[]"
		}
		games := v.([]*Game)
		if ft.reverse {
			for i := len(games) - 1; i >= 0; i-- {
				if cb(games[i]) {
					break
				}
			}
		} else {
			for _, game := range games {
				if cb(game) {
					break
				}
			}
		}
	} else {
		fn := gameStore.Iterate
		if ft.reverse {
			fn = gameStore.ReverseIterate
		}
		fn(ft.minID, ft.maxID, func(_ string, v interface{}) bool {
			return cb(v.(*Game))
		})
	}

	// fast path: no results
	if len(results) == 0 {
		return "[]"
	}

	// encode json
	var buf bytes.Buffer
	buf.WriteByte('[')
	for idx, g := range results {
		buf.WriteString(g.json())
		if idx != len(results)-1 {
			buf.WriteByte(',')
		}
	}
	buf.WriteByte(']')

	return buf.String()
}

type listGamesFilters struct {
	filters []func(*Game) bool
	u2gAddr std.Address
	maxID   string
	minID   string
	limit   int
	reverse bool
}

func (l *listGamesFilters) valid(game *Game) bool {
	for _, filt := range l.filters {
		if !filt(game) {
			return false
		}
	}
	return true
}

func parseFilters(filters string) (r listGamesFilters) {
	// default to desc order
	r.reverse = true

	parts := strings.Fields(filters)
	for _, part := range parts {
		idx := strings.IndexAny(part, ":<>")
		if idx < 0 {
			panic("invalid filter: " + part)
		}
		filt, pred := part[:idx+1], part[idx+1:]
		switch filt {
		case "player:":
			a := parsePlayer(pred)
			r.filters = append(r.filters, func(g *Game) bool { return g.White == a || g.Black == a })
			if r.u2gAddr == "" {
				r.u2gAddr = a
			}
		case "white:":
			a := parsePlayer(pred)
			r.filters = append(r.filters, func(g *Game) bool { return g.White == a })
			if r.u2gAddr == "" {
				r.u2gAddr = a
			}
		case "black:":
			a := parsePlayer(pred)
			r.filters = append(r.filters, func(g *Game) bool { return g.Black == a })
			if r.u2gAddr == "" {
				r.u2gAddr = a
			}
		case "finished:":
			b := parseBool(pred)
			r.filters = append(r.filters, func(g *Game) bool { return g.State.IsFinished() == b })
		case "id<":
			r.maxID = pred
		case "id>":
			r.minID = pred
		case "limit:":
			n, err := strconv.Atoi(pred)
			checkErr(err)
			r.limit = n
		case "sort:":
			r.reverse = pred == "desc"
		default:
			panic("invalid filter: " + filt)
		}
	}
	return
}

func parseBool(s string) bool {
	switch s {
	case "true", "True", "TRUE", "1":
		return true
	case "false", "False", "FALSE", "0":
		return false
	}
	panic("invalid bool " + s)
}

func parsePlayer(s string) std.Address {
	switch {
	case s == "":
		panic("invalid address/user")
	case s == "caller":
		return std.PreviousRealm().Address()
	case s[0] == '@':
		u, _ := users.ResolveName(s[1:])
		if u == nil {
			panic("user not found: " + s[1:])
		}
		return u.Addr()
	case s[0] == 'g':
		return std.Address(s)
	default:
		panic("invalid address/user: " + s)
	}
}

package chess

import (
	"bytes"
	"errors"
	"std"
	"strconv"
	"time"

	"gno.land/p/morgan/chess"
	"gno.land/p/morgan/chess/glicko2"
	"gno.land/r/sys/users"
)

// This file contains a bunch of JSON marshalers.
// These should disappear eventually!
// https://github.com/gnolang/gno/issues/1655

func (g Game) MarshalJSON() (retBytes []byte, err error) {
	var b bytes.Buffer
	b.WriteByte('{')

	nilAddr := func(na *std.Address) string {
		if na == nil {
			return `null`
		}
		return `"` + na.String() + `"`
	}
	mjson := func(s string, val interface{ MarshalJSON() ([]byte, error) }, comma bool) {
		if err != nil {
			return
		}
		var res []byte
		res, err = val.MarshalJSON()
		if err != nil {
			return
		}
		b.WriteString(`"` + s + `":`)
		b.Write(res)
		if comma {
			b.WriteByte(',')
		}
	}

	b.WriteString(`"id":"` + g.ID + `",`)
	b.WriteString(`"white":"` + g.White.String() + `",`)
	b.WriteString(`"black":"` + g.Black.String() + `",`)

	mjson("position", jsonPosition{g.Position}, true)
	mjson("state", g.State, true)
	mjson("winner", g.Winner, true)
	if err != nil {
		return
	}

	b.WriteString(`"creator":"` + g.Creator.String() + `",`)
	b.WriteString(`"created_at":"` + g.CreatedAt.Format(time.RFC3339) + `",`)
	b.WriteString(`"draw_offerer":` + nilAddr(g.DrawOfferer) + ",")
	b.WriteString(`"concluder":` + nilAddr(g.Concluder) + ",")

	mjson("time", g.Time, false)
	if err != nil {
		return
	}

	b.WriteByte('}')
	return b.Bytes(), nil
}

type jsonPosition struct {
	chess.Position
}

func (p jsonPosition) MarshalJSON() ([]byte, error) {
	var b bytes.Buffer
	b.WriteByte('{')

	bfen := p.EncodeFEN()
	b.WriteString(`"fen":"` + bfen + `",`)

	b.WriteString(`"moves":[`)

	for idx, m := range p.Moves {
		b.WriteString(`"` + m.String() + `"`)
		if idx != len(p.Moves)-1 {
			b.WriteByte(',')
		}
	}

	b.WriteByte(']')
	b.WriteByte('}')
	return b.Bytes(), nil
}

func (w Winner) MarshalJSON() ([]byte, error) {
	if n := int(w); n < len(winnerString) {
		return []byte(`"` + winnerString[n] + `"`), nil
	}
	return nil, errors.New("invalid winner value")
}

func (g GameState) MarshalJSON() ([]byte, error) {
	if int(g) >= len(gameStatesSnake) {
		return nil, errors.New("invalid game state")
	}
	return []byte(`"` + gameStatesSnake[g] + `"`), nil
}

func (tc *TimeControl) MarshalJSON() ([]byte, error) {
	if tc == nil {
		return []byte("null"), nil
	}
	var buf bytes.Buffer

	buf.WriteByte('{')
	buf.WriteString(`"seconds":` + strconv.Itoa(tc.Seconds) + `,`)
	buf.WriteString(`"increment":` + strconv.Itoa(tc.Increment) + `,`)
	buf.WriteString(`"started_at":"` + tc.StartedAt.Format(time.RFC3339) + `",`)

	buf.WriteString(`"move_timestamps":[`)
	for idx, mt := range tc.MoveTimestamps {
		buf.WriteString(`"` + mt.Format(time.RFC3339) + `"`)
		if idx != len(tc.MoveTimestamps)-1 {
			buf.WriteByte(',')
		}
	}
	buf.WriteString("],")

	buf.WriteString(`"white_time":` + strconv.FormatInt(tc.WhiteTime.Milliseconds(), 10) + ",")
	buf.WriteString(`"black_time":` + strconv.FormatInt(tc.BlackTime.Milliseconds(), 10))
	buf.WriteByte('}')

	return buf.Bytes(), nil
}

func (p Player) MarshalJSON() ([]byte, error) {
	u := users.ResolveAddress(p.Address)

	var buf bytes.Buffer
	buf.WriteByte('{')

	buf.WriteString(`"address":"` + p.Address.String() + `",`)
	if u == nil {
		buf.WriteString(`"username":"",`)
	} else {
		buf.WriteString(`"username":"` + u.Name() + `",`)
	}

	for idx, cat := range categoryList {
		stat := p.CategoryInfo[cat]
		buf.WriteString(`"` + cat.String() + `":{`)
		buf.WriteString(`"wins":` + strconv.Itoa(stat.Wins) + ",")
		buf.WriteString(`"losses":` + strconv.Itoa(stat.Losses) + ",")
		buf.WriteString(`"draws":` + strconv.Itoa(stat.Draws) + ",")
		buf.WriteString(`"rating":`)
		if res, err := (jsonPlayerRating{stat.PlayerRating}).MarshalJSON(); err != nil {
			return nil, err
		} else {
			buf.Write(res)
		}
		buf.WriteByte(',')
		buf.WriteString(`"position":` + strconv.Itoa(p.LeaderboardPosition(cat)))
		buf.WriteByte('}')
		if idx != len(categoryList)-1 {
			buf.WriteByte(',')
		}
	}

	buf.WriteByte('}')
	return buf.Bytes(), nil
}

type jsonPlayerRating struct{ *glicko2.PlayerRating }

func (p jsonPlayerRating) MarshalJSON() ([]byte, error) {
	var buf bytes.Buffer
	buf.WriteByte('{')
	buf.WriteString(`"rating":` + strconv.FormatFloat(p.Rating, 'f', 5, 64) + `,`)
	buf.WriteString(`"deviation":` + strconv.FormatFloat(p.RatingDeviation, 'f', 5, 64) + `,`)
	buf.WriteString(`"volatility":` + strconv.FormatFloat(p.RatingVolatility, 'f', 5, 64))
	buf.WriteByte('}')
	return buf.Bytes(), nil
}

package chess

import (
	"std"
	"time"

	"gno.land/p/nt/avl"
)

type lobbyPlayer struct {
	joinedAt time.Time
	seenAt   time.Time
	player   *Player
}

func (l lobbyPlayer) r(cat Category) float64 { return l.player.CategoryInfo[cat].Rating }

// returns whether the rating r is within l's current "acceptable" range.
func (l lobbyPlayer) acceptRating(cat Category, r float64) bool {
	delta := int(time.Since(l.joinedAt) / time.Second)
	if delta >= 10 {
		return true
	}

	halfsize := bracketSize[delta] / 2
	rat := l.r(cat)
	return r >= (rat-halfsize) && r <= (rat+halfsize)
}

type tcLobby byte

// time control lobby divisions. N+M -> tcLobbyNpM
const (
	tcLobby5p0 tcLobby = iota
	tcLobby10p5
	tcLobbyMax
)

func (tc tcLobby) Category() Category {
	switch tc {
	case tcLobby5p0:
		return Blitz
	default:
		return Rapid
	}
}

func (tc tcLobby) Time() (secs, incr int) {
	switch tc {
	case tcLobby5p0:
		return 60 * 5, 0
	case tcLobby10p5:
		return 60 * 10, 5
	default:
		panic("invalid tc value")
	}
}

var (
	lobby            [tcLobbyMax][]lobbyPlayer
	lobbyPlayer2Game avl.Tree // player addr -> *Game. set after a user is matched; reset when joining again.
)

func LobbyJoin(cur realm, seconds, increment int) {
	assertOriginCall()
	var tc tcLobby
	switch {
	case seconds == (60*5) && increment == 0:
		tc = tcLobby5p0
	case seconds == (60*10) && increment == 5:
		tc = tcLobby10p5
	default:
		panic("can only use time controls 5+0 or 10+5")
	}

	// Ensure that user's previous games are finished (or timed out).
	caller := std.PreviousRealm().Address()

	games := getUserGames(caller)
	// XXX: temporary to avoid ever prohibiting a user from joining the lobby.
	// when possible, change back to assertGamesFinished(games)
	for _, g := range games {
		if !g.State.IsFinished() {
			if err := resign(g); err != nil {
				panic("internal error (could not resign game " + g.ID + "): " + err.Error())
			}
		}
	}
	assertUserNotInLobby(caller)

	// remove caller from lobbyPlayer2Game, so LobbyGameFound
	// returns the right value.
	lobbyPlayer2Game.Remove(caller.String())

	now := time.Now()
	lobby[tc] = append(lobby[tc], lobbyPlayer{joinedAt: now, seenAt: now, player: getPlayer(caller)})
	refreshLobby(tc)
}

func assertUserNotInLobby(caller std.Address) {
	for _, sublob := range lobby {
		for _, pl := range sublob {
			if pl.player.Address == caller {
				panic("you are already in the lobby")
			}
		}
	}
}

// refreshLobby serves to run through the lobby, kick timed out users, and see if any users
// can be matched with the current user.
func refreshLobby(tc tcLobby) {
	callerAddr := std.PreviousRealm().Address()
	now := time.Now()
	for idx, player := range lobby[tc] {
		if player.player.Address == callerAddr {
			// mark player as seen now
			lobby[tc][idx].seenAt = now
			break
		}
	}

	// lobby housekeeping: kick any player that hasn't contacted us for the
	// past 30 seconds.
	// do this BEFORE matching the caller, as we want to give them someone who
	// is seemingly active in the lobby.
	for i := 0; i < len(lobby[tc]); i++ {
		if now.Sub(lobby[tc][i].seenAt) >= time.Second*30 {
			newLobby := append([]lobbyPlayer{}, lobby[tc][:i]...)
			lobby[tc] = append(newLobby, lobby[tc][i+1:]...)
			i--
		}
	}

	// determine sub lobby
	sublob := lobby[tc]

	callerPos := -1
	var caller lobbyPlayer
	for idx, player := range sublob {
		if player.player.Address == callerAddr {
			callerPos = idx
			caller = player
			break
		}
	}
	// caller is not involved in lobby, or lobby only contains the player
	if callerPos < 0 || len(sublob) < 2 {
		return
	}

	cat := tc.Category()
	callerRating := caller.r(cat)
	callerForce := now.Sub(caller.joinedAt) >= time.Second*10

	for i, player := range sublob {
		if i == callerPos {
			continue
		}
		// force if either the caller or the player have been waiting for more than 10s.
		force := callerForce || (now.Sub(player.joinedAt) >= time.Second*10)
		// find player whose rating falls in each other's range.
		if force || (caller.acceptRating(cat, player.r(cat)) &&
			player.acceptRating(cat, callerRating)) {
			lobbyMatch(tc, callerPos, i)
			return
		}
	}
}

func lobbyMatch(tc tcLobby, p1, p2 int) {
	// Get the two players, create a new game with them.
	secs, incr := tc.Time()
	a1, a2 := lobby[tc][p1].player.Address, lobby[tc][p2].player.Address

	game := newGame(a1, a2, secs, incr)

	// remove p1 and p2 from lobby
	if p1 > p2 {
		p1, p2 = p2, p1
	}
	nl := append([]lobbyPlayer{}, lobby[tc][:p1]...)
	nl = append(nl, lobby[tc][p1+1:p2]...)
	nl = append(nl, lobby[tc][p2+1:]...)
	lobby[tc] = nl

	// add to lobbyPlayer2Game
	lobbyPlayer2Game.Set(a1.String(), game)
	lobbyPlayer2Game.Set(a2.String(), game)
}

/*
generated by python code:

	for i in range(0,10):
		print((i+1)**(3.694692926)+49, ',')

rationale: give brackets in an exponential range between
50 and 5000, dividing it into 10 steps.
"magic constant" obtained solving for in c in the equation:

	5000=(x+1)^c+49 (x = steps, 10 in our case)

which comes out to be ln(delta)/ln(steps), delta = 5000-49, steps = 10.
*/
var bracketSize = [...]float64{
	50.0,
	61.9483191543645,
	106.91839582826664,
	216.65896892328266,
	431.3662312611604,
	798.94587409321,
	1374.4939512498888,
	2219.9018387103433,
	3403.5405753197747,
	5000,
}

func LobbyGameFound(cur realm) string {
	refreshLobby(tcLobby5p0)
	refreshLobby(tcLobby10p5)

	val, ok := lobbyPlayer2Game.Get(std.PreviousRealm().Address().String())
	if !ok {
		return "null"
	}
	return val.(*Game).json()
}

func LobbyQuit(cur realm) {
	caller := std.PreviousRealm().Address()
	for tc, sublob := range lobby {
		for i, pl := range sublob {
			if pl.player.Address == caller {
				newLobby := append([]lobbyPlayer{}, sublob[:i]...)
				newLobby = append(newLobby, sublob[i+1:]...)
				lobby[tc] = newLobby
				lobbyPlayer2Game.Remove(caller.String())
				return
			}
		}
	}

	panic("you are not in the lobby")
}

