package chess

import (
	"time"
)

// TimeControl keeps track of time control information for the game.
type TimeControl struct {
	Seconds   int
	Increment int

	StartedAt      time.Time
	MoveTimestamps []time.Time
	WhiteTime      time.Duration
	BlackTime      time.Duration
}

func NewTimeControl(seconds, incr int) *TimeControl {
	if seconds <= 0 {
		return nil
	}
	return &TimeControl{
		Seconds:   seconds,
		Increment: incr,

		StartedAt: time.Now(),
		WhiteTime: time.Duration(seconds) * time.Second,
		BlackTime: time.Duration(seconds) * time.Second,
	}
}

// AddMove records that at the current time, a new move was added.
func (tc *TimeControl) AddMove() (valid bool) {
	nd, v := tc.timedOut()
	if v {
		return false
	}
	if len(tc.MoveTimestamps)&1 == 0 {
		tc.WhiteTime = nd
	} else {
		tc.BlackTime = nd
	}
	tc.MoveTimestamps = append(tc.MoveTimestamps, time.Now())
	return true
}

func (tc *TimeControl) TimedOut() bool {
	_, v := tc.timedOut()
	return v
}

func (tc *TimeControl) timedOut() (time.Duration, bool) {
	mts := tc.MoveTimestamps

	// First move for each player: they both have up to 30 seconds.
	switch len(mts) {
	case 0:
		delta := time.Since(tc.StartedAt)
		return tc.WhiteTime, delta > time.Second*30
	case 1:
		delta := time.Since(mts[0])
		return tc.BlackTime, delta > time.Second*30
	}

	// Determine color. Determine time since last move. Try subtracting from
	// color's time. If >= 0, good. If < 0, timeout.
	delta := time.Since(mts[len(mts)-1])

	if len(mts)&1 == 0 { // white
		nt := tc.WhiteTime - delta
		return nt + tc.incr(), nt < 0
	}

	nt := tc.BlackTime - delta
	return nt + tc.incr(), nt < 0
}

func (tc *TimeControl) incr() time.Duration {
	// there is always at least a one second increment, to account for
	// block time and the delay between user making a move and tx happening
	return time.Second + time.Duration(tc.Increment)*time.Second
}

package guestbook

import (
	"gno.land/p/nt/ownable"
	"gno.land/p/nt/seqid"
)

var owner = ownable.NewWithOrigin()

// AdminDelete removes the guestbook message with the given ID.
// The user will still be marked as having submitted a message, so they
// won't be able to re-submit a new message.
func AdminDelete(cur realm, signatureID string) {
	owner.AssertOwnedByPrevious()
	id, err := seqid.FromString(signatureID)
	if err != nil {
		panic(err)
	}
	idb := id.Binary()
	if !guestbook.Has(idb) {
		panic("signature does not exist")
	}
	guestbook.Remove(idb)
}

// Realm guestbook contains an implementation of a simple guestbook.
// Come and sign yourself up!
package guestbook

import (
	"std"
	"strconv"
	"strings"
	"time"
	"unicode"

	"gno.land/p/nt/avl"
	"gno.land/p/nt/seqid"
)

// Signature is a single entry in the guestbook.
type Signature struct {
	Message string
	Author  std.Address
	Time    time.Time
}

const (
	maxMessageLength = 140
	maxPerPage       = 25
)

var (
	signatureID seqid.ID
	guestbook   avl.Tree // id -> Signature
	hasSigned   avl.Tree // address -> struct{}
)

func init(cur realm) {
	Sign(cur, "You reached the end of the guestbook!")
}

const (
	errNotAUser                  = "this guestbook can only be signed by users"
	errAlreadySigned             = "you already signed the guestbook!"
	errInvalidCharacterInMessage = "invalid character in message"
)

// Sign signs the guestbook, with the specified message.
func Sign(cur realm, message string) {
	prev := std.PreviousRealm()
	switch {
	case !prev.IsUser():
		panic(errNotAUser)
	case hasSigned.Has(prev.Address().String()):
		panic(errAlreadySigned)
	}
	message = validateMessage(message)

	guestbook.Set(signatureID.Next().Binary(), Signature{
		Message: message,
		Author:  prev.Address(),
		// NOTE: time.Now() will yield the "block time", which is deterministic.
		Time: time.Now(),
	})
	hasSigned.Set(prev.Address().String(), struct{}{})
}

func validateMessage(msg string) string {
	if len(msg) > maxMessageLength {
		panic("Keep it brief! (max " + strconv.Itoa(maxMessageLength) + " bytes!)")
	}
	out := ""
	for _, ch := range msg {
		switch {
		case unicode.IsLetter(ch),
			unicode.IsNumber(ch),
			unicode.IsSpace(ch),
			unicode.IsPunct(ch):
			out += string(ch)
		default:
			panic(errInvalidCharacterInMessage)
		}
	}
	return out
}

func Render(maxID string) string {
	var bld strings.Builder

	bld.WriteString("# Guestbook ð\n\n[Come sign the guestbook!](./guestbook$help&func=Sign)\n\n---\n\n")

	var maxIDBinary string
	if maxID != "" {
		mid, err := seqid.FromString(maxID)
		if err != nil {
			panic(err)
		}

		// AVL iteration is exclusive, so we need to decrease the ID value to get the "true" maximum.
		mid--
		maxIDBinary = mid.Binary()
	}

	var lastID seqid.ID
	var printed int
	guestbook.ReverseIterate("", maxIDBinary, func(key string, val any) bool {
		sig := val.(Signature)
		message := strings.ReplaceAll(sig.Message, "\n", "\n> ")
		bld.WriteString("> " + message + "\n>\n")
		idValue, ok := seqid.FromBinary(key)
		if !ok {
			panic("invalid seqid id")
		}

		bld.WriteString("> _Written by " + sig.Author.String() + " at " + sig.Time.Format(time.DateTime) + "_ (#" + idValue.String() + ")\n\n---\n\n")
		lastID = idValue

		printed++
		// stop after exceeding limit
		return printed >= maxPerPage
	})

	if printed == 0 {
		bld.WriteString("No messages!")
	} else if printed >= maxPerPage {
		bld.WriteString("<p style='text-align:right'><a href='./guestbook:" + lastID.String() + "'>Next page</a></p>")
	}

	return bld.String()
}

