		if len(project.Sponsors) > 0 {
			sb.WriteString(ufmt.Sprintf("#### %s Sponsors\n", project.Name))
			for _, sponsor := range project.Sponsors {
				sb.WriteString(ufmt.Sprintf("- %s: %s\n", sponsor.Address.String(), sponsor.Amount.String()))
