import (
	"std"

	"gno.land/p/nt/avl/rotree"
	"gno.land/p/nt/commondao"
)

// NewIterator returns a SubDAO iterator.
func NewIterator(rootID uint64, options ...IteratorOption) Iterator {
	assertIsOwner(std.CurrentRealm().Address(), rootID)

	tree := getTree(rootID)
	if tree == nil {
		return Iterator{}
	}

	it := Iterator{tree: tree}
	for _, apply := range options {
		apply(&it)
	}

	size := it.tree.Size()
	if it.count > 0 {
		it.maxIndex = it.current + it.count
		if it.maxIndex > size {
			it.maxIndex = size
		}
	} else {
		it.maxIndex = size
	}

	it.count = it.maxIndex - it.current

	return it
}

// Iterator defines an iterator of SubDAOs.
type Iterator struct {
	tree                     rotree.IReadOnlyTree
	count, current, maxIndex int
	path                     string
	dao                      *commondao.CommonDAO
}

// Count returns the number of DAOs to iterate.
func (it Iterator) Count() int {
	return it.count
}

// Next returns true when a new DAO is available.
func (it *Iterator) Next() bool {
	if it.tree == nil || it.current == it.maxIndex {
		return false
	}

	path, v := it.tree.GetByIndex(it.current)
	if v == nil {
		return false
	}

	it.dao = v.(*commondao.CommonDAO)
	it.path = path
	it.current++
	return true
}

// DAO returns the current DAO.
func (it Iterator) DAO() *commondao.CommonDAO {
	return it.dao
}

// Path returns the current DAO path.
func (it Iterator) Path() string {
	return it.path
}

package commondao

// IteratorOption configures iterators.
type IteratorOption func(*Iterator)

// WithOffset assigns a initial offset to start iterating DAOs.
// Offset can be used to reduce the number of iterated DAOs for example during pagination.
func WithOffset(offset uint) IteratorOption {
	return func(it *Iterator) {
		it.current = int(offset)
	}
}

// WithCount assigns a number to limit the number of iterated DAOs.
// A zero count means to iterates until the last DAO.
func WithCount(count uint) IteratorOption {
	return func(it *Iterator) {
		it.count = int(count)
	}
}

