		// Table section
		Add(
			md.H3(md.Bold("16. Table")),
			md.Paragraph("To create a table, use the `md.Table()` function. Here's an example of a table:"),
		)

	// Create a table using the table package
	tb, _ := table.New([]string{"Feature", "Description"}, [][]string{
