func TestMemberGroupingAdd(t *testing.T) {
	t.Run("defauls", func(t *testing.T) {
		name := "Foo"
		g := commondao.NewMemberGrouping()

		uassert.False(t, g.Has(name), "expect grouping group not to be found")
		uassert.Equal(t, 0, g.Size(), "expect grouping to be empty")
	})

	t.Run("success", func(t *testing.T) {
		name := "Foo"
		g := commondao.NewMemberGrouping()
		mg, err := g.Add(name)

		urequire.NoError(t, err, "expect no error")
		uassert.True(t, g.Has(name), "expect grouping group to be found")
		uassert.Equal(t, 1, g.Size(), "expect grouping to have a single group")

		urequire.True(t, mg != nil, "expected grouping group to be not nil")
		uassert.Equal(t, name, mg.Name(), "expect group to have the right name")
	})

	t.Run("duplicated name", func(t *testing.T) {
		name := "Foo"
		g := commondao.NewMemberGrouping()
		_, err := g.Add(name)
		urequire.NoError(t, err, "expect no error")

		_, err = g.Add(name)
		uassert.ErrorContains(t, err, "member group already exists: Foo", "expect duplication error")
	})
}

func TestMemberGroupingGet(t *testing.T) {
	t.Run("success", func(t *testing.T) {
		name := "Foo"
		g := commondao.NewMemberGrouping()
		g.Add(name)

		mg, found := g.Get(name)

		urequire.True(t, found, "expect grouping group to be found")
		urequire.True(t, mg != nil, "expect grouping group to be not nil")
		uassert.Equal(t, name, mg.Name(), "expect group to have the right name")
	})

	t.Run("group not found", func(t *testing.T) {
		g := commondao.NewMemberGrouping()

		_, found := g.Get("Foo")

		urequire.False(t, found, "expect grouping group to be not found")
	})
}

func TestMemberGroupingDelete(t *testing.T) {
	name := "Foo"
	g := commondao.NewMemberGrouping()
	g.Add(name)

	err := g.Delete(name)

	urequire.NoError(t, err, "expect no error")
	uassert.False(t, g.Has(name), "expect grouping group not to be found")
}

func TestMemberGroupingIterate(t *testing.T) {
	groups := []string{"Tier 1", "Tier 2", "Tier 3"}
	g := commondao.NewMemberGrouping()
	for _, name := range groups {
		g.Add(name)
	}

	var i int
	g.IterateByOffset(0, g.Size(), func(mg commondao.MemberGroup) bool {
		urequire.True(t, mg != nil, "expect member group not to be nil")
		urequire.Equal(t, groups[i], mg.Name(), "expect group to be iterated in order")

		i++
		return false
	})

	uassert.Equal(t, len(groups), i, "expect all groups to be iterated")
}

func TestMemberGroupingUseStorageFactory(t *testing.T) {
	var (
		groupName     string
		defaultMember = address("g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5")
	)

	fn := func(group string) commondao.MemberStorage {
		groupName = group

		// Create storage and add a default member
		s := commondao.NewMemberStorage()
		s.Add(defaultMember)
		return s
	}

	grouping := commondao.NewMemberGrouping(commondao.UseStorageFactory(fn))
	group, err := grouping.Add("foo")

	urequire.NoError(t, err, "expect no group creation error")
	uassert.True(t, group.Members().Has(defaultMember), "expect storage to have a default member")
	uassert.Equal(t, "foo", groupName, "expect group name to match")
}

func TestMemberGroupingWithGroups(t *testing.T) {
	defaultGroups := []string{"foo", "bar", "baz"}
	grouping := commondao.NewMemberGrouping(commondao.WithGroups(defaultGroups...))

	urequire.Equal(t, len(defaultGroups), grouping.Size(), "expect groups to be created")
	for _, group := range defaultGroups {
		uassert.True(t, grouping.Has(group), "expect group to be found: "+group)
	}
}

package commondao

import (
	"gno.land/p/moul/addrset"
)

// MemberStorage defines an interface for member storages.
type MemberStorage interface {
	// Size returns the number of members in the storage.
	Size() int

	// Has checks if a member exists in the storage.
	Has(address) bool

	// Add adds a member to the storage.
	// Returns true if the member is added, or false if it already existed.
	Add(address) bool

	// Remove removes a member from the storage.
	// Returns true if member was removed, or false if it was not found.
	Remove(address) bool

	// Grouping returns member groups when supported.
	// When nil is returned it means that grouping of members is not supported.
	// Member groups can be used by implementations that require grouping users
	// by roles or by tiers for example.
	Grouping() MemberGrouping

	// IterateByOffset iterates members starting at the given offset.
	// The callback can return true to stop iteration.
	IterateByOffset(offset, count int, fn func(address) bool)
}

// NewMemberStorage creates a new member storage.
// Function returns a new member storage that doesn't support member groups.
// This type of storage is useful when there is no need to group members.
func NewMemberStorage() MemberStorage {
	return &memberStorage{}
}

// NewMemberStorageWithGrouping a new member storage with support for member groups.
// Member groups can be used by implementations that require grouping users by roles
// or by tiers for example.
func NewMemberStorageWithGrouping(options ...MemberGroupingOption) MemberStorage {
	return &memberStorage{grouping: NewMemberGrouping(options...)}
}

type memberStorage struct {
	addrset.Set

	grouping MemberGrouping
}

// Grouping returns member groups.
func (s memberStorage) Grouping() MemberGrouping {
	return s.grouping
}

// CountStorageMembers returns the total number of members in the storage.
// It counts all members in each group and the ones without group.
func CountStorageMembers(s MemberStorage) int {
	if s == nil {
		return 0
	}

	c := s.Size()
	s.Grouping().IterateByOffset(0, s.Grouping().Size(), func(g MemberGroup) bool {
		c += g.Members().Size()
		return false
	})
	return c
}

package commondao_test

import (
	"testing"

	"gno.land/p/nt/uassert"
	"gno.land/p/nt/urequire"

	"gno.land/p/devx000/wip/nt/commondao"
)

func TestMemberStorageWithGrouping(t *testing.T) {
	// Prepare
	tiers := []struct {
		Name    string
		Weight  int
		Members []address
	}{
		{
			Name:   "Tier 1",
			Weight: 3,
			Members: []address{
				"g16jpf0puufcpcjkph5nxueec8etpcldz7zwgydq",
				"g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5",
			},
		},
		{
			Name:   "Tier 2",
			Weight: 2,
			Members: []address{
				"g1us8428u2a5satrlxzagqqa5m6vmuze025anjlj",
			},
		},
	}

	storage := commondao.NewMemberStorageWithGrouping()
	for _, tier := range tiers {
		mg, err := storage.Grouping().Add(tier.Name)
		urequire.NoError(t, err, "expect no error adding tier")

		mg.SetMeta(tier.Weight)

		for _, addr := range tier.Members {
			ok := mg.Members().Add(addr)
			urequire.True(t, ok, "expect member to be added")
		}
	}

	// Assert
	for i := 0; i < len(tiers); i++ {
		tier := tiers[i]
		mg, found := storage.Grouping().Get(tier.Name)
		urequire.True(t, found, "expect member group to be found")

		v := mg.GetMeta()
		urequire.True(t, v != nil, "expect meta to be not nil")

		weight, ok := v.(int)
		urequire.True(t, ok, "expect group metadata to be an integer")
		uassert.Equal(t, tier.Weight, weight, "expect group weight to match")

		var i int
		mg.Members().IterateByOffset(0, len(tier.Members), func(addr address) bool {
			uassert.Equal(t, tier.Members[i], addr, "expect tier member to match")

			i++
			return false
		})

		uassert.Equal(t, len(tier.Members), i, "expect all tier members to be iterated")
	}
}

func TestCountStorageMembers(t *testing.T) {
	storage := commondao.NewMemberStorageWithGrouping()
	storage.Add("g16jpf0puufcpcjkph5nxueec8etpcldz7zwgydq")

	g, err := storage.Grouping().Add("A")
	urequire.NoError(t, err, "expect no error creating member group A")

	g.Members().Add("g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5")
	g.Members().Add("g1us8428u2a5satrlxzagqqa5m6vmuze025anjlj")

	g, err = storage.Grouping().Add("B")
	urequire.NoError(t, err, "expect no error creating member group B")

	g.Members().Add("g1us8428u2a5satrlxzagqqa5m6vmuze025anjlj") // Add a member that exists in other group

	uassert.Equal(t, 4, commondao.CountStorageMembers(storage))
}

package commondao

// NewMemberSet creates a new readonly memberset.
func NewMemberSet(s MemberStorage) MemberSet {
	return MemberSet{s}
}

// MemberSet contains a readonly list of member addresses.
type MemberSet struct {
	storage MemberStorage
}

// Size returns the number of addresses in the set.
func (s MemberSet) Size() int {
	return s.storage.Size()
}

// Has checks if an address exists in the set.
func (s MemberSet) Has(member address) bool {
	return s.storage.Has(member)
}

// IterateByOffset walks through addresses starting at the given offset.
// The callback should return true to stop iteration.
func (s MemberSet) IterateByOffset(offset, count int, fn func(address) bool) {
	s.storage.IterateByOffset(offset, count, fn)
}

package commondao

import (
	"errors"
	"time"

	"gno.land/p/nt/avl"
)

const (
	StatusActive ProposalStatus = "active"
	StatusFailed                = "failed"
	StatusPassed                = "passed"
)

const (
	ChoiceNone       VoteChoice = ""
	ChoiceYes                   = "YES"
	ChoiceNo                    = "NO"
	ChoiceNoWithVeto            = "NO WITH VETO"
	ChoiceAbstain               = "ABSTAIN"
)

const (
	QuorumOneThird     float64 = 0.33 // percentage
	QuorumHalf                 = 0.5
	QuorumTwoThirds            = 0.66
	QuorumThreeFourths         = 0.75
	QuorumFull                 = 1
)

// MaxCustomVoteChoices defines the maximum number of custom
// vote choices that a proposal definition can define.
const MaxCustomVoteChoices = 10

var (
	ErrInvalidCreatorAddress      = errors.New("invalid proposal creator address")
	ErrMaxCustomVoteChoices       = errors.New("max number of custom vote choices exceeded")
	ErrProposalDefinitionRequired = errors.New("proposal definition is required")
	ErrNoQuorum                   = errors.New("no quorum")
	ErrStatusIsNotActive          = errors.New("proposal status is not active")
)

type (
	// ProposalStatus defines a type for different proposal states.
	ProposalStatus string

	// VoteChoice defines a type for proposal vote choices.
	VoteChoice string

	// Proposal defines a DAO proposal.
	Proposal struct {
		id             uint64
		status         ProposalStatus
		definition     ProposalDefinition
		creator        address
		record         *VotingRecord // TODO: Add support for multiple voting records
		statusReason   string
		voteChoices    *avl.Tree // string(VoteChoice) -> struct{}
		votingDeadline time.Time
		createdAt      time.Time
	}

	// ProposalDefinition defines an interface for custom proposal definitions.
	// These definitions define proposal content and behavior, they esentially
	// allow the definition for different proposal types.
	ProposalDefinition interface {
		// Title returns the proposal title.
		Title() string

		// Body returns proposal's body.
		// It usually contains description or values that are specific to the proposal,
		// like a description of the proposal's motivation or the list of values that
		// would be applied when the proposal is approved.
		Body() string

		// VotingPeriod returns the period where votes are allowed after proposal creation.
		// It is used to calculate the voting deadline from the proposal's creationd date.
		VotingPeriod() time.Duration

		// Tally counts the number of votes and verifies if proposal passes.
		// It receives a readonly record containing the votes that has been
		// submitted for the proposal and also the list of current DAO members.
		Tally(ReadonlyVotingRecord, MemberSet) (passes bool, _ error)
	}

	// Validable defines an interface for proposal definitions that require state validation.
	// Validation is done before execution and normally also during proposal rendering.
	Validable interface {
		// Validate validates that the proposal is valid for the current state.
		Validate() error
	}

	// Executable defines an interface for proposal definitions that modify state on approval.
	// Once proposals are executed they are archived and considered finished.
	Executable interface {
		// Execute executes the proposal.
		Execute(realm) error
	}

	// CustomizableVoteChoices defines an interface for proposal definitions that want
	// to customize the list of allowed voting choices.
	CustomizableVoteChoices interface {
		// CustomVoteChoices returns a list of valid voting choices.
		// Choices are considered valid only when there are at least two possible choices
		// otherwise proposal defaults to using YES, NO and ABSTAIN as valid choices.
		CustomVoteChoices() []VoteChoice
	}
)

// MustValidate validates that a proposal is valid for the current state or panics on error.
func MustValidate(v Validable) {
	if v == nil {
		panic("validable proposal definition is nil")
	}

	if err := v.Validate(); err != nil {
		panic(err)
	}
}

// MustExecute executes an executable proposal or panics on error.
func MustExecute(e Executable) {
	if e == nil {
		panic("executable proposal definition is nil")
	}

	if err := e.Execute(cross); err != nil {
		panic(err)
	}
}

// NewProposal creates a new DAO proposal.
func NewProposal(id uint64, creator address, d ProposalDefinition) (*Proposal, error) {
	if d == nil {
		return nil, ErrProposalDefinitionRequired
	}

	if !creator.IsValid() {
		return nil, ErrInvalidCreatorAddress
	}

	now := time.Now()
	p := &Proposal{
		id:             id,
		status:         StatusActive,
		definition:     d,
		creator:        creator,
		record:         &VotingRecord{},
		voteChoices:    avl.NewTree(),
		votingDeadline: now.Add(d.VotingPeriod()),
		createdAt:      now,
	}

	if v, ok := d.(CustomizableVoteChoices); ok {
		choices := v.CustomVoteChoices()
		if len(choices) > MaxCustomVoteChoices {
			return nil, ErrMaxCustomVoteChoices
		}

		for _, c := range choices {
			p.voteChoices.Set(string(c), struct{}{})
		}
	}

	// Use default voting choices when the definition returns none or a single vote choice
	if p.voteChoices.Size() < 2 {
		p.voteChoices.Set(string(ChoiceYes), struct{}{})
		p.voteChoices.Set(string(ChoiceNo), struct{}{})
		p.voteChoices.Set(string(ChoiceAbstain), struct{}{})
	}
	return p, nil
}

// ID returns the unique proposal identifies.
func (p Proposal) ID() uint64 {
	return p.id
}

// Definition returns the proposal definition.
// Proposal definitions define proposal content and behavior.
func (p Proposal) Definition() ProposalDefinition {
	return p.definition
}

// Status returns the current proposal status.
func (p Proposal) Status() ProposalStatus {
	return p.status
}

// Creator returns the address of the account that created the proposal.
func (p Proposal) Creator() address {
	return p.creator
}

// CreatedAt returns the time that proposal was created.
func (p Proposal) CreatedAt() time.Time {
	return p.createdAt
}

// VotingRecord returns a record that contains all the votes submitted for the proposal.
func (p Proposal) VotingRecord() *VotingRecord {
	return p.record
}

// StatusReason returns an optional reason that lead to the current proposal status.
// Reason is mostyl useful when a proposal fails.
func (p Proposal) StatusReason() string {
	return p.statusReason
}

// VotingDeadline returns the deadline after which no more votes should be allowed.
func (p Proposal) VotingDeadline() time.Time {
	return p.votingDeadline
}

// VoteChoices returns the list of vote choices allowed for the proposal.
func (p Proposal) VoteChoices() []VoteChoice {
	choices := make([]VoteChoice, 0, p.voteChoices.Size())
	p.voteChoices.Iterate("", "", func(c string, _ any) bool {
		choices = append(choices, VoteChoice(c))
		return false
	})
	return choices
}

// HasVotingDeadlinePassed checks if the voting deadline has been met.
func (p Proposal) HasVotingDeadlinePassed() bool {
	return !time.Now().Before(p.VotingDeadline())
}

// Validate validates that a proposal is valid for the current state.
// Validation is done when proposal status is active and when the definition supports validation.
func (p Proposal) Validate() error {
	if p.status != StatusActive {
		return nil
	}

	if v, ok := p.definition.(Validable); ok {
		return v.Validate()
	}
	return nil
}

// IsVoteChoiceValid checks if a vote choice is valid for the proposal.
func (p Proposal) IsVoteChoiceValid(c VoteChoice) bool {
	return p.voteChoices.Has(string(c))
}

// IsQuorumReached checks if a participation quorum is reach.
func IsQuorumReached(quorum float64, r ReadonlyVotingRecord, members MemberSet) bool {
	if members.Size() <= 0 || quorum <= 0 {
		return false
	}

	var totalCount int
	r.IterateVotesCount(func(c VoteChoice, voteCount int) bool {
		// Don't count explicit abstentions or invalid votes
		if c != ChoiceNone && c != ChoiceAbstain {
			totalCount += r.VoteCount(c)
		}
		return false
	})

	percentage := float64(totalCount) / float64(members.Size())
	return percentage >= quorum
}

package commondao

import (
	"gno.land/p/nt/avl"
	"gno.land/p/nt/seqid"
)

// ProposalStorage defines an interface for proposal storages.
type ProposalStorage interface {
	// Has checks if a proposal exists.
	Has(id uint64) bool

	// Get returns a proposal or nil when proposal doesn't exist.
	Get(id uint64) *Proposal

	// Add adds a proposal to the storage.
	Add(*Proposal)

	// Remove removes a proposal from the storage.
	Remove(id uint64)

	// Size returns the number of proposals that the storage contains.
	Size() int

	// Iterate iterates proposals.
	Iterate(offset, count int, reverse bool, fn func(*Proposal) bool) bool
}

// NewProposalStorage creates a new proposal storage.
func NewProposalStorage() ProposalStorage {
	return &proposalStorage{avl.NewTree()}
}

type proposalStorage struct {
	storage *avl.Tree // string(proposal ID) -> *Proposal
}

// Has checks if a proposal exists.
func (s proposalStorage) Has(id uint64) bool {
	return s.storage.Has(makeProposalKey(id))
}

// Get returns a proposal or nil when proposal doesn't exist.
func (s proposalStorage) Get(id uint64) *Proposal {
	if v, found := s.storage.Get(makeProposalKey(id)); found {
		return v.(*Proposal)
	}
	return nil
}

// Add adds a proposal to the storage.
func (s *proposalStorage) Add(p *Proposal) {
	if p == nil {
		return
	}

	s.storage.Set(makeProposalKey(p.ID()), p)
}

// Remove removes a proposal from the storage.
func (s *proposalStorage) Remove(id uint64) {
	s.storage.Remove(makeProposalKey(id))
}

// Size returns the number of proposals that the storage contains.
func (s proposalStorage) Size() int {
	return s.storage.Size()
}

// Iterate iterates proposals.
func (s proposalStorage) Iterate(offset, count int, reverse bool, fn func(*Proposal) bool) bool {
	if fn == nil {
		return false
	}

	cb := func(_ string, v any) bool { return fn(v.(*Proposal)) }

	if reverse {
		return s.storage.ReverseIterateByOffset(offset, count, cb)
	}
	return s.storage.IterateByOffset(offset, count, cb)
}

func makeProposalKey(id uint64) string {
	return seqid.ID(id).String()
}

package commondao_test

import (
	"testing"

	"gno.land/p/nt/uassert"
	"gno.land/p/nt/urequire"

	"gno.land/p/devx000/wip/nt/commondao"
)

func TestProposalStorageAdd(t *testing.T) {
	p, _ := commondao.NewProposal(1, "g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5", testPropDef{})
	s := commondao.NewProposalStorage()
	initialSize := s.Size()

	s.Add(p)

	uassert.Equal(t, 0, initialSize, "expect initial storage to be empty")
	uassert.Equal(t, 1, s.Size(), "expect storage to have one proposal")
	uassert.True(t, s.Has(p.ID()), "expect proposal to be found")
}

func TestProposalStorageGet(t *testing.T) {
	p, _ := commondao.NewProposal(1, "g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5", testPropDef{})
	s := commondao.NewProposalStorage()
	s.Add(p)

	p2 := s.Get(p.ID())

	urequire.NotEqual(t, nil, p2, "expect proposal to be found")
	uassert.Equal(t, p.ID(), p2.ID(), "expect proposal ID to match")
}

func TestProposalStorageRemove(t *testing.T) {
	p, _ := commondao.NewProposal(1, "g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5", testPropDef{})
	s := commondao.NewProposalStorage()
	s.Add(p)

	s.Remove(p.ID())

	uassert.Equal(t, 0, s.Size(), "expect storage to be empty")
	uassert.False(t, s.Has(p.ID()), "expect proposal to be not found")
}

func TestProposalStorageIterate(t *testing.T) {
	var (
		i   int
		ids = []uint64{22, 33, 44}
		s   = commondao.NewProposalStorage()
	)

	for _, id := range ids {
		p, _ := commondao.NewProposal(id, "g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5", testPropDef{})
		s.Add(p)
	}

	s.Iterate(0, s.Size(), false, func(p *commondao.Proposal) bool {
		uassert.Equal(t, ids[i], p.ID(), "expect proposal ID to match")

		i++
		return i == s.Size()
	})

	uassert.Equal(t, len(ids), i, "expect storage to iterate all proposals")

	i = s.Size() - 1
	s.Iterate(0, s.Size(), true, func(p *commondao.Proposal) bool {
		uassert.Equal(t, ids[i], p.ID(), "expect proposal ID to match")

		i--
		return i == -1
	})

	uassert.Equal(t, -1, i, "expect storage to iterate all proposals in reverse order")
}

package commondao_test

import (
	"errors"
	"testing"
	"time"

	"gno.land/p/nt/uassert"
	"gno.land/p/nt/urequire"

	"gno.land/p/devx000/wip/nt/commondao"
)

func TestProposalNew(t *testing.T) {
	cases := []struct {
		name       string
		creator    address
		definition commondao.ProposalDefinition
		err        error
	}{
		{
			name:       "success",
			creator:    "g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5",
			definition: testPropDef{votingPeriod: time.Minute * 10},
		},
		{
			name:       "invalid creator address",
			creator:    "invalid",
			definition: testPropDef{},
			err:        commondao.ErrInvalidCreatorAddress,
		},
		{
			name:    "max custom vote choices exceeded",
			creator: "g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5",
			definition: testPropDef{
				voteChoices: make([]commondao.VoteChoice, commondao.MaxCustomVoteChoices+1),
			},
			err: commondao.ErrMaxCustomVoteChoices,
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			id := uint64(1)

			p, err := commondao.NewProposal(id, tc.creator, tc.definition)

			if tc.err != nil {
				urequire.ErrorIs(t, err, tc.err, "expected an error")
				return
			}

			urequire.NoError(t, err, "unexpected error")
			uassert.Equal(t, p.ID(), id)
			uassert.NotEqual(t, p.Definition(), nil)
			uassert.True(t, p.Status() == commondao.StatusActive)
			uassert.Equal(t, p.Creator(), tc.creator)
			uassert.False(t, p.CreatedAt().IsZero())
			uassert.NotEqual(t, p.VotingRecord(), nil)
			uassert.Empty(t, p.StatusReason())
			uassert.True(t, p.VotingDeadline() == p.CreatedAt().Add(tc.definition.VotingPeriod()))
		})
	}
}

func TestProposalVoteChoices(t *testing.T) {
	cases := []struct {
		name       string
		definition commondao.ProposalDefinition
		choices    []commondao.VoteChoice
	}{
		{
			name:       "custom choices",
			definition: testPropDef{voteChoices: []commondao.VoteChoice{"FOO", "BAR", "BAZ"}},
			choices: []commondao.VoteChoice{
				"BAR",
				"BAZ",
				"FOO",
			},
		},
		{
			name:       "defaults because of empty custom choice list",
			definition: testPropDef{voteChoices: []commondao.VoteChoice{}},
			choices: []commondao.VoteChoice{
				commondao.ChoiceAbstain,
				commondao.ChoiceNo,
				commondao.ChoiceYes,
			},
		},
		{
			name:       "defaults because of single custom choice list",
			definition: testPropDef{voteChoices: []commondao.VoteChoice{"FOO"}},
			choices: []commondao.VoteChoice{
				commondao.ChoiceAbstain,
				commondao.ChoiceNo,
				commondao.ChoiceYes,
			},
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			p, _ := commondao.NewProposal(1, "g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5", testPropDef{
				voteChoices: tc.choices,
			})

			choices := p.VoteChoices()

			urequire.Equal(t, len(choices), len(tc.choices), "expect vote choice count to match")
			for i, c := range choices {
				urequire.True(t, tc.choices[i] == c, "expect vote choice to match")
			}
		})
	}
}

func TestIsQuorumReached(t *testing.T) {
	cases := []struct {
		name    string
		quorum  float64
		members []address
		votes   []commondao.Vote
		fail    bool
	}{
		{
			name:   "one third",
			quorum: commondao.QuorumOneThird,
			members: []address{
				"g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5",
				"g1w4ek2u33ta047h6lta047h6lta047h6ldvdwpn",
				"g1w4ek2u3jta047h6lta047h6lta047h6l9huexc",
			},
			votes: []commondao.Vote{
				{
					Address: "g1w4ek2u33ta047h6lta047h6lta047h6ldvdwpn",
					Choice:  commondao.ChoiceYes,
				},
			},
		},
		{
			name:   "one third no quorum",
			quorum: commondao.QuorumOneThird,
			members: []address{
				"g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5",
				"g1w4ek2u33ta047h6lta047h6lta047h6ldvdwpn",
				"g1w4ek2u3jta047h6lta047h6lta047h6l9huexc",
			},
			fail: true,
		},
		{
			name:   "half",
			quorum: commondao.QuorumHalf,
			members: []address{
				"g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5",
				"g1w4ek2u33ta047h6lta047h6lta047h6ldvdwpn",
				"g1w4ek2u3jta047h6lta047h6lta047h6l9huexc",
				"g125t352u4pmdrr57emc4pe04y40sknr5ztng5mt",
			},
			votes: []commondao.Vote{
				{
					Address: "g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5",
					Choice:  commondao.ChoiceYes,
				},
				{
					Address: "g1w4ek2u3jta047h6lta047h6lta047h6l9huexc",
					Choice:  commondao.ChoiceNo,
				},
