type (
	// Permission defines the type for permissions.
	Permission string

	// Role defines the type for user roles.
	Role string

	// Args is a list of generic arguments.
	Args []interface{}

	// User contains user info.
	User struct {
		Address address
		Roles   []Role
	}

	// UsersIterFn defines a function type to iterate users.
	UsersIterFn func(User) bool

	// Permissions define an interface to for permissioned execution.
	Permissions interface {
		// HasRole checks if a user has a specific role assigned.
		HasRole(address, Role) bool

		// HasPermission checks if a user has a specific permission.
		HasPermission(address, Permission) bool

		// WithPermission calls a callback when a user has a specific permission.
		// It panics on error.
		WithPermission(realm, address, Permission, Args, func(realm))

		// SetUserRoles adds a new user when it doesn't exist and sets its roles.
		// Method can also be called to change the roles of an existing user.
		// It panics on error.
		SetUserRoles(realm, address, ...Role)

		// RemoveUser removes a user from the permissioner.
		// It panics on error.
		RemoveUser(realm, address) (removed bool)

		// HasUser checks if a user exists.
		HasUser(address) bool

		// UsersCount returns the total number of users the permissioner contains.
		UsersCount() int

		// IterateUsers iterates permissions' users.
		IterateUsers(start, count int, fn UsersIterFn) bool
	}
)

package boards

import (
	"strings"
	"time"
)

// Post defines a generic type for posts.
// A post can be either a thread or a reply.
type Post struct {
	// ID is the unique identifier of the post.
	ID ID

	// ParentID is the ID of the parent post.
	ParentID ID

	// ThreadID contains the post ID of the thread where current post is created.
	// If current post is a thread it contains post's ID.
	// It should be used when current post is a thread or reply.
	ThreadID ID

	// OriginalBoardID contains the board ID of the original post when current post is a repost.
	OriginalBoardID ID

	//  Board contains the board where post is created.
	Board *Board

	// Title contains the post's title.
	Title string

	// Body contains content of the post.
	Body string

	// Hidden indicates that the post is hidden.
	Hidden bool

	// Readonly indicates that the post is readonly.
	Readonly bool

	// Replies stores post replies.
	Replies PostStorage

	// Reposts stores reposts of the current post.
	// It should be used when post is a thread.
	Reposts RepostStorage

	// Flags stores users flags for the current post.
	Flags FlagStorage

	// Creator is the account address that created the post.
	Creator address

	// CreatedAt is the post's creation time.
	CreatedAt time.Time

	// UpdatedAt is the post's update time.
	UpdatedAt time.Time
}

// Summary return a summary of the post's body.
// It returns the body making sure that the length is limited to 80 characters.
func (post Post) Summary() string {
	return SummaryOf(post.Body, 80)
}

// IsThread checks if a post is a thread.
// When a post is not a thread it's considered a thread's reply/comment.
func IsThread(p *Post) bool {
	if p == nil {
		return false
	}
	return p.ThreadID == p.ID
}

// IsRepost checks if a thread is a repost.
func IsRepost(thread *Post) bool {
	if thread == nil {
		return false
	}
	return thread.OriginalBoardID != 0
}

// SummaryOf returns a summary of a text.
func SummaryOf(text string, length int) string {
	text = strings.TrimSpace(text)
	if text == "" {
		return ""
	}

	lines := strings.SplitN(text, "\n", 2)
	line := lines[0]
	if len(line) > length {
		line = line[:(length-3)] + "..."
	} else if len(lines) > 1 {
		line = line + "..."
	}
	return line
}

package boards

import (
	"errors"

	"gno.land/p/nt/avl"
)

type (
	// PostIterFn defines a function type to iterate posts.
	PostIterFn func(*Post) bool

	// PostStorage defines an interface for posts storage.
	PostStorage interface {
		// Get retruns a post that matches an ID.
		Get(ID) (_ *Post, found bool)

		// Remove removes a post from the storage.
		Remove(ID) (_ *Post, removed bool)

		// Add adds a post in the storage.
		Add(*Post) error

		// Size returns the number of posts in the storage.
		Size() int

		// Iterate iterates posts.
		// To reverse iterate posts use a negative count.
		// If the callback returns true, the iteration is stopped.
		Iterate(start, count int, fn PostIterFn) bool
	}
)

// NewPostStorage creates a new storage for posts.
// The new storage uses an AVL tree to store posts.
func NewPostStorage() PostStorage {
	return &postStorage{avl.NewTree()}
}

type postStorage struct {
	posts *avl.Tree // string(Post.ID) -> *Post
}

// Get retruns a post that matches an ID.
func (s postStorage) Get(id ID) (*Post, bool) {
	k := makePostKey(id)
	v, found := s.posts.Get(k)
	if !found {
		return nil, false
	}
	return v.(*Post), true
}

// Remove removes a post from the storage.
func (s *postStorage) Remove(id ID) (*Post, bool) {
	k := makePostKey(id)
	v, removed := s.posts.Remove(k)
	if !removed {
		return nil, false
	}
	return v.(*Post), true
}

// Add adds a post in the storage.
// It updates existing posts when storage contains one with the same ID.
func (s *postStorage) Add(p *Post) error {
	if p == nil {
		return errors.New("saving nil posts is not allowed")
	}

	s.posts.Set(makePostKey(p.ID), p)
	return nil
}

// Size returns the number of posts in the storage.
func (s postStorage) Size() int {
	return s.posts.Size()
}

// Iterate iterates posts.
// To reverse iterate posts use a negative count.
// If the callback returns true, the iteration is stopped.
func (s postStorage) Iterate(start, count int, fn PostIterFn) bool {
	if count < 0 {
		return s.posts.ReverseIterateByOffset(start, -count, func(_ string, v any) bool {
			return fn(v.(*Post))
		})
	}

	return s.posts.IterateByOffset(start, count, func(_ string, v any) bool {
		return fn(v.(*Post))
	})
}

func makePostKey(postID ID) string {
	return postID.PaddedString()
}

