package permissions

import (
	"chain"
	"chain/runtime"
	"errors"
	"strings"
	"time"

	boards "gno.land/p/devx000/wip/gnoland/boards"
	"gno.land/p/moul/ulist"
	"gno.land/p/nt/avl"

	boards2 "gno.land/r/boards000/v1rc1"
)

// TODO: Figure out how to delete permissions when board is deleted

var (
	permissions       = ulist.New()   // []*Permissions
	permissionsByPath = avl.NewTree() // string(realm:board) -> *Permissions
)

// New creates a new custom permissions for an open board.
// Path is the full Gno.Land realm path to the board including the render path, for example:
//
//	gno.land/r/gnoland/boards/v1:MyBoard
func New(path string) *Permissions {
	path = strings.TrimSpace(path)
	if path == "" {
		panic("realm path is required")
	}

	realmPath, renderPath, err := splitRealmPath(path)
	if err != nil {
		panic(err)
	}

	owner := runtime.OriginCaller()
	p := &Permissions{
		open:       true, // TODO: Allow changing open status (threads and/or replies)
		realmPath:  realmPath,
		renderPath: renderPath,
		boardsAddr: chain.PackageAddress(realmPath),
		basic:      createBasicPermissions(owner),
		createdBy:  owner,
		createdAt:  time.Now(),
	}

	permissions.Append(p)
	permissionsByPath.Set(path, p)

	p.basic.ValidateFunc(boards2.PermissionBoardRename, p.validateBoardRename)
	p.basic.ValidateFunc(boards2.PermissionMemberInvite, p.validateMemberInvite)
	p.basic.ValidateFunc(boards2.PermissionRoleChange, p.validateRoleChange)
	p.basic.ValidateFunc(boards2.PermissionThreadCreate, p.validateThreadCreate)
	p.basic.ValidateFunc(boards2.PermissionReplyCreate, p.validateReplyCreate)
	return p
}

// Permissions defines a type for custom permissions that support open boards.
type Permissions struct {
	open       bool
	boardsAddr address
	realmPath  string
	renderPath string
	basic      *boards2.BasicPermissions
	createdBy  address
	createdAt  time.Time
}

// HasRole checks if a user has a specific role assigned.
func (p Permissions) HasRole(user address, r boards.Role) bool {
	return p.basic.HasRole(user, r)
}

// HasPermission checks if a user has a specific permission.
func (p Permissions) HasPermission(user address, perm boards.Permission) bool {
	return p.basic.HasPermission(user, perm)
}

// WithPermission calls a callback when a user has a specific permission.
// It panics on error.
func (p *Permissions) WithPermission(_ realm, user address, perm boards.Permission, args boards.Args, fn func(realm)) {
	p.AssertPreviousRealmIsBoards()

	p.basic.WithPermission(cross, user, perm, args, fn)
}

// SetUserRoles adds a new user when it doesn't exist and sets its roles.
// Method can also be called to change the roles of an existing user.
// It panics on error.
func (p *Permissions) SetUserRoles(_ realm, user address, roles ...boards.Role) {
	p.AssertPreviousRealmIsBoards()

	p.basic.SetUserRoles(cross, user, roles...)
}

// RemoveUser removes a user from the permissioner.
// It panics on error.
func (p *Permissions) RemoveUser(_ realm, user address) (removed bool) {
	p.AssertPreviousRealmIsBoards()

	return p.basic.RemoveUser(cross, user)
}

// HasUser checks if a user exists.
func (p Permissions) HasUser(user address) bool {
	return p.basic.HasUser(user)
}

// UsersCount returns the total number of users the permissioner contains.
func (p Permissions) UsersCount() int {
	return p.basic.UsersCount()
}

// IterateUsers iterates permissions' users.
func (p Permissions) IterateUsers(start, count int, fn boards.UsersIterFn) bool {
	return p.basic.IterateUsers(start, count, fn)
}

// AssertPreviousRealmIsBoards asserts that previous realm is the boards realm.
func (p Permissions) AssertPreviousRealmIsBoards() {
	if runtime.PreviousRealm().Address() != p.boardsAddr {
		panic("unauthorized")
	}
}

func createBasicPermissions(owner address) *boards2.BasicPermissions {
	perms := boards2.NewBasicPermissions()
	perms.SetSuperRole(boards2.RoleOwner)
	perms.SetPublicPermissions(
		boards2.PermissionThreadCreate,
		boards2.PermissionReplyCreate,
	)
	perms.AddRole(
		boards2.RoleAdmin,
		boards2.PermissionBoardRename,
		boards2.PermissionBoardFlaggingUpdate,
		boards2.PermissionMemberInvite,
		boards2.PermissionMemberInviteRevoke,
		boards2.PermissionMemberRemove,
		boards2.PermissionThreadCreate,
		boards2.PermissionThreadEdit,
		boards2.PermissionThreadDelete,
		boards2.PermissionThreadRepost,
		boards2.PermissionThreadFlag,
		boards2.PermissionThreadFreeze,
		boards2.PermissionReplyCreate,
		boards2.PermissionReplyDelete,
		boards2.PermissionReplyFlag,
		boards2.PermissionRoleChange,
		boards2.PermissionUserBan,
		boards2.PermissionUserUnban,
	)
	perms.AddRole(
		boards2.RoleModerator,
		boards2.PermissionThreadCreate,
		boards2.PermissionThreadEdit,
		boards2.PermissionThreadRepost,
		boards2.PermissionThreadFlag,
		boards2.PermissionReplyCreate,
		boards2.PermissionReplyFlag,
		boards2.PermissionUserBan,
		boards2.PermissionUserUnban,
	)
	perms.AddRole(
		boards2.RoleGuest,
		boards2.PermissionThreadCreate,
		boards2.PermissionThreadRepost,
		boards2.PermissionReplyCreate,
	)
	perms.SetUserRoles(cross, owner, boards2.RoleOwner)
	return perms
}

func splitRealmPath(path string) (realmPath, renderPath string, err error) {
	path, _ = strings.CutSuffix(path, "?")
	parts := strings.SplitN(path, ":", 2)
	realmPath = parts[0]
	if !strings.HasPrefix(realmPath, "gno.land/r/") {
		return "", "", errors.New(`invalid realm path, it must start with "gno.land/r/"`)
	}

	if len(parts) == 2 {
		renderPath = parts[1]
	}
	return
}

package permissions

import (
	"errors"

	"gno.land/p/devx000/wip/gnoland/boards"

	boards2 "gno.land/r/boards000/v1rc1"
	"gno.land/r/sys/users"
)

// validateBoardCreate validates PermissionBoardCreate.
//
// Expected `args` values:
// 1. Caller address
// 2. Board name
// 3. Board ID
// 4. Is board listed
func (Permissions) validateBoardCreate(_ boards.Permissions, args boards.Args) error {
	caller, ok := args[0].(address)
	if !ok {
		return errors.New("expected a valid caller address")
	}

	name, ok := args[1].(string)
	if !ok {
		return errors.New("expected board name to be a string")
	}

	if err := checkBoardNameIsNotAddress(name); err != nil {
		return err
	}

	if err := checkBoardNameBelongsToAddress(caller, name); err != nil {
		return err
	}
	return nil
}

// validateBoardRename validates PermissionBoardRename.
//
// Expected `args` values:
// 1. Caller address
// 2. Board ID
// 3. Current board name
// 4. New board name
func (Permissions) validateBoardRename(_ boards.Permissions, args boards.Args) error {
	caller, ok := args[0].(address)
	if !ok {
		return errors.New("expected a valid caller address")
	}

	newName, ok := args[3].(string)
	if !ok {
		return errors.New("expected new board name to be a string")
	}

	if err := checkBoardNameIsNotAddress(newName); err != nil {
		return err
	}

	if err := checkBoardNameBelongsToAddress(caller, newName); err != nil {
		return err
	}
	return nil
}

// validateMemberInvite validates PermissionMemberInvite.
//
// Expected `args` values:
// 1. Caller address
// 2. Board ID
// 3. Invites
func (Permissions) validateMemberInvite(perms boards.Permissions, args boards.Args) error {
	caller, ok := args[0].(address)
	if !ok {
		return errors.New("expected a valid caller address")
	}

	invites, ok := args[2].([]boards2.Invite)
	if !ok {
		return errors.New("expected valid user invites")
	}

	// Make sure that only owners invite other owners
	callerIsOwner := perms.HasRole(caller, boards2.RoleOwner)
	for _, v := range invites {
		if v.Role == boards2.RoleOwner && !callerIsOwner {
			return errors.New("only owners are allowed to invite other owners")
		}
	}
	return nil
}

// validateRoleChange validates PermissionRoleChange.
//
// Expected `args` values:
// 1. Caller address
// 2. Board ID
// 3. Member address
// 4. Role
func (Permissions) validateRoleChange(perms boards.Permissions, args boards.Args) error {
	caller, ok := args[0].(address)
	if !ok {
		return errors.New("expected a valid caller address")
	}

	// Owners and Admins can change roles.
	// Admins should not be able to assign or remove the Owner role from members.
	if perms.HasRole(caller, boards2.RoleAdmin) {
		role, ok := args[3].(boards.Role)
		if !ok {
			return errors.New("expected a valid member role")
		}

		if role == boards2.RoleOwner {
			return errors.New("admins are not allowed to promote members to Owner")
		} else {
			member, ok := args[2].(address)
			if !ok {
				return errors.New("expected a valid member address")
			}

			if perms.HasRole(member, boards2.RoleOwner) {
				return errors.New("admins are not allowed to remove the Owner role")
			}
		}
	}
	return nil
}

// validateReplyCreate validates PermissionReplyCreate.
//
// Expected `args` values:
// 1. Caller address
// 2. Board ID
// 3. Thread ID
// 4. Parent ID
// 5. Reply ID
// 6. Body
func (p Permissions) validateReplyCreate(perms boards.Permissions, args boards.Args) error {
	// Only open boards require specific conditions to allow creating a reply
	if !p.open {
		return nil
	}

	caller, ok := args[0].(address)
	if !ok {
		return errors.New("expected a valid caller address")
	}

	if users.ResolveAddress(caller) == nil {
		return errors.New("a registered username is required to comment on open boards")
	}
	return nil
}

// validateThreadCreate validates PermissionThreadCreate.
//
// Expected `args` values:
// 1. Caller address
// 2. Board ID
// 3. Thread ID
// 4. Title
// 5. Body
func (p Permissions) validateThreadCreate(perms boards.Permissions, args boards.Args) error {
	// Only open boards require specific conditions to allow creating a thread
	if !p.open {
		return nil
	}

	caller, ok := args[0].(address)
	if !ok {
		return errors.New("expected a valid caller address")
	}

	if users.ResolveAddress(caller) == nil {
		return errors.New("a registered username is required to create threads on open boards")
	}
	return nil
}

func checkBoardNameIsNotAddress(s string) error {
	if address(s).IsValid() {
		return errors.New("addresses are not allowed as board name")
	}
	return nil
}

func checkBoardNameBelongsToAddress(owner address, name string) error {
	// When the board name is the name of a registered user
	// check that caller is the owner of the name.
	user, _ := users.ResolveName(name)
	if user != nil && user.Addr() != owner {
		return errors.New("board name is a user name registered to a different user")
	}
	return nil
}

package permissions

import (
	"strings"
	"time"

	"gno.land/p/moul/md"
	"gno.land/p/moul/mdtable"

	"gno.land/r/sys/users"
)

func Render(string) string {
	table := mdtable.Table{Headers: []string{"Boards", "Assigned by", "Created on"}}

	// TODO: Add pagination support
	// TODO: Add search by path support
	// TODO: Add sorting support

	permissions.Iterator(0, permissions.Size(), func(_ int, v any) bool {
		perm := v.(*Permissions)
		table.Append([]string{
			createBoardLink(perm),
			createUserLink(perm.createdBy),
			perm.createdAt.UTC().Format(time.RFC1123),
		})
		return false
	})

	var b strings.Builder
	b.WriteString("# Open Boards Permissions\n")
	b.WriteString(table.String())
	return b.String()
}

func createBoardLink(p *Permissions) string {
	path := p.realmPath
	if p.renderPath != "" {
		path = path + ":" + p.renderPath
	}

	return md.Link(path, strings.TrimPrefix(path, "gno.land"))
}

func createUserLink(user address) string {
	if u := users.ResolveAddress(user); u != nil {
		return md.UserLink(u.Name())
	}
	return md.UserLink(user.String())
}

