package sv1

import (
	"strings"

	"gno.land/p/nt/ufmt"
)

var (
	title = "Staking Rewards Calculator"
)

// Calculate staking rewards with compounding
func calculateRewards(amount float64, apr float64, commission float64, compound string) (daily, monthly, yearly, dailyComp, monthlyComp, yearlyComp float64) {
	// Adjust APR for commission
	effectiveAPR := apr * (1.0 - commission/100.0) / 100.0

	// Simple interest calculations
	yearlySimple := amount * effectiveAPR
	monthlySimple := yearlySimple / 12.0
	dailySimple := yearlySimple / 365.0

	// Compound interest calculations
	var n float64 // compounding periods per year
	switch compound {
	case "daily":
		n = 365.0
	case "weekly":
		n = 52.0
	case "monthly":
		n = 12.0
	case "yearly":
		n = 1.0
	default:
		n = 365.0 // default to daily
	}

	// Compound interest formula: A = P(1 + r/n)^(nt) - P
	// For 1 year: A = P(1 + r/n)^n - P
	finalAmount := amount * power(1.0 + effectiveAPR/n, n)
	yearlyCompound := finalAmount - amount

	// For monthly compound return (interest earned in 1 month)
	monthlyFinal := amount * power(1.0 + effectiveAPR/n, n/12.0)
	monthlyCompound := monthlyFinal - amount

	// For daily compound return (interest earned in 1 day)
	dailyFinal := amount * power(1.0 + effectiveAPR/n, n/365.0)
	dailyCompound := dailyFinal - amount

	return dailySimple, monthlySimple, yearlySimple, dailyCompound, monthlyCompound, yearlyCompound
}

// Power function (a^b) using repeated multiplication
func power(base float64, exp float64) float64 {
	if exp == 0 {
		return 1.0
	}

	result := 1.0
	isNegative := exp < 0
	if isNegative {
		exp = -exp
	}

	// Handle integer part
	intExp := int(exp)
	for i := 0; i < intExp; i++ {
		result *= base
	}

	// Approximate fractional part using Taylor series if needed
	fracExp := exp - float64(intExp)
	if fracExp > 0.001 {
		// Simple approximation: (1+x)^f ≈ 1 + f*x for small x
		// This works reasonably well for our use case
		x := base - 1.0
		result *= (1.0 + fracExp*x)
	}

	if isNegative {
		result = 1.0 / result
	}

	return result
}

