// Render function with interactive UI
func Render(path string) string {
	realmPath := strings.TrimPrefix(runtime.CurrentRealm().PkgPath(), runtime.ChainDomain())

	var out strings.Builder

	out.WriteString("# 🏘️ Community Pantry & Shopping List\n\n")
	out.WriteString("*A shared pantry managed by the community*\n\n")
	out.WriteString("---\n\n")

	// Check if pantry is empty and this is first time setup
	if len(communityPantry) == 0 && len(communityShoppingList) == 0 && path == "" {
		out.WriteString("## 🎯 Getting Started\n\n")
		out.WriteString("Welcome! Let's build your initial pantry by selecting common items you have.\n\n")
		out.WriteString("**Click items to add to your pantry:**\n\n")

		// Common pantry items as clickable links
		commonItems := []struct{ name, quantity string }{
			{"Milk", "1 gallon"},
			{"Bread", "1 loaf"},
			{"Eggs", "12 count"},
			{"Butter", "1 stick"},
			{"Cheese", "8 oz"},
			{"Rice", "2 lbs"},
			{"Pasta", "1 box"},
			{"Chicken", "2 lbs"},
			{"Ground Beef", "1 lb"},
			{"Tomatoes", "4 count"},
			{"Onions", "3 count"},
			{"Potatoes", "5 lbs"},
			{"Carrots", "1 bag"},
			{"Lettuce", "1 head"},
			{"Apples", "6 count"},
			{"Bananas", "1 bunch"},
			{"Orange Juice", "64 oz"},
			{"Coffee", "1 bag"},
			{"Sugar", "2 lbs"},
			{"Flour", "5 lbs"},
		}

		out.WriteString("<gno-columns>\n\n")
		for i, item := range commonItems {
			out.WriteString(ufmt.Sprintf("[+ %s](%s:add/%s/%s)", item.name, realmPath, item.name, item.quantity))
			if (i+1)%4 == 0 {
				out.WriteString("\n\n</gno-columns>\n\n<gno-columns>\n\n")
			} else if i < len(commonItems)-1 {
				out.WriteString(" | ")
			}
		}
		out.WriteString("\n\n</gno-columns>\n\n")

		out.WriteString("---\n\n")
		out.WriteString("*After adding items, your pantry will be created and you can manage it.*\n")
		return out.String()
	}

	// Handle adding items via path
	if strings.HasPrefix(path, "add/") {
		parts := strings.Split(strings.TrimPrefix(path, "add/"), "/")
		if len(parts) == 2 {
			AddToPantry(parts[0], parts[1])
			// Redirect back to main view
			out.WriteString("✅ Added **" + parts[0] + "** to pantry!\n\n")
			out.WriteString("[← Back to Pantry](" + realmPath + ")\n")
			return out.String()
		}
	}

	// Handle using item from pantry
	if strings.HasPrefix(path, "use/") {
		itemName := strings.TrimPrefix(path, "use/")
		UseFromPantry(itemName)
		out.WriteString("✅ Used **" + itemName + "** - moved to shopping list!\n\n")
		out.WriteString("[← Back to Pantry](" + realmPath + ")\n")
		return out.String()
	}

	// Handle buying item (moving from shopping list to pantry)
	if strings.HasPrefix(path, "buy/") {
		itemName := strings.TrimPrefix(path, "buy/")
		MarkAsBought(itemName)
		out.WriteString("✅ Bought **" + itemName + "** - added to pantry!\n\n")
		out.WriteString("[← Back to Pantry](" + realmPath + ")\n")
		return out.String()
	}

	// Handle removing from shopping list
	if strings.HasPrefix(path, "remove-shopping/") {
		itemName := strings.TrimPrefix(path, "remove-shopping/")
		RemoveFromShoppingList(itemName)
		out.WriteString("✅ Removed **" + itemName + "** from shopping list!\n\n")
		out.WriteString("[← Back to Pantry](" + realmPath + ")\n")
		return out.String()
	}

	// Handle removing from pantry
	if strings.HasPrefix(path, "remove-pantry/") {
		itemName := strings.TrimPrefix(path, "remove-pantry/")
		RemoveFromPantry(itemName)
		out.WriteString("✅ Removed **" + itemName + "** from pantry!\n\n")
		out.WriteString("[← Back to Pantry](" + realmPath + ")\n")
		return out.String()
	}

	// Main pantry view
	out.WriteString("## 🥫 Community Pantry\n\n")

	if len(communityPantry) == 0 {
		out.WriteString("*The pantry is empty.*\n\n")
	} else {
		out.WriteString("**Current Stock:** (" + intToString(len(communityPantry)) + " items)\n\n")
		for _, item := range communityPantry {
			out.WriteString(ufmt.Sprintf("• **%s** (*%s*) - [Use It](%s:use/%s) | [Remove](%s:remove-pantry/%s)\n", 
				item.Name, item.Quantity, realmPath, item.Name, realmPath, item.Name))
		}
		out.WriteString("\n")
	}

	out.WriteString("---\n\n")

	// Shopping List section
	out.WriteString("## 🛒 Shopping List\n\n")

	if len(communityShoppingList) == 0 {
		out.WriteString("*Shopping list is empty. All stocked up!*\n\n")
	} else {
		out.WriteString("**Need to Buy:** (" + intToString(len(communityShoppingList)) + " items)\n\n")
		for _, item := range communityShoppingList {
			out.WriteString(ufmt.Sprintf("• **%s** (*%s*) - [✓ Bought It](%s:buy/%s) | [Remove](%s:remove-shopping/%s)\n", 
				item.Name, item.Quantity, realmPath, item.Name, realmPath, item.Name))
		}
		out.WriteString("\n")
	}

	out.WriteString("---\n\n")

	// Quick add section
	out.WriteString("## ➕ Quick Add Items\n\n")
	out.WriteString("**Add common items:**\n\n")

	quickItems := []struct{ name, quantity string }{
		{"Milk", "1 gallon"},
		{"Bread", "1 loaf"},
		{"Eggs", "12 count"},
		{"Coffee", "1 bag"},
		{"Chicken", "2 lbs"},
		{"Rice", "2 lbs"},
	}

	out.WriteString("<gno-columns>\n\n")
	for i, item := range quickItems {
		out.WriteString(ufmt.Sprintf("[+ %s](%s:add/%s/%s)", item.name, realmPath, item.name, item.quantity))
		if i < len(quickItems)-1 {
			out.WriteString(" | ")
		}
	}
	out.WriteString("\n\n</gno-columns>\n\n")

	out.WriteString("---\n\n")
	out.WriteString("*💡 Tip: Click 'Use It' on pantry items to move them to shopping list. Click 'Bought It' to move items back to pantry.*\n")

	return out.String()
}

