// Helper to convert int to string
func intToString(n int) string {
	if n == 0 {
		return "0"
	}

	negative := n < 0
	if negative {
		n = -n
	}

	result := ""
	for n > 0 {
		result = string(rune('0' + n%10)) + result
		n /= 10
	}

	if negative {
		result = "-" + result
	}

	return result
}

